INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7346','0','cms','item','update','2101','948','','宣传处','修改内容 | 陈波','/index.php/cms/item-list-category-948-page-1','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?model=article&id=2101&verified=1\',\n  \'cid\' => \'948\',\n  \'title\' => \'陈波\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2021-07-14 10:51:38\',\n  \'sub_title\' => \'主任医师\',\n  \'author\' => \'\',\n  \'source\' => \'\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'https://www.ycsyy.com/attachment/cms/item/2021_07/14_10/f95be52ea92fb1d8.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，主任医师，南通大学医学院兼职教授，本科学历。从事感染科专业三十多年，熟练掌握感染性疾病的诊治，具有丰富临床经验，擅长慢性肝炎抗病毒治疗及耐药的处理研究，擅长慢性肝炎，肝硬化，肝癌，各种肝脏功能异常的诊疗。对发热待查，腹水待查，出血热等疑难杂症有深入研究。掌握人工肝腹水浓缩回输等技术。发表20多篇医学论文，其中国家级杂志2篇，SCI论文1篇。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，主任医师，南通大学医学院兼职教授，本科学历。从事感染科专业三十多年，熟练掌握感染性疾病的诊治，具有丰富临床经验，擅长慢性肝炎抗病毒治疗及耐药的处理研究，擅长慢性肝炎，肝硬化，肝癌，各种肝脏功能异常的诊疗。对发热待查，腹水待查，出血热等疑难杂症有深入研究。掌握人工肝腹水浓缩回输等技术。发表20多篇医学论文，其中国家级杂志2篇，SCI论文1篇。\',\n  ),\n  \'editer\' => \'\',\n  \'list_order\' => \'2021-07-14 10:51:38\',\n  \'views\' => 389,\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'慢乙肝、肝硬化腹水、肝癌、伤寒、出血热、重症肝炎、发热待查、腹水待查的诊断与治疗，对慢性乙型肝炎抗病毒治疗及耐药后的处理有深入的研究。\',\n  \'model\' => \'article\',\n  \'verified\' => \'1\',\n  \'id\' => \'2101\',\n  \'iid\' => \'2101\',\n  \'page\' => \'1\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-list?model=article&cid=834\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'update\',\n  \'attachment_hash\' => \'527e589b8ab905bc\',\n  \'verifier\' => \'admin5\',\n)','1683508052');
