INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7389','0','cms','item','add','0','859','','宣传处','添加内容 | 盐城市第一人民医院口腔种植牙公示','/index.php/cms/item-list-category-859-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院口腔种植牙公示\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'source\' => \'财务处\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';line-height:32.0pt\"><span style=\"font-size:16.0pt;\r\nfont-family:方正仿宋_GBK;color:black\">根据盐医保函〔</span><span style=\"font-size:16.0pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';color:black\">2023</span><span style=\"font-size:16.0pt;font-family:方正仿宋_GBK;color:black\">〕</span><span style=\"font-size:16.0pt;font-family:\\\'Times New Roman\\\',\\\'serif\\\';\r\ncolor:black\">15</span><span style=\"font-size:16.0pt;font-family:方正仿宋_GBK;\r\ncolor:black\">号《</span><span style=\"font-size:16.0pt;font-family:方正仿宋_GBK;\r\ncolor:black\">关于加强口腔种植类医疗服务价格管理工作的通知</span><span style=\"font-size:16.0pt;\r\nfont-family:方正仿宋_GBK;color:black\">》，我院在不突破市单颗常规种植牙价格调控目标和实际总价以及省单颗常规种植牙通科医疗服务费用的前提下，确定本院的单颗常规种植牙医疗服务费用明细。现将有关材料予以公示。</span></p>\r\n<div><img src=\"/ewebeditor/sysimage/icon16/pdf.gif\" border=\"0\"><a href=\"/ewebeditor/uploadfile/file/2023/05/15/20230515151336361001.pdf\" target=\"_blank\">一、盐医保发〔2023〕30号 盐城市医疗保障局 盐城市