INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('7392','0','cms','item','update','3899','858','','宣传处','修改内容 | 盐城市卫生健康委部分直属事业单位2023年公开招聘高层次人才公告','/index.php/cms/item-list-category-858-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?model=article&id=3899&verified=1\',\n  \'cid\' => \'858\',\n  \'title\' => \'盐城市卫生健康委部分直属事业单位2023年公开招聘高层次人才公告\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2023-05-17 08:24:48\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'source\' => \'人事处\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'/ewebeditor/sysimage/icon16/xls.gif\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'为进一步加强卫生人才队伍建设，盐城市卫生健康委员会部分直属事业单位面向社会公开招聘高层次人才24名。根据《江苏省事业单位公开招聘人员办法》（苏办发﹝2020﹞9号）等规定，现将有关事项公告如下：一、招聘岗位本次招聘相关信息在盐城市人力资源和社会保障局网（htt\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">为进一步加强卫生人才队伍建设，盐城市卫生健康委员会部分直属事业单位面向社会公开招聘高层次人才</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">24</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">名。根据《江苏省事业单位公开招聘人员办法》（苏办发﹝</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2020</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">﹞</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">9</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">号）等规定，现将有关事项公告如下：</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333;background:white\">一、招聘岗位</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">本次招聘相关信息在盐城市人力资源和社会保障局网（</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">http://jsychrss.yancheng.gov.cn</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">）、盐城市卫生健康委员会网（</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">http://wsj.yancheng.gov.cn</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">）及各招聘单位网站上发布，具体的招聘单位、招聘岗位、数量、资格条件等详见《盐城市卫生健康委部分直属事业单位</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年公开招聘高层次人才岗位表》（附件</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">1</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">，以下简称《招聘岗位表》）。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333;background:white\">二、招聘</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">条件</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333;background:white\">（一）应聘人员应具备以下条件：</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">1</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．具有中华人民共和国国籍，拥护中国共产党的领导和社会主义制度，遵守中华人民共和国宪法和法律；</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．遵纪守法，品行端正，团结同志，廉洁奉公，具有良好的职业道德；</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">3</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．身心健康，能适应岗位要求；</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">4</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．具备岗位要求的其他资格条件（详见《招聘岗位表》），资格条件中“专业”的设置参考目录为《江苏省</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年度考试录用公务员专业参考目录》，部分未列入专业参考目录的专业以高等学校实际开设的为准；</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">5</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．年龄以《招聘岗位表》中写明的为准（如</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">35</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">周岁及以下，即</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">1987</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">5</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">月</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">18</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">日及以后出生），年龄计算方法不变；《招聘岗位表》中有工作经历要求的，其工作经历计算截止时间均为</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">5</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">月</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">31</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">日；</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333;background:white\">（二）有下列情形之一的，不得应聘：</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">1</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．现役军人和普通高校在读非</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年毕业生；</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．尚未解除纪律处分或者正在接受纪律审查的人员、刑事处罚期限未满或者涉嫌违法犯罪正在接受调查的人员；</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">3</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．应聘人员与事业单位工作人员有夫妻关系、直系血亲关系、三代以内旁系血亲关系、近姻亲及其他须回避的亲属关系的，不得应聘具有直接上下级领导关系的管理岗位，以及《事业单位人事管理回避规定》要求回避的岗位；</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">4</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．依据《江苏省事业单位公开招聘人员办法》（苏办发〔</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2020</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">〕</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">9</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">号）被聘用到江苏省地方各类事业单位服务期未满的人员；</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">5</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．国家和省另有规定不得应聘的人员。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333;background:white\">三、</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#111111;background:white\">招聘程序</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333;background:\r\nwhite\">1</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．<span style=\"background:white\">报名</span>时间<span style=\"background:white\">、方式</span></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">报名时间：</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">5</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">月</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">18</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">日—</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">5</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">月</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">26</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">日。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">报名方式：采取网络报名（电子邮件）的方式，各招聘单位报名邮箱详见附件</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">1</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">。符合相应报名条件的应聘人员，下载并填写《盐城市卫生健康委部分直属事业单位</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年公开招聘高层次人才报名表》（附件</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">）。报名人员通过电子邮件形式发送至相关单位报名邮箱，邮件以“应聘岗位</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">+</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">应聘人姓名”命名，按照报名所需资料要求将原件正面扫描制定为</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">PDF</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">文件以附件形式发送。应聘人员须在规定时间按要求进行电子邮件报名，逾期不再接受报名，所需报名材料请勿以复印件扫描，否则视为无效材料。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333;background:\r\nwhite\">2</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．<span style=\"background:white\">报名注意事项及资格审查</span></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">（</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">1</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">）每位应聘人员只能选择一个岗位进行报名。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">（</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">）报考者报名时所需材料：①《盐城市卫生健康委部分直属事业单位</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年公开招聘高层次人才报名表》（附件</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">）；②本人近期免冠正面二寸（</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">35</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">×</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">45</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">毫米）证件照，</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">jpg</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">格式，大小为</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">20kb</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">以下；③身份证扫描件；④学历、学位证书、专业方向证明等扫描件，其中未取得毕业证书的</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年毕业生须提供所在院校出具的省教育厅统一印制的《毕业生双向选择就业推荐表》；⑤其他材料：岗位报名需要的执业资格证书、专业技术资格证书、工作经历证明等扫描件。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">（</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">3</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">）资格初审。应聘人员网上提交报名信息后，招聘单位根据报名信息进行审核并及时反馈。应聘人员在报名结束前没有收到审核结果的，应联系招聘单位了解审核情况。应聘人员如对初审结果有异议，可从收到审核结果之日起</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">个工作日内向招聘单位陈述申辩，逾期视为没有异议。应聘人员因个人信息填写不完整、不准确导致资格初审未通过等导致报名不成功的，均视为报名无效，由应聘人员本人承担责任。应聘人员须使用在有效期内的第二代居民身份证进行报名，报名与考察使用的身份证必须一致。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">（</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">4</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">）应聘人员对所填写的报名信息和提交报名材料的真实性、准确性、完整性、有效性负责。除本公告另有说明，或者国家、省另有规定外，信息真实性以报名时的实际情况为准。资格审查工作贯穿于招聘工作全过程。凡弄虚作假的，一经查实，即取消应聘资格。考察前将进行现场资格复审（复审时提供所有报名材料原件，在职人员还必须提供现工作单位同意报考证明）。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">3</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．实施办法</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">本次招聘采取直接考察的形式进行，考察侧重于应聘人员的思想政治表现、道德品质、与应聘岗位相关的业务能力和工作实绩，核实应聘人员资格条件等，考察成绩即为总成绩，采用百分制计算。成绩均保留两位小数，第三位小数按“四舍五入”办法处理。考察具体时间、地点另行通知。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">4</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．考察、体检</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">考察结束后，根据考察总成绩，按招聘岗位拟招聘人数</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">1</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">：</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">1</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">的比例从高分到低分确定参加体检人选。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">若总成绩相同，对成绩相同的人员组织加试（加试时间、方式、地点另行通知）。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">体检标准按照最新的《公务员录用体检通用标准（试行）》执行。体检结束后，招聘单位对体检合格人员组织考察（制定考察方案，明确考察的内容、方法等，考察标准参照盐城市公务员招录考察相关要求进行），并根据考察和体检结果，研究确定拟聘用人员名单。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">因个人放弃、体检、考察不合格等原因出现招聘岗位空缺时，在该岗位的考察合格人员中，按总成绩从高分到低分依次递补（成绩相同取舍办法同前）。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">5</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">．公示、聘用</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">拟聘用人员名单将在市人力资源和社会保障局、市卫生健康委网站上公示</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">7</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">个工作日。公示内容包括招聘单位、岗位名称、拟聘用人员姓名、学历、专业、毕业院校、现工作单位、考察的总成绩、排名等。拟聘用人员名单公示后，应聘人员如无正当理由放弃聘用资格的，招聘单位可以在名单公示结束后的</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">1</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年内取消其再次应聘本单位的资格。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">对公示无异议人员，招聘单位按规定为其办理有关聘用手续，与拟聘用人员签订聘用合同，约定试用期。试用期满考核合格，予以定岗定级；考核不合格者，取消聘用资格，解除聘用合同。拟聘用人员与招聘单位订立</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">3</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年以上（含试用期）聘用合同的，除依法依规解除聘用合同外，应当在招聘单位最低服务</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">3</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年（含试用期）。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年毕业生于</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">8</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">月</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">31</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">日前提供毕业证书、学位证书；国（境）外同期毕业人员，取得学历（学位）证书的日期可适当放宽，但须在</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">2023</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">年</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">12</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">月</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">31</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">日前完成教育部留学服务中心学历认证，逾期不能提供的，取消聘用资格。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">四、招聘纪律</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">应聘人员在报名、考察、体检过程中或聘用后查实存在违纪违规行为的，按照《事业单位公开招聘违纪违规行为处理规定》（中华人民共和国人力资源和社会保障部第</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">35</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">号令），视具体情形和情节轻重，分别给予取消报考资格、取消考察成绩、不予聘用、记入诚信档案库等处理，构成犯罪的依法追究刑事责任。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">招聘单位和招聘工作人员有违纪违规行为的，按照《事业单位公开招聘违纪违规行为处理规定》（中华人民共和国人力资源和社会保障部第</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">35</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">号令）进行处理。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">五、咨询、监督</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">招聘工作坚持公开、公平、公正的原则，全过程接受纪检监察部门的监督。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">咨询电话：</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">0515-66696806</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">（盐城市第一人民医院人事处）</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:\r\ninter-ideograph;text-indent:32.25pt;line-height:24.0pt;background:white\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312;color:#333333\">0515-882456