INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2914','3738','1','','<!--#p8_attach#-->/cms/item/2022_09/02_17/a0eb951840debfb3.jpg.thumb.jpg','现任盐城市第一人民医院重症医学科副主任。熟练掌握心肺复苏、多发性创伤、MODS、ARDS、急性中毒等各类危重症救治。近年来在核心期刊发表论文10余篇，其中SCI5篇，中华级论文1篇。开展的科研项目获2012年盐城市科学技术进步二等奖1项、2017年盐城市新技术引进奖二等奖1项、2018年盐城市新技术引进奖二等奖1项，目前还承担市级课题3项。','172.16.1.2','172.16.1.2','1662110402','<p align=\"\" style=\"margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: justify; line-height: 2; font-family: 宋体; font-size: 10.5pt;\">现任盐城市第一人民医院重症医学科副主任。熟练掌握心肺复苏、多发性创伤、MODS、ARDS、急性中毒等各类危重症救治。近年来在核心期刊发表论文10余篇，其中SCI5篇，中华级论文1篇。开展的科研项目获2012年盐城市科学技术进步二等奖1项、2017年盐城市新技术引进奖二等奖1项、2018年盐城市新技术引进奖二等奖1项，目前还承担市级课题3项。</p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2915','3739','1','','<!--#p8_attach#-->/cms/item/2022_09/05_14/041dfd4c3a04941e.jpg.thumb.jpg','5岁女童佳佳从幼儿园回来后直喊胸部痛，家长检查一番，见没有外伤就没在意。没想到过了几天孩子又喊胸痛，带孩子到医院一检查，结果令全家人大跌眼镜……怀二胎与大宝分享补品5岁女童提早发育了“孙女洗澡时又说胸部疼痛，会不会长什么东西？”几天前，60多岁的佳佳奶奶','172.16.1.2','172.16.1.2','1648828800','<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">5</span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">岁女童佳佳从幼儿园回来后直喊胸部痛，家长检查一番，见没有外伤就没在意。没想到过了几天孩子又喊胸痛，带孩子到医院一检查，结果令全家人大跌眼镜……</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">怀二胎与大宝分享补品</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">5</span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">岁女童提早发育了</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">“孙女洗澡时又说胸部疼痛，会不会长什么东西？”几天前，<span>60</span>多岁的佳佳奶奶带着佳佳踏进盐城市儿童医院儿科诊室，焦急地询问医生。</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">按惯例，医生详细询问了孩子近段时间的饮食等情况。佳佳奶奶说，自从佳佳妈妈怀上二胎后，看佳佳身体比较弱，就在进补时顺带给她也增加营养，于是这几个月<strong>佳佳跟着妈妈鸡鸭鱼肉轮着吃，还吃了燕窝、海参等补品，佳佳的体重足足增加了近<span>5</span>公斤</strong>。</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">医生检查了佳佳的身体，分析了骨龄、<span>B</span>超、性激素等检查结果后，告诉佳佳奶奶，<strong>孩子胸部不是长东西，而是有点发育了</strong>。</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">“她才<span>5</span>岁，怎么可能发育呢？”</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">医生解释说，儿童饮食，营养均衡是关键。比如<strong><span>1</span></strong><strong>岁以上的孩子每天吃<span>50</span>克肉、<span>1</span>个鸡蛋，<span>1</span>拳头主食，喝<span>500</span>毫升奶</strong>，就可以基本满足身高生长和智力发育的需要。“<strong>过多高蛋白高脂肪高碳水化合物食物都会使宝宝长胖，甚至性早熟。至于补品，宝宝更不能吃</strong>，否则会增加性早熟风险。”</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">家长应了解儿童生长发育特点</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">不可“拔苗助长”</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">“性早熟是指女童在<span>8</span>岁前、男童在<span>9</span>岁前出现第二性征，即女孩表现为乳房开始发育，男孩子表现为睾丸增大。”吴美珍介绍说，性早熟对孩子来说影响最大的是身高。身高开始加速增长，达到身高增长高峰，并持续<span>2.5~3</span>年。<strong>孩子出现性早熟，生长高峰提前，那身高发育停止增长提早，孩子成年后的身高也比别的孩子矮小</strong>。</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">除了不可控的体质性性早熟外，肥胖的孩子比正常体重的孩子更容易发生性早熟。</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><strong><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">经常食用高蛋白的儿童</span></strong><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">，可能会诱使性激素分泌，增加性早熟风险，还会促进骨龄的快速进展，让孩子丢失了生长潜力；而各种零食、饮料、油炸食品、膨化食品等高热量食物摄入，导致孩子营养过剩，变得肥胖。</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><strong><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">部分保健品会促进性发育</span></strong><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">，导致孩子雌激素水平升高，虽然短时间内孩子看起来的确长高了，但孩子被“催熟”，缩短了长高的整体时间，到真正该长高的年纪反而不长了。</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><strong><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">部分成人护肤品中含有激素成分</span></strong><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">，也是导致孩子出现性早熟的诱因。</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">医生表示，要想避免孩子性早熟，就要让孩子<strong>均衡饮食、适当锻炼；避免随意给孩子服用保健品</strong>。即使要服用，也要先咨询医生。“孩子的生长速度有自己的规律，家长不要‘拔苗助长’。同时，\r\n家长要注意不要让孩子过早接触各类成人护肤品和化妆品。”</span></p>\r\n<p style=\"line-height: 18pt; font-family:;\"><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">此外，家长还应系统了解儿童生长发育特点，定期评估孩子的生长发育情况，如绘制生长发育曲线图、<strong>定期监测骨龄（<span>6</span>个月至<span>1</span>年<span>1</span>次）</strong>。<strong>当女孩<span>8</span>岁之前出现乳房发育或<span>10</span>岁前初潮、男孩<span>9</span>岁前出现睾丸发育</strong>，或者出现没有原因的身高蹿长（年生长速率大于等于<span>10</span>厘米），需尽早到相关专科就诊。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2922','3746','1','','<!--#p8_attach#-->/cms/item/2022_09/06_10/67adfadcb4e78f46.jpg.thumb.jpg','最近这段时间，王大爷（化名）一侧的腰背陆陆续续长了一串密集的水泡“长蛇”，像触电一样一阵阵刺痛。起初王大爷并不在意，可那种刺痛感一直伴随着他，后来越发疼痛，让他寝食难安。用了各种民间土方也无法缓解疼痛，最后在老伴的陪同下，王大爷到盐城市第一人民医院就','172.16.1.2','172.16.1.2','1651802976','<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">最近这段时间，王大爷（化名）一侧的腰背陆陆续续长了一串密集的水泡“长蛇”，像触电一样一阵阵刺痛。起初王大爷并不在意，可那种刺痛感一直伴随着他，后来越发疼痛，让他寝食难安。用了各种民间土方也无法缓解疼痛，最后在老伴的陪同下，王大爷到盐城市第一人民医院就诊。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">水泡密密麻麻，民间土方越治越严重</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">“医生，我是怎么了？都说民间土方能管用，我怎么还是痛？”王大爷小心翼翼地掀起衣服，腰上依稀可见还未消退的水泡，密密麻麻地在皮肤上延伸，像蛇一样。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">“这是带状疱疹啊！”医生说，“这种病在中老年群体中比较常见，带状疱疹经常呈带状地长在腰腹部，一般不超过正中线，有些地方就流传了一些民间土方，非但不能治愈，还会延误治疗，使病情恶化。”</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">延误治疗，可能带来长久神经痛</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">医生表示，有些患者的带状疱疹可以自愈，所以就有人以为长点疹子，过一段时间就消退了，亦或是想着找土方子用用，不愿去医院治疗。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">“这是非常错误的想法。延误治疗会使病情加重，并增加带状疱疹后遗症的概率，导致带状疱疹后遗神经痛。”</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">带状疱疹后遗神经痛有多痛？</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">在接诊的患者中，很多人后遗神经痛至少持续</span><span>30</span><span style=\"font-family: 宋体;\">天，有些疼痛持续</span><span>90</span><span style=\"font-family: 宋体;\">天甚至长达</span><span>10</span><span style=\"font-family: 宋体;\">年，影响夜间睡眠，进而影响生活和工作。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">而带状疱疹后遗神经痛可能以一种疼痛为主或多样疼痛并存，疼痛可表现为：烧灼样、电击样、刀割样、针刺样、撕裂样。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span>50</span><span style=\"font-family: 宋体;\">岁以上易发，这个预防方法可以有！</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">医生提醒，</span><span>50</span><span style=\"font-family: 宋体;\">岁以上的中老年为带状疱疹的易发人群，年龄越大越容易患病，病情也更严重。即使身体健康，</span><span>50</span><span style=\"font-family: 宋体;\">岁以后患带状疱疹的风险也会陡然增加。在中国，</span><span>50</span><span style=\"font-family: 宋体;\">岁以上人群每年新发带状疱疹约</span><span>156</span><span style=\"font-family: 宋体;\">万人。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">而对于糖尿病、慢性肾病、心血管疾病、慢阻肺、类风湿关节炎等慢性病患者以及免疫功能低下人群，患带状疱疹的风险也会随之增加。如果出现一侧皮肤的丘疱疹，伴随明显的阵发性疼痛，建议尽早就医排除带状疱疹可能。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">目前临床对于带状疱疹及其并发症的治疗主要是抗病毒、营养神经及镇痛治疗，但中间过程还是非常痛苦的。因此，医生再三强调，及时尽早接种疫苗是有效的预防控制手段。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">重组带状疱疹疫苗可以有效预防</span><span>50</span><span style=\"font-family: 宋体;\">岁及以上成人带状疱疹。</span><span>III</span><span style=\"font-family: 宋体;\">期临床研究数据显示，带状疱疹疫苗可以对</span><span>50</span><span style=\"font-family: 宋体;\">岁及以上各年龄组的人群提供超过</span><span>90%</span><span style=\"font-family: 宋体;\">的疫苗保护效力，同时对于</span><span>50</span><span style=\"font-family: 宋体;\">岁及以上合并基础疾病患者也和总体人群保护效力相一致。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">所有年龄都可以接种吗？</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span>50</span><span style=\"font-family: 宋体;\">岁以上就可以接种。建议如果符合接种条件的话，应该尽早去接种带状疱疹疫苗，减少发作，避免出现不必要的痛苦。但目前对小于</span><span>50</span><span style=\"font-family: 宋体;\">岁人群尚未开放。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">得过带状疱疹是否也有必要打？</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">有研究证实，带状疱疹发病确实会激发并增强机体针对带状疱疹病毒的特异性免疫，但是这种状态一般只能持续数年时间。为了尽可能避免复发，还是有必要接种带状疱疹疫苗的。但如果在带状疱疹发作期，要等急性期过后再进行接种。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">疫苗接种是安全的吗？</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span style=\"font-family: 宋体;\">目前在国内上市的带状疱疹疫苗本身是通过</span><span>DNA</span><span style=\"font-family: 宋体;\">重组技术形成的病毒的某个蛋白，本身并不含有病毒，但它们可以诱导机体出现针对病毒的特异性免疫反应，从而发挥预防病毒感染的作用，所以带状疱疹疫苗安全性好，可以放心接种。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span>Tips</span><span style=\"font-family: 宋体;\">：个别接种后可能出现注射局部发红、肿胀、疼痛等情况或者肌痛、疲乏、头痛、寒颤、发热、胃肠道症状等全身反应，一般不需特殊处理，中位持续时间为</span><span>1-3</span><span style=\"font-family: 宋体;\">天，可自行缓解。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; text-align: justify; text-indent: 21pt; font-family:;\"><span>&nbsp;</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2938','3763','1','','<!--#p8_attach#-->/cms/item/2022_09/06_16/10aebe5c9a3546fe.jpg.thumb.jpg','&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;“我的门关了吗?”　　“我的煤气灶关了吗?”　　“我刚刚想做什么呢?”　　当身边的老年人出现常忘事的症状需警惕，当心阿尔茨海默病!　　阿尔茨海默病又称老年痴呆,是一种进行性发展的神经系统退行性疾病,多发于65岁以上人群,最明显的症状是','172.16.1.2','172.16.1.2','1652976000','<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>&ensp;&ensp;&ensp;&ensp;“我的门关了吗<span>?</span>”</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　“我的煤气灶关了吗<span>?</span>”</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　“我刚刚想做什么呢<span>?</span>”</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　当身边的老年人出现常忘事的症状需警惕，当心阿尔茨海默病<span>!</span></span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　阿尔茨海默病又称老年痴呆<span>,</span>是一种进行性发展的神经系统退行性疾病<span>,</span>多发于<span>65</span>岁以上人群<span>,</span>最明显的症状是记忆力下降</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　记性不好<span>=</span>阿尔茨海默病<span>?</span></span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　记忆力下降是阿尔茨海默病最明显的症状<span>,</span>但记忆力不好<span>,</span>并不等于患有阿尔茨海默病。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　由此，如何辨别正常记忆老化与阿尔茨海默病的记忆力下降，显得尤为重要。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　除了记忆力衰退外，还有失语、失认等其它症状，阿尔茨海默病十大危险信号</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>01</span>、记忆力日渐衰退，影响日常起居</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>02</span>、处理熟悉的事情出现困难</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>03</span>、语言表达出现困难</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>04</span>、对时间、地点、人物日渐模糊</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>05</span>、判断力日渐减弱</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>06</span>、理解力或合理安排失误能力下降</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>07</span>、常把东西乱放在不适当的地方</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>08</span>、情绪表现不稳，行为较先前异常</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>09</span>、性格出现转变</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>10</span>、失去做事的主动性</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　当老年人出现以上症状时，应及早至医院就诊及早干预治疗，早识别、早治疗，延缓病情发展。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　目前，引起阿尔茨海默病的病因尚不明确。相关研究表明可能与家族史、头部外伤、躯体疾病、甲状腺病、病毒感染等因素有关。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　虽然对于该病有一些有效的治疗方法，也有药物可使用，例如多奈哌齐、利斯的明、美金刚等。合并有精神症状、情绪问题者，可采用联合抗精神病药、抗抑郁药及抗焦虑药物。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　但需要强调的是，目前没有根治的办法，现有的治疗也只能尽量延缓阿尔茨海默症病程的恶化发展。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　随着病程的发展，阿尔茨海默症患者生活逐渐不能自理，需要安排专人照料，对于家庭是个沉重的负担，所以应早识别、早治疗，改善认知功能，提高日常生活能力，延缓病情进展，改善生活质量。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　早期预防攻略</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　鉴于目前没有理想的治愈方法，所以早期预防同样不容忽视，可以做到以下<span>4</span>点：</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>01</span>规律生活</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　阿尔茨海默病的很多危险因素与不良生活习惯有关，所以规律作息，戒烟戒酒，保持良好的用脑习惯，避免脑外伤和高血压、糖尿病等慢性病。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>02</span>勤于用脑</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　老年人可以走出家门多与人接触，通过多参加社会活动、培养日常兴趣爱好，如下棋、练书法等方式锻炼大脑。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>03</span>均衡营养</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　食品多样化，多食用三高<span>(</span>高蛋白、高纤维素、高维生素<span>)</span>和三低<span>(</span>低脂肪、低糖、低盐<span>)</span>食物，一日三餐确保摄取足够的人体必需营养成分。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　<span>04</span>适度锻炼</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\";\'>　　适度锻炼可以促进血液循环，延缓大脑衰老。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-size: 12pt;\">&nbsp;</span></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2939','3764','1','','<!--#p8_attach#-->/cms/item/2022_09/06_16/8983283fc8e160a0.jpg.thumb.jpg','&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;一、无偿献血的作用　　生命离不开血液，输血是抢救危重病人的一种特殊医疗措施。在目前人造血液尚不能完全代替人体血液之时，临床用血只能靠健康人体捐献。过去，临床用血来自个体卖血，他们为了卖更多钱，往往弄虚作假，隐瞒病史，甚至冒名顶','172.16.1.2','172.16.1.2','1654185600','<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>&ensp;&ensp;&ensp;&ensp;一、无偿献血的作用</span></strong></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　生命离不开血液，输血是抢救危重病人的一种特殊医疗措施。在目前人造血液尚不能完全代替人体血液之时，临床用血只能靠健康人体捐献。过去，临床用血来自个体卖血，他们为了卖更多钱，往往弄虚作假，隐瞒病史，甚至冒名顶替，重复多次卖血，严重影响了血液质量和供血者的自身健康，有的患者通过输血还染上了疾病，造成了经血液途径传播的疾病的进一步传播。因此只有实行无偿献血制度，坚决制止买血卖血行为，才能保证医疗用血的质量，才能遏制严重影响人民身体健康和社会安定的艾滋病、肝炎等经血液传播疾病的传播，减少医疗费用支出，最大限度地保护供血者和受血者的身体健康。无偿献血是团结友爱、无私奉献精神的具体表现，也是一种互救互助的方式，你今天献血救助他人，以后一旦自己或亲属得病之时，又会得到他人的帮助。这也充分体现了社会主义社会人与人之间团结互助和人道友爱的精神。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　二、献血会影响健康吗<span lang=\"EN-US\">?</span></span></strong></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　据科学测定：正常人体血液总量占体重的<span lang=\"EN-US\">8%</span>左右，一个<span lang=\"EN-US\">50</span>公斤体重的人，约有血液<span lang=\"EN-US\">4000ml</span>，在一般情况下，这些血液并不都参与血液循环<span lang=\"EN-US\">.</span>每次献血<span lang=\"EN-US\">200</span>—<span lang=\"EN-US\">400</span>毫升，只占全身血量的<span lang=\"EN-US\">5%-10%</span>左右，人体中平时只有<span lang=\"EN-US\">80%</span>的血液在全身循环，还有<span lang=\"EN-US\">20%(</span>约有<span lang=\"EN-US\">800</span>毫升<span lang=\"EN-US\">)</span>的血液是贮存于肝脏、脾脏和皮下毛细血管贮备而不用的，人们习惯地把这称作人体的“小血库”。当人体从事剧烈活动或少量失血时，贮血库中的血液会立即释放出来，参与血液循环，以维持人体正常的生理功能。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　有人担心献血会影响健康，其实这种担心是多余的。首先，献血<span lang=\"EN-US\">200</span>毫升不到全身血量的二十分之一，是不会影响血液的正常生理功能的。其次，人体自身具有很强的调节功能，献血后<span lang=\"EN-US\">1-2</span>小时血容量就会得到恢复，各种血液成份可在两周内达到原来的水平。第三，人体的血液都在新陈代谢，即使不献血，血细胞也在不断衰老死亡，被新生的血细胞代替。献血后，人体造血功能更加旺盛，会加速血细胞的生成。从而使血液维持相对恒定。第四，献血前要做好严格的身体检查和化验，合格者方能献血。第五，现代医学实践证明，适量献血对冠心病、高血压等心脑血管疾病的预防都有一定好处。适量献血，可促进人体的新陈代谢，增强免疫力的抗病能力，还可刺激人体骨髓的造血器官，使其始终保持青春时期一样旺盛的造血状态，收到延年益寿的效果，并能防止动脉硬化等心脑血管疾病。据报道，有人对<span lang=\"EN-US\">66</span>岁以上的<span lang=\"EN-US\">332</span>名曾献血者与同条件的<span lang=\"EN-US\">399</span>名未曾献血者存活率作了比较，结果曾献血者存活率显著高于未献血者，献血总量较多的存活年龄还较长。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　血液本身具有旺盛的新陈代谢能力，人体每时每刻都有大量的血细胞在衰老死亡，献血后反而会刺激人体造血功能更加旺盛，加速细胞的生成，促进血液的新陈代谢，以适应机体的需要。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　人体具有很强的自我调节能力，在规定的时间间隔内一次献血<span lang=\"EN-US\">200</span>—<span lang=\"EN-US\">400ml</span>是无损身体健康的。</span></p>\r\n<p align=\"center\" style=\"margin: 11.3pt 0cm 7.5pt; text-align: center; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　三、国内献血者体检标准有哪些<span lang=\"EN-US\">?</span></span></strong></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　年龄：<span lang=\"EN-US\">18</span>～<span lang=\"EN-US\">55</span>周岁。体重：男<span lang=\"EN-US\">&gt;=50</span>千克，女<span lang=\"EN-US\">&gt;=45</span>千克。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　血压<span lang=\"EN-US\">12</span>～<span lang=\"EN-US\">20/8</span>～<span lang=\"EN-US\">12</span>千帕<span lang=\"EN-US\">(90</span>～<span lang=\"EN-US\">140/60</span>～<span lang=\"EN-US\">90</span>毫米汞柱<span lang=\"EN-US\">)</span>，脉压差<span lang=\"EN-US\">&gt;=4</span>千帕<span lang=\"EN-US\">(&gt;=30</span>毫米汞柱<span lang=\"EN-US\">)</span>。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　脉搏：<span lang=\"EN-US\">60</span>～<span lang=\"EN-US\">100</span>次<span lang=\"EN-US\">/</span>分，高度耐力的运动员〉<span lang=\"EN-US\">=50</span>次<span lang=\"EN-US\">/</span>分。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　体温正常。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　皮肤无黄染，无创面感染，无大面积皮肤病，浅表淋巴结无明显肿大。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　五官无严重疾病，巩膜无黄染，甲状腺不肿大。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　四肢无严重残疾，无严重功能性障碍及关节无红肿。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　胸部：心肺正常<span lang=\"EN-US\">(</span>心脏生理性杂音可视为正常<span lang=\"EN-US\">)</span>。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　腹部：腹平软，无肿块、无压痛，肝脾不肿大。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　四、输血前为什么必须检验用血者<span lang=\"EN-US\">RH(D)</span>和<span lang=\"EN-US\">ABO</span>血型<span lang=\"EN-US\">?</span></span></strong></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　因为血液必须只能同型输注，血型是以血液抗原形式表现出来的一种遗传性状。狭义地讲，血型专指红细胞抗原在个体间的差异，目前一般有意义的血型主要是指<span lang=\"EN-US\">ABO</span>和<span lang=\"EN-US\">Rh(D)</span>血型。即<span lang=\"EN-US\">A</span>型<span lang=\"EN-US\">Rh(D)</span>阴性患者只能输<span lang=\"EN-US\">A</span>型<span lang=\"EN-US\">Rh(D)</span>阴性血，<span lang=\"EN-US\">B</span>型的只能输<span lang=\"EN-US\">B</span>型<span lang=\"EN-US\">Rh(D)</span>阴性血，假如您生病或手术需要输血时，您一定要将您是<span lang=\"EN-US\">Rh(D)</span>阴性血型的情况告知医生，以便医生及早和血站联系，组织您所需要的<span lang=\"EN-US\">Rh(D)</span>阴性血源。假如您是未婚女性，请您做好计划生育以避免人工流产，若您已有流产或输血史，妊娠期务必要进行新生儿溶血病的预测检查，以防止今后新生儿溶血病的发生。<span lang=\"EN-US\"> Rh(D)</span>阴性者不能接受<span lang=\"EN-US\">Rh(D)</span>阳性者血液，因为<span lang=\"EN-US\">RH</span>阳性血液中的抗原将刺激<span lang=\"EN-US\">RH</span>阴性人体产生<span lang=\"EN-US\">RH</span>抗体。如果再次输入<span lang=\"EN-US\">RH</span>阳性血液，即可导致溶血性输血反应。但是，<span lang=\"EN-US\">RH</span>阳性者可以接受<span lang=\"EN-US\">RH</span>阴性者的血液。</span></p>\r\n<p style=\"margin: 11.3pt 0cm 7.5pt; line-height: 21pt; mso-pagination: widow-orphan;\"><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt; mso-bidi-font-family: 微软雅黑;\'>　　紧急情况时能否及时向<span lang=\"EN-US\">Rh(D)</span>阴性患者供应同型血液是一件生命攸关的大事，血站的工作者以保障每一位<span lang=\"EN-US\">Rh(D)</span>阴性患者的输血安全为己任，常年不懈地对数以万计的供血人群进行<span lang=\"EN-US\">Rh(D)</span>血型筛选，一旦筛选到<span lang=\"EN-US\">Rh(D)</span>阴性的合格血液，这袋血就立即进行特殊处理并保存在<span lang=\"EN-US\">-80</span>℃冰柜中保存以备急需。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2940','3765','1','','<!--#p8_attach#-->/cms/item/2022_09/06_16/7558d7753565e4ce.jpg.thumb.jpg','&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;胆囊炎是细菌性感染或化学性刺激引起的胆囊炎性病变，为胆囊的常见病。在腹部外科中其发病率仅次于阑尾炎。但是由于大多数人对于胆囊炎没有一定的认识，所以常常会忽略身体传递的信号。　　慢性胆囊炎的早期症状　　慢性胆囊炎症状、体征不典型','172.16.1.2','172.16.1.2','1655913600','<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>&ensp;&ensp;&ensp;&ensp;胆囊炎是细菌性感染或化学性刺激引起的胆囊炎性病变，为胆囊的常见病。在腹部外科中其发病率仅次于阑尾炎。但是由于大多数人对于胆囊炎没有一定的认识，所以常常会忽略身体传递的信号。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><strong><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　慢性胆囊炎的早期症状</span></strong></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　慢性胆囊炎症状、体征不典型。多数表现为胆源性消化不良，厌油腻食品、上腹部闷胀、嗳气、胃部灼热等，与溃疡病或慢性阑尾炎近似。有时因结石阻塞胆囊管，可呈急性发作，但当结石移动、阻塞解除，即迅速好转。</span></p>\r\n<p align=\"center\" style=\'margin: 11.3pt 0cm 7.5pt; text-align: center; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span>&nbsp;</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><strong><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　急性胆囊炎的早期症状</span></strong></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　不少急性胆囊炎患者在进油腻晚餐后半夜发病，因高脂饮食能使胆囊加强收缩，而平卧又易于小胆石滑进并嵌顿胆囊管。主要表现为右上腹持续性疼痛、阵发性加剧，可向右肩背放射<span>;</span>常伴发热、恶心呕吐，但冷战少见，黄疸轻。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><strong><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　胆囊炎的手术疗法</span></strong></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　急性胆囊炎：一般主张经<span>12</span>～<span>24-</span>小<span>-</span>时积极的内科治疗，待症状缓解再择期手术。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　慢性胆囊炎：无论有无结石，因胆囊已丧失功能，且为感染病灶，均应择期手术切除。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><strong><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　胆囊炎的日常护理</span></strong></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　<span>1</span>、胆囊炎急性发作期宜卧床并暂时禁食，使胆囊得到充分的休息，以能动解疼痛。由静脉补充营养，或给予完全胃肠外营养。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　<span>2</span>、慢性胆囊炎患者可适当参加一些体育活动，增强体质，但不可过量，因活动量过大，消耗大，需补充高能食物，这样会大大增加胆囊的负担。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　<span>3</span>、避免过度劳累及经常熬夜，因为过度疲劳是临床上胆囊炎发作的常见诱因。</span></p>\r\n<p style=\'margin: 11.3pt 0cm 7.5pt; line-height: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\'color: rgb(51, 51, 51); font-family: \"微软雅黑\",\"sans-serif\"; font-size: 13.5pt;\'>　　<span>4</span>、保持良好的心态。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span>&nbsp;</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2941','3766','1','','<!--#p8_attach#-->/cms/item/2022_09/06_16/30892040b0deeded.jpg.thumb.jpg','对很多小朋友来说，甜食永远是他们的最爱。不过，甜食虽然好吃，但是却不宜多吃哦。育儿专家告诉我们，孩子甜食吃太多，不仅危害健康，还容易引发内分泌疾病。所以，在给孩子吃甜食过程中，家长一定要谨慎。孩子吃甜食的危害1、内分泌疾病如果孩子一直过多地食用含糖量','172.16.1.2','172.16.1.2','1656950400','<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">对很多小朋友来说，甜食永远是他们的最爱。不过，甜食虽然好吃，但是却不宜多吃哦。<span><a target=\"https://www.hospitalstar.com/_blank\" href=\"http://baby.wenkang.cn/\"><span style=\"color: rgb(51, 51, 51);\"><span>育儿</span></span></a></span>专家告诉我们，孩子甜食吃太多，不仅危害<span><a target=\"https://www.hospitalstar.com/_blank\" href=\"http://baby.wenkang.cn/mmjk/hyq/ywq/jk/\"><span style=\"color: rgb(51, 51, 51);\"><span>健康</span></span></a></span>，还容易引发内分泌疾病。所以，在给孩子吃甜食过程中，家长一定要谨慎。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\"><br>\r\n</span><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">孩子吃甜食的危害</span></p>\r\n<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><strong><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">1</span></strong><strong><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">、内分泌疾病</span></strong></p>\r\n<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">如果孩子一直过多地食用含糖量很高的甜食，就会引发许多潜在的内分泌疾病。比如，孩子过多食用甜食，糖分摄入过多，血糖浓度提高，就会加重孩子体内代谢中胰岛的负担，从而诱发糖尿病。</span></p>\r\n<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><strong><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">2</span></strong><strong><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">、甜食依赖</span></strong></p>\r\n<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">由于孩子甜食吃得太多，让他的味觉发生改变，一般的食物，如口味比较清淡的食物就不再能满足他的需要了。</span></p>\r\n<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><strong><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">3</span></strong><strong><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">、精神烦躁</span></strong></p>\r\n<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">如果一直吃含糖分过多的食物，机体就会加速糖的氧化，同时消耗大量的维生素<span>B1</span>，使它供不应求。因为人体自身是不能合成维生素<span>B1</span>的，完全依靠从食物中吸收。</span></p>\r\n<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><strong><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">4</span></strong><strong><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">、加重钙负荷</span></strong></p>\r\n<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">孩子吃了过多的糖和碳水化合物，人体内的代谢过程中就会产生大量的中间产物如丙铜酸，它们会使机体呈酸中毒状态。为了维持人体酸碱平衡，体内的碱性物质钙、镁、钠就要参加中和作用，使孩子体内的钙质减少。</span></p>\r\n<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><strong><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">5</span></strong><strong><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">、影响睡眠</span></strong></p>\r\n<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">研究发现，甜食会降低人体的免疫力。在正常情况下，人体血液中的一个白血球的平均吞噬病菌的能力为<span>14</span>，吃了一个甜馒头后会变成<span>10</span>，吃了一块含糖的点心后，会变成<span>5</span>，吃了一块奶油巧克力后会变成<span>2</span>。可见，甜食对免疫力是有一定影响的。</span></p>\r\n<p style=\'line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体; font-size: 10.5pt;\">孩子吃甜食的危害有哪些？看完上面的介绍，相信各位妈妈，都有了一定的了解吧。虽然甜食是孩子的最爱，不过作为家长，一定要采取理性的育儿方式，不要因为孩子喜欢吃，就一直不限制，这样，可是对孩子健康很不负责任的表现哦。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span>&nbsp;</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2942','3767','1','','<!--#p8_attach#-->/cms/item/2022_09/06_16/2788c0ca8ccdaceb.jpg.thumb.jpg','&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;一、在睡觉的前两个小时之类不要进食，这样会让我们体内的食物堆积，形成脂肪，很容易发胖。我们在睡眠的时候，我们肠胃都已经进入了休眠状态，而那些在临睡之前进食的食物只会在肠胃里堆积，这些都是引发肠胃疾病的罪魁祸首。　　二、切忌在入','172.16.1.2','172.16.1.2','1657987200','<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">&ensp;&ensp;&ensp;&ensp;一、在<span><a target=\"https://www.hospitalstar.com/_blank\" href=\"http://www.99.com.cn/special/2011/moya/\"><span style=\"color: rgb(51, 51, 51);\"><span>睡觉</span></span></a></span>的前两个小时之类不要进食，这样会让我们体内的<span><a target=\"https://www.hospitalstar.com/_blank\" href=\"http://www.99.com.cn/special/yangganhugan.htm\"><span style=\"color: rgb(51, 51, 51);\"><span>食物</span></span></a></span>堆积，形成脂肪，很容易发胖。我们在睡眠的时候，我们肠胃都已经进入了休眠状态，而那些在临睡之前进食的食物只会在肠胃里堆积，这些都是引发肠胃<span><a target=\"https://www.hospitalstar.com/_blank\" href=\"http://jbk.99.com.cn/\"><span style=\"color: rgb(51, 51, 51);\"><span>疾病</span></span></a></span>的罪魁祸首。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　二、切忌在入睡之前跟别人聊天，因为聊天能够带动我们积极的情绪，思维也跟着活跃，在闭着<span><a target=\"https://www.hospitalstar.com/_blank\" href=\"http://www.99.com.cn/stopic/2012022420/jinshiyan.htm\"><span style=\"color: rgb(51, 51, 51);\"><span>眼睛</span></span></a></span>等待睡眠的时间里面，就处于一种兴奋状态。从而很难入睡。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　三、在入睡之前，不要过度用脑，在临睡之前要做好各种事情。也不要在睡眠的时间之内想一些比较复杂费脑筋的事情。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　四、切忌大喜大悲的情绪，应该以一种轻松的心态入眠。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　五、在临睡之前，我们不要饮浓茶、喝<span><a target=\"https://www.hospitalstar.com/_blank\" href=\"http://ye.99.com.cn/yunqian/ysyy/2010/0813/56434.html\"><span style=\"color: rgb(51, 51, 51);\"><span>咖啡</span></span></a></span>，此类饮料都属于刺激性饮料，饮用过多，很容易造成入睡困难。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　<strong>　睡中禁忌</strong></span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　一、在入睡的时候不要张着嘴，很多病菌会通过<span><a target=\"https://www.hospitalstar.com/_blank\" href=\"http://www.99.com.cn/stopic/201202217/kouqiangkuiyang.htm\"><span style=\"color: rgb(51, 51, 51);\"><span>口腔</span></span></a></span>传入体内，而且容易使肺部和胃部受到冷空气和灰尘的剌激，引发多种疾病。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　二、很多老人都有一个蒙头睡的习惯，这样会导致我们呼出的二氧化碳又被吸入体内。晨起就会因缺氧头疼。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　三、忌仰面而睡，以向右侧身而卧为最好，这样全身骨骼、肌肉都处于自然放松状态，容易入睡，也容易消除疲劳。仰卧则使全身骨骼、肌肉仍处于紧张状态，不利于消除疲劳，而且还容易造成因手搭胸部而产生噩梦，影响睡眠质量。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　四、在睡觉的时候最好关掉所有的灯，因为人在睡眠的时候，虽然闭着眼睛，但是仍然能感觉到灯光，冲着光亮而睡，容易使人心神不安，难以入睡，而且即使睡着也容易惊醒。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　五、在睡觉的时候，房间要保持空气流通，但不要让风直接吹到身上。因为人睡熟后，身体对外界环境的适应能力降低，如果冲风而睡，时间长了，冷空气就会侵入身体，引起<span><a target=\"https://www.hospitalstar.com/_blank\" href=\"http://www.99.com.cn/erke/xegm/75225.htm\"><span style=\"color: rgb(51, 51, 51);\"><span>感冒</span></span></a></span>风寒，或中风等疾病。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　<strong>醒后禁忌</strong></span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　一、不要睡回笼觉，有些<span><a target=\"https://www.hospitalstar.com/_blank\" href=\"http://bj.99.com.cn/lrbj/\"><span style=\"color: rgb(51, 51, 51);\"><span>老年人</span></span></a></span>晨练之后，感到有些疲劳，于是回来接着睡，这种习惯很不利于老人的<span><a target=\"https://www.hospitalstar.com/_blank\" href=\"http://www.99.com.cn/\"><span style=\"color: rgb(51, 51, 51);\"><span>健康</span></span></a></span>，首先不利于晨练时肌肉产生的乳酸的清除，使人更加感到疲乏困倦，四肢无力，其次这也不利于老年人心肺功能的恢复。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(51, 51, 51); font-family: 宋体;\">　　二、老人一觉刚醒，无论是半夜还是在早晨，如需起床，都应平躺床上<span>“</span>养神三分钟<span>”</span>，然后慢慢坐起，在床沿坐半分钟，再离床走动。这样可防止突然的体位改变造成心脑血管供血不足所致的跌倒。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span>&nbsp;</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2943','3768','1','','<!--#p8_attach#-->/cms/item/2022_09/06_16/5c2c6d419c09467f.jpg.thumb.jpg','0-6岁是视力发育关键期。 这一阶段眼部的发育，对孩子一生有决定性的影响，小孩如果因为斜视、远视、近视、散光或者先天性白内障等看不清物体，视觉功能将会停止发育，导致视力低下，形成弱视，丧失立体视觉等，从而影响今后的生活、学习、工作。有关资料显示，约每700','172.16.1.2','172.16.1.2','1659456000','<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">0-6</span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">岁是视力发育关键期。 这一阶段眼部的发育，对孩子一生有决定性的影响，小孩如果因为斜视、远视、近视、散光或者先天性白内障等看不清物体，视觉功能将会停止发育，导致视力低下，形成弱视，丧失立体视觉等，从而影响今后的生活、学习、工作。有关资料显示，约每<span>700</span>个新生儿中就有<span>1</span>例发生先天性白内障，<span>4%</span>的儿童患有斜视，<span>3%-4%</span>的儿童患有弱视。</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">一般而言，孩子眼病越早治疗效果越好。比如弱视，<span>8</span>岁以后弱视治疗效果差，<span>10</span>岁以后治疗就非常困难了。由于幼儿无法讲述自己有何不适，所以要想早期发现孩子的眼病，做到早防早治，家长就一定要细心观察孩子的眼睛发育情况。一般情况下，家长可以从以下几点进行观察：看电视习惯走到电视跟前；经常出现眨眼睛、揉眼睛的情况；看人看物的时候喜欢斜看；经常眯眼看东西，经常侧着头看东西。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体; font-size: 12pt;\">当经常出现以上情况中的某一种或者几种时，家长就要及时带孩子到医院检查，以确定孩子是否有视力损伤情况。此外，出生有高危因素的宝宝，如早产、低出生体重、有眼病家族史等更需要做眼病筛查，即使初筛通过，也需要严密随访观察。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2944','3769','1','','<!--#p8_attach#-->/cms/item/2022_09/06_16/31d67d3e8680b54b.jpg.thumb.jpg','&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;运动只需要用到身体吗？不是的，其实还要用脑，活用知识和智慧才能有效运动。不过活动身体之后，常常会因为肌肉酸痛和疲劳感而感到灰心想中途放弃。　　应该从什么开始？有氧运动和无氧运动　　要练出均衡紧致的体形，肌肉训练和有氧运动的组合','172.16.1.2','172.16.1.2','1660579200','<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>&ensp;&ensp;&ensp;&ensp;运动只需要用到身体吗？不是的，其实还要用脑，活用知识和智慧才能有效运动。不过活动身体之后，常常会因为肌肉酸痛和疲劳感而感到灰心想中途放弃。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　应该从什么开始？有氧运动和无氧运动</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　要练出均衡紧致的体形，肌肉训练和有氧运动的组合是十分有效的。但是，应该从哪个开始呢？以前，如果要练肌肉的话，就是在肌肉训练之后做有氧运动。如果要瘦身的话，就要在有氧运动之后进行肌肉锻炼，不过最近也有说肌肉训练后做有氧运动会很有效。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　这么做的原因，是因为先做肌肉训练的话，可以分泌出生长荷尔蒙，可以大量排出从脂肪组织放出到血液中成为能源的脂肪，这时候体脂肪就处于十分容易燃烧的状态。肌肉训练主要消耗的是糖分，先把糖分消耗完的话，进行有氧运动的时候就可以调动脂肪，使其有效燃烧。但是，长时间运动之后作为能源的糖分耗尽，就很容易产生疲劳感。如果肌肉突然动不了或者突然感到十分疲劳的话，可以饮用含有糖分的运动饮料等。</span></p>\r\n<p style=\'background: white; line-height: 18pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>有氧运动和肌肉训练的注意点和负荷设定</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　肌肉训练之前一定要热身。理由很简单，就是要防止受伤和将肌肉力量最大化。简单的伸展运动是达不到热身效果的，推荐用步行和跑步让身体暖起来。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　另外，进行肌肉训练的时候，先从大块的肌肉开始练起。这样一来，刺激大块肌肉的时候就能连带刺激小块的肌肉。如果反过来的话，就可以造成肌肉疲劳甚至受伤。活动大块肌肉能提高代谢效率，促进脂肪燃烧，打造易瘦体质。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　至于负荷的设定，初学者的话要选较轻的器材，保持正确姿势慢慢做<span>20</span>下以上。不要屏住呼吸，发力的时候吐气，放松的时候吸气，尽量留有余力。习惯了的人或者想练肌肉的人，最好可以用全力做至少<span>8</span>～<span>10</span>下一组。重点在于要不留余力，让肌肉完全疲劳。这也叫作<span>“allout”</span>。但是，最重要的还是要先找到适合自己的负荷设定。做有氧运动的话，长期坚持才能有效消耗体脂肪，所以要选择可以轻松呼吸，而且能坚持一次<span>20</span>分钟以上的运动。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体; font-size: 12pt;\">　 别忘了还要补充水分！</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　成人的身体大概<span>60%</span>都是由水构成的。水的作用有很多，包括搬运养分，排出和搬运肌肉细胞内的废物和乳酸，通过排汗调整体温等。运动时的排汗和发热会导致水分不足，降低水的运作机能，血液水分减少会增加心脏的负担，造成肌肉的收缩机能下降，引起中暑甚至致命的疾病。为了防止水分不足，运动过程中每<span>20</span>分钟就要喝一些水或者运动饮料，预防肌肉紧绷痉挛、头痛恶心等水分不足的症状。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　什么是消除肌肉痛和疲劳的<span>“</span>积极休养法<span>”</span>？</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　运动的时候心情极佳，但是第二天却肌肉酸痛难忍，这样的经验你有过吗？肌肉痛是由于运动的负荷造成肌肉内部损伤，白血球在损伤部分聚集以修复时引起的现象。疲劳感是由肌肉周边的疲劳物质在血管内的积聚引起的。想要避免这些状态的出现，在这里向大家推荐积极休养法。在运动之后轻松步行一段或者做适量伸展运动，可以让硬化的肌肉重新软化，疏通血液循环。这样一来，积聚疲劳的血液会被含氧的新鲜血液代替，身体就不会感到疲劳了。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>&nbsp;</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　什么时候摄取氨基酸和蛋白质呢？</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　摄取氨基酸可以确保能量，减轻运动中的疲劳，还可以促进肌肉收紧。所以，在肌肉训练的约<span>30</span>分前摄取氨基酸会有很好的效果。氨基酸是促进肝脏代谢的基本物质，在各种氨基酸的成分中，含大量缬氨酸、亮氨酸、异亮氨酸、谷酰胺及精氨酸的物质效果更好。因为这<span>5</span>种成分在肌肉中也可以促进代谢，能在训练过程中提供能量，异亮氨酸还可以防止传达疲劳的物质进入脑血管，因而可以减轻疲劳感。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　蛋白质是高蛋白的加工食品，对于肌肉形成是必不可少的。平时只要均衡饮食就可以摄取蛋白质，无需另外摄取，但是如果要进行每周<span>3</span>、<span>4</span>次的肌肉训练的话，最好可以另外摄取一些以帮助修复肌肉。至于饮用的时间，可以在运动后，让身体分泌生长荷尔蒙以促进肌肉生长和恢复，或者可以在就寝前饮用。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体; font-size: 12pt;\">　 生理期要减少运动量</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　生理期的时候要注意观察，不要勉强继续运动</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　生理期的时候人会容易疲劳，什么都不想做。这些时候就不要勉强运动，让身体好好休息一下。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　但是，有些人想用运动减轻因为吃了太多甜食而感到的压力，这些人会继续坚持肌肉训练和有氧运动，但是会减少强度。不过生理期的时候体内的血液量减少，属于贫血体质，所以还是不要太勉强自己。另外，骨盆不正以及受寒是引起生理痛的原因，所以生理期的时候可以做一点伸展运动，调整骨盆，缓解生理痛的症状。</span></p>\r\n<p style=\'background: white; line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\'background: white; color: rgb(51, 51, 51); font-family: \"Arial\",\"sans-serif\"; font-size: 10.5pt;\'>　　感觉如何呢？运动可以让生活更加精彩。不过在做运动的时候，如果能将知识加以运用，可以说会跟身心带来完全不一样的体验。一次一点掌握更多运动知识，高效锻炼身体吧。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span>&nbsp;</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2945','3770','1','','<!--#p8_attach#-->/cms/item/2022_09/06_16/94924b81b8f0866a.jpg.thumb.jpg','&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;&amp;amp;ensp;各位朋友是否经常出现腰痛或活动受限的情况，尤其在久坐或长时间站时会加重！接下来我们就针对比较常见的姿势性或功能性引起腰痛的朋友分析一下首先我们看一下腰椎的解剖与生物力学。  腰椎位于活动度较小的胸椎和骶骨之间，是躯干活动的','172.16.1.2','172.16.1.2','1662220800','<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">&ensp;&ensp;&ensp;&ensp;&ensp;各位朋友是否经常出现腰痛或活动受限的情况，尤其在久坐或长时间站时会加重！接下来我们就针对比较常见的姿势性或功能性引起腰痛的朋友分析一下首先我们看一下腰椎的解剖与生物力学。</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">&nbsp; &nbsp; </span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">腰椎位于活动度较小的胸椎和骶骨之间，是躯干活动的中枢。腰椎共<span>5</span>节，椎体粗壮，横断面呈肾形，椎孔大，呈三角形。上下关节突关节的关节面呈矢状位。椎体之间的连接结构包括：椎间盘、前纵韧带、后纵韧带、黄韧带、棘上韧带、棘间韧带等。正常的腰曲凸向前。腰椎间盘很厚，可做较灵活的运动，但矢状位的关节突关节面限制了它的旋转运动。正常腰椎活动范围为：屈曲<span>0-50</span>度，伸展<span>0-30</span>度，侧屈<span>0-40</span>度。</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">&nbsp; &nbsp; </span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">腰痛引起的原因有很多，解决的方法也很多。接下来我们从结构上和大家分享两种引起腰痛的原因和解决方案</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">&nbsp; &nbsp; </span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">第一种腰痛人群会有如下之一症状：骨盆后倾、腰椎曲度变小变直或反弓，长时间坐着工作，一般在身体前屈时受限或伴有痛疼加重，严重者伴有腰椎盘的膨出或凸出。怎么解决呢？很简单，只需要坚持做如图动作，每天做三次，每次做<span>3</span>组，每组做<span>10</span>个，做每一个后伸位保持<span>5-10</span>秒</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">&nbsp; &nbsp; </span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">刚开始后伸的幅度可能会比较小，</span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">所以可以用肘关节支撑，后期可以进阶用手腕支撑，此动作可以让整个前表链得到充分的伸展，根据腰椎的生物力学骨盆会前倾、腰曲的弧度会增大，向后突出的髓核也会向身体前方移动。长时间坚持就会轻松改善因为姿势或功能引起的此类腰痛甚至腰椎盘凸出</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">动作看似很简单，但要注意以下几个细节：</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">1</span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">、初期不要过份追求动作的幅度而影响动作质量，循序渐进。</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">2</span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">、身体的位置，头要后伸，手在俯卧位初始端时是放在耳朵两侧，骨盆尽量贴住地面不要抬起，两腿自然放松，足踝在跖屈和内翻状态（这个姿势腰腰压力最小）</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">3</span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">、抬起身体的过程中不是靠腰背力量而是靠手臂的支撑，我们称为摆位治疗而不是腰背肌的训练</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">4</span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">、自然呼吸不要憋气！</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">在腰痛恢复的后期也可以进阶做如下动作保健，久坐上班族，要多做！</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">&nbsp; &nbsp; </span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">第二种常见腰痛人群往往会伴有如下之一症状：骨盆的前倾、腰曲的前凸幅度很大，长时间站立或穿高跟鞋，部分孕妇和大肚子男人也会有，<span>L5\\S1</span>的痛点明显，往往在做躯干后伸位会受限或痛疼加重，有的骶骨也会痛疼，严重着椎间孔或锥孔狭窄压迫神经和脊髓。</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">如上图双手抱膝关节拉向胸部，让骨盆的坐骨结节离开地面，双手给到拉起来的双腿以向上和身体后方的的弹动力，每次弹动<span>50-100</span>次（腰椎盘突出者禁做）坚持一段时间后你会发现，做躯干前屈和后伸的幅度都增加了，腰也不痛了</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">好了，做了一个周期后感觉腰不痛了就可以做如下保健的进阶动作</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">接下来做说说急性腰痛的处理方法吧：</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">1</span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">、身体俯卧位自然放松，足踝在跖屈和内翻位</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">2</span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">、手臂放于身体两侧，掌心向上，头转向一侧</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">3</span><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">、做深呼吸</span></p>\r\n<p style=\'line-height: 18pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; margin-right: 0cm; margin-bottom: 7.5pt; margin-left: 0cm; -ms-word-break: break-all;\'><span style=\"background: white; color: rgb(102, 102, 102); font-family: 宋体;\">备注：如果因腰椎或骨盆移位引起的腰痛最好配合中医手法来解决效果更佳，当然了后期的稳定核心训练必不可少！</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span>&nbsp;</span></p>','','','','','');
