INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2448','3271','1','','<!--#p8_attach#-->/cms/item/2022_05/18_11/0fd9ab6c19110049.jpg.thumb.jpg','随着社会经济的发展和生活方式的改变，高血压已成为影响我国乃至全球居民健康的公共卫生问题之一。而在疫情形势严峻的当下，高血压患者因长时间隔离居家、作息不规律、活动量减少，又不断接触各种疫情信息，往往会出现血压控制不佳。高血压作为公认的心血管事件链的“启','10.10.20.14','10.10.20.14','1652843247','<p class=\"MsoNormal\" style=\"text-indent:28.0000pt;mso-char-indent-count:2.0000;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:\'Times New Roman\';\r\nfont-size:14.0000pt;mso-font-kerning:1.0000pt;\"><font face=\"仿宋\">随着社会经济的发展和生活方式的改变，高血压已成为影响我国乃至全球居民健康的公共卫生问题之一。而在疫情形势严峻的当下，高血压患者因长时间隔离居家、作息不规律、活动量减少，又不断接触各种疫情信息，往往会出现血压控制不佳。</font></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent:28.0000pt;mso-char-indent-count:2.0000;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:\'Times New Roman\';\r\nfont-size:14.0000pt;mso-font-kerning:1.0000pt;\"><font face=\"仿宋\">高血压作为公认的心血管事件链的</font>“启动钥匙”，有研究显示收缩压每升高20mmHg或舒张压每升高10mmHg，脑卒中、冠心病、心衰等严重心、脑血管病发生风险即可倍。所以对于高血压患者，特别是正值这一特殊时期，做好血压管理、积极控制心血管疾病危险因素具有重大意义</span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent:28.0000pt;mso-char-indent-count:2.0000;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:\'Times New Roman\';\r\nfont-size:14.0000pt;mso-font-kerning:1.0000pt;\"><font face=\"仿宋\">1978年4月7日，世界卫生组织和国际心脏病学会联合会决定将每年的5月17日定为“世界高血压日”，旨在引起人们对防治高血压的重视。转眼2022年高血压日也已到来，今年其主题延续以往，呼吁人们“精准测量，控制血压，健康长寿”。5月17日,盐城市第一人民医院心血管内科在北院区开展大型义诊活动,为群众提供免费诊疗、健康咨询、科普宣教等医疗服务。</font></span></p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\"><img width=\"379\" height=\"284\" src=\"/ewebeditor/uploadfile/image/2022/05/18/20220518111013904001.png\">&ensp;<img width=\"383\" height=\"287\" src=\"/ewebeditor/uploadfile/image/2022/05/18/20220518111014167002.png\" style=\"font-size: 9pt;\">&ensp;<img width=\"383\" height=\"287\" src=\"/ewebeditor/uploadfile/image/2022/05/18/20220518111014140003.png\" style=\"font-size: 9pt;\"></p>\r\n<p class=\"MsoNormal\" style=\"text-indent:28.0000pt;mso-char-indent-count:2.0000;text-align:left;\"><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:\'Times New Roman\';\r\nfont-size:14.0000pt;mso-font-kerning:1.0000pt;\"><font face=\"仿宋\"><br>\r\n</font></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2453','3276','1','','','中共中央总书记、国家主席、中央军委主席习近平5月18日给南京大学的留学归国青年学者回信，对他们寄予殷切期望。习近平在回信中说，得知你们以李四光、程开甲等老一辈科学家为榜样，在海外学成后回国投身科教事业，在各自岗位上努力报效祖国、服务人民，取得丰硕成果，','10.10.20.14','10.10.20.14','1653106053','<p class=\"MsoNormal\" style=\"margin-right:5.0000pt;margin-left:5.0000pt;text-indent:28.0000pt;\r\nmso-char-indent-count:2.0000;mso-pagination:widow-orphan;text-align:left;\r\nline-height:21.0000pt;\"><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"><font face=\"仿宋\">中共中央总书记、国家主席、中央军委主席习近平</font>5月18日给南京大学的留学归国青年学者回信，对他们寄予殷切期望。</span><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-right:5.0000pt;margin-left:5.0000pt;text-indent:28.0000pt;\r\nmso-char-indent-count:2.0000;mso-pagination:widow-orphan;text-align:left;\r\nline-height:21.0000pt;\"><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"><font face=\"仿宋\">习近平在回信中说，得知你们以李四光、程开甲等老一辈科学家为榜样，在海外学成后回国投身科教事业，在各自岗位上努力报效祖国、服务人民，取得丰硕成果，我感到很欣慰。值此南京大学建校</font>120周年之际，谨向你们并向全校师生员工、广大校友致以热烈的祝贺和诚挚的问候！</span><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-right:5.0000pt;margin-left:5.0000pt;text-indent:28.0000pt;\r\nmso-char-indent-count:2.0000;mso-pagination:widow-orphan;text-align:left;\r\nline-height:21.0000pt;\"><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"><font face=\"仿宋\">习近平强调，你们在信中表示，生逢伟大时代是人生之幸，留学归国青年要心系</font>“国家事”、肩扛“国家责”，这些话讲得很好。希望同志们大力弘扬留学报国的光荣传统，以报效国家、服务人民为自觉追求，在坚持立德树人、推动科技自立自强上再创佳绩，在坚定文化自信、讲好中国故事上争做表率，为全面建设社会主义现代化国家、实现中华民族伟大复兴的中国梦积极贡献智慧和力量！</span><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-right:5.0000pt;margin-left:5.0000pt;text-indent:28.0000pt;\r\nmso-char-indent-count:2.0000;mso-pagination:widow-orphan;text-align:left;\r\nline-height:21.0000pt;\"><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"><font face=\"仿宋\">南京大学的前身是创建于</font>1902年的三江师范学堂，1950年正式定名为南京大学。建校120年来，一批批留学归国人员在南京大学留下了报国为民的奋斗足迹，李四光、程开甲等是其中的杰出代表。近日，党的十八大以来从海外学成归国到南京大学工作的120名青年学者代表给习近平总书记写信，汇报教书育人、科研创新等方面工作感悟，表达了弘扬优良传统、担当强国使命的坚定决心。</span><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent:28.1000pt;mso-char-indent-count:2.0000;mso-pagination:widow-orphan;\r\ntext-align:left;\"><b><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\ncolor:rgb(171,25,66);font-weight:bold;font-size:14.0000pt;\r\nmso-font-kerning:0.0000pt;\">&nbsp;</span></b></p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-indent:35.1500pt;mso-char-indent-count:2.5000;mso-pagination:widow-orphan;\r\ntext-align:center;\"><b><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\ncolor:rgb(171,25,66);font-weight:bold;font-size:14.0000pt;\r\nmso-font-kerning:0.0000pt;\">&nbsp;</span></b></p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-indent:35.0000pt;mso-char-indent-count:2.5000;mso-pagination:widow-orphan;\r\ntext-align:center;\"><span style=\"mso-spacerun:\'yes\';font-family:黑体;mso-bidi-font-family:宋体;\r\ncolor:rgb(171,25,66);mso-bidi-font-weight:bold;font-size:14.0000pt;\r\nmso-font-kerning:0.0000pt;\"><font face=\"黑体\">习近平给南京大学留学归国青年学者的</font></span><span style=\"mso-spacerun:\'yes\';font-family:黑体;mso-bidi-font-family:宋体;\r\ncolor:rgb(171,25,66);letter-spacing:0.3500pt;mso-bidi-font-weight:bold;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"><font face=\"黑体\">回信</font></span><span style=\"mso-spacerun:\'yes\';font-family:黑体;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"mso-pagination:widow-orphan;\"><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"><font face=\"仿宋\">南京大学留学归国的青年学者们：</font></span><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-right:5.0000pt;margin-left:5.0000pt;text-indent:28.0000pt;\r\nmso-char-indent-count:2.0000;mso-pagination:widow-orphan;text-align:left;\r\nline-height:21.0000pt;\"><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"><font face=\"仿宋\">你们好！得知你们以李四光、程开甲等老一辈科学家为榜样，在海外学成后回国投身科教事业，在各自岗位上努力报效祖国、服务人民，取得丰硕成果，我感到很欣慰。值此南京大学建校</font>120周年之际，谨向你们并向全校师生员工、广大校友致以热烈的祝贺和诚挚的问候！</span><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-right:5.0000pt;margin-left:5.0000pt;text-indent:28.0000pt;\r\nmso-char-indent-count:2.0000;mso-pagination:widow-orphan;text-align:left;\r\nline-height:21.0000pt;\"><span style=\"mso-spacerun:\'yes\';font-family:仿宋;mso-bidi-font-family:宋体;\r\nfont-size:14.0000pt;mso-font-kerning:0.0000pt;\"><font face=\"仿宋\">你们在信中表示，生逢伟大时代是人生之幸，留学归国青年要心系</font>“国家事”、肩扛“国家责”，这些话讲得很好。希望同志们大力弘扬留学报国的光荣传统，以报效国家、服务人民为自觉追求，在坚持立德树人、推动科技自立自强上再创佳绩，在坚定文化自信、讲好中国故事上争做表率，为全面建设社会主义现代化国家、实现中华民族伟大复兴的中国梦积极贡献智慧和力量！</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-right:5.0000pt;margin-left:5.0000pt;text-indent:28.0000pt;\r\nmso-char-indent-count:2.0000;mso-pagination:widow-orphan;text-align:left;\r\nline-height:21.0000pt;\"><span style=\"font-family: 仿宋; font-size: 14pt; text-align: right;\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;习近平</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-right:5.0000pt;margin-left:5.0000pt;text-indent:28.0000pt;\r\nmso-char-indent-count:2.0000;mso-pagination:widow-orphan;text-align:left;\r\nline-height:21.0000pt;\"><span style=\"font-family: 仿宋; font-size: 14pt; text-align: right;\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;2022年5月18日</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2459','3283','1','','','盐城市第一人民医院2022年5月零星维修项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。1、工程概况1.1项目名称：盐城市第一人民医院2022年5月零星维修项目。1.2项目地点：盐城市第一人民医院主院区（人民南路66号）、北区','10.10.20.14','10.10.20.14','1653286966','<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院<span>2022</span>年<span>5</span>月零星维修项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">1</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、工程概况</span></b></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目名称：盐城市第一人民医院<span>2022</span>年<span>5</span>月零星维修项目。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目地点：盐城市第一人民医院主院区（人民南路<span>66</span>号）、北区（毓龙西路<span>166</span>号）。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标内容：主院区神内一东门更换钢制防火门、门诊四楼室外铺贴地砖；北区洗衣房新做不锈钢送货车、<span>7</span>号楼公务用车停车库更换电动卷帘门等项目。具体内容详见招标人提供的工程量清单。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">质量标准：符合国家“合格”标准，质保期<span>1</span>年，防水工程质保期<span>5</span>年。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">工期要求：<span>15</span>日历天，具体开工日期以招标人的通知。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标规模：工程造价约<span>7.5</span>万元。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.7</span><span style=\"font-family: 宋体; font-size: 12pt;\">发包方式：包工包料。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标人应当具备的主要资格条件</span></b></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人须具有有效的建筑装修装饰工程专业承包二级及以上施工资质的独立法人，并具有有效的安全生产许可证。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2.2</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">截止<span>2022</span>年<span>4</span>月在盐城市第一人民医院存在已中标工程项目，且投标人未按合同工期完成并通过招标人验收的，本次不得报名参与本单位招标项目。</span></b></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.3.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.3.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标不接受联合体投标。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 21pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">3</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white;\">请符合上述资质条件的投标人携带单位介绍信到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">号</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">楼</span><span style=\"background: white; font-family: 宋体;\">805</span><span style=\"background: white;\">室）投标报名并领取招标文件，投标报名时间</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">23</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">14</span><span style=\"background: white;\">时</span><span style=\"background: white; font-family: 宋体;\">30</span><span style=\"background: white;\">分至</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">28</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">18</span><span style=\"background: white;\">时。联系人：刘工，</span><span style=\"background: white; font-family: 宋体;\">0515-69057406</span><span style=\"background: white;\">；手机：</span><span style=\"background: white; font-family: 宋体;\">13626222271</span><span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">4</span><span style=\"background: white;\">、</span><b>本项目采用“资格后审”，“合理低价法”</b><span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">6</span><span style=\"background: white;\">、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话</span><span style=\"background: white; font-family: 宋体;\">0515-66696810</span><span style=\"background: white;\">。</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">地址：盐城市毓龙西路<span>166</span>号</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">联系人：凌工</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">电话：</span><span style=\"font-family: 宋体;\">0515-83578932</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">地址：盐城市青年西路<span>8</span>号<span>805</span>室<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">联系人：刘工<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">电话：<span>0515-69057406&nbsp;&nbsp; </span></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2460','3284','1','','','盐城市第一人民医院北区电梯维保项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。1、项目概况1.1项目名称：盐城市第一人民医院北区电梯维保项目。1.2服务地点：盐城市第一人民医院各院区。1.3招标内容：北区33部、五官科','10.10.20.14','10.10.20.14','1653287586','<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院北区电梯维保项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">1</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、项目概况</span></b></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目名称：盐城市第一人民医院北区电梯维保项目。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">服务地点：盐城市第一人民医院各院区。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标内容：北区<span>33</span>部、五官科医院<span>3</span>部、广济医院<span>1</span>部电梯维保，电梯参数具体详见项目需求。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">质量标准：确保招标范围内的电梯安全稳定运行，并通过盐城特检院的定期检验合格。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">合同履行期限：叁年，合同按年签订，第一年维保合同结束后，年度考核平均得分≥<span>90</span>分续签下一年维保合同，否则招标人有权终止合同，重新组织招标。具体开始日期以招标人的书面通知为准。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标规模：服务费约<span>21</span>万元<span>/</span>年。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标人应当具备的主要资格条件</span></b></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人应具有独立法人资格，能够独立承担民事责任的能力。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人必须是具有《中华人民共和国特种设备安装改造维修许可证》（许可范围必须包含电梯）且具有<span>B</span>级（含）以上电梯维修<span>(</span>或在<span>2019</span>年<span>6</span>月<span>1</span>日以后新取（换）资质证书电梯安装（含修理）<span>)</span>资质。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">维保人员均为本单位的正式职工，并确保从投标截止之日当月向前连续<span>6</span>个月均已在本单位缴纳养老保险（例：如开标日期为<span>2022</span>年<span>3</span>月的某一天，则投标单位需保证以上人员<span>2021</span>年<span>9</span>月<span>-2022</span>年<span>2</span>月养老保险均在本单位缴纳）（提供社保证明材料）。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人投标时须提供配件由电梯原厂供应的承诺原件。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.5.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.5.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标不接受联合体投标。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 21pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">3</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white;\">请符合上述资质条件的投标人携带单位介绍信到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">号</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">楼</span><span style=\"background: white; font-family: 宋体;\">805</span><span style=\"background: white;\">室）投标报名并领取招标文件，投标报名时间</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">23</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">14</span><span style=\"background: white;\">时</span><span style=\"background: white; font-family: 宋体;\">30</span><span style=\"background: white;\">分至</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">28</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">18</span><span style=\"background: white;\">时。联系人：刘工，</span><span style=\"background: white; font-family: 宋体;\">0515-69057406</span><span style=\"background: white;\">；手机：</span><span style=\"background: white; font-family: 宋体;\">13626222271</span><span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">4</span><span style=\"background: white;\">、</span><b>本项目采用“资格后审”，“综合评估法”</b><span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">6</span><span style=\"background: white;\">、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话</span><span style=\"background: white; font-family: 宋体;\">0515-66696810</span><span style=\"background: white;\">。</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">地址：盐城市毓龙西路<span>166</span>号</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">联系人：凌工</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">电话：<span>0515-83578932</span></span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">地址：盐城市青年西路<span>8</span>号<span>805</span>室<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">联系人：刘工<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">电话：<span>0515-69057406&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2461','3285','1','','','盐城市第一人民医院北区净化空调系统维保项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。1、工程概况1.1项目名称：盐城市第一人民医院北区净化空调系统维保项目。1.2服务地点：盐城市第一人民医院北区（毓龙西路166号）','10.10.20.14','10.10.20.14','1653287640','<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院北区净化空调系统维保项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">1</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、工程概况</span></b></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目名称：盐城市第一人民医院北区净化空调系统维保项目。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">服务地点：盐城市第一人民医院北区（毓龙西路<span>166</span>号）。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标内容：盐城市第一人民医院</span><span style=\"font-family: 宋体; font-size: 12pt;\">北区净化空调系统维保项目，具体内容详见招标人要求和工程量清单。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">质量标准：符合国家及行业合格标准，满足净化空调相关规范及实际工作需要。质保期<span>18</span>个月。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">中标人维保范围内设备在质保期内发生的所有故障由中标人负责修复，因此产生的相关费用（包含但不限于人工费、材料费等所有费用）均由中标人承担。上述费用由投标人考虑到投标报价中，无须单独列项，招标人不另支付上述费用。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">工期要求：<span>15</span>日历天，具体开工日期以招标人的通知。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标规模：工程造价约<span>6.9</span>万元。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标人应当具备的主要资格条件</span></b></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人具备建设行政主管部门核发的机电工程施工总承包或建筑机电安装工程专业承包三级（含）以上施工资质的独立法人，并具有有效的安全生产许可证。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标不接受联合体投标。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 21pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">3</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white;\">请符合上述资质条件的投标人携带单位介绍信到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">号</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">楼</span><span style=\"background: white; font-family: 宋体;\">805</span><span style=\"background: white;\">室）投标报名并领取招标文件，投标报名时间</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">23</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">14</span><span style=\"background: white;\">时</span><span style=\"background: white; font-family: 宋体;\">30</span><span style=\"background: white;\">分至</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">28</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">18</span><span style=\"background: white;\">时。联系人：刘工，</span><span style=\"background: white; font-family: 宋体;\">0515-69057406</span><span style=\"background: white;\">；手机：</span><span style=\"background: white; font-family: 宋体;\">13626222271</span><span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">4</span><span style=\"background: white;\">、</span><b>本项目采用“资格后审”，“综合评估法”</b><span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">6</span><span style=\"background: white;\">、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话</span><span style=\"background: white; font-family: 宋体;\">0515-66696810</span><span style=\"background: white;\">。</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">地址：盐城市毓龙西路<span>166</span>号</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">联系人：凌工</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">电话：</span><span style=\"font-family: 宋体;\">0515-83578932</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">地址：盐城市青年西路<span>8</span>号<span>805</span>室<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">联系人：刘工<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">电话：<span>0515-69057406&nbsp;&nbsp; </span></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2464','3288','1','','','因医院实际工作需要，于2022年5月24日进行招标的项目，经评标小组评审，中标结果如下：一、盐城市第一人民医院北区门诊4楼东侧改造项目中标单位：江苏亮辉建设工程有限公司中标价（元）：553935.64项目负责人：蔡静为体现公开、公平、公正的原则，特此公告三天。如有疑','10.10.20.14','10.10.20.14','1653438691','<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 27.75pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">因医院实际工作需要，于<span>2022</span>年<span>5</span>月<span>24</span>日进行招标的项目，经评标小组评审，中标结果如下：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 28pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-autospace:; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">一、盐城市第一人民医院北区门诊<span>4</span>楼东侧改造项目</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 28pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-autospace:; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">中标单位：江苏亮辉建设工程有限公司</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 28pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-autospace:; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">中标价（元）：<span>553935.64</span></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 28pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-autospace:; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">项目负责人：蔡静</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 28pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-autospace:; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">&nbsp;</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 27.75pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监督审查室联系，电话<span>0515-66696810</span>。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 27.75pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">&nbsp;</span></p>\r\n<p align=\"right\" style=\'margin: 0cm 0cm 0pt; text-align: right; line-height: 21pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">&nbsp;</span></p>\r\n<p align=\"right\" style=\'margin: 0cm 0cm 0pt; text-align: right; line-height: 21pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">盐城市第一人民医院</span></p>\r\n<p align=\"right\" style=\'margin: 0cm 0cm 0pt; text-align: right; line-height: 21pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;2022</span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 14pt;\">年<span>5</span>月<span>25</span>日</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span>&nbsp;</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2465','3289','1','','','（2021年10月23日第十三届全国人民代表大会常务委员会第三十一次会议通过，自2022年1月1日起施行。）目　　录第一章　总　　则第二章　家庭责任第三章　国家支持第四章　社会协同第五章　法律责任第六章　附　　则第一章　总则第一条　为了发扬中华民族重视家庭教育的优','10.10.20.14','10.10.20.14','1653961671','<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 楷体,楷体_GB2312; font-size: 16pt;\">（<span>2021</span>年<span>10</span>月<span>23</span>日第十三届全国人民代表大会常务委员会第三十一次会议通过，自<span>2022</span>年<span>1</span>月<span>1</span>日起施行。）</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">&nbsp;</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">目　　录</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第一章　总　　则</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二章　家庭责任</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三章　国家支持</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四章　社会协同</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第五章　法律责任</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第六章　附　　则</span><sup><span style=\"color: rgb(51, 102, 204); font-family: 宋体; font-size: 16pt;\">&nbsp;</span></sup><span style=\"color: rgb(19, 110, 194); font-family: 宋体; font-size: 16pt;\">&nbsp;</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">&nbsp;</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32.15pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(51, 51, 51); font-family: 方正黑体_GBK; font-size: 16pt;\">第一章　总则</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第一条　为了发扬中华民族重视家庭教育的优良传统，引导全社会注重家庭、家教、家风，增进家庭幸福与社会和谐，培养德智体美劳全面发展的社会主义建设者和接班人，制定本法。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二条　本法所称家庭教育，是指父母或者其他监护人为促进未成年人全面健康成长，对其实施的道德品质、身体素质、生活技能、文化修养、行为习惯等方面的培育、引导和影响。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三条　家庭教育以立德树人为根本任务，培育和践行社会主义核心价值观，弘扬中华民族优秀传统文化、革命文化、社会主义先进文化，促进未成年人健康成长。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四条　未成年人的父母或者其他监护人负责实施家庭教育。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">国家和社会为家庭教育提供指导、支持和服务。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">国家工作人员应当带头树立良好家风，履行家庭教育责任。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第五条　家庭教育应当符合以下要求：</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（一）尊重未成年人身心发展规律和个体差异；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（二）尊重未成年人人格尊严，保护未成年人隐私权和个人信息，保障未成年人合法权益；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（三）遵循家庭教育特点，贯彻科学的家庭教育理念和方法；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（四）家庭教育、学校教育、社会教育紧密结合、协调一致；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（五）结合实际情况采取灵活多样的措施。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第六条　各级人民政府指导家庭教育工作，建立健全家庭学校社会协同育人机制。县级以上人民政府负责妇女儿童工作的机构，组织、协调、指导、督促有关部门做好家庭教育工作。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">教育行政部门、妇女联合会</span><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">统筹协调社会资源，协同推进覆盖城乡的家庭教育指导服务体系建设，并按照职责分工承担家庭教育工作的日常事务。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">县级以上精神文明建设部门和县级以上人民政府公安、民政、司法行政、人力资源和社会保障、文化和旅游、卫生健康、市场监督管理、广播电视、体育、新闻出版、网信等有关部门</span><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">在各自的职责范围内做好家庭教育工作。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第七条　县级以上人民政府应当制定家庭教育工作专项规划，将家庭教育指导服务纳入城乡公共服务体系和政府购买服务目录，将相关经费列入财政预算，鼓励和支持以政府购买服务的方式提供家庭教育指导。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第八条　人民法院、人民检察院发挥职能作用，配合同级人民政府及其有关部门建立家庭教育工作联动机制，共同做好家庭教育工作。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第九条　工会、共产主义青年团、</span><span><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E6%AE%8B%E7%96%BE%E4%BA%BA%E8%81%94%E5%90%88%E4%BC%9A/1527556\"><span style=\"color: windowtext; font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\"><span>残疾人联合会</span></span></a></span><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">、科学技术协会、关心下一代工作委员会以及居民委员会、村民委员会</span><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">等应当结合自身工作，积极开展家庭教育工作，为家庭教育提供社会支持。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第十条　国家鼓励和支持企业事业单位、社会组织及个人依法开展公益性家庭教育服务活动。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第十一条　国家鼓励开展家庭教育研究，鼓励高等学校开设家庭教育专业课程，支持师范院校和有条件的高等学校加强家庭教育学科建设，培养家庭教育服务专业人才，开展家庭教育服务人员培训。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第十二条　国家鼓励和支持自然人、法人和非法人组织为家庭教育事业进行捐赠或者提供志愿服务，对符合条件的，依法给予税收优惠。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">国家对在家庭教育工作中做出突出贡献的组织和个人，按照有关规定给予表彰、奖励。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第十三条　每年<span>5</span>月<span>15</span>日国际家庭日所在周为全国家庭教育宣传周。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">&nbsp;</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32.15pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(51, 51, 51); font-family: 方正黑体_GBK; font-size: 16pt;\">第二章　家庭责任</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第十四条　父母或者其他监护人应当树立家庭是第一个课堂、家长是第一任老师的责任意识，承担对未成年人实施家庭教育的主体责任，用正确思想、方法和行为教育未成年人养成良好思想、品行和习惯。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">共同生活的具有完全民事行为能力的其他家庭成员应当协助和配合未成年人的父母或者其他监护人实施家庭教育。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第十五条　未成年人的父母或者其他监护人及其他家庭成员应当注重家庭建设，培育积极健康的家庭文化，树立和传承优良家风，弘扬中华民族家庭美德，共同构建文明、和睦的家庭关系，为未成年人健康成长营造良好的家庭环境。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第十六条　未成年人的父母或者其他监护人应当针对不同年龄段未成年人的身心发展特点，以下列内容为指引，开展家庭教育：</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（一）教育未成年人爱党、爱国、爱人民、爱集体、爱社会主义，树立维护国家统一的观念，铸牢中华民族共同体意识，培养家国情怀；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（二）教育未成年人崇德向善、尊老爱幼、热爱家庭、勤俭节约、团结互助、诚信友爱、遵纪守法，培养其良好社会公德、家庭美德、个人品德意识和法治意识；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（三）帮助未成年人树立正确的成才观，引导其培养广泛兴趣爱好、健康审美追求和良好学习习惯，增强科学探索精神、创新意识和能力；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（四）保证未成年人营养均衡、科学运动、睡眠充足、身心愉悦，引导其养成良好生活习惯和行为习惯，促进其身心健康发展；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（五）关注未成年人心理健康，教导其珍爱生命，对其进行交通出行、健康上网和防欺凌、防溺水、防诈骗、防拐卖、防性侵等方面的安全知识教育，帮助其掌握安全知识和技能，增强其自我保护的意识和能力；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（六）帮助未成年人树立正确的劳动观念，参加力所能及的劳动，提高生活自理能力和独立生活能力，养成吃苦耐劳的优秀品格和热爱劳动的良好习惯。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第十七条　未成年人的父母或者其他监护人实施家庭教育，应当关注未成年人的生理、心理、智力发展状况，尊重其参与相关家庭事务和发表意见的权利，合理运用以下方式方法：</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（一）亲自养育，加强亲子陪伴；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（二）共同参与，发挥父母双方的作用；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（三）相机而教，寓教于日常生活之中；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（四）潜移默化，言传与身教相结合；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（五）严慈相济，关心爱护与严格要求并重；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（六）尊重差异，根据年龄和个性特点进行科学引导；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（七）平等交流，予以尊重、理解和鼓励；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（八）相互促进，父母与子女共同成长；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（九）其他有益于未成年人全面发展、健康成长的方式方法。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第十八条　未成年人的父母或者其他监护人应当树立正确的家庭教育理念，自觉学习家庭教育知识，在孕期和未成年人进入婴幼儿照护服务机构、幼儿园、中小学校等重要时段进行有针对性的学习，掌握科学的家庭教育方法，提高家庭教育的能力。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第十九条　未成年人的父母或者其他监护人应当与中小学校、幼儿园、婴幼儿照护服务机构、社区密切配合，积极参加其提供的公益性家庭教育指导和实践活动，共同促进未成年人健康成长。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二十条　未成年人的父母分居或者离异的，应当相互配合履行家庭教育责任，任何一方不得拒绝或者怠于履行；除法律另有规定外，不得阻碍另一方实施家庭教育。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二十一条　未成年人的父母或者其他监护人依法委托他人代为照护未成年人的，应当与被委托人、未成年人保持联系，定期了解未成年人学习、生活情况和心理状况，与被委托人共同履行家庭教育责任。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二十二条　未成年人的父母或者其他监护人应当合理安排未成年人学习、休息、娱乐和体育锻炼的时间，避免加重未成年人学习负担，预防未成年人沉迷网络。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二十三条　未成年人的父母或者其他监护人不得因性别、身体状况、智力等歧视未成年人，不得实施家庭暴力，不得胁迫、引诱、教唆、纵容、利用未成年人从事违反法律法规和社会公德的活动。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32.15pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">&nbsp;</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32.15pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(51, 51, 51); font-family: 方正黑体_GBK; font-size: 16pt;\">第三章　国家支持</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二十四条　国务院应当组织有关部门制定、修订并及时颁布全国家庭教育指导大纲。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">省级人民政府或者有条件的设区的市级人民政府</span><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">应当组织有关部门编写或者采用适合当地实际的家庭教育指导读本，制定相应的家庭教育指导服务工作规范和评估规范。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二十五条　省级以上人民政府应当组织有关部门统筹建设家庭教育信息化共享服务平台，开设公益性网上家长学校和网络课程，开通服务热线，提供线上家庭教育指导服务。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二十六条　县级以上地方人民政府应当加强监督管理，减轻义务教育阶段学生作业负担和校外培训负担，畅通学校家庭沟通渠道，推进学校教育和家庭教育相互配合。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二十七条　县级以上地方人民政府及有关部门组织建立家庭教育指导服务专业队伍，加强对专业人员的培养，鼓励社会工作者、志愿者参与家庭教育指导服务工作。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二十八条　县级以上地方人民政府可以结合当地实际情况和需要，通过多种途径和方式确定家庭教育指导机构。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">家庭教育指导机构对辖区内社区家长学校、学校家长学校及其他家庭教育指导服务站点进行指导，同时开展家庭教育研究、服务人员队伍建设和培训、公共服务产品研发。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第二十九条　家庭教育指导机构应当及时向有需求的家庭提供服务。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">对于父母或者其他监护人履行家庭教育责任存在一定困难的家庭，家庭教育指导机构应当根据具体情况，与相关部门协作配合，提供有针对性的服务。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三十条　设区的市、县、乡级人民政府应当结合当地实际采取措施，对留守未成年人和困境未成年人家庭建档立卡，提供生活帮扶、创业就业支持等关爱服务，为留守未成年人和困境未成年人的父母或者其他监护人实施家庭教育创造条件。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">教育行政部门、妇女联合会</span><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">应当采取有针对性的措施，为留守未成年人和困境未成年人的父母或者其他监护人实施家庭教育提供服务，引导其积极关注未成年人身心健康状况、加强亲情关爱。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三十一条　家庭教育指导机构开展家庭教育指导服务活动，不得组织或者变相组织营利性教育培训。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三十二条　婚姻登记机构和收养登记机构应当通过现场咨询辅导、播放宣传教育片等形式，向办理婚姻登记、收养登记的当事人宣传家庭教育知识，提供家庭教育指导。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三十三条　儿童福利机构、未成年人救助保护机构应当对本机构安排的寄养家庭、接受救助保护的未成年人的父母或者其他监护人提供家庭教育指导。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三十四条　人民法院在审理离婚案件时，应当对有未成年子女的夫妻双方提供家庭教育指导。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三十五条　妇女联合会发挥妇女在弘扬中华民族家庭美德、树立良好家风等方面的独特作用，宣传普及家庭教育知识，通过家庭教育指导机构、社区家长学校、文明家庭建设等多种渠道组织开展家庭教育实践活动，提供家庭教育指导服务。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三十六条　自然人、法人和非法人组织可以依法设立非营利性家庭教育服务机构。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">县级以上地方人民政府及有关部门</span><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">可以采取政府补贴、奖励激励、购买服务等扶持措施，培育家庭教育服务机构。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">教育、民政、卫生健康、市场监督管理等有关部门</span><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">应当在各自职责范围内，依法对家庭教育服务机构及从业人员进行指导和监督。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三十七条　国家机关、企业事业单位、群团组织、社会组织应当将家风建设纳入单位文化建设，支持职工参加相关的家庭教育服务活动。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">文明城市、文明村镇、文明单位、文明社区、文明校园和文明家庭等创建活动，应当将家庭教育情况作为重要内容。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32.15pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">&nbsp;</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32.15pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(51, 51, 51); font-family: 方正黑体_GBK; font-size: 16pt;\">第四章　社会协同</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三十八条　居民委员会、村民委员会可以依托城乡社区公共服务设施，设立社区家长学校等家庭教育指导服务站点，配合家庭教育指导机构组织面向居民、村民的家庭教育知识宣传，为未成年人的父母或者其他监护人提供家庭教育指导服务。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第三十九条　中小学校、幼儿园应当将家庭教育指导服务纳入工作计划，作为教师业务培训的内容。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四十条　中小学校、幼儿园可以采取建立家长学校等方式，针对不同年龄段未成年人的特点，定期组织公益性家庭教育指导服务和实践活动，并及时联系、督促未成年人的父母或者其他监护人参加。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四十一条　中小学校、幼儿园应当根据家长的需求，邀请有关人员传授家庭教育理念、知识和方法，组织开展家庭教育指导服务和实践活动，促进家庭与学校共同教育。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四十二条　具备条件的中小学校、幼儿园应当在教育行政部门的指导下，为家庭教育指导服务站点开展公益性家庭教育指导服务活动提供支持。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四十三条　中小学校发现未成年学生严重违反校规校纪的，应当及时制止、管教，告知其父母或者其他监护人，并为其父母或者其他监护人提供有针对性的家庭教育指导服务；发现未成年学生有不良行为或者严重不良行为的，按照有关法律规定处理。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四十四条　婴幼儿照护服务机构、早期教育服务机构应当为未成年人的父母或者其他监护人提供科学养育指导等家庭教育指导服务。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四十五条　医疗保健机构在开展婚前保健、孕产期保健、儿童保健、预防接种等服务时，应当对有关成年人、未成年人的父母或者其他监护人开展科学养育知识和婴幼儿早期发展的宣传和指导。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四十六条　图书馆、博物馆、文化馆、纪念馆、美术馆、科技馆、体育场馆、青少年宫、儿童活动中心等公共文化服务机构和爱国主义教育基地每年应当定期开展公益性家庭教育宣传、家庭教育指导服务和实践活动，开发家庭教育类公共文化服务产品。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">广播、电视、报刊、互联网等新闻媒体</span><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">应当宣传正确的家庭教育知识，传播科学的家庭教育理念和方法，营造重视家庭教育的良好社会氛围。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四十七条　家庭教育服务机构应当加强自律管理，制定家庭教育服务规范，组织从业人员培训，提高从业人员的业务素质和能力。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32.15pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">&nbsp;</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32.15pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(51, 51, 51); font-family: 方正黑体_GBK; font-size: 16pt;\">第五章　法律责任</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四十八条　未成年人住所地的居民委员会、村民委员会、妇女联合会，未成年人的父母或者其他监护人所在单位，以及中小学校、幼儿园等有关密切接触未成年人的单位，发现父母或者其他监护人拒绝、怠于履行家庭教育责任，或者非法阻碍其他监护人实施家庭教育的，应当予以批评教育、劝诫制止，必要时督促其接受家庭教育指导。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">未成年人的父母或者其他监护人依法委托他人代为照护未成年人，有关单位发现被委托人不依法履行家庭教育责任的，适用前款规定。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第四十九条　公安机关、人民检察院、人民法院在办理案件过程中，发现未成年人存在严重不良行为或者实施犯罪行为，或者未成年人的父母或者其他监护人不正确实施家庭教育侵害未成年人合法权益的，根据情况对父母或者其他监护人予以训诫，并可以责令其接受家庭教育指导。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第五十条　负有家庭教育工作职责的政府部门、机构有下列情形之一的，由其上级机关或者主管单位责令限期改正；情节严重的，对直接负责的主管人员和其他直接责任人员依法予以处分：</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（一）不履行家庭教育工作职责；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（二）截留、挤占、挪用或者虚报、冒领家庭教育工作经费；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（三）其他滥用职权、玩忽职守或者徇私舞弊的情形。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第五十一条　家庭教育指导机构、中小学校、幼儿园、婴幼儿照护服务机构、早期教育服务机构违反本法规定，不履行或者不正确履行家庭教育指导服务职责的，由主管部门责令限期改正；情节严重的，对直接负责的主管人员和其他直接责任人员依法予以处分。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第五十二条　家庭教育服务机构有下列情形之一的，由主管部门责令限期改正；拒不改正或者情节严重的，由主管部门责令停业整顿、吊销营业执照或者撤销登记：</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（一）未依法办理设立手续；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（二）从事超出许可业务范围的行为或作虚假、引人误解宣传，产生不良后果；</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">（三）侵犯未成年人及其父母或者其他监护人合法权益。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第五十三条　未成年人的父母或者其他监护人在家庭教育过程中对未成年人实施家庭暴力的，依照《中华人民共和国未成年人保护法》、《中华人民共和国反家庭暴力法》等法律的规定追究法律责任。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第五十四条　违反本法规定，构成违反治安管理行为的，由公安机关依法予以治安管理处罚；构成犯罪的，依法追究刑事责任。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32.15pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">&nbsp;</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32.15pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(51, 51, 51); font-family: 方正黑体_GBK; font-size: 16pt;\">第六章　附则</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 28pt; text-indent: 32pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(51, 51, 51); font-family: 仿宋,仿宋_GB2312; font-size: 16pt;\">第五十五条　本法自<span>2022</span>年<span>1</span>月<span>1</span>日起施行。</span></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2466','3290','1','','','（2021年8月20日第十三届全国人民代表大会常务委员会第三十次会议通过）目　　录第一章　总　　则第二章　考试和注册第三章　执业规则第四章　培训和考核第五章　保障措施第六章　法律责任第七章　附　　则第一章　总　　则第一条　为了保障医师合法权益，规范医师执业','172.16.1.2','172.16.1.2','1654658415','<h3 style=\"line-height: 13.5pt;\"><span style=\"background: white;\">（<span>2021</span>年<span>8</span>月<span>20</span>日第十三届全国人民代表大会常务委员会第三十次会议通过）</span></h3>\r\n<p align=\"center\" style=\'margin: 0cm 0cm 0pt; text-align: center; line-height: 13.5pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">目　　录</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 楷体,楷体_GB2312; font-size: 9pt;\">第一章　总　　则</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 楷体,楷体_GB2312; font-size: 9pt;\">第二章　考试和注册</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 楷体,楷体_GB2312; font-size: 9pt;\">第三章　执业规则</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 楷体,楷体_GB2312; font-size: 9pt;\">第四章　培训和考核</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 楷体,楷体_GB2312; font-size: 9pt;\">第五章　保障措施</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 楷体,楷体_GB2312; font-size: 9pt;\">第六章　法律责任</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 楷体,楷体_GB2312; font-size: 9pt;\">第七章　附　　则</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span>&nbsp;</span></p>\r\n<p align=\"center\" style=\'margin: 0cm 0cm 0pt; text-align: center; line-height: 13.5pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第一章　总　　则</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第一条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　为了保障医师合法权益，规范医师执业行为，加强医师队伍建设，保护人民健康，推进健康中国建设，制定本法。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　本法所称医师，是指依法取得医师资格，经注册在医疗卫生机构中执业的专业医务人员，包括执业医师和执业助理医师。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师应当坚持人民至上、生命至上，发扬人道主义精神，弘扬敬佑生命、救死扶伤、甘于奉献、大爱无疆的崇高职业精神，恪守职业道德，遵守执业规范，提高执业水平，履行防病治病、保护人民健康的神圣职责。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医师依法执业，受法律保护。医师的人格尊严、人身安全不受侵犯。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国务院卫生健康主管部门负责全国的医师管理工作。国务院教育、人力资源社会保障、中医药等有关部门在各自职责范围内负责有关的医师管理工作。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">县级以上地方人民政府卫生健康主管部门负责本行政区域内的医师管理工作。县级以上地方人民政府教育、人力资源社会保障、中医药等有关部门在各自职责范围内负责有关的医师管理工作。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　每年<span>8</span>月<span>19</span>日为中国医师节。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">对在医疗卫生服务工作中做出突出贡献的医师，按照国家有关规定给予表彰、奖励。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">全社会应当尊重医师。各级人民政府应当关心爱护医师，弘扬先进事迹，加强业务培训，支持开拓创新，帮助解决困难，推动在全社会广泛形成尊医重卫的良好氛围。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第六条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家建立健全医师医学专业技术职称设置、评定和岗位聘任制度，将职业道德、专业实践能力和工作业绩作为重要条件，科学设置有关评定、聘任标准。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第七条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师可以依法组织和参加医师协会等有关行业组织、专业学术团体。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医师协会等有关行业组织应当加强行业自律和医师执业规范，维护医师合法权益，协助卫生健康主管部门和其他有关部门开展相关工作。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span>&nbsp;</span></p>\r\n<p align=\"center\" style=\'margin: 0cm 0cm 0pt; text-align: center; line-height: 13.5pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二章　考试和注册</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第八条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家实行医师资格考试制度。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医师资格考试分为执业医师资格考试和执业助理医师资格考试。医师资格考试由省级以上人民政府卫生健康主管部门组织实施。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医师资格考试的类别和具体办法，由国务院卫生健康主管部门制定。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第九条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　具有下列条件之一的，可以参加执业医师资格考试：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（一）具有高等学校相关医学专业本科以上学历，在执业医师指导下，在医疗卫生机构中参加医学专业工作实践满一年；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（二）具有高等学校相关医学专业专科学历，取得执业助理医师执业证书后，在医疗卫生机构中执业满二年。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第十条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　具有高等学校相关医学专业专科以上学历，在执业医师指导下，在医疗卫生机构中参加医学专业工作实践满一年的，可以参加执业助理医师资格考试。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第十一条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　以师承方式学习中医满三年，或者经多年实践医术确有专长的，经县级以上人民政府卫生健康主管部门委托的中医药专业组织或者医疗卫生机构考核合格并推荐，可以参加中医医师资格考试。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">以师承方式学习中医或者经多年实践，医术确有专长的，由至少二名中医医师推荐，经省级人民政府中医药主管部门组织实践技能和效果考核合格后，即可取得中医医师资格及相应的资格证书。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">本条规定的相关考试、考核办法，由国务院中医药主管部门拟订，报国务院卫生健康主管部门审核、发布。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第十二条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师资格考试成绩合格，取得执业医师资格或者执业助理医师资格，发给医师资格证书。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第十三条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家实行医师执业注册制度。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">取得医师资格的，可以向所在地县级以上地方人民政府卫生健康主管部门申请注册。医疗卫生机构可以为本机构中的申请人集体办理注册手续。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">除有本法规定不予注册的情形外，卫生健康主管部门应当自受理申请之日起二十个工作日内准予注册，将注册信息录入国家信息平台，并发给医师执业证书。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">未注册取得医师执业证书，不得从事医师执业活动。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医师执业注册管理的具体办法，由国务院卫生健康主管部门制定。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第十四条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师经注册后，可以在医疗卫生机构中按照注册的执业地点、执业类别、执业范围执业，从事相应的医疗卫生服务。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">中医、中西医结合医师可以在医疗机构中的中医科、中西医结合科或者其他临床科室按照注册的执业类别、执业范围执业。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医师经相关专业培训和考核合格，可以增加执业范围。法律、行政法规对医师从事特定范围执业活动的资质条件有规定的，从其规定。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">经考试取得医师资格的中医医师按照国家有关规定，经培训和考核合格，在执业活动中可以采用与其专业相关的西医药技术方法。西医医师按照国家有关规定，经培训和考核合格，在执业活动中可以采用与其专业相关的中医药技术方法。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第十五条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师在二个以上医疗卫生机构定期执业的，应当以一个医疗卫生机构为主，并按照国家有关规定办理相关手续。国家鼓励医师定期定点到县级以下医疗卫生机构，包括乡镇卫生院、村卫生室、社区卫生服务中心等，提供医疗卫生服务，主执业机构应当支持并提供便利。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">卫生健康主管部门、医疗卫生机构应当加强对有关医师的监督管理，规范其执业行为，保证医疗卫生服务质量。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第十六条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　有下列情形之一的，不予注册：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（一）无民事行为能力或者限制民事行为能力；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（二）受刑事处罚，刑罚执行完毕不满二年或者被依法禁止从事医师职业的期限未满；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（三）被吊销医师执业证书不满二年；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（四）因医师定期考核不合格被注销注册不满一年；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（五）法律、行政法规规定不得从事医疗卫生服务的其他情形。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">受理申请的卫生健康主管部门对不予注册的，应当自受理申请之日起二十个工作日内书面通知申请人和其所在医疗卫生机构，并说明理由。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第十七条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师注册后有下列情形之一的，注销注册，废止医师执业证书：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（一）死亡；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（二）受刑事处罚；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（三）被吊销医师执业证书；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（四）医师定期考核不合格，暂停执业活动期满，再次考核仍不合格；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（五）中止医师执业活动满二年；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（六）法律、行政法规规定不得从事医疗卫生服务或者应当办理注销手续的其他情形。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">有前款规定情形的，医师所在医疗卫生机构应当在三十日内报告准予注册的卫生健康主管部门；卫生健康主管部门依职权发现医师有前款规定情形的，应当及时通报准予注册的卫生健康主管部门。准予注册的卫生健康主管部门应当及时注销注册，废止医师执业证书。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第十八条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师变更执业地点、执业类别、执业范围等注册事项的，应当依照本法规定到准予注册的卫生健康主管部门办理变更注册手续。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医师从事下列活动的，可以不办理相关变更注册手续：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（一）参加规范化培训、进修、对口支援、会诊、突发事件医疗救援、慈善或者其他公益性医疗、义诊；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（二）承担国家任务或者参加政府组织的重要活动等；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（三）在医疗联合体内的医疗机构中执业。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第十九条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　中止医师执业活动二年以上或者本法规定不予注册的情形消失，申请重新执业的，应当由县级以上人民政府卫生健康主管部门或者其委托的医疗卫生机构、行业组织考核合格，并依照本法规定重新注册。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二十条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师个体行医应当依法办理审批或者备案手续。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">执业医师个体行医，须经注册后在医疗卫生机构中执业满五年；但是，依照本法第十一条第二款规定取得中医医师资格的人员，按照考核内容进行执业注册后，即可在注册的执业范围内个体行医。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">县级以上地方人民政府卫生健康主管部门对个体行医的医师，应当按照国家有关规定实施监督检查，发现有本法规定注销注册的情形的，应当及时注销注册，废止医师执业证书。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二十一条　</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">县级以上地方人民政府卫生健康主管部门应当将准予注册和注销注册的人员名单及时予以公告，由省级人民政府卫生健康主管部门汇总，报国务院卫生健康主管部门备案，并按照规定通过网站提供医师注册信息查询服务。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span>&nbsp;</span></p>\r\n<p align=\"center\" style=\'margin: 0cm 0cm 0pt; text-align: center; line-height: 13.5pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三章　执业规则</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二十二条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师在执业活动中享有下列权利：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（一）在注册的执业范围内，按照有关规范进行医学诊查、疾病调查、医学处置、出具相应的医学证明文件，选择合理的医疗、预防、保健方案；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（二）获取劳动报酬，享受国家规定的福利待遇，按照规定参加社会保险并享受相应待遇；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（三）获得符合国家规定标准的执业基本条件和职业防护装备；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（四）从事医学教育、研究、学术交流；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（五）参加专业培训，接受继续医学教育；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（六）对所在医疗卫生机构和卫生健康主管部门的工作提出意见和建议，依法参与所在机构的民主管理；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（七）法律、法规规定的其他权利。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二十三条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师在执业活动中履行下列义务：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（一）树立敬业精神，恪守职业道德，履行医师职责，尽职尽责救治患者，执行疫情防控等公共卫生措施；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（二）遵循临床诊疗指南，遵守临床技术操作规范和医学伦理规范等；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（三）尊重、关心、爱护患者，依法保护患者隐私和个人信息；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（四）努力钻研业务，更新知识，提高医学专业技术能力和水平，提升医疗卫生服务质量；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（五）宣传推广与岗位相适应的健康科普知识，对患者及公众进行健康教育和健康指导；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（六）法律、法规规定的其他义务。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二十四条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师实施医疗、预防、保健措施，签署有关医学证明文件，必须亲自诊查、调查，并按照规定及时填写病历等医学文书，不得隐匿、伪造、篡改或者擅自销毁病历等医学文书及有关资料。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医师不得出具虚假医学证明文件以及与自己执业范围无关或者与执业类别不相符的医学证明文件。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二十五条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师在诊疗活动中应当向患者说明病情、医疗措施和其他需要告知的事项。需要实施手术、特殊检查、特殊治疗的，医师应当及时向患者具体说明医疗风险、替代医疗方案等情况，并取得其明确同意；不能或者不宜向患者说明的，应当向患者的近亲属说明，并取得其明确同意。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二十六条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师开展药物、医疗器械临床试验和其他医学临床研究应当符合国家有关规定，遵守医学伦理规范，依法通过伦理审查，取得书面知情同意。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二十七条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　对需要紧急救治的患者，医师应当采取紧急措施进行诊治，不得拒绝急救处置。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">因抢救生命垂危的患者等紧急情况，不能取得患者或者其近亲属意见的，经医疗机构负责人或者授权的负责人批准，可以立即实施相应的医疗措施。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">国家鼓励医师积极参与公共交通工具等公共场所急救服务；医师因自愿实施急救造成受助人损害的，不承担民事责任。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二十八条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师应当使用经依法批准或者备案的药品、消毒药剂、医疗器械，采用合法、合规、科学的诊疗方法。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">除按照规范用于诊断治疗外，不得使用麻醉药品、医疗用毒性药品、精神药品、放射性药品等。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第二十九条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师应当坚持安全有效、经济合理的用药原则，遵循药品临床应用指导原则、临床诊疗指南和药品说明书等合理用药。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">在尚无有效或者更好治疗手段等特殊情况下，医师取得患者明确知情同意后，可以采用药品说明书中未明确但具有循证医学证据的药品用法实施治疗。医疗机构应当建立管理制度，对医师处方、用药医嘱的适宜性进行审核，严格规范医师用药行为。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三十条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　执业医师按照国家有关规定，经所在医疗卫生机构同意，可以通过互联网等信息技术提供部分常见病、慢性病复诊等适宜的医疗卫生服务。国家支持医疗卫生机构之间利用互联网等信息技术开展远程医疗合作。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三十一条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师不得利用职务之便，索要、非法收受财物或者牟取其他不正当利益；不得对患者实施不必要的检查、治疗。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三十二条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　遇有自然灾害、事故灾难、公共卫生事件和社会安全事件等严重威胁人民生命健康的突发事件时，县级以上人民政府卫生健康主管部门根据需要组织医师参与卫生应急处置和医疗救治，医师应当服从调遣。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三十三条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　在执业活动中有下列情形之一的，医师应当按照有关规定及时向所在医疗卫生机构或者有关部门、机构报告：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（一）发现传染病、突发不明原因疾病或者异常健康事件；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（二）发生或者发现医疗事故；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（三）发现可能与药品、医疗器械有关的不良反应或者不良事件；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（四）发现假药或者劣药；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（五）发现患者涉嫌伤害事件或者非正常死亡；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（六）法律、法规规定的其他情形。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三十四条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　执业助理医师应当在执业医师的指导下，在医疗卫生机构中按照注册的执业类别、执业范围执业。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">在乡、民族乡、镇和村医疗卫生机构以及艰苦边远地区县级医疗卫生机构中执业的执业助理医师，可以根据医疗卫生服务情况和本人实践经验，独立从事一般的执业活动。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三十五条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　参加临床教学实践的医学生和尚未取得医师执业证书、在医疗卫生机构中参加医学专业工作实践的医学毕业生，应当在执业医师监督、指导下参与临床诊疗活动。医疗卫生机构应当为有关医学生、医学毕业生参与临床诊疗活动提供必要的条件。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三十六条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　有关行业组织、医疗卫生机构、医学院校应当加强对医师的医德医风教育。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医疗卫生机构应当建立健全医师岗位责任、内部监督、投诉处理等制度，加强对医师的管理。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span>&nbsp;</span></p>\r\n<p align=\"center\" style=\'margin: 0cm 0cm 0pt; text-align: center; line-height: 13.5pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四章　培训和考核</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三十七条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家制定医师培养规划，建立适应行业特点和社会需求的医师培养和供需平衡机制，统筹各类医学人才需求，加强全科、儿科、精神科、老年医学等紧缺专业人才培养。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">国家采取措施，加强医教协同，完善医学院校教育、毕业后教育和继续教育体系。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">国家通过多种途径，加强以全科医生为重点的基层医疗卫生人才培养和配备。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">国家采取措施，完善中医西医相互学习的教育制度，培养高层次中西医结合人才和能够提供中西医结合服务的全科医生。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三十八条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家建立健全住院医师规范化培训制度，健全临床带教激励机制，保障住院医师培训期间待遇，严格培训过程管理和结业考核。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">国家建立健全专科医师规范化培训制度，不断提高临床医师专科诊疗水平。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第三十九条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　县级以上人民政府卫生健康主管部门和其他有关部门应当制定医师培训计划，采取多种形式对医师进行分级分类培训，为医师接受继续医学教育提供条件。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">县级以上人民政府应当采取有力措施，优先保障基层、欠发达地区和民族地区的医疗卫生人员接受继续医学教育。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四十条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医疗卫生机构应当合理调配人力资源，按照规定和计划保证本机构医师接受继续医学教育。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">县级以上人民政府卫生健康主管部门应当有计划地组织协调县级以上医疗卫生机构对乡镇卫生院、村卫生室、社区卫生服务中心等基层医疗卫生机构中的医疗卫生人员开展培训，提高其医学专业技术能力和水平。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">有关行业组织应当为医师接受继续医学教育提供服务和创造条件，加强继续医学教育的组织、管理。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四十一条　</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">国家在每年的医学专业招生计划和教育培训计划中，核定一定比例用于定向培养、委托培训，加强基层和艰苦边远地区医师队伍建设。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">有关部门、医疗卫生机构与接受定向培养、委托培训的人员签订协议，约定相关待遇、服务年限、违约责任等事项，有关人员应当履行协议约定的义务。县级以上人民政府有关部门应当采取措施，加强履约管理。协议各方违反约定的，应当承担违约责任。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四十二条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家实行医师定期考核制度。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">县级以上人民政府卫生健康主管部门或者其委托的医疗卫生机构、行业组织应当按照医师执业标准，对医师的业务水平、工作业绩和职业道德状况进行考核，考核周期为三年。对具有较长年限执业经历、无不良行为记录的医师，可以简化考核程序。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">受委托的机构或者组织应当将医师考核结果报准予注册的卫生健康主管部门备案。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">对考核不合格的医师，县级以上人民政府卫生健康主管部门应当责令其暂停执业活动三个月至六个月，并接受相关专业培训。暂停执业活动期满，再次进行考核，对考核合格的，允许其继续执业。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四十三条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　省级以上人民政府卫生健康主管部门负责指导、检查和监督医师考核工作。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span>&nbsp;</span></p>\r\n<p align=\"center\" style=\'margin: 0cm 0cm 0pt; text-align: center; line-height: 13.5pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五章　保障措施</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四十四条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家建立健全体现医师职业特点和技术劳动价值的人事、薪酬、职称、奖励制度。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">对从事传染病防治、放射医学和精神卫生工作以及其他特殊岗位工作的医师，应当按照国家有关规定给予适当的津贴。津贴标准应当定期调整。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">在基层和艰苦边远地区工作的医师，按照国家有关规定享受津贴、补贴政策，并在职称评定、职业发展、教育培训和表彰奖励等方面享受优惠待遇。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四十五条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家加强疾病预防控制人才队伍建设，建立适应现代化疾病预防控制体系的医师培养和使用机制。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">疾病预防控制机构、二级以上医疗机构以及乡镇卫生院、社区卫生服务中心等基层医疗卫生机构应当配备一定数量的公共卫生医师，从事人群疾病及危害因素监测、风险评估研判、监测预警、流行病学调查、免疫规划管理、职业健康管理等公共卫生工作。医疗机构应当建立健全管理制度，严格执行院内感染防控措施。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">国家建立公共卫生与临床医学相结合的人才培养机制，通过多种途径对临床医师进行疾病预防控制、突发公共卫生事件应对等方面业务培训，对公共卫生医师进行临床医学业务培训，完善医防结合和中西医协同防治的体制机制。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四十六条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家采取措施，统筹城乡资源，加强基层医疗卫生队伍和服务能力建设，对乡村医疗卫生人员建立县乡村上下贯通的职业发展机制，通过县管乡用、乡聘村用等方式，将乡村医疗卫生人员纳入县域医疗卫生人员管理。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">执业医师晋升为副高级技术职称的，应当有累计一年以上在县级以下或者对口支援的医疗卫生机构提供医疗卫生服务的经历；晋升副高级技术职称后，在县级以下或者对口支援的医疗卫生机构提供医疗卫生服务，累计一年以上的，同等条件下优先晋升正高级技术职称。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">国家采取措施，鼓励取得执业医师资格或者执业助理医师资格的人员依法开办村医疗卫生机构，或者在村医疗卫生机构提供医疗卫生服务。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四十七条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家鼓励在村医疗卫生机构中向村民提供预防、保健和一般医疗服务的乡村医生通过医学教育取得医学专业学历；鼓励符合条件的乡村医生参加医师资格考试，依法取得医师资格。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">国家采取措施，通过信息化、智能化手段帮助乡村医生提高医学技术能力和水平，进一步完善对乡村医生的服务收入多渠道补助机制和养老等政策。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">乡村医生的具体管理办法，由国务院制定。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四十八条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医师有下列情形之一的，按照国家有关规定给予表彰、奖励：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（一）在执业活动中，医德高尚，事迹突出；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（二）在医学研究、教育中开拓创新，对医学专业技术有重大突破，做出显著贡献；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（三）遇有突发事件时，在预防预警、救死扶伤等工作中表现突出；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（四）长期在艰苦边远地区的县级以下医疗卫生机构努力工作；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（五）在疾病预防控制、健康促进工作中做出突出贡献；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（六）法律、法规规定的其他情形。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第四十九条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　县级以上人民政府及其有关部门应当将医疗纠纷预防和处理工作纳入社会治安综合治理体系，加强医疗卫生机构及周边治安综合治理，维护医疗卫生机构良好的执业环境，有效防范和依法打击涉医违法犯罪行为，保护医患双方合法权益。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医疗卫生机构应当完善安全保卫措施，维护良好的医疗秩序，及时主动化解医疗纠纷，保障医师执业安全。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">禁止任何组织或者个人阻碍医师依法执业，干扰医师正常工作、生活；禁止通过侮辱、诽谤、威胁、殴打等方式，侵犯医师的人格尊严、人身安全。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五十条　</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医疗卫生机构应当为医师提供职业安全和卫生防护用品，并采取有效的卫生防护和医疗保健措施。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">医师受到事故伤害或者在职业活动中因接触有毒、有害因素而引起疾病、死亡的，依照有关法律、行政法规的规定享受工伤保险待遇。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五十一条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　医疗卫生机构应当为医师合理安排工作时间，落实带薪休假制度，定期开展健康检查。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五十二条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家建立完善医疗风险分担机制。医疗机构应当参加医疗责任保险或者建立、参加医疗风险基金。鼓励患者参加医疗意外保险。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五十三条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　新闻媒体应当开展医疗卫生法律、法规和医疗卫生知识的公益宣传，弘扬医师先进事迹，引导公众尊重医师、理性对待医疗卫生风险。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span>&nbsp;</span></p>\r\n<p align=\"center\" style=\'margin: 0cm 0cm 0pt; text-align: center; line-height: 13.5pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第六章　法律责任</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五十四条　</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">在医师资格考试中有违反考试纪律等行为，情节严重的，一年至三年内禁止参加医师资格考试。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">以不正当手段取得医师资格证书或者医师执业证书的，由发给证书的卫生健康主管部门予以撤销，三年内不受理其相应申请。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">伪造、变造、买卖、出租、出借医师执业证书的，由县级以上人民政府卫生健康主管部门责令改正，没收违法所得，并处违法所得二倍以上五倍以下的罚款，违法所得不足一万元的，按一万元计算；情节严重的，吊销医师执业证书。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五十五条　</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">违反本法规定，医师在执业活动中有下列行为之一的，由县级以上人民政府卫生健康主管部门责令改正，给予警告；情节严重的，责令暂停六个月以上一年以下执业活动直至吊销医师执业证书：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（一）在提供医疗卫生服务或者开展医学临床研究中，未按照规定履行告知义务或者取得知情同意；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（二）对需要紧急救治的患者，拒绝急救处置，或者由于不负责任延误诊治；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（三）遇有自然灾害、事故灾难、公共卫生事件和社会安全事件等严重威胁人民生命健康的突发事件时，不服从卫生健康主管部门调遣；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（四）未按照规定报告有关情形；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（五）违反法律、法规、规章或者执业规范，造成医疗事故或者其他严重后果。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五十六条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　违反本法规定，医师在执业活动中有下列行为之一的，由县级以上人民政府卫生健康主管部门责令改正，给予警告，没收违法所得，并处一万元以上三万元以下的罚款；情节严重的，责令暂停六个月以上一年以下执业活动直至吊销医师执业证书：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（一）泄露患者隐私或者个人信息；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（二）出具虚假医学证明文件，或者未经亲自诊查、调查，签署诊断、治疗、流行病学等证明文件或者有关出生、死亡等证明文件；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（三）隐匿、伪造、篡改或者擅自销毁病历等医学文书及有关资料；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（四）未按照规定使用麻醉药品、医疗用毒性药品、精神药品、放射性药品等；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（五）利用职务之便，索要、非法收受财物或者牟取其他不正当利益，或者违反诊疗规范，对患者实施不必要的检查、治疗造成不良后果；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（六）开展禁止类医疗技术临床应用。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五十七条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　违反本法规定，医师未按照注册的执业地点、执业类别、执业范围执业的，由县级以上人民政府卫生健康主管部门或者中医药主管部门责令改正，给予警告，没收违法所得，并处一万元以上三万元以下的罚款；情节严重的，责令暂停六个月以上一年以下执业活动直至吊销医师执业证书。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五十八条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　严重违反医师职业道德、医学伦理规范，造成恶劣社会影响的，由省级以上人民政府卫生健康主管部门吊销医师执业证书或者责令停止非法执业活动，五年直至终身禁止从事医疗卫生服务或者医学临床研究。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第五十九条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　违反本法规定，非医师行医的，由县级以上人民政府卫生健康主管部门责令停止非法执业活动，没收违法所得和药品、医疗器械，并处违法所得二倍以上十倍以下的罚款，违法所得不足一万元的，按一万元计算。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第六十条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　违反本法规定，阻碍医师依法执业，干扰医师正常工作、生活，或者通过侮辱、诽谤、威胁、殴打等方式，侵犯医师人格尊严、人身安全，构成违反治安管理行为的，依法给予治安管理处罚。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第六十一条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　违反本法规定，医疗卫生机构未履行报告职责，造成严重后果的，由县级以上人民政府卫生健康主管部门给予警告，对直接负责的主管人员和其他直接责任人员依法给予处分。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第六十二条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　违反本法规定，卫生健康主管部门和其他有关部门工作人员或者医疗卫生机构工作人员弄虚作假、滥用职权、玩忽职守、徇私舞弊的，依法给予处分。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第六十三条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　违反本法规定，构成犯罪的，依法追究刑事责任；造成人身、财产损害的，依法承担民事责任。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span>&nbsp;</span></p>\r\n<p align=\"center\" style=\'margin: 0cm 0cm 0pt; text-align: center; line-height: 13.5pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第七章　附　　则</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第六十四条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　国家采取措施，鼓励具有中等专业学校医学专业学历的人员通过参加更高层次学历教育等方式，提高医学技术能力和水平。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">在本法施行前以及在本法施行后一定期限内取得中等专业学校相关医学专业学历的人员，可以参加医师资格考试。具体办法由国务院卫生健康主管部门会同国务院教育、中医药等有关部门制定。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第六十五条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　中国人民解放军和中国人民武装警察部队执行本法的具体办法，由国务院、中央军事委员会依据本法制定。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第六十六条　</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">境外人员参加医师资格考试、申请注册、执业或者从事临床示教、临床研究、临床学术交流等活动的具体管理办法，由国务院卫生健康主管部门制定。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; line-height: 13.5pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 黑体; font-size: 9pt;\">第六十七条</span><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">　本法自<span>2022</span>年<span>3</span>月<span>1</span>日起施行。《中华人民共和国执业医师法》同时废止。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span>&nbsp;&nbsp;&nbsp; </span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span>&nbsp;</span></p>\r\n<p align=\"right\" style=\'margin: 0cm 0cm 0pt; text-align: right; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"background: white; font-family: 宋体; font-size: 9pt;\">（转自中国人大网）</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2467','3291','1','','','一、合法按劳取酬，不接受商业提成。依法依规按劳取酬。严禁利用执业之便开单提成；严禁以商业目的进行统方；除就诊医院所在医联体的其他医疗机构，和被纳入医保“双通道”管理的定点零售药店外，严禁安排患者到其他指定地点购买医药耗材等产品；严禁向患者推销商品或服','172.16.1.2','172.16.1.2','1654658690','<p class=\"MsoNormal\" style=\"text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">一、合法按劳取酬，不接受商业提成。依法依规按劳取酬。严禁利用执业之便开单提成；严禁以商业目的进行统方；除就诊医院所在医联体的其他医疗机构，和被纳入医保“双通道”管理的定点零售药店外，严禁安排患者到其他指定地点购买医药耗材等产品；严禁向患者推销商品或服务并从中谋取私利；严禁接受互联网企业与开处方配药有关的费用。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">二、严守诚信原则，不参与欺诈骗保。依法依规合理使用医疗保障基金，遵守医保协议管理，向医保患者告知提供的医药服务是否在医保规定的支付范围内。严禁诱导、协助他人冒名或者虚假就医、购药、提供虚假证明材料、串通他人虚开费用单据等手段骗取、套取医疗保障基金。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">三、依据规范行医，不实施过度诊疗。严格执行各项规章制度，在诊疗活动中应当向患者说明病情、医疗措施。严禁以单纯增加医疗机构收入或谋取私利为目的过度治疗和过度检查，给患者增加不必要的风险和费用负担。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">四、遵守工作规程，不违规接受捐赠。依法依规接受捐赠。严禁医疗机构工作人员以个人名义，或者假借单位名义接受利益相关者的捐赠资助，并据此区别对待患者。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">五、恪守保密准则，不泄露患者隐私。确保患者院内信息安全。严禁违规收集、使用、加工、传输、透露、买卖患者在医疗机构内所提供的个人资料、产生的医疗信息。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">六、服从诊疗需要，不牟利转介患者。客观公正合理地根据患者需要提供医学信息、运用医疗资源。除因需要在医联体内正常转诊外，严禁以谋取个人利益为目的，经由网上或线下途径介绍、引导患者到指定医疗机构就诊。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">七、维护诊疗秩序，不破坏就医公平。坚持平等原则，共建公平就医环境。严禁利用号源、床源、紧缺药品耗材等医疗资源或者检查、手术等诊疗安排收受好处、损公肥私。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">八、共建和谐关系，不收受患方“红包”。恪守医德、严格自律。严禁索取或者收受患者及其亲友的礼品、礼金、消费卡和有价证券、股权、其他金融产品等财物；严禁参加其安排、组织或者支付费用的宴请或者旅游、健身、娱乐等活动安排。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">九、恪守交往底线，不收受企业回扣。遵纪守法、廉洁从业。严禁接受药品、医疗设备、医疗器械、医用卫生材料等医疗产品生产、经营企业或者经销人员以任何名义、形式给予的回扣；严禁参加其安排、组织或者支付费用的宴请或者旅游、健身、娱乐等活动安排。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">医疗机构内工作人员，包括但不限于卫生专业技术人员、管理人员、后勤人员以及在医疗机构内提供服务、接受医疗机构管理的其他社会从业人员，应当依据《九项准则》有关要求，服从管理、严格执行。违反法律法规等有关规定并符合法定处罚处分情形的，可依据《中华人民共和国基本医疗卫生与健康促进法》《中华人民共和国传染病防治法》《中华人民共和国社会保险法》《中华人民共和国公益事业捐赠法》《中华人民共和国医师法》《中华人民共和国药品管理法》《护士条例》《医疗纠纷预防和处理条例》《医疗保障基金使用监督管理条例》《医疗机构医疗保障定点管理暂行办法》《处方管理办法》等规定的责令改正、给予警告、给予相关人员或科室中止或者终止医保结算、追回医疗保障基金、没收违法所得、并处罚款、暂停处方权或者执业活动直至吊销执业证书等措施，依法追究有关机构和人员责任；依据《中华人民共和国劳动合同法》《事业单位工作人员处分暂行规定》等规定的给予解除劳动合同、警告、记过、降低岗位等级或者撤职、开除处分等措施，对有关人员依法作出处理；依据《医疗机构从业人员行为规范》等规定的由所在单位给予批评教育、取消当年评优评职资格或低聘、缓聘、解职待聘、解聘等措施，由所在单位依法作出处理。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">有关人员违反党纪、政纪的，移交纪检监察机关给予党纪政务处分；涉嫌犯罪的，移送司法机关追究刑事责任。对于违反《九项准则》行为多发或者造成恶劣社会影响等其他严重后果的医疗机构负责人，依照有关规定，予以问责。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\"></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2468','3292','1','','','第一章　总则　　第一条　为了坚持和加强党对信访工作的全面领导，做好新时代信访工作，保持党和政府同人民群众的密切联系，制定本条例。　　第二条　本条例适用于各级党的机关、人大机关、行政机关、政协机关、监察机关、审判机关、检察机关以及群团组织、国有企事业单','172.16.1.2','172.16.1.2','1654658722','<p class=\"MsoNormal\" style=\"text-indent: 21pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">第一章　总则</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第一条　为了坚持和加强党对信访工作的全面领导，做好新时代信访工作，保持党和政府同人民群众的密切联系，制定本条例。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二条　本条例适用于各级党的机关、人大机关、行政机关、政协机关、监察机关、审判机关、检察机关以及群团组织、国有企事业单位等开展信访工作。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三条　信访工作是党的群众工作的重要组成部分，是党和政府了解民情、集中民智、维护民利、凝聚民心的一项重要工作，是各级机关、单位及其领导干部、工作人员接受群众监督、改进工作作风的重要途径。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四条　信访工作坚持以马克思列宁主义、毛泽东思想、邓小平理论、“三个代表”重要思想、科学发展观、习近平新时代中国特色社会主义思想为指导，贯彻落实习近平总书记关于加强和改进人民信访工作的重要思想，增强“四个意识”、坚定“四个自信”、做到“两个维护”，牢记为民解难、为党分忧的政治责任，坚守人民情怀，坚持底线思维、法治思维，服务党和国家工作大局，维护群众合法权益，化解信访突出问题，促进社会和谐稳定。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第五条　信访工作应当遵循下列原则：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）坚持党的全面领导。把党的领导贯彻到信访工作各方面和全过程，确保正确政治方向。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）坚持以人民为中心。践行党的群众路线，倾听群众呼声，关心群众疾苦，千方百计为群众排忧解难。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）坚持落实信访工作责任。党政同责、一岗双责，属地管理、分级负责，谁主管、谁负责。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（四）坚持依法按政策解决问题。将信访纳入法治化轨道，依法维护群众权益、规范信访秩序。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（五）坚持源头治理化解矛盾。多措并举、综合施策，着力点放在源头预防和前端化解，把可能引发信访问题的矛盾纠纷化解在基层、化解在萌芽状态。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第六条　各级机关、单位应当畅通信访渠道，做好信访工作，认真处理信访事项，倾听人民群众建议、意见和要求，接受人民群众监督，为人民群众服务。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二章　信访工作体制</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第七条　坚持和加强党对信访工作的全面领导，构建党委统一领导、政府组织落实、信访工作联席会议协调、信访部门推动、各方齐抓共管的信访工作格局。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第八条　党中央加强对信访工作的统一领导：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）强化政治引领，确立信访工作的政治方向和政治原则，严明政治纪律和政治规矩；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）制定信访工作方针政策，研究部署信访工作中事关党和国家工作大局、社会和谐稳定、群众权益保障的重大改革措施；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）领导建设一支对党忠诚可靠、恪守为民之责、善做群众工作的高素质专业化信访工作队伍，为信访工作提供组织保证。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第九条　地方党委领导本地区信访工作，贯彻落实党中央关于信访工作的方针政策和决策部署，执行上级党组织关于信访工作的部署要求，统筹信访工作责任体系构建，支持和督促下级党组织做好信访工作。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　地方党委常委会应当定期听取信访工作汇报，分析形势，部署任务，研究重大事项，解决突出问题。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第十条　各级政府贯彻落实上级党委和政府以及本级党委关于信访工作的部署要求，科学民主决策、依法履行职责，组织各方力量加强矛盾纠纷排查化解，及时妥善处理信访事项，研究解决政策性、群体性信访突出问题和疑难复杂信访问题。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第十一条　中央信访工作联席会议在党中央、国务院领导下，负责全国信访工作的统筹协调、整体推进、督促落实，履行下列职责：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）研究分析全国信访形势，为中央决策提供参考；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）督促落实党中央关于信访工作的方针政策和决策部署；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）研究信访制度改革和信访法治化建设重大问题和事项；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（四）研究部署重点工作任务，协调指导解决具有普遍性的信访突出问题；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（五）领导组织信访工作责任制落实、督导考核等工作；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（六）指导地方各级信访工作联席会议工作；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（七）承担党中央、国务院交办的其他事项。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　中央信访工作联席会议由党中央、国务院领导同志以及有关部门负责同志担任召集人，各成员单位负责同志参加。中央信访工作联席会议办公室设在国家信访局，承担联席会议的日常工作，督促检查联席会议议定事项的落实。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第十二条　中央信访工作联席会议根据工作需要召开全体会议或者工作会议。研究涉及信访工作改革发展的重大问题和重要信访事项的处理意见，应当及时向党中央、国务院请示报告。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　中央信访工作联席会议各成员单位应当落实联席会议确定的工作任务和议定事项，及时报送落实情况；及时将本领域重大敏感信访问题提请联席会议研究。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第十三条　地方各级信访工作联席会议在本级党委和政府领导下，负责本地区信访工作的统筹协调、整体推进、督促落实，协调处理发生在本地区的重要信访问题，指导下级信访工作联席会议工作。联席会议召集人一般由党委和政府负责同志担任。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　地方党委和政府应当根据信访工作形势任务，及时调整成员单位，健全规章制度，建立健全信访信息分析研判、重大信访问题协调处理、联合督查等工作机制，提升联席会议工作的科学化、制度化、规范化水平。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　根据工作需要，乡镇党委和政府、街道党工委和办事处可以建立信访工作联席会议机制，或者明确党政联席会定期研究本地区信访工作，协调处理发生在本地区的重要信访问题。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第十四条　各级党委和政府信访部门是开展信访工作的专门机构，履行下列职责：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）受理、转送、交办信访事项；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）协调解决重要信访问题；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）督促检查重要信访事项的处理和落实；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（四）综合反映信访信息，分析研判信访形势，为党委和政府提供决策参考；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（五）指导本级其他机关、单位和下级的信访工作；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（六）提出改进工作、完善政策和追究责任的建议；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（七）承担本级党委和政府交办的其他事项。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级党委和政府信访部门以外的其他机关、单位应当根据信访工作形势任务，明确负责信访工作的机构或者人员，参照党委和政府信访部门职责，明确相应的职责。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第十五条　各级党委和政府以外的其他机关、单位应当做好各自职责范围内的信访工作，按照规定及时受理办理信访事项，预防和化解政策性、群体性信访问题，加强对下级机关、单位信访工作的指导。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级机关、单位应当拓宽社会力量参与信访工作的制度化渠道，发挥群团组织、社会组织和“两代表一委员”、社会工作者等作用，反映群众意见和要求，引导群众依法理性反映诉求、维护权益，推动矛盾纠纷及时有效化解。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　乡镇党委和政府、街道党工委和办事处以及村（社区）“两委”应当全面发挥职能作用，坚持和发展新时代“枫桥经验”，积极协调处理化解发生在当地的信访事项和矛盾纠纷，努力做到小事不出村、大事不出镇、矛盾不上交。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第十六条　各级党委和政府应当加强信访部门建设，选优配强领导班子，配备与形势任务相适应的工作力量，建立健全信访督查专员制度，打造高素质专业化信访干部队伍。各级党委和政府信访部门主要负责同志应当由本级党委或者政府副秘书长〔办公厅（室）副主任〕兼任。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级党校（行政学院）应当将信访工作作为党性教育内容纳入教学培训，加强干部教育培训。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级机关、单位应当建立健全年轻干部和新录用干部到信访工作岗位锻炼制度。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级党委和政府应当为信访工作提供必要的支持和保障，所需经费列入本级预算。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三章　信访事项的提出和受理</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第十七条　公民、法人或者其他组织可以采用信息网络、书信、电话、传真、走访等形式，向各级机关、单位反映情况，提出建议、意见或者投诉请求，有关机关、单位应当依规依法处理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　采用前款规定的形式，反映情况，提出建议、意见或者投诉请求的公民、法人或者其他组织，称信访人。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第十八条　各级机关、单位应当向社会公布网络信访渠道、通信地址、咨询投诉电话、信访接待的时间和地点、查询信访事项处理进展以及结果的方式等相关事项，在其信访接待场所或者网站公布与信访工作有关的党内法规和法律、法规、规章，信访事项的处理程序，以及其他为信访人提供便利的相关事项。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级机关、单位领导干部应当阅办群众来信和网上信访、定期接待群众来访、定期下访，包案化解群众反映强烈的突出问题。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　市、县级党委和政府应当建立和完善联合接访工作机制，根据工作需要组织有关机关、单位联合接待，一站式解决信访问题。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　任何组织和个人不得打击报复信访人。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第十九条　信访人一般应当采用书面形式提出信访事项，并载明其姓名（名称）、住址和请求、事实、理由。对采用口头形式提出的信访事项，有关机关、单位应当如实记录。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　信访人提出信访事项，应当客观真实，对其所提供材料内容的真实性负责，不得捏造、歪曲事实，不得诬告、陷害他人。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　信访事项已经受理或者正在办理的，信访人在规定期限内向受理、办理机关、单位的上级机关、单位又提出同一信访事项的，上级机关、单位不予受理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二十条　信访人采用走访形式提出信访事项的，应当到有权处理的本级或者上一级机关、单位设立或者指定的接待场所提出。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　信访人采用走访形式提出涉及诉讼权利救济的信访事项，应当按照法律法规规定的程序向有关政法部门提出。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　多人采用走访形式提出共同的信访事项的，应当推选代表，代表人数不得超过</span><span lang=\"EN-US\">5</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">人。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级机关、单位应当落实属地责任，认真接待处理群众来访，把问题解决在当地，引导信访人就地反映问题。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二十一条　各级党委和政府应当加强信访工作信息化、智能化建设，依规依法有序推进信访信息系统互联互通、信息共享。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级机关、单位应当及时将信访事项录入信访信息系统，使网上信访、来信、来访、来电在网上流转，方便信访人查询、评价信访事项办理情况。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二十二条　各级党委和政府信访部门收到信访事项，应当予以登记，并区分情况，在</span><span lang=\"EN-US\">15</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">日内分别按照下列方式处理：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）对依照职责属于本级机关、单位或者其工作部门处理决定的，应当转送有权处理的机关、单位；情况重大、紧急的，应当及时提出建议，报请本级党委和政府决定。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）涉及下级机关、单位或者其工作人员的，按照“属地管理、分级负责，谁主管、谁负责”的原则，转送有权处理的机关、单位。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）对转送信访事项中的重要情况需要反馈办理结果的，可以交由有权处理的机关、单位办理，要求其在指定办理期限内反馈结果，提交办结报告。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级党委和政府信访部门对收到的涉法涉诉信件，应当转送同级政法部门依法处理；对走访反映涉诉问题的信访人，应当释法明理，引导其向有关政法部门反映问题。对属于纪检监察机关受理的检举控告类信访事项，应当按照管理权限转送有关纪检监察机关依规依纪依法处理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二十三条　党委和政府信访部门以外的其他机关、单位收到信访人直接提出的信访事项，应当予以登记；对属于本机关、单位职权范围的，应当告知信访人接收情况以及处理途径和程序；对属于本系统下级机关、单位职权范围的，应当转送、交办有权处理的机关、单位，并告知信访人转送、交办去向；对不属于本机关、单位或者本系统职权范围的，应当告知信访人向有权处理的机关、单位提出。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　对信访人直接提出的信访事项，有关机关、单位能够当场告知的，应当当场书面告知；不能当场告知的，应当自收到信访事项之日起</span><span lang=\"EN-US\">15</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">日内书面告知信访人，但信访人的姓名（名称）、住址不清的除外。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　对党委和政府信访部门或者本系统上级机关、单位转送、交办的信访事项，属于本机关、单位职权范围的，有关机关、单位应当自收到之日起</span><span lang=\"EN-US\">15</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">日内书面告知信访人接收情况以及处理途径和程序；不属于本机关、单位或者本系统职权范围的，有关机关、单位应当自收到之日起</span><span lang=\"EN-US\">5</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">个工作日内提出异议，并详细说明理由，经转送、交办的信访部门或者上级机关、单位核实同意后，交还相关材料。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　政法部门处理涉及诉讼权利救济事项、纪检监察机关处理检举控告事项的告知按照有关规定执行。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二十四条　涉及两个或者两个以上机关、单位的信访事项，由所涉及的机关、单位协商受理；受理有争议的，由其共同的上一级机关、单位决定受理机关；受理有争议且没有共同的上一级机关、单位的，由共同的信访工作联席会议协调处理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　应当对信访事项作出处理的机关、单位分立、合并、撤销的，由继续行使其职权的机关、单位受理；职责不清的，由本级党委和政府或者其指定的机关、单位受理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二十五条　各级机关、单位对可能造成社会影响的重大、紧急信访事项和信访信息，应当及时报告本级党委和政府，通报相关主管部门和本级信访工作联席会议办公室，在职责范围内依法及时采取措施，防止不良影响的产生、扩大。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　地方各级党委和政府信访部门接到重大、紧急信访事项和信访信息，应当向上一级信访部门报告，同时报告国家信访局。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二十六条　信访人在信访过程中应当遵守法律、法规，不得损害国家、社会、集体的利益和其他公民的合法权利，自觉维护社会公共秩序和信访秩序，不得有下列行为：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）在机关、单位办公场所周围、公共场所非法聚集，围堵、冲击机关、单位，拦截公务车辆，或者堵塞、阻断交通；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）携带危险物品、管制器具；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）侮辱、殴打、威胁机关、单位工作人员，非法限制他人人身自由，或者毁坏财物；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（四）在信访接待场所滞留、滋事，或者将生活不能自理的人弃留在信访接待场所；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（五）煽动、串联、胁迫、以财物诱使、幕后操纵他人信访，或者以信访为名借机敛财；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（六）其他扰乱公共秩序、妨害国家和公共安全的行为。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四章　信访事项的办理</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二十七条　各级机关、单位及其工作人员应当根据各自职责和有关规定，按照诉求合理的解决问题到位、诉求无理的思想教育到位、生活困难的帮扶救助到位、行为违法的依法处理的要求，依法按政策及时就地解决群众合法合理诉求，维护正常信访秩序。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二十八条　各级机关、单位及其工作人员办理信访事项，应当恪尽职守、秉公办事，查明事实、分清责任，加强教育疏导，及时妥善处理，不得推诿、敷衍、拖延。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级机关、单位应当按照诉讼与信访分离制度要求，将涉及民事、行政、刑事等诉讼权利救济的信访事项从普通信访体制中分离出来，由有关政法部门依法处理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级机关、单位工作人员与信访事项或者信访人有直接利害关系的，应当回避。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第二十九条　对信访人反映的情况、提出的建议意见类事项，有权处理的机关、单位应当认真研究论证。对科学合理、具有现实可行性的，应当采纳或者部分采纳，并予以回复。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　信访人反映的情况、提出的建议意见，对国民经济和社会发展或者对改进工作以及保护社会公共利益有贡献的，应当按照有关规定给予奖励。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级党委和政府应当健全人民建议征集制度，对涉及国计民生的重要工作，主动听取群众的建议意见。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三十条　对信访人提出的检举控告类事项，纪检监察机关或者有权处理的机关、单位应当依规依纪依法接收、受理、办理和反馈。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　党委和政府信访部门应当按照干部管理权限向组织（人事）部门通报反映干部问题的信访情况，重大情况向党委主要负责同志和分管组织（人事）工作的负责同志报送。组织（人事）部门应当按照干部选拔任用监督的有关规定进行办理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　不得将信访人的检举、揭发材料以及有关情况透露或者转给被检举、揭发的人员或者单位。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三十一条　对信访人提出的申诉求决类事项，有权处理的机关、单位应当区分情况，分别按照下列方式办理：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）应当通过审判机关诉讼程序或者复议程序、检察机关刑事立案程序或者法律监督程序、公安机关法律程序处理的，涉法涉诉信访事项未依法终结的，按照法律法规规定的程序处理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）应当通过仲裁解决的，导入相应程序处理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）可以通过党员申诉、申请复审等解决的，导入相应程序处理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（四）可以通过行政复议、行政裁决、行政确认、行政许可、行政处罚等行政程序解决的，导入相应程序处理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（五）属于申请查处违法行为、履行保护人身权或者财产权等合法权益职责的，依法履行或者答复。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（六）不属于以上情形的，应当听取信访人陈述事实和理由，并调查核实，出具信访处理意见书。对重大、复杂、疑难的信访事项，可以举行听证。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三十二条　信访处理意见书应当载明信访人投诉请求、事实和理由、处理意见及其法律法规依据：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）请求事实清楚，符合法律、法规、规章或者其他有关规定的，予以支持；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）请求事由合理但缺乏法律依据的，应当作出解释说明；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）请求缺乏事实根据或者不符合法律、法规、规章或者其他有关规定的，不予支持。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　有权处理的机关、单位作出支持信访请求意见的，应当督促有关机关、单位执行；不予支持的，应当做好信访人的疏导教育工作。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三十三条　各级机关、单位在处理申诉求决类事项过程中，可以在不违反政策法规强制性规定的情况下，在裁量权范围内，经争议双方当事人同意进行调解；可以引导争议双方当事人自愿和解。经调解、和解达成一致意见的，应当制作调解协议书或者和解协议书。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三十四条　对本条例第三十一条第六项规定的信访事项应当自受理之日起</span><span lang=\"EN-US\">60</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">日内办结；情况复杂的，经本机关、单位负责人批准，可以适当延长办理期限，但延长期限不得超过</span><span lang=\"EN-US\">30</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">日，并告知信访人延期理由。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三十五条　信访人对信访处理意见不服的，可以自收到书面答复之日起</span><span lang=\"EN-US\">30</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">日内请求原办理机关、单位的上一级机关、单位复查。收到复查请求的机关、单位应当自收到复查请求之日起</span><span lang=\"EN-US\">30</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">日内提出复查意见，并予以书面答复。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三十六条　信访人对复查意见不服的，可以自收到书面答复之日起</span><span lang=\"EN-US\">30</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">日内向复查机关、单位的上一级机关、单位请求复核。收到复核请求的机关、单位应当自收到复核请求之日起</span><span lang=\"EN-US\">30</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">日内提出复核意见。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　复核机关、单位可以按照本条例第三十一条第六项的规定举行听证，经过听证的复核意见可以依法向社会公示。听证所需时间不计算在前款规定的期限内。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　信访人对复核意见不服，仍然以同一事实和理由提出投诉请求的，各级党委和政府信访部门和其他机关、单位不再受理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三十七条　各级机关、单位应当坚持社会矛盾纠纷多元预防调处化解，人民调解、行政调解、司法调解联动，综合运用法律、政策、经济、行政等手段和教育、协商、疏导等办法，多措并举化解矛盾纠纷。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级机关、单位在办理信访事项时，对生活确有困难的信访人，可以告知或者帮助其向有关机关或者机构依法申请社会救助。符合国家司法救助条件的，有关政法部门应当按照规定给予司法救助。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　地方党委和政府以及基层党组织和基层单位对信访事项已经复查复核和涉法涉诉信访事项已经依法终结的相关信访人，应当做好疏导教育、矛盾化解、帮扶救助等工作。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第五章　监督和追责</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三十八条　各级党委和政府应当对开展信访工作、落实信访工作责任的情况组织专项督查。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　信访工作联席会议及其办公室、党委和政府信访部门应当根据工作需要开展督查，就发现的问题向有关地方和部门进行反馈，重要问题向本级党委和政府报告。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　各级党委和政府督查部门应当将疑难复杂信访问题列入督查范围。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第三十九条　各级党委和政府应当以依规依法及时就地解决信访问题为导向，每年对信访工作情况进行考核。考核结果应当在适当范围内通报，并作为对领导班子和有关领导干部综合考核评价的重要参考。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　对在信访工作中作出突出成绩和贡献的机关、单位或者个人，可以按照有关规定给予表彰和奖励。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　对在信访工作中履职不力、存在严重问题的领导班子和领导干部，视情节轻重，由信访工作联席会议进行约谈、通报、挂牌督办，责令限期整改。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四十条　党委和政府信访部门发现有关机关、单位存在违反信访工作规定受理、办理信访事项，办理信访事项推诿、敷衍、拖延、弄虚作假或者拒不执行信访处理意见等情形的，应当及时督办，并提出改进工作的建议。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　对工作中发现的有关政策性问题，应当及时向本级党委和政府报告，并提出完善政策的建议。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　对在信访工作中推诿、敷衍、拖延、弄虚作假造成严重后果的机关、单位及其工作人员，应当向有管理权限的机关、单位提出追究责任的建议。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　对信访部门提出的改进工作、完善政策、追究责任的建议，有关机关、单位应当书面反馈采纳情况。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四十一条　党委和政府信访部门应当编制信访情况年度报告，每年向本级党委和政府、上一级党委和政府信访部门报告。年度报告应当包括下列内容：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）信访事项的数据统计、信访事项涉及领域以及被投诉较多的机关、单位；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）党委和政府信访部门转送、交办、督办情况；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）党委和政府信访部门提出改进工作、完善政策、追究责任建议以及被采纳情况；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（四）其他应当报告的事项。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　根据巡视巡察工作需要，党委和政府信访部门应当向巡视巡察机构提供被巡视巡察地区、单位领导班子及其成员和下一级主要负责人有关信访举报，落实信访工作责任制，具有苗头性、倾向性的重要信访问题，需要巡视巡察工作关注的重要信访事项等情况。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四十二条　因下列情形之一导致信访事项发生，造成严重后果的，对直接负责的主管人员和其他直接责任人员，依规依纪依法严肃处理；构成犯罪的，依法追究刑事责任：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）超越或者滥用职权，侵害公民、法人或者其他组织合法权益；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）应当作为而不作为，侵害公民、法人或者其他组织合法权益；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）适用法律、法规错误或者违反法定程序，侵害公民、法人或者其他组织合法权益；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（四）拒不执行有权处理机关、单位作出的支持信访请求意见。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四十三条　各级党委和政府信访部门对收到的信访事项应当登记、转送、交办而未按照规定登记、转送、交办，或者应当履行督办职责而未履行的，由其上级机关责令改正；造成严重后果的，对直接负责的主管人员和其他直接责任人员依规依纪依法严肃处理。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四十四条　负有受理信访事项职责的机关、单位有下列情形之一的，由其上级机关、单位责令改正；造成严重后果的，对直接负责的主管人员和其他直接责任人员依规依纪依法严肃处理：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）对收到的信访事项不按照规定登记；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）对属于其职权范围的信访事项不予受理；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）未在规定期限内书面告知信访人是否受理信访事项。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四十五条　对信访事项有权处理的机关、单位有下列情形之一的，由其上级机关、单位责令改正；造成严重后果的，对直接负责的主管人员和其他直接责任人员依规依纪依法严肃处理：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）推诿、敷衍、拖延信访事项办理或者未在规定期限内办结信访事项；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）对事实清楚，符合法律、法规、规章或者其他有关规定的投诉请求未予支持；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）对党委和政府信访部门提出的改进工作、完善政策等建议重视不够、落实不力，导致问题长期得不到解决；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（四）其他不履行或者不正确履行信访事项处理职责的情形。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四十六条　有关机关、单位及其领导干部、工作人员有下列情形之一的，由其上级机关、单位责令改正；造成严重后果的，对直接负责的主管人员和其他直接责任人员依规依纪依法严肃处理；构成犯罪的，依法追究刑事责任：</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（一）对待信访人态度恶劣、作风粗暴，损害党群干群关系；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（二）在处理信访事项过程中吃拿卡要、谋取私利；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（三）对规模性集体访、负面舆情等处置不力，导致事态扩大；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（四）对可能造成社会影响的重大、紧急信访事项和信访信息隐瞒、谎报、缓报，或者未依法及时采取必要措施；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（五）将信访人的检举、揭发材料或者有关情况透露、转给被检举、揭发的人员或者单位；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（六）打击报复信访人；</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　（七）其他违规违纪违法的情形。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四十七条　信访人违反本条例第二十条、第二十六条规定的，有关机关、单位工作人员应当对其进行劝阻、批评或者教育。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　信访人滋事扰序、缠访闹访情节严重，构成违反治安管理行为的，或者违反集会游行示威相关法律法规的，由公安机关依法采取必要的现场处置措施、给予治安管理处罚；构成犯罪的，依法追究刑事责任。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　信访人捏造歪曲事实、诬告陷害他人，构成违反治安管理行为的，依法给予治安管理处罚；构成犯罪的，依法追究刑事责任。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第六章　附则</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四十八条　对外国人、无国籍人、外国组织信访事项的处理，参照本条例执行。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第四十九条　本条例由国家信访局负责解释。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">　　第五十条　本条例自</span><span lang=\"EN-US\">2022</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">年</span><span lang=\"EN-US\">5</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">月</span><span lang=\"EN-US\">1</span><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">日起施行。</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 210pt; mso-char-indent-count: 20.0;\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">（转自中央纪委国家监委网站）</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2469','3293','1','','','（2021年7月20日国家监察委员会全体会议决定，自2021年9月20日起施行）　　目 录　　第一章 总 则　　第二章 监察机关及其职责　　第三章 监察范围和管辖　　第四章 监察权限　　第五章 监察程序　　第六章 反腐败国际合作　　第七章 对监察机关和监察人员的监督　　第','172.16.1.2','172.16.1.2','1654658827','<p align=\"center\" style=\"background: white; margin: 0cm 0cm 6.75pt; text-align: center; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>（<span lang=\"EN-US\">2021</span>年<span lang=\"EN-US\">7</span>月<span lang=\"EN-US\">20</span>日国家监察委员会全体会议决定，自<span lang=\"EN-US\">2021</span>年<span lang=\"EN-US\">9</span>月<span lang=\"EN-US\">20</span>日起施行）</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>　　目 录</span></strong><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一章 总 则</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二章 监察机关及其职责</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三章 监察范围和管辖</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四章 监察权限</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五章 监察程序</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六章 反腐败国际合作</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七章 对监察机关和监察人员的监督</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八章 法律责任</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九章 附 则</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　<strong><span style=\"mso-bidi-font-weight: bold;\">第一章 总 则</span></strong></span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一条 为了推动监察工作法治化、规范化，根据《中华人民共和国监察法》（以下简称监察法），结合工作实际，制定本条例。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二条 坚持中国共产党对监察工作的全面领导，增强政治意识、大局意识、核心意识、看齐意识，坚定中国特色社会主义道路自信、理论自信、制度自信、文化自信，坚决维护习近平总书记党中央的核心、全党的核心地位，坚决维护党中央权威和集中统一领导，把党的领导贯彻到监察工作各方面和全过程。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三条 监察机关与党的纪律检查机关合署办公，坚持法治思维和法治方式，促进执纪执法贯通、有效衔接司法，实现依纪监督和依法监察、适用纪律和适用法律有机融合。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四条 监察机关应当依法履行监督、调查、处置职责，坚持实事求是，坚持惩前毖后、治病救人，坚持惩戒与教育相结合，实现政治效果、法律效果和社会效果相统一。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五条 监察机关应当坚定不移惩治腐败，推动深化改革、完善制度，规范权力运行，加强思想道德教育、法治教育、廉洁教育，引导公职人员提高觉悟、担当作为、依法履职，一体推进不敢腐、不能腐、不想腐体制机制建设。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六条 监察机关坚持民主集中制，对于线索处置、立案调查、案件审理、处置执行、复审复核中的重要事项应当集体研究，严格按照权限履行请示报告程序。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七条 监察机关应当在适用法律上一律平等，充分保障监察对象以及相关人员的人身权、知情权、财产权、申辩权、申诉权以及申请复审复核权等合法权益。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八条 监察机关办理职务犯罪案件，应当与人民法院、人民检察院互相配合、互相制约，在案件管辖、证据审查、案件移送、涉案财物处置等方面加强沟通协调，对于人民法院、人民检察院提出的退回补充调查、排除非法证据、调取同步录音录像、要求调查人员出庭等意见依法办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九条 监察机关开展监察工作，可以依法提请组织人事、公安、国家安全、审计、统计、市场监管、金融监管、财政、税务、自然资源、银行、证券、保险等有关部门、单位予以协助配合。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　有关部门、单位应当根据监察机关的要求，依法协助采取有关措施、共享相关信息、提供相关资料和专业技术支持，配合开展监察工作。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>　　第二章 监察机关及其职责</span></strong><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一节 领导体制</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十条 国家监察委员会在党中央领导下开展工作。地方各级监察委员会在同级党委和上级监察委员会双重领导下工作，监督执法调查工作以上级监察委员会领导为主，线索处置和案件查办在向同级党委报告的同时应当一并向上一级监察委员会报告。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　上级监察委员会应当加强对下级监察委员会的领导。下级监察委员会对上级监察委员会的决定必须执行，认为决定不当的，应当在执行的同时向上级监察委员会反映。上级监察委员会对下级监察委员会作出的错误决定，应当按程序予以纠正，或者要求下级监察委员会予以纠正。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十一条 上级监察委员会可以依法统一调用所辖各级监察机关的监察人员办理监察事项。调用决定应当以书面形式作出。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关办理监察事项应当加强互相协作和配合，对于重要、复杂事项可以提请上级监察机关予以协调。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十二条 各级监察委员会依法向本级中国共产党机关、国家机关、法律法规授权或者受委托管理公共事务的组织和单位以及所管辖的国有企业事业单位等派驻或者派出监察机构、监察专员。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　省级和设区的市级监察委员会依法向地区、盟、开发区等不设置人民代表大会的区域派出监察机构或者监察专员。县级监察委员会和直辖市所辖区（县）监察委员会可以向街道、乡镇等区域派出监察机构或者监察专员。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机构、监察专员开展监察工作，受派出机关领导。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十三条 派驻或者派出的监察机构、监察专员根据派出机关授权，按照管理权限依法对派驻或者派出监督单位、区域等的公职人员开展监督，对职务违法和职务犯罪进行调查、处置。监察机构、监察专员可以按规定与地方监察委员会联合调查严重职务违法、职务犯罪，或者移交地方监察委员会调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　未被授予职务犯罪调查权的监察机构、监察专员发现监察对象涉嫌职务犯罪线索的，应当及时向派出机关报告，由派出机关调查或者依法移交有关地方监察委员会调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二节 监察监督</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十四条 监察机关依法履行监察监督职责，对公职人员政治品行、行使公权力和道德操守情况进行监督检查，督促有关机关、单位加强对所属公职人员的教育、管理、监督。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十五条 监察机关应当坚决维护宪法确立的国家指导思想，加强对公职人员特别是领导人员坚持党的领导、坚持中国特色社会主义制度，贯彻落实党和国家路线方针政策、重大决策部署，履行从严管理监督职责，依法行使公权力等情况的监督。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十六条 监察机关应当加强对公职人员理想教育、为人民服务教育、宪法法律法规教育、优秀传统文化教育，弘扬社会主义核心价值观，深入开展警示教育，教育引导公职人员树立正确的权力观、责任观、利益观，保持为民务实清廉本色。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十七条 监察机关应当结合公职人员的职责加强日常监督，通过收集群众反映、座谈走访、查阅资料、召集或者列席会议、听取工作汇报和述责述廉、开展监督检查等方式，促进公职人员依法用权、秉公用权、廉洁用权。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十八条 监察机关可以与公职人员进行谈心谈话，发现政治品行、行使公权力和道德操守方面有苗头性、倾向性问题的，及时进行教育提醒。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十九条 监察机关对于发现的系统性、行业性的突出问题，以及群众反映强烈的问题，可以通过专项检查进行深入了解，督促有关机关、单位强化治理，促进公职人员履职尽责。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十条 监察机关应当以办案促进整改、以监督促进治理，在查清问题、依法处置的同时，剖析问题发生的原因，发现制度建设、权力配置、监督机制等方面存在的问题，向有关机关、单位提出改进工作的意见或者监察建议，促进完善制度，提高治理效能。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十一条 监察机关开展监察监督，应当与纪律监督、派驻监督、巡视监督统筹衔接，与人大监督、民主监督、行政监督、司法监督、审计监督、财会监督、统计监督、群众监督和舆论监督等贯通协调，健全信息、资源、成果共享等机制，形成监督合力。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三节 监察调查</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十二条 监察机关依法履行监察调查职责，依据监察法、《中华人民共和国公职人员政务处分法》（以下简称政务处分法）和《中华人民共和国刑法》（以下简称刑法）等规定对职务违法和职务犯罪进行调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十三条 监察机关负责调查的职务违法是指公职人员实施的与其职务相关联，虽不构成犯罪但依法应当承担法律责任的下列违法行为：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）利用职权实施的违法行为；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）利用职务上的影响实施的违法行为；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）履行职责不力、失职失责的违法行为；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）其他违反与公职人员职务相关的特定义务的违法行为。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十四条 监察机关发现公职人员存在其他违法行为，具有下列情形之一的，可以依法进行调查、处置：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）超过行政违法追究时效，或者超过犯罪追诉时效、未追究刑事责任，但需要依法给予政务处分的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）被追究行政法律责任，需要依法给予政务处分的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）监察机关调查职务违法或者职务犯罪时，对被调查人实施的事实简单、清楚，需要依法给予政务处分的其他违法行为一并查核的。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关发现公职人员成为监察对象前有前款规定的违法行为的，依照前款规定办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十五条 监察机关依法对监察法第十一条第二项规定的职务犯罪进行调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十六条 监察机关依法调查涉嫌贪污贿赂犯罪，包括贪污罪，挪用公款罪，受贿罪，单位受贿罪，利用影响力受贿罪，行贿罪，对有影响力的人行贿罪，对单位行贿罪，介绍贿赂罪，单位行贿罪，巨额财产来源不明罪，隐瞒境外存款罪，私分国有资产罪，私分罚没财物罪，以及公职人员在行使公权力过程中实施的职务侵占罪，挪用资金罪，对外国公职人员、国际公共组织官员行贿罪，非国家工作人员受贿罪和相关联的对非国家工作人员行贿罪。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十七条 监察机关依法调查公职人员涉嫌滥用职权犯罪，包括滥用职权罪，国有公司、企业、事业单位人员滥用职权罪，滥用管理公司、证券职权罪，食品、药品监管渎职罪，故意泄露国家秘密罪，报复陷害罪，阻碍解救被拐卖、绑架妇女、儿童罪，帮助犯罪分子逃避处罚罪，违法发放林木采伐许可证罪，办理偷越国（边）境人员出入境证件罪，放行偷越国（边）境人员罪，挪用特定款物罪，非法剥夺公民宗教信仰自由罪，侵犯少数民族风俗习惯罪，打击报复会计、统计人员罪，以及司法工作人员以外的公职人员利用职权实施的非法拘禁罪、虐待被监管人罪、非法搜查罪。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十八条 监察机关依法调查公职人员涉嫌玩忽职守犯罪，包括玩忽职守罪，国有公司、企业、事业单位人员失职罪，签订、履行合同失职被骗罪，国家机关工作人员签订、履行合同失职被骗罪，环境监管失职罪，传染病防治失职罪，商检失职罪，动植物检疫失职罪，不解救被拐卖、绑架妇女、儿童罪，失职造成珍贵文物损毁、流失罪，过失泄露国家秘密罪。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十九条 监察机关依法调查公职人员涉嫌徇私舞弊犯罪，包括徇私舞弊低价折股、出售国有资产罪，非法批准征收、征用、占用土地罪，非法低价出让国有土地使用权罪，非法经营同类营业罪，为亲友非法牟利罪，枉法仲裁罪，徇私舞弊发售发票、抵扣税款、出口退税罪，商检徇私舞弊罪，动植物检疫徇私舞弊罪，放纵走私罪，放纵制售伪劣商品犯罪行为罪，招收公务员、学生徇私舞弊罪，徇私舞弊不移交刑事案件罪，违法提供出口退税凭证罪，徇私舞弊不征、少征税款罪。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十条 监察机关依法调查公职人员在行使公权力过程中涉及的重大责任事故犯罪，包括重大责任事故罪，教育设施重大安全事故罪，消防责任事故罪，重大劳动安全事故罪，强令、组织他人违章冒险作业罪，危险作业罪，不报、谎报安全事故罪，铁路运营安全事故罪，重大飞行事故罪，大型群众性活动重大安全事故罪，危险物品肇事罪，工程重大安全事故罪。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十一条 监察机关依法调查公职人员在行使公权力过程中涉及的其他犯罪，包括破坏选举罪，背信损害上市公司利益罪，金融工作人员购买假币、以假币换取货币罪，利用未公开信息交易罪，诱骗投资者买卖证券、期货合约罪，背信运用受托财产罪，违法运用资金罪，违法发放贷款罪，吸收客户资金不入账罪，违规出具金融票证罪，对违法票据承兑、付款、保证罪，非法转让、倒卖土地使用权罪，私自开拆、隐匿、毁弃邮件、电报罪，故意延误投递邮件罪，泄露不应公开的案件信息罪，披露、报道不应公开的案件信息罪，接送不合格兵员罪。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十二条 监察机关发现依法由其他机关管辖的违法犯罪线索，应当及时移送有管辖权的机关。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关调查结束后，对于应当给予被调查人或者涉案人员行政处罚等其他处理的，依法移送有关机关。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四节 监察处置</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十三条 监察机关对违法的公职人员，依据监察法、政务处分法等规定作出政务处分决定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十四条 监察机关在追究违法的公职人员直接责任的同时，依法对履行职责不力、失职失责，造成严重后果或者恶劣影响的领导人员予以问责。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关应当组成调查组依法开展问责调查。调查结束后经集体讨论形成调查报告，需要进行问责的按照管理权限作出问责决定，或者向有权作出问责决定的机关、单位书面提出问责建议。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十五条 监察机关对涉嫌职务犯罪的人员，经调查认为犯罪事实清楚，证据确实、充分，需要追究刑事责任的，依法移送人民检察院审查起诉。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十六条 监察机关根据监督、调查结果，发现监察对象所在单位在廉政建设、权力制约、监督管理、制度执行以及履行职责等方面存在问题需要整改纠正的，依法提出监察建议。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关应当跟踪了解监察建议的采纳情况，指导、督促有关单位限期整改，推动监察建议落实到位。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>　　第三章 监察范围和管辖</span></strong><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一节 监察对象</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十七条 监察机关依法对所有行使公权力的公职人员进行监察，实现国家监察全面覆盖。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十八条 监察法第十五条第一项所称公务员范围，依据《中华人民共和国公务员法》（以下简称公务员法）确定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察法第十五条第一项所称参照公务员法管理的人员，是指有关单位中经批准参照公务员法进行管理的工作人员。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十九条 监察法第十五条第二项所称法律、法规授权或者受国家机关依法委托管理公共事务的组织中从事公务的人员，是指在上述组织中，除参照公务员法管理的人员外，对公共事务履行组织、领导、管理、监督等职责的人员，包括具有公共事务管理职能的行业协会等组织中从事公务的人员，以及法定检验检测、检疫等机构中从事公务的人员。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十条 监察法第十五条第三项所称国有企业管理人员，是指国家出资企业中的下列人员：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）在国有独资、全资公司、企业中履行组织、领导、管理、监督等职责的人员；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）经党组织或者国家机关，国有独资、全资公司、企业，事业单位提名、推荐、任命、批准等，在国有控股、参股公司及其分支机构中履行组织、领导、管理、监督等职责的人员；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）经国家出资企业中负有管理、监督国有资产职责的组织批准或者研究决定，代表其在国有控股、参股公司及其分支机构中从事组织、领导、管理、监督等工作的人员。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十一条 监察法第十五条第四项所称公办的教育、科研、文化、医疗卫生、体育等单位中从事管理的人员，是指国家为了社会公益目的，由国家机关举办或者其他组织利用国有资产举办的教育、科研、文化、医疗卫生、体育等事业单位中，从事组织、领导、管理、监督等工作的人员。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十二条 监察法第十五条第五项所称基层群众性自治组织中从事管理的人员，是指该组织中的下列人员：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）从事集体事务和公益事业管理的人员；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）从事集体资金、资产、资源管理的人员；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）协助人民政府从事行政管理工作的人员，包括从事救灾、防疫、抢险、防汛、优抚、帮扶、移民、救济款物的管理，社会捐助公益事业款物的管理，国有土地的经营和管理，土地征收、征用补偿费用的管理，代征、代缴税款，有关计划生育、户籍、征兵工作，协助人民政府等国家机关在基层群众性自治组织中从事的其他管理工作。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十三条 下列人员属于监察法第十五条第六项所称其他依法履行公职的人员：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）履行人民代表大会职责的各级人民代表大会代表，履行公职的中国人民政治协商会议各级委员会委员、人民陪审员、人民监督员；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）虽未列入党政机关人员编制，但在党政机关中从事公务的人员；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）在集体经济组织等单位、组织中，由党组织或者国家机关，国有独资、全资公司、企业，国家出资企业中负有管理监督国有和集体资产职责的组织，事业单位提名、推荐、任命、批准等，从事组织、领导、管理、监督等工作的人员；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）在依法组建的评标、谈判、询价等组织中代表国家机关，国有独资、全资公司、企业，事业单位，人民团体临时履行公共事务组织、领导、管理、监督等职责的人员；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）其他依法行使公权力的人员。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十四条 有关机关、单位、组织集体作出的决定违法或者实施违法行为的，监察机关应当对负有责任的领导人员和直接责任人员中的公职人员依法追究法律责任。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二节 管 辖</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十五条 监察机关开展监督、调查、处置，按照管理权限与属地管辖相结合的原则，实行分级负责制。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十六条 设区的市级以上监察委员会按照管理权限，依法管辖同级党委管理的公职人员涉嫌职务违法和职务犯罪案件。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　县级监察委员会和直辖市所辖区（县）监察委员会按照管理权限，依法管辖本辖区内公职人员涉嫌职务违法和职务犯罪案件。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　地方各级监察委员会按照本条例第十三条、第四十九条规定，可以依法管辖工作单位在本辖区内的有关公职人员涉嫌职务违法和职务犯罪案件。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关调查公职人员涉嫌职务犯罪案件，可以依法对涉嫌行贿犯罪、介绍贿赂犯罪或者共同职务犯罪的涉案人员中的非公职人员一并管辖。非公职人员涉嫌利用影响力受贿罪的，按照其所利用的公职人员的管理权限确定管辖。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十七条 上级监察机关对于下一级监察机关管辖范围内的职务违法和职务犯罪案件，具有下列情形之一的，可以依法提级管辖：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）在本辖区有重大影响的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）涉及多个下级监察机关管辖的监察对象，调查难度大的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）其他需要提级管辖的重大、复杂案件。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　上级监察机关对于所辖各级监察机关管辖范围内有重大影响的案件，必要时可以依法直接调查或者组织、指挥、参与调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　地方各级监察机关所管辖的职务违法和职务犯罪案件，具有第一款规定情形的，可以依法报请上一级监察机关管辖。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十八条 上级监察机关可以依法将其所管辖的案件指定下级监察机关管辖。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　设区的市级监察委员会将同级党委管理的公职人员涉嫌职务违法或者职务犯罪案件指定下级监察委员会管辖的，应当报省级监察委员会批准；省级监察委员会将同级党委管理的公职人员涉嫌职务违法或者职务犯罪案件指定下级监察委员会管辖的，应当报国家监察委员会相关监督检查部门备案。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　上级监察机关对于下级监察机关管辖的职务违法和职务犯罪案件，具有下列情形之一，认为由其他下级监察机关管辖更为适宜的，可以依法指定给其他下级监察机关管辖：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）管辖有争议的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）指定管辖有利于案件公正处理的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）下级监察机关报请指定管辖的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）其他有必要指定管辖的。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　被指定的下级监察机关未经指定管辖的监察机关批准，不得将案件再行指定管辖。发现新的职务违法或者职务犯罪线索，以及其他重要情况、重大问题，应当及时向指定管辖的监察机关请示报告。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十九条 工作单位在地方、管理权限在主管部门的公职人员涉嫌职务违法和职务犯罪，一般由驻在主管部门、有管辖权的监察机构、监察专员管辖；经协商，监察机构、监察专员可以按规定移交公职人员工作单位所在地的地方监察委员会调查，或者与地方监察委员会联合调查。地方监察委员会在工作中发现上述公职人员有关问题线索，应当向驻在主管部门、有管辖权的监察机构、监察专员通报，并协商确定管辖。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　前款规定单位的其他公职人员涉嫌职务违法和职务犯罪，可以由地方监察委员会管辖；驻在主管部门的监察机构、监察专员自行立案调查的，应当及时通报地方监察委员会。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　地方监察委员会调查前两款规定案件，应当将立案、留置、移送审查起诉、撤销案件等重要情况向驻在主管部门的监察机构、监察专员通报。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十条 监察机关办理案件中涉及无隶属关系的其他监察机关的监察对象，认为需要立案调查的，应当商请有管理权限的监察机关依法立案调查。商请立案时，应当提供涉案人员基本情况、已经查明的涉嫌违法犯罪事实以及相关证据材料。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　承办案件的监察机关认为由其一并调查更为适宜的，可以报请有权决定的上级监察机关指定管辖。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十一条 公职人员既涉嫌贪污贿赂、失职渎职等严重职务违法和职务犯罪，又涉嫌公安机关、人民检察院等机关管辖的犯罪，依法由监察机关为主调查的，应当由监察机关和其他机关分别依职权立案，监察机关承担组织协调职责，协调调查和侦查工作进度、重要调查和侦查措施使用等重要事项。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十二条 监察机关必要时可以依法调查司法工作人员利用职权实施的涉嫌非法拘禁、刑讯逼供、非法搜查等侵犯公民权利、损害司法公正的犯罪，并在立案后及时通报同级人民检察院。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关在调查司法工作人员涉嫌贪污贿赂等职务犯罪中，可以对其涉嫌的前款规定的犯罪一并调查，并及时通报同级人民检察院。人民检察院在办理直接受理侦查的案件中，发现犯罪嫌疑人同时涉嫌监察机关管辖的其他职务犯罪，经沟通全案移送监察机关管辖的，监察机关应当依法进行调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十三条 监察机关对于退休公职人员在退休前或者退休后，或者离职、死亡的公职人员在履职期间实施的涉嫌职务违法或者职务犯罪行为，可以依法进行调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对前款规定人员，按照其原任职务的管辖规定确定管辖的监察机关；由其他监察机关管辖更为适宜的，可以依法指定或者交由其他监察机关管辖。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>　　第四章 监察权限</span></strong><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一节 一般要求</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十四条 监察机关应当加强监督执法调查工作规范化建设，严格按规定对监察措施进行审批和监管，依照法定的范围、程序和期限采取相关措施，出具、送达法律文书。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十五条 监察机关在初步核实中，可以依法采取谈话、询问、查询、调取、勘验检查、鉴定措施；立案后可以采取讯问、留置、冻结、搜查、查封、扣押、通缉措施。需要采取技术调查、限制出境措施的，应当按照规定交有关机关依法执行。设区的市级以下监察机关在初步核实中不得采取技术调查措施。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　开展问责调查，根据具体情况可以依法采取相关监察措施。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十六条 开展讯问、搜查、查封、扣押以及重要的谈话、询问等调查取证工作，应当全程同步录音录像，并保持录音录像资料的完整性。录音录像资料应当妥善保管、及时归档，留存备查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　人民检察院、人民法院需要调取同步录音录像的，监察机关应当予以配合，经审批依法予以提供。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十七条 需要商请其他监察机关协助收集证据材料的，应当依法出具《委托调查函》；商请其他监察机关对采取措施提供一般性协助的，应当依法出具《商请协助采取措施函》。商请协助事项涉及协助地监察机关管辖的监察对象的，应当由协助地监察机关按照所涉人员的管理权限报批。协助地监察机关对于协助请求，应当依法予以协助配合。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十八条 采取监察措施需要告知、通知相关人员的，应当依法办理。告知包括口头、书面两种方式，通知应当采取书面方式。采取口头方式告知的，应当将相关情况制作工作记录；采取书面方式告知、通知的，可以通过直接送交、邮寄、转交等途径送达，将有关回执或者凭证附卷。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　无法告知、通知，或者相关人员拒绝接收的，调查人员应当在工作记录或者有关文书上记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二节 证 据</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十九条 可以用于证明案件事实的材料都是证据，包括：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）物证；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）书证；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）证人证言；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）被害人陈述；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）被调查人陈述、供述和辩解；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）鉴定意见；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（七）勘验检查、辨认、调查实验等笔录；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（八）视听资料、电子数据。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关向有关单位和个人收集、调取证据时，应当告知其必须依法如实提供证据。对于不按要求提供有关材料，泄露相关信息，伪造、隐匿、毁灭证据，提供虚假情况或者阻止他人提供证据的，依法追究法律责任。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关依照监察法和本条例规定收集的证据材料，经审查符合法定要求的，在刑事诉讼中可以作为证据使用。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十条 监察机关认定案件事实应当以证据为根据，全面、客观地收集、固定被调查人有无违法犯罪以及情节轻重的各种证据，形成相互印证、完整稳定的证据链。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　只有被调查人陈述或者供述，没有其他证据的，不能认定案件事实；没有被调查人陈述或者供述，证据符合法定标准的，可以认定案件事实。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十一条 证据必须经过查证属实，才能作为定案的根据。审查认定证据，应当结合案件的具体情况，从证据与待证事实的关联程度、各证据之间的联系、是否依照法定程序收集等方面进行综合判断。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十二条 监察机关调查终结的职务违法案件，应当事实清楚、证据确凿。证据确凿，应当符合下列条件：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）定性处置的事实都有证据证实；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）定案证据真实、合法；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）据以定案的证据之间不存在无法排除的矛盾；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）综合全案证据，所认定事实清晰且令人信服。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十三条 监察机关调查终结的职务犯罪案件，应当事实清楚，证据确实、充分。证据确实、充分，应当符合下列条件：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）定罪量刑的事实都有证据证明；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）据以定案的证据均经法定程序查证属实；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）综合全案证据，对所认定事实已排除合理怀疑。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　证据不足的，不得移送人民检察院审查起诉。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十四条 严禁以暴力、威胁、引诱、欺骗以及非法限制人身自由等非法方法收集证据，严禁侮辱、打骂、虐待、体罚或者变相体罚被调查人、涉案人员和证人。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十五条 对于调查人员采用暴力、威胁以及非法限制人身自由等非法方法收集的被调查人供述、证人证言、被害人陈述，应当依法予以排除。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　前款所称暴力的方法，是指采用殴打、违法使用戒具等方法或者变相肉刑的恶劣手段，使人遭受难以忍受的痛苦而违背意愿作出供述、证言、陈述；威胁的方法，是指采用以暴力或者严重损害本人及其近亲属合法权益等进行威胁的方法，使人遭受难以忍受的痛苦而违背意愿作出供述、证言、陈述。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　收集物证、书证不符合法定程序，可能严重影响案件公正处理的，应当予以补正或者作出合理解释；不能补正或者作出合理解释的，对该证据应当予以排除。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十六条 监察机关监督检查、调查、案件审理、案件监督管理等部门发现监察人员在办理案件中，可能存在以非法方法收集证据情形的，应当依据职责进行调查核实。对于被调查人控告、举报调查人员采用非法方法收集证据，并提供涉嫌非法取证的人员、时间、地点、方式和内容等材料或者线索的，应当受理并进行审核。根据现有材料无法证明证据收集合法性的，应当进行调查核实。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　经调查核实，确认或者不能排除以非法方法收集证据的，对有关证据依法予以排除，不得作为案件定性处置、移送审查起诉的依据。认定调查人员非法取证的，应当依法处理，另行指派调查人员重新调查取证。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关接到对下级监察机关调查人员采用非法方法收集证据的控告、举报，可以直接进行调查核实，也可以交由下级监察机关调查核实。交由下级监察机关调查核实的，下级监察机关应当及时将调查结果报告上级监察机关。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十七条 对收集的证据材料及扣押的财物应当妥善保管，严格履行交接、调用手续，定期对账核实，不得违规使用、调换、损毁或者自行处理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十八条 监察机关对行政机关在行政执法和查办案件中收集的物证、书证、视听资料、电子数据，勘验、检查等笔录，以及鉴定意见等证据材料，经审查符合法定要求的，可以作为证据使用。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　根据法律、行政法规规定行使国家行政管理职权的组织在行政执法和查办案件中收集的证据材料，视为行政机关收集的证据材料。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十九条 监察机关对人民法院、人民检察院、公安机关、国家安全机关等在刑事诉讼中收集的物证、书证、视听资料、电子数据，勘验、检查、辨认、侦查实验等笔录，以及鉴定意见等证据材料，经审查符合法定要求的，可以作为证据使用。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关办理职务违法案件，对于人民法院生效刑事判决、裁定和人民检察院不起诉决定采信的证据材料，可以直接作为证据使用。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三节 谈 话</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七十条 监察机关在问题线索处置、初步核实和立案调查中，可以依法对涉嫌职务违法的监察对象进行谈话，要求其如实说明情况或者作出陈述。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　谈话应当个别进行。负责谈话的人员不得少于二人。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七十一条 对一般性问题线索的处置，可以采取谈话方式进行，对监察对象给予警示、批评、教育。谈话应当在工作地点等场所进行，明确告知谈话事项，注重谈清问题、取得教育效果。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七十二条 采取谈话方式处置问题线索的，经审批可以由监察人员或者委托被谈话人所在单位主要负责人等进行谈话。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关谈话应当形成谈话笔录或者记录。谈话结束后，可以根据需要要求被谈话人在十五个工作日以内作出书面说明。被谈话人应当在书面说明每页签名，修改的地方也应当签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　委托谈话的，受委托人应当在收到委托函后的十五个工作日以内进行谈话。谈话结束后及时形成谈话情况材料报送监察机关，必要时附被谈话人的书面说明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七十三条 监察机关开展初步核实工作，一般不与被核查人接触；确有需要与被核查人谈话的，应当按规定报批。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七十四条 监察机关对涉嫌职务违法的被调查人立案后，可以依法进行谈话。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　与被调查人首次谈话时，应当出示《被调查人权利义务告知书》，由其签名、捺指印。被调查人拒绝签名、捺指印的，调查人员应当在文书上记明。对于被调查人未被限制人身自由的，应当在首次谈话时出具《谈话通知书》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　与涉嫌严重职务违法的被调查人进行谈话的，应当全程同步录音录像，并告知被调查人。告知情况应当在录音录像中予以反映，并在笔录中记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七十五条 立案后，与未被限制人身自由的被调查人谈话的，应当在具备安全保障条件的场所进行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查人员按规定通知被调查人所在单位派员或者被调查人家属陪同被调查人到指定场所的，应当与陪同人员办理交接手续，填写《陪送交接单》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七十六条 调查人员与被留置的被调查人谈话的，按照法定程序在留置场所进行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　与在押的犯罪嫌疑人、被告人谈话的，应当持以监察机关名义出具的介绍信、工作证件，商请有关案件主管机关依法协助办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　与在看守所、监狱服刑的人员谈话的，应当持以监察机关名义出具的介绍信、工作证件办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七十七条 与被调查人进行谈话，应当合理安排时间、控制时长，保证其饮食和必要的休息时间。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七十八条 谈话笔录应当在谈话现场制作。笔录应当详细具体，如实反映谈话情况。笔录制作完成后，应当交给被调查人核对。被调查人没有阅读能力的，应当向其宣读。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　笔录记载有遗漏或者差错的，应当补充或者更正，由被调查人在补充或者更正处捺指印。被调查人核对无误后，应当在笔录中逐页签名、捺指印。被调查人拒绝签名、捺指印的，调查人员应当在笔录中记明。调查人员也应当在笔录中签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七十九条 被调查人请求自行书写说明材料的，应当准许。必要时，调查人员可以要求被调查人自行书写说明材料。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　被调查人应当在说明材料上逐页签名、捺指印，在末页写明日期。对说明材料有修改的，在修改之处应当捺指印。说明材料应当由二名调查人员接收，在首页记明接收的日期并签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八十条 本条例第七十四条至第七十九条的规定，也适用于在初步核实中开展的谈话。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四节 讯 问</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八十一条 监察机关对涉嫌职务犯罪的被调查人，可以依法进行讯问，要求其如实供述涉嫌犯罪的情况。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八十二条 讯问被留置的被调查人，应当在留置场所进行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八十三条 讯问应当个别进行，调查人员不得少于二人。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　首次讯问时，应当向被讯问人出示《被调查人权利义务告知书》，由其签名、捺指印。被讯问人拒绝签名、捺指印的，调查人员应当在文书上记明。被讯问人未被限制人身自由的，应当在首次讯问时向其出具《讯问通知书》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　讯问一般按照下列顺序进行：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）核实被讯问人的基本情况，包括姓名、曾用名、出生年月日、户籍地、身份证件号码、民族、职业、政治面貌、文化程度、工作单位及职务、住所、家庭情况、社会经历，是否属于党代表大会代表、人大代表、政协委员，是否受到过党纪政务处分，是否受到过刑事处罚等；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）告知被讯问人如实供述自己罪行可以依法从宽处理和认罪认罚的法律规定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）讯问被讯问人是否有犯罪行为，让其陈述有罪的事实或者无罪的辩解，应当允许其连贯陈述。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查人员的提问应当与调查的案件相关。被讯问人对调查人员的提问应当如实回答。调查人员对被讯问人的辩解，应当如实记录，认真查核。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　讯问时，应当告知被讯问人将进行全程同步录音录像。告知情况应当在录音录像中予以反映，并在笔录中记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八十四条 本条例第七十五条至第七十九条的要求，也适用于讯问。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五节 询 问</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八十五条 监察机关按规定报批后，可以依法对证人、被害人等人员进行询问，了解核实有关问题或者案件情况。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八十六条 证人未被限制人身自由的，可以在其工作地点、住所或者其提出的地点进行询问，也可以通知其到指定地点接受询问。到证人提出的地点或者调查人员指定的地点进行询问的，应当在笔录中记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查人员认为有必要或者证人提出需要由所在单位派员或者其家属陪同到询问地点的，应当办理交接手续并填写《陪送交接单》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八十七条 询问应当个别进行。负责询问的调查人员不得少于二人。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　首次询问时，应当向证人出示《证人权利义务告知书》，由其签名、捺指印。证人拒绝签名、捺指印的，调查人员应当在文书上记明。证人未被限制人身自由的，应当在首次询问时向其出具《询问通知书》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　询问时，应当核实证人身份，问明证人的基本情况，告知证人应当如实提供证据、证言，以及作伪证或者隐匿证据应当承担的法律责任。不得向证人泄露案情，不得采用非法方法获取证言。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　询问重大或者有社会影响案件的重要证人，应当对询问过程全程同步录音录像，并告知证人。告知情况应当在录音录像中予以反映，并在笔录中记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八十八条 询问未成年人，应当通知其法定代理人到场。无法通知或者法定代理人不能到场的，应当通知未成年人的其他成年亲属或者所在学校、居住地基层组织的代表等有关人员到场。询问结束后，由法定代理人或者有关人员在笔录中签名。调查人员应当将到场情况记录在案。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　询问聋、哑人，应当有通晓聋、哑手势的人员参加。调查人员应当在笔录中记明证人的聋、哑情况，以及翻译人员的姓名、工作单位和职业。询问不通晓当地通用语言、文字的证人，应当有翻译人员。询问结束后，由翻译人员在笔录中签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八十九条 凡是知道案件情况的人，都有如实作证的义务。对故意提供虚假证言的证人，应当依法追究法律责任。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　证人或者其他任何人不得帮助被调查人隐匿、毁灭、伪造证据或者串供，不得实施其他干扰调查活动的行为。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九十条 证人、鉴定人、被害人因作证，本人或者近亲属人身安全面临危险，向监察机关请求保护的，监察机关应当受理并及时进行审查；对于确实存在人身安全危险的，监察机关应当采取必要的保护措施。监察机关发现存在上述情形的，应当主动采取保护措施。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关可以采取下列一项或者多项保护措施：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）不公开真实姓名、住址和工作单位等个人信息；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）禁止特定的人员接触证人、鉴定人、被害人及其近亲属；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）对人身和住宅采取专门性保护措施；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）其他必要的保护措施。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　依法决定不公开证人、鉴定人、被害人的真实姓名、住址和工作单位等个人信息的，可以在询问笔录等法律文书、证据材料中使用化名。但是应当另行书面说明使用化名的情况并标明密级，单独成卷。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关采取保护措施需要协助的，可以提请公安机关等有关单位和要求有关个人依法予以协助。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九十一条 本条例第七十六条至第七十九条的要求，也适用于询问。询问重要涉案人员，根据情况适用本条例第七十五条的规定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　询问被害人，适用询问证人的规定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六节 留 置</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九十二条 监察机关调查严重职务违法或者职务犯罪，对于符合监察法第二十二条第一款规定的，经依法审批，可以对被调查人采取留置措施。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察法第二十二条第一款规定的严重职务违法，是指根据监察机关已经掌握的事实及证据，被调查人涉嫌的职务违法行为情节严重，可能被给予撤职以上政务处分；重要问题，是指对被调查人涉嫌的职务违法或者职务犯罪，在定性处置、定罪量刑等方面有重要影响的事实、情节及证据。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察法第二十二条第一款规定的已经掌握其部分违法犯罪事实及证据，是指同时具备下列情形：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）有证据证明发生了违法犯罪事实；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）有证据证明该违法犯罪事实是被调查人实施；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）证明被调查人实施违法犯罪行为的证据已经查证属实。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　部分违法犯罪事实，既可以是单一违法犯罪行为的事实，也可以是数个违法犯罪行为中任何一个违法犯罪行为的事实。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九十三条 被调查人具有下列情形之一的，可以认定为监察法第二十二条第一款第二项所规定的可能逃跑、自杀：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）着手准备自杀、自残或者逃跑的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）曾经有自杀、自残或者逃跑行为的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）有自杀、自残或者逃跑意图的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）其他可能逃跑、自杀的情形。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九十四条 被调查人具有下列情形之一的，可以认定为监察法第二十二条第一款第三项所规定的可能串供或者伪造、隐匿、毁灭证据：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）曾经或者企图串供，伪造、隐匿、毁灭、转移证据的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）曾经或者企图威逼、恐吓、利诱、收买证人，干扰证人作证的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）有同案人或者与被调查人存在密切关联违法犯罪的涉案人员在逃，重要证据尚未收集完成的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）其他可能串供或者伪造、隐匿、毁灭证据的情形。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九十五条 被调查人具有下列情形之一的，可以认定为监察法第二十二条第一款第四项所规定的可能有其他妨碍调查行为：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）可能继续实施违法犯罪行为的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）有危害国家安全、公共安全等现实危险的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）可能对举报人、控告人、被害人、证人、鉴定人等相关人员实施打击报复的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）无正当理由拒不到案，严重影响调查的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）其他可能妨碍调查的行为。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九十六条 对下列人员不得采取留置措施：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）患有严重疾病、生活不能自理的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）怀孕或者正在哺乳自己婴儿的妇女；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）系生活不能自理的人的唯一扶养人。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　上述情形消除后，根据调查需要可以对相关人员采取留置措施。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九十七条 采取留置措施时，调查人员不得少于二人，应当向被留置人员宣布《留置决定书》，告知被留置人员权利义务，要求其在《留置决定书》上签名、捺指印。被留置人员拒绝签名、捺指印的，调查人员应当在文书上记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九十八条 采取留置措施后，应当在二十四小时以内通知被留置人员所在单位和家属。当面通知的，由有关人员在《留置通知书》上签名。无法当面通知的，可以先以电话等方式通知，并通过邮寄、转交等方式送达《留置通知书》，要求有关人员在《留置通知书》上签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　因可能毁灭、伪造证据，干扰证人作证或者串供等有碍调查情形而不宜通知的，应当按规定报批，记录在案。有碍调查的情形消失后，应当立即通知被留置人员所在单位和家属。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九十九条 县级以上监察机关需要提请公安机关协助采取留置措施的，应当按规定报批，请同级公安机关依法予以协助。提请协助时，应当出具《提请协助采取留置措施函》，列明提请协助的具体事项和建议，协助采取措施的时间、地点等内容，附《留置决定书》复印件。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　因保密需要，不适合在采取留置措施前向公安机关告知留置对象姓名的，可以作出说明，进行保密处理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　需要提请异地公安机关协助采取留置措施的，应当按规定报批，向协作地同级监察机关出具协作函件和相关文书，由协作地监察机关提请当地公安机关依法予以协助。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百条 留置过程中，应当保障被留置人员的合法权益，尊重其人格和民族习俗，保障饮食、休息和安全，提供医疗服务。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百零一条 留置时间不得超过三个月，自向被留置人员宣布之日起算。具有下列情形之一的，经审批可以延长一次，延长时间不得超过三个月：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）案情重大，严重危害国家利益或者公共利益的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）案情复杂，涉案人员多、金额巨大，涉及范围广的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）重要证据尚未收集完成，或者重要涉案人员尚未到案，导致违法犯罪的主要事实仍须继续调查的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）其他需要延长留置时间的情形。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　省级以下监察机关采取留置措施的，延长留置时间应当报上一级监察机关批准。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　延长留置时间的，应当在留置期满前向被留置人员宣布延长留置时间的决定，要求其在《延长留置时间决定书》上签名、捺指印。被留置人员拒绝签名、捺指印的，调查人员应当在文书上记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　延长留置时间的，应当通知被留置人员家属。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百零二条 对被留置人员不需要继续采取留置措施的，应当按规定报批，及时解除留置。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查人员应当向被留置人员宣布解除留置措施的决定，由其在《解除留置决定书》上签名、捺指印。被留置人员拒绝签名、捺指印的，调查人员应当在文书上记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　解除留置措施的，应当及时通知被留置人员所在单位或者家属。调查人员应当与交接人办理交接手续，并由其在《解除留置通知书》上签名。无法通知或者有关人员拒绝签名的，调查人员应当在文书上记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　案件依法移送人民检察院审查起诉的，留置措施自犯罪嫌疑人被执行拘留时自动解除，不再办理解除法律手续。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百零三条 留置场所应当建立健全保密、消防、医疗、餐饮及安保等安全工作责任制，制定紧急突发事件处置预案，采取安全防范措施。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　留置期间发生被留置人员死亡、伤残、脱逃等办案安全事故、事件的，应当及时做好处置工作。相关情况应当立即报告监察机关主要负责人，并在二十四小时以内逐级上报至国家监察委员会。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七节 查询、冻结</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百零四条 监察机关调查严重职务违法或者职务犯罪，根据工作需要，按规定报批后，可以依法查询、冻结涉案单位和个人的存款、汇款、债券、股票、基金份额等财产。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百零五条 查询、冻结财产时，调查人员不得少于二人。调查人员应当出具《协助查询财产通知书》或者《协助冻结财产通知书》，送交银行或者其他金融机构、邮政部门等单位执行。有关单位和个人应当予以配合，并严格保密。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　查询财产应当在《协助查询财产通知书》中填写查询账号、查询内容等信息。没有具体账号的，应当填写足以确定账户或者权利人的自然人姓名、身份证件号码或者企业法人名称、统一社会信用代码等信息。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　冻结财产应当在《协助冻结财产通知书》中填写冻结账户名称、冻结账号、冻结数额、冻结期限起止时间等信息。冻结数额应当具体、明确，暂时无法确定具体数额的，应当在《协助冻结财产通知书》上明确写明“只收不付”。冻结证券和交易结算资金时，应当明确冻结的范围是否及于孳息。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　冻结财产，应当为被调查人及其所扶养的亲属保留必需的生活费用。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百零六条 调查人员可以根据需要对查询结果进行打印、抄录、复制、拍照，要求相关单位在有关材料上加盖证明印章。对查询结果有疑问的，可以要求相关单位进行书面解释并加盖印章。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百零七条 监察机关对查询信息应当加强管理，规范信息交接、调阅、使用程序和手续，防止滥用和泄露。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查人员不得查询与案件调查工作无关的信息。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百零八条 冻结财产的期限不得超过六个月。冻结期限到期未办理续冻手续的，冻结自动解除。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　有特殊原因需要延长冻结期限的，应当在到期前按原程序报批，办理续冻手续。每次续冻期限不得超过六个月。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百零九条 已被冻结的财产可以轮候冻结，不得重复冻结。轮候冻结的，监察机关应当要求有关银行或者其他金融机构等单位在解除冻结或者作出处理前予以通知。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关接受司法机关、其他监察机关等国家机关移送的涉案财物后，该国家机关采取的冻结期限届满，监察机关续行冻结的顺位与该国家机关冻结的顺位相同。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百一十条 冻结财产应当通知权利人或者其法定代理人、委托代理人，要求其在《冻结财产告知书》上签名。冻结股票、债券、基金份额等财产，应当告知权利人或者其法定代理人、委托代理人有权申请出售。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于被冻结的股票、债券、基金份额等财产，权利人或者其法定代理人、委托代理人申请出售，不损害国家利益、被害人利益，不影响调查正常进行的，经审批可以在案件办结前由相关机构依法出售或者变现。对于被冻结的汇票、本票、支票即将到期的，经审批可以在案件办结前由相关机构依法出售或者变现。出售上述财产的，应当出具《许可出售冻结财产通知书》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　出售或者变现所得价款应当继续冻结在其对应的银行账户中；没有对应的银行账户的，应当存入监察机关指定的专用账户保管，并将存款凭证送监察机关登记。监察机关应当及时向权利人或者其法定代理人、委托代理人出具《出售冻结财产通知书》，并要求其签名。拒绝签名的，调查人员应当在文书上记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百一十一条 对于冻结的财产，应当及时核查。经查明与案件无关的，经审批，应当在查明后三日以内将《解除冻结财产通知书》送交有关单位执行。解除情况应当告知被冻结财产的权利人或者其法定代理人、委托代理人。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八节 搜 查</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百一十二条 监察机关调查职务犯罪案件，为了收集犯罪证据、查获被调查人，按规定报批后，可以依法对被调查人以及可能隐藏被调查人或者犯罪证据的人的身体、物品、住处、工作地点和其他有关地方进行搜查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百一十三条 搜查应当在调查人员主持下进行，调查人员不得少于二人。搜查女性的身体，由女性工作人员进行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　搜查时，应当有被搜查人或者其家属、其所在单位工作人员或者其他见证人在场。监察人员不得作为见证人。调查人员应当向被搜查人或者其家属、见证人出示《搜查证》，要求其签名。被搜查人或者其家属不在场，或者拒绝签名的，调查人员应当在文书上记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百一十四条 搜查时，应当要求在场人员予以配合，不得进行阻碍。对以暴力、威胁等方法阻碍搜查的，应当依法制止。对阻碍搜查构成违法犯罪的，依法追究法律责任。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百一十五条 县级以上监察机关需要提请公安机关依法协助采取搜查措施的，应当按规定报批，请同级公安机关予以协助。提请协助时，应当出具《提请协助采取搜查措施函》，列明提请协助的具体事项和建议，搜查时间、地点、目的等内容，附《搜查证》复印件。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　需要提请异地公安机关协助采取搜查措施的，应当按规定报批，向协作地同级监察机关出具协作函件和相关文书，由协作地监察机关提请当地公安机关予以协助。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百一十六条 对搜查取证工作，应当全程同步录音录像。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对搜查情况应当制作《搜查笔录》，由调查人员和被搜查人或者其家属、见证人签名。被搜查人或者其家属不在场，或者拒绝签名的，调查人员应当在笔录中记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于查获的重要物证、书证、视听资料、电子数据及其放置、存储位置应当拍照，并在《搜查笔录》中作出文字说明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百一十七条 搜查时，应当避免未成年人或者其他不适宜在搜查现场的人在场。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　搜查人员应当服从指挥、文明执法，不得擅自变更搜查对象和扩大搜查范围。搜查的具体时间、方法，在实施前应当严格保密。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百一十八条 在搜查过程中查封、扣押财物和文件的，按照查封、扣押的有关规定办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九节 调 取</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百一十九条 监察机关按规定报批后，可以依法向有关单位和个人调取用以证明案件事实的证据材料。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百二十条 调取证据材料时，调查人员不得少于二人。调查人员应当依法出具《调取证据通知书》，必要时附《调取证据清单》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　有关单位和个人配合监察机关调取证据，应当严格保密。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百二十一条 调取物证应当调取原物。原物不便搬运、保存，或者依法应当返还，或者因保密工作需要不能调取原物的，可以将原物封存，并拍照、录像。对原物拍照或者录像时，应当足以反映原物的外形、内容。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调取书证、视听资料应当调取原件。取得原件确有困难或者因保密工作需要不能调取原件的，可以调取副本或者复制件。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调取物证的照片、录像和书证、视听资料的副本、复制件的，应当书面记明不能调取原物、原件的原因，原物、原件存放地点，制作过程，是否与原物、原件相符，并由调查人员和物证、书证、视听资料原持有人签名或者盖章。持有人无法签名、盖章或者拒绝签名、盖章的，应当在笔录中记明，由见证人签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百二十二条 调取外文材料作为证据使用的，应当交由具有资质的机构和人员出具中文译本。中文译本应当加盖翻译机构公章。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百二十三条 收集、提取电子数据，能够扣押原始存储介质的，应当予以扣押、封存并在笔录中记录封存状态。无法扣押原始存储介质的，可以提取电子数据，但应当在笔录中记明不能扣押的原因、原始存储介质的存放地点或者电子数据的来源等情况。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　由于客观原因无法或者不宜采取前款规定方式收集、提取电子数据的，可以采取打印、拍照或者录像等方式固定相关证据，并在笔录中说明原因。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　收集、提取的电子数据，足以保证完整性，无删除、修改、增加等情形的，可以作为证据使用。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　收集、提取电子数据，应当制作笔录，记录案由、对象、内容，收集、提取电子数据的时间、地点、方法、过程，并附电子数据清单，注明类别、文件格式、完整性校验值等，由调查人员、电子数据持有人（提供人）签名或者盖章；电子数据持有人（提供人）无法签名或者拒绝签名的，应当在笔录中记明，由见证人签名或者盖章。有条件的，应当对相关活动进行录像。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百二十四条 调取的物证、书证、视听资料等原件，经查明与案件无关的，经审批，应当在查明后三日以内退还，并办理交接手续。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十节 查封、扣押</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百二十五条 监察机关按规定报批后，可以依法查封、扣押用以证明被调查人涉嫌违法犯罪以及情节轻重的财物、文件、电子数据等证据材料。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于被调查人到案时随身携带的物品，以及被调查人或者其他相关人员主动上交的财物和文件，依法需要扣押的，依照前款规定办理。对于被调查人随身携带的与案件无关的个人用品，应当逐件登记，随案移交或者退还。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百二十六条 查封、扣押时，应当出具《查封<span lang=\"EN-US\">/</span>扣押通知书》，调查人员不得少于二人。持有人拒绝交出应当查封、扣押的财物和文件的，可以依法强制查封、扣押。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查人员对于查封、扣押的财物和文件，应当会同在场见证人和被查封、扣押财物持有人进行清点核对，开列《查封<span lang=\"EN-US\">/</span>扣押财物、文件清单》，由调查人员、见证人和持有人签名或者盖章。持有人不在场或者拒绝签名、盖章的，调查人员应当在清单上记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　查封、扣押财物，应当为被调查人及其所扶养的亲属保留必需的生活费用和物品。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百二十七条 查封、扣押不动产和置于该不动产上不宜移动的设施、家具和其他相关财物，以及车辆、船舶、航空器和大型机械、设备等财物，必要时可以依法扣押其权利证书，经拍照或者录像后原地封存。调查人员应当在查封清单上记明相关财物的所在地址和特征，已经拍照或者录像及其权利证书被扣押的情况，由调查人员、见证人和持有人签名或者盖章。持有人不在场或者拒绝签名、盖章的，调查人员应当在清单上记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　查封、扣押前款规定财物的，必要时可以将被查封财物交给持有人或者其近亲属保管。调查人员应当告知保管人妥善保管，不得对被查封财物进行转移、变卖、毁损、抵押、赠予等处理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查人员应当将《查封<span lang=\"EN-US\">/</span>扣押通知书》送达不动产、生产设备或者车辆、船舶、航空器等财物的登记、管理部门，告知其在查封期间禁止办理抵押、转让、出售等权属关系变更、转移登记手续。相关情况应当在查封清单上记明。被查封、扣押的财物已经办理抵押登记的，监察机关在执行没收、追缴、责令退赔等决定时应当及时通知抵押权人。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百二十八条 查封、扣押下列物品，应当依法进行相应的处理：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）查封、扣押外币、金银珠宝、文物、名贵字画以及其他不易辨别真伪的贵重物品，具备当场密封条件的，应当当场密封，由二名以上调查人员在密封材料上签名并记明密封时间。不具备当场密封条件的，应当在笔录中记明，以拍照、录像等方法加以保全后进行封存。查封、扣押的贵重物品需要鉴定的，应当及时鉴定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）查封、扣押存折、银行卡、有价证券等支付凭证和具有一定特征能够证明案情的现金，应当记明特征、编号、种类、面值、张数、金额等，当场密封，由二名以上调查人员在密封材料上签名并记明密封时间。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）查封、扣押易损毁、灭失、变质等不宜长期保存的物品以及有消费期限的卡、券，应当在笔录中记明，以拍照、录像等方法加以保全后进行封存，或者经审批委托有关机构变卖、拍卖。变卖、拍卖的价款存入专用账户保管，待调查终结后一并处理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）对于可以作为证据使用的录音录像、电子数据存储介质，应当记明案由、对象、内容，录制、复制的时间、地点、规格、类别、应用长度、文件格式及长度等，制作清单。具备查封、扣押条件的电子设备、存储介质应当密封保存。必要时，可以请有关机关协助。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）对被调查人使用违法犯罪所得与合法收入共同购置的不可分割的财产，可以先行查封、扣押。对无法分割退还的财产，涉及违法的，可以在结案后委托有关单位拍卖、变卖，退还不属于违法所得的部分及孳息；涉及职务犯罪的，依法移送司法机关处置。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）查封、扣押危险品、违禁品，应当及时送交有关部门，或者根据工作需要严格封存保管。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百二十九条 对于需要启封的财物和文件，应当由二名以上调查人员共同办理。重新密封时，由二名以上调查人员在密封材料上签名、记明时间。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百三十条 查封、扣押涉案财物，应当按规定将涉案财物详细信息、《查封<span lang=\"EN-US\">/</span>扣押财物、文件清单》录入并上传监察机关涉案财物信息管理系统。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于涉案款项，应当在采取措施后十五日以内存入监察机关指定的专用账户。对于涉案物品，应当在采取措施后三十日以内移交涉案财物保管部门保管。因特殊原因不能按时存入专用账户或者移交保管的，应当按规定报批，将保管情况录入涉案财物信息管理系统，在原因消除后及时存入或者移交。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百三十一条 对于已移交涉案财物保管部门保管的涉案财物，根据调查工作需要，经审批可以临时调用，并应当确保完好。调用结束后，应当及时归还。调用和归还时，调查人员、保管人员应当当面清点查验。保管部门应当对调用和归还情况进行登记，全程录像并上传涉案财物信息管理系统。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百三十二条 对于被扣押的股票、债券、基金份额等财产，以及即将到期的汇票、本票、支票，依法需要出售或者变现的，按照本条例关于出售冻结财产的规定办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百三十三条 监察机关接受司法机关、其他监察机关等国家机关移送的涉案财物后，该国家机关采取的查封、扣押期限届满，监察机关续行查封、扣押的顺位与该国家机关查封、扣押的顺位相同。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百三十四条 对查封、扣押的财物和文件，应当及时进行核查。经查明与案件无关的，经审批，应当在查明后三日以内解除查封、扣押，予以退还。解除查封、扣押的，应当向有关单位、原持有人或者近亲属送达《解除查封<span lang=\"EN-US\">/</span>扣押通知书》，附《解除查封<span lang=\"EN-US\">/</span>扣押财物、文件清单》，要求其签名或者盖章。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百三十五条 在立案调查之前，对监察对象及相关人员主动上交的涉案财物，经审批可以接收。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　接收时，应当由二名以上调查人员，会同持有人和见证人进行清点核对，当场填写《主动上交财物登记表》。调查人员、持有人和见证人应当在登记表上签名或者盖章。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于主动上交的财物，应当根据立案及调查情况及时决定是否依法查封、扣押。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十一节 勘验检查</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百三十六条 监察机关按规定报批后，可以依法对与违法犯罪有关的场所、物品、人身、尸体、电子数据等进行勘验检查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百三十七条 依法需要勘验检查的，应当制作《勘验检查证》；需要委托勘验检查的，应当出具《委托勘验检查书》，送具有专门知识、勘验检查资格的单位（人员）办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百三十八条 勘验检查应当由二名以上调查人员主持，邀请与案件无关的见证人在场。勘验检查情况应当制作笔录，并由参加勘验检查人员和见证人签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　勘验检查现场、拆封电子数据存储介质应当全程同步录音录像。对现场情况应当拍摄现场照片、制作现场图，并由勘验检查人员签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百三十九条 为了确定被调查人或者相关人员的某些特征、伤害情况或者生理状态，可以依法对其人身进行检查。必要时可以聘请法医或者医师进行人身检查。检查女性身体，应当由女性工作人员或者医师进行。被调查人拒绝检查的，可以依法强制检查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　人身检查不得采用损害被检查人生命、健康或者贬低其名誉、人格的方法。对人身检查过程中知悉的个人隐私，应当严格保密。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对人身检查的情况应当制作笔录，由参加检查的调查人员、检查人员、被检查人员和见证人签名。被检查人员拒绝签名的，调查人员应当在笔录中记明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百四十条 为查明案情，在必要的时候，经审批可以依法进行调查实验。调查实验，可以聘请有关专业人员参加，也可以要求被调查人、被害人、证人参加。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　进行调查实验，应当全程同步录音录像，制作调查实验笔录，由参加实验的人签名。进行调查实验，禁止一切足以造成危险、侮辱人格的行为。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百四十一条 调查人员在必要时，可以依法让被害人、证人和被调查人对与违法犯罪有关的物品、文件、尸体或者场所进行辨认；也可以让被害人、证人对被调查人进行辨认，或者让被调查人对涉案人员进行辨认。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　辨认工作应当由二名以上调查人员主持进行。在辨认前，应当向辨认人详细询问辨认对象的具体特征，避免辨认人见到辨认对象，并告知辨认人作虚假辨认应当承担的法律责任。几名辨认人对同一辨认对象进行辨认时，应当由辨认人个别进行。辨认应当形成笔录，并由调查人员、辨认人签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百四十二条 辨认人员时，被辨认的人数不得少于七人，照片不得少于十张。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　辨认人不愿公开进行辨认时，应当在不暴露辨认人的情况下进行辨认，并为其保守秘密。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百四十三条 组织辨认物品时一般应当辨认实物。被辨认的物品系名贵字画等贵重物品或者存在不便搬运等情况的，可以对实物照片进行辨认。辨认人进行辨认时，应当在辨认出的实物照片与附纸骑缝上捺指印予以确认，在附纸上写明该实物涉案情况并签名、捺指印。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　辨认物品时，同类物品不得少于五件，照片不得少于五张。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于难以找到相似物品的特定物，可以将该物品照片交由辨认人进行确认后，在照片与附纸骑缝上捺指印，在附纸上写明该物品涉案情况并签名、捺指印。在辨认人确认前，应当向其详细询问物品的具体特征，并对确认过程和结果形成笔录。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百四十四条 辨认笔录具有下列情形之一的，不得作为认定案件的依据：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）辨认开始前使辨认人见到辨认对象的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）辨认活动没有个别进行的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）辨认对象没有混杂在具有类似特征的其他对象中，或者供辨认的对象数量不符合规定的，但特定辨认对象除外；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）辨认中给辨认人明显暗示或者明显有指认嫌疑的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）辨认不是在调查人员主持下进行的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）违反有关规定，不能确定辨认笔录真实性的其他情形。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　辨认笔录存在其他瑕疵的，应当结合全案证据审查其真实性和关联性，作出综合判断。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十二节 鉴 定</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百四十五条 监察机关为解决案件中的专门性问题，按规定报批后，可以依法进行鉴定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　鉴定时应当出具《委托鉴定书》，由二名以上调查人员送交具有鉴定资格的鉴定机构、鉴定人进行鉴定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百四十六条 监察机关可以依法开展下列鉴定：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）对笔迹、印刷文件、污损文件、制成时间不明的文件和以其他形式表现的文件等进行鉴定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）对案件中涉及的财务会计资料及相关财物进行会计鉴定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）对被调查人、证人的行为能力进行精神病鉴定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）对人体造成的损害或者死因进行人身伤亡医学鉴定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）对录音录像资料进行鉴定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）对因电子信息技术应用而出现的材料及其派生物进行电子证据鉴定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（七）其他可以依法进行的专业鉴定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百四十七条 监察机关应当为鉴定提供必要条件，向鉴定人送交有关检材和对比样本等原始材料，介绍与鉴定有关的情况。调查人员应当明确提出要求鉴定事项，但不得暗示或者强迫鉴定人作出某种鉴定意见。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关应当做好检材的保管和送检工作，记明检材送检环节的责任人，确保检材在流转环节的同一性和不被污染。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百四十八条 鉴定人应当在出具的鉴定意见上签名，并附鉴定机构和鉴定人的资质证明或者其他证明文件。多个鉴定人的鉴定意见不一致的，应当在鉴定意见上记明分歧的内容和理由，并且分别签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关对于法庭审理中依法决定鉴定人出庭作证的，应当予以协调。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　鉴定人故意作虚假鉴定的，应当依法追究法律责任。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百四十九条 调查人员应当对鉴定意见进行审查。对经审查作为证据使用的鉴定意见，应当告知被调查人及相关单位、人员，送达《鉴定意见告知书》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　被调查人或者相关单位、人员提出补充鉴定或者重新鉴定申请，经审查符合法定要求的，应当按规定报批，进行补充鉴定或者重新鉴定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对鉴定意见告知情况可以制作笔录，载明告知内容和被告知人的意见等。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百五十条 经审查具有下列情形之一的，应当补充鉴定：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）鉴定内容有明显遗漏的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）发现新的有鉴定意义的证物的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）对鉴定证物有新的鉴定要求的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）鉴定意见不完整，委托事项无法确定的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）其他需要补充鉴定的情形。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百五十一条 经审查具有下列情形之一的，应当重新鉴定：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）鉴定程序违法或者违反相关专业技术要求的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）鉴定机构、鉴定人不具备鉴定资质和条件的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）鉴定人故意作出虚假鉴定或者违反回避规定的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）鉴定意见依据明显不足的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）检材虚假或者被损坏的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）其他应当重新鉴定的情形。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　决定重新鉴定的，应当另行确定鉴定机构和鉴定人。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百五十二条 因无鉴定机构，或者根据法律法规等规定，监察机关可以指派、聘请具有专门知识的人就案件的专门性问题出具报告。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十三节 技术调查</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百五十三条 监察机关根据调查涉嫌重大贪污贿赂等职务犯罪需要，依照规定的权限和程序报经批准，可以依法采取技术调查措施，按照规定交公安机关或者国家有关执法机关依法执行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　前款所称重大贪污贿赂等职务犯罪，是指具有下列情形之一：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）案情重大复杂，涉及国家利益或者重大公共利益的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）被调查人可能被判处十年以上有期徒刑、无期徒刑或者死刑的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）案件在全国或者本省、自治区、直辖市范围内有较大影响的。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百五十四条 依法采取技术调查措施的，监察机关应当出具《采取技术调查措施委托函》《采取技术调查措施决定书》和《采取技术调查措施适用对象情况表》，送交有关机关执行。其中，设区的市级以下监察机关委托有关执行机关采取技术调查措施，还应当提供《立案决定书》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百五十五条 技术调查措施的期限按照监察法的规定执行，期限届满前未办理延期手续的，到期自动解除。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于不需要继续采取技术调查措施的，监察机关应当按规定及时报批，将《解除技术调查措施决定书》送交有关机关执行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　需要依法变更技术调查措施种类或者增加适用对象的，监察机关应当重新办理报批和委托手续，依法送交有关机关执行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百五十六条 对于采取技术调查措施收集的信息和材料，依法需要作为刑事诉讼证据使用的，监察机关应当按规定报批，出具《调取技术调查证据材料通知书》向有关执行机关调取。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于采取技术调查措施收集的物证、书证及其他证据材料，监察机关应当制作书面说明，写明获取证据的时间、地点、数量、特征以及采取技术调查措施的批准机关、种类等。调查人员应当在书面说明上签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于采取技术调查措施获取的证据材料，如果使用该证据材料可能危及有关人员的人身安全，或者可能产生其他严重后果的，应当采取不暴露有关人员身份、技术方法等保护措施。必要时，可以建议由审判人员在庭外进行核实。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百五十七条 调查人员对采取技术调查措施过程中知悉的国家秘密、商业秘密、个人隐私，应当严格保密。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　采取技术调查措施获取的证据、线索及其他有关材料，只能用于对违法犯罪的调查、起诉和审判，不得用于其他用途。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对采取技术调查措施获取的与案件无关的材料，应当经审批及时销毁。对销毁情况应当制作记录，由调查人员签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十四节 通 缉</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百五十八条 县级以上监察机关对在逃的应当被留置人员，依法决定在本行政区域内通缉的，应当按规定报批，送交同级公安机关执行。送交执行时，应当出具《通缉决定书》，附《留置决定书》等法律文书和被通缉人员信息，以及承办单位、承办人员等有关情况。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　通缉范围超出本行政区域的，应当报有决定权的上级监察机关出具《通缉决定书》，并附《留置决定书》及相关材料，送交同级公安机关执行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百五十九条 国家监察委员会依法需要提请公安部对在逃人员发布公安部通缉令的，应当先提请公安部采取网上追逃措施。如情况紧急，可以向公安部同时出具《通缉决定书》和《提请采取网上追逃措施函》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　省级以下监察机关报请国家监察委员会提请公安部发布公安部通缉令的，应当先提请本地公安机关采取网上追逃措施。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百六十条 监察机关接到公安机关抓获被通缉人员的通知后，应当立即核实被抓获人员身份，并在接到通知后二十四小时以内派员办理交接手续。边远或者交通不便地区，至迟不得超过三日。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　公安机关在移交前，将被抓获人员送往当地监察机关留置场所临时看管的，当地监察机关应当接收，并保障临时看管期间的安全，对工作信息严格保密。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关需要提请公安机关协助将被抓获人员带回的，应当按规定报批，请本地同级公安机关依法予以协助。提请协助时，应当出具《提请协助采取留置措施函》，附《留置决定书》复印件及相关材料。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百六十一条 监察机关对于被通缉人员已经归案、死亡，或者依法撤销留置决定以及发现有其他不需要继续采取通缉措施情形的，应当经审批出具《撤销通缉通知书》，送交协助采取原措施的公安机关执行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十五节 限制出境</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百六十二条 监察机关为防止被调查人及相关人员逃匿境外，按规定报批后，可以依法决定采取限制出境措施，交由移民管理机构依法执行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百六十三条 监察机关采取限制出境措施应当出具有关函件，与《采取限制出境措施决定书》一并送交移民管理机构执行。其中，采取边控措施的，应当附《边控对象通知书》；采取法定不批准出境措施的，应当附《法定不准出境人员报备表》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百六十四条 限制出境措施有效期不超过三个月，到期自动解除。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　到期后仍有必要继续采取措施的，应当按原程序报批。承办部门应当出具有关函件，在到期前与《延长限制出境措施期限决定书》一并送交移民管理机构执行。延长期限每次不得超过三个月。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百六十五条 监察机关接到口岸移民管理机构查获被决定采取留置措施的边控对象的通知后，应当于二十四小时以内到达口岸办理移交手续。无法及时到达的，应当委托当地监察机关及时前往口岸办理移交手续。当地监察机关应当予以协助。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百六十六条 对于不需要继续采取限制出境措施的，应当按规定报批，及时予以解除。承办部门应当出具有关函件，与《解除限制出境措施决定书》一并送交移民管理机构执行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百六十七条 县级以上监察机关在重要紧急情况下，经审批可以依法直接向口岸所在地口岸移民管理机构提请办理临时限制出境措施。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>　　第五章 监察程序</span></strong><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一节 线索处置</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百六十八条 监察机关应当对问题线索归口受理、集中管理、分类处置、定期清理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百六十九条 监察机关对于报案或者举报应当依法接受。属于本级监察机关管辖的，依法予以受理；属于其他监察机关管辖的，应当在五个工作日以内予以转送。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关可以向下级监察机关发函交办检举控告，并进行督办，下级监察机关应当按期回复办理结果。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百七十条 对于涉嫌职务违法或者职务犯罪的公职人员主动投案的，应当依法接待和办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百七十一条 监察机关对于执法机关、司法机关等其他机关移送的问题线索，应当及时审核，并按照下列方式办理：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）本单位有管辖权的，及时研究提出处置意见；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）本单位没有管辖权但其他监察机关有管辖权的，在五个工作日以内转送有管辖权的监察机关；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）本单位对部分问题线索有管辖权的，对有管辖权的部分提出处置意见，并及时将其他问题线索转送有管辖权的机关；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）监察机关没有管辖权的，及时退回移送机关。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百七十二条 信访举报部门归口受理本机关管辖监察对象涉嫌职务违法和职务犯罪问题的检举控告，统一接收有关监察机关以及其他单位移送的相关检举控告，移交本机关监督检查部门或者相关部门，并将移交情况通报案件监督管理部门。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　案件监督管理部门统一接收巡视巡察机构和审计机关、执法机关、司法机关等其他机关移送的职务违法和职务犯罪问题线索，按程序移交本机关监督检查部门或者相关部门办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监督检查部门、调查部门在工作中发现的相关问题线索，属于本部门受理范围的，应当报送案件监督管理部门备案；属于本机关其他部门受理范围的，经审批后移交案件监督管理部门分办。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百七十三条 案件监督管理部门应当对问题线索实行集中管理、动态更新，定期汇总、核对问题线索及处置情况，向监察机关主要负责人报告，并向相关部门通报。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　问题线索承办部门应当指定专人负责管理线索，逐件编号登记、建立管理台账。线索管理处置各环节应当由经手人员签名，全程登记备查，及时与案件监督管理部门核对。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百七十四条 监督检查部门应当结合问题线索所涉及地区、部门、单位总体情况进行综合分析，提出处置意见并制定处置方案，经审批按照谈话、函询、初步核实、暂存待查、予以了结等方式进行处置，或者按照职责移送调查部门处置。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　函询应当以监察机关办公厅（室）名义发函给被反映人，并抄送其所在单位和派驻监察机构主要负责人。被函询人应当在收到函件后十五个工作日以内写出说明材料，由其所在单位主要负责人签署意见后发函回复。被函询人为所在单位主要负责人的，或者被函询人所作说明涉及所在单位主要负责人的，应当直接发函回复监察机关。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　被函询人已经退休的，按照第二款规定程序办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关根据工作需要，经审批可以对谈话、函询情况进行核实。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百七十五条 检举控告人使用本人真实姓名或者本单位名称，有电话等具体联系方式的，属于实名检举控告。监察机关对实名检举控告应当优先办理、优先处置，依法给予答复。虽有署名但不是检举控告人真实姓名（单位名称）或者无法验证的检举控告，按照匿名检举控告处理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　信访举报部门对属于本机关受理的实名检举控告，应当在收到检举控告之日起十五个工作日以内按规定告知实名检举控告人受理情况，并做好记录。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查人员应当将实名检举控告的处理结果在办结之日起十五个工作日以内向检举控告人反馈，并记录反馈情况。对检举控告人提出异议的应当如实记录，并向其进行说明；对提供新证据材料的，应当依法核查处理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二节 初步核实</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百七十六条 监察机关对具有可查性的职务违法和职务犯罪问题线索，应当按规定报批后，依法开展初步核实工作。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百七十七条 采取初步核实方式处置问题线索，应当确定初步核实对象，制定工作方案，明确需要核实的问题和采取的措施，成立核查组。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　在初步核实中应当注重收集客观性证据，确保真实性和准确性。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百七十八条 在初步核实中发现或者受理被核查人新的具有可查性的问题线索的，应当经审批纳入原初核方案开展核查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百七十九条 核查组在初步核实工作结束后应当撰写初步核实情况报告，列明被核查人基本情况、反映的主要问题、办理依据、初步核实结果、存在疑点、处理建议，由全体人员签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　承办部门应当综合分析初步核实情况，按照拟立案调查、予以了结、谈话提醒、暂存待查，或者移送有关部门、机关处理等方式提出处置建议，按照批准初步核实的程序报批。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三节 立 案</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百八十条 监察机关经过初步核实，对于已经掌握监察对象涉嫌职务违法或者职务犯罪的部分事实和证据，认为需要追究其法律责任的，应当按规定报批后，依法立案调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百八十一条 监察机关立案调查职务违法或者职务犯罪案件，需要对涉嫌行贿犯罪、介绍贿赂犯罪或者共同职务犯罪的涉案人员立案调查的，应当一并办理立案手续。需要交由下级监察机关立案的，经审批交由下级监察机关办理立案手续。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对单位涉嫌受贿、行贿等职务犯罪，需要追究法律责任的，依法对该单位办理立案调查手续。对事故（事件）中存在职务违法或者职务犯罪问题，需要追究法律责任，但相关责任人员尚不明确的，可以以事立案。对单位立案或者以事立案后，经调查确定相关责任人员的，按照管理权限报批确定被调查人。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关根据人民法院生效刑事判决、裁定和人民检察院不起诉决定认定的事实，需要对监察对象给予政务处分的，可以由相关监督检查部门依据司法机关的生效判决、裁定、决定及其认定的事实、性质和情节，提出给予政务处分的意见，按程序移送审理。对依法被追究行政法律责任的监察对象，需要给予政务处分的，应当依法办理立案手续。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百八十二条 对案情简单、经过初步核实已查清主要职务违法事实，应当追究监察对象法律责任，不再需要开展调查的，立案和移送审理可以一并报批，履行立案程序后再移送审理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百八十三条 上级监察机关需要指定下级监察机关立案调查的，应当按规定报批，向被指定管辖的监察机关出具《指定管辖决定书》，由其办理立案手续。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百八十四条 批准立案后，应当由二名以上调查人员出示证件，向被调查人宣布立案决定。宣布立案决定后，应当及时向被调查人所在单位等相关组织送达《立案通知书》，并向被调查人所在单位主要负责人通报。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对涉嫌严重职务违法或者职务犯罪的公职人员立案调查并采取留置措施的，应当按规定通知被调查人家属，并向社会公开发布。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四节 调 查</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百八十五条 监察机关对已经立案的职务违法或者职务犯罪案件应当依法进行调查，收集证据查明违法犯罪事实。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查职务违法或者职务犯罪案件，对被调查人没有采取留置措施的，应当在立案后一年以内作出处理决定；对被调查人解除留置措施的，应当在解除留置措施后一年以内作出处理决定。案情重大复杂的案件，经上一级监察机关批准，可以适当延长，但延长期限不得超过六个月。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　被调查人在监察机关立案调查以后逃匿的，调查期限自被调查人到案之日起重新计算。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百八十六条 案件立案后，监察机关主要负责人应当依照法定程序批准确定调查方案。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关应当组成调查组依法开展调查。调查工作应当严格按照批准的方案执行，不得随意扩大调查范围、变更调查对象和事项，对重要事项应当及时请示报告。调查人员在调查工作期间，未经批准不得单独接触任何涉案人员及其特定关系人，不得擅自采取调查措施。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百八十七条 调查组应当将调查认定的涉嫌违法犯罪事实形成书面材料，交给被调查人核对，听取其意见。被调查人应当在书面材料上签署意见。对被调查人签署不同意见或者拒不签署意见的，调查组应当作出说明或者注明情况。对被调查人提出申辩的事实、理由和证据应当进行核实，成立的予以采纳。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查组对于立案调查的涉嫌行贿犯罪、介绍贿赂犯罪或者共同职务犯罪的涉案人员，在查明其涉嫌犯罪问题后，依照前款规定办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于按照本条例规定，对立案和移送审理一并报批的案件，应当在报批前履行本条第一款规定的程序。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百八十八条 调查组在调查工作结束后应当集体讨论，形成调查报告。调查报告应当列明被调查人基本情况、问题线索来源及调查依据、调查过程，涉嫌的主要职务违法或者职务犯罪事实，被调查人的态度和认识，处置建议及法律依据，并由调查组组长以及有关人员签名。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对调查过程中发现的重要问题和形成的意见建议，应当形成专题报告。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百八十九条 调查组对被调查人涉嫌职务犯罪拟依法移送人民检察院审查起诉的，应当起草《起诉建议书》。《起诉建议书》应当载明被调查人基本情况，调查简况，认罪认罚情况，采取留置措施的时间，涉嫌职务犯罪事实以及证据，对被调查人从重、从轻、减轻或者免除处罚等情节，提出对被调查人移送起诉的理由和法律依据，采取强制措施的建议，并注明移送案卷数及涉案财物等内容。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查组应当形成被调查人到案经过及量刑情节方面的材料，包括案件来源、到案经过，自动投案、如实供述、立功等量刑情节，认罪悔罪态度、退赃、避免和减少损害结果发生等方面的情况说明及相关材料。被检举揭发的问题已被立案、查破，被检举揭发人已被采取调查措施或者刑事强制措施、起诉或者审判的，还应当附有关法律文书。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百九十条 经调查认为被调查人构成职务违法或者职务犯罪的，应当区分不同情况提出相应处理意见，经审批将调查报告、职务违法或者职务犯罪事实材料、涉案财物报告、涉案人员处理意见等材料，连同全部证据和文书手续移送审理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对涉嫌职务犯罪的案件材料应当按照刑事诉讼要求单独立卷，与《起诉建议书》、涉案财物报告、同步录音录像资料及其自查报告等材料一并移送审理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　调查全过程形成的材料应当案结卷成、事毕归档。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五节 审 理</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百九十一条 案件审理部门收到移送审理的案件后，应当审核材料是否齐全、手续是否完备。对被调查人涉嫌职务犯罪的，还应当审核相关案卷材料是否符合职务犯罪案件立卷要求，是否在调查报告中单独表述已查明的涉嫌犯罪问题，是否形成《起诉建议书》。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　经审核符合移送条件的，应当予以受理；不符合移送条件的，经审批可以暂缓受理或者不予受理，并要求调查部门补充完善材料。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百九十二条 案件审理部门受理案件后，应当成立由二人以上组成的审理组，全面审理案卷材料。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　案件审理部门对于受理的案件，应当以监察法、政务处分法、刑法、《中华人民共和国刑事诉讼法》等法律法规为准绳，对案件事实证据、性质认定、程序手续、涉案财物等进行全面审理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　案件审理部门应当强化监督制约职能，对案件严格审核把关，坚持实事求是、独立审理，依法提出审理意见。坚持调查与审理相分离的原则，案件调查人员不得参与审理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百九十三条 审理工作应当坚持民主集中制原则，经集体审议形成审理意见。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百九十四条 审理工作应当在受理之日起一个月以内完成，重大复杂案件经批准可以适当延长。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百九十五条 案件审理部门根据案件审理情况，经审批可以与被调查人谈话，告知其在审理阶段的权利义务，核对涉嫌违法犯罪事实，听取其辩解意见，了解有关情况。与被调查人谈话时，案件审理人员不得少于二人。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　具有下列情形之一的，一般应当与被调查人谈话：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）对被调查人采取留置措施，拟移送起诉的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）可能存在以非法方法收集证据情形的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）被调查人对涉嫌违法犯罪事实材料签署不同意见或者拒不签署意见的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）被调查人要求向案件审理人员当面陈述的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）其他有必要与被调查人进行谈话的情形。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百九十六条 经审理认为主要违法犯罪事实不清、证据不足的，应当经审批将案件退回承办部门重新调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　有下列情形之一，需要补充完善证据的，经审批可以退回补充调查：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）部分事实不清、证据不足的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）遗漏违法犯罪事实的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）其他需要进一步查清案件事实的情形。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　案件审理部门将案件退回重新调查或者补充调查的，应当出具审核意见，写明调查事项、理由、调查方向、需要补充收集的证据及其证明作用等，连同案卷材料一并送交承办部门。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　承办部门补充调查结束后，应当经审批将补证情况报告及相关证据材料，连同案卷材料一并移送案件审理部门；对确实无法查明的事项或者无法补充的证据，应当作出书面说明。重新调查终结后，应当重新形成调查报告，依法移送审理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　重新调查完毕移送审理的，审理期限重新计算。补充调查期间不计入审理期限。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百九十七条 审理工作结束后应当形成审理报告，载明被调查人基本情况、调查简况、涉嫌违法或者犯罪事实、被调查人态度和认识、涉案财物处置、承办部门意见、审理意见等内容，提请监察机关集体审议。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对被调查人涉嫌职务犯罪需要追究刑事责任的，应当形成《起诉意见书》，作为审理报告附件。《起诉意见书》应当忠实于事实真象，载明被调查人基本情况，调查简况，采取留置措施的时间，依法查明的犯罪事实和证据，从重、从轻、减轻或者免除处罚等情节，涉案财物情况，涉嫌罪名和法律依据，采取强制措施的建议，以及其他需要说明的情况。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　案件审理部门经审理认为现有证据不足以证明被调查人存在违法犯罪行为，且通过退回补充调查仍无法达到证明标准的，应当提出撤销案件的建议。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百九十八条 上级监察机关办理下级监察机关管辖案件的，可以经审理后按程序直接进行处置，也可以经审理形成处置意见后，交由下级监察机关办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一百九十九条 被指定管辖的监察机关在调查结束后应当将案件移送审理，提请监察机关集体审议。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　上级监察机关将其所管辖的案件指定管辖的，被指定管辖的下级监察机关应当按照前款规定办理后，将案件报上级监察机关依法作出政务处分决定。上级监察机关在作出决定前，应当进行审理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　上级监察机关将下级监察机关管辖的案件指定其他下级监察机关管辖的，被指定管辖的监察机关应当按照第一款规定办理后，将案件送交有管理权限的监察机关依法作出政务处分决定。有管理权限的监察机关应当进行审理，审理意见与被指定管辖的监察机关意见不一致的，双方应当进行沟通；经沟通不能取得一致意见的，报请有权决定的上级监察机关决定。经协商，有管理权限的监察机关在被指定管辖的监察机关审理阶段可以提前阅卷，沟通了解情况。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于前款规定的重大、复杂案件，被指定管辖的监察机关经集体审议后将处理意见报有权决定的上级监察机关审核同意的，有管理权限的监察机关可以经集体审议后依法处置。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六节 处 置</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百条 监察机关根据监督、调查结果，依据监察法、政务处分法等规定进行处置。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百零一条 监察机关对于公职人员有职务违法行为但情节较轻的，可以依法进行谈话提醒、批评教育、责令检查，或者予以诫勉。上述方式可以单独使用，也可以依据规定合并使用。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　谈话提醒、批评教育应当由监察机关相关负责人或者承办部门负责人进行，可以由被谈话提醒、批评教育人所在单位有关负责人陪同；经批准也可以委托其所在单位主要负责人进行。对谈话提醒、批评教育情况应当制作记录。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　被责令检查的公职人员应当作出书面检查并进行整改。整改情况在一定范围内通报。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　诫勉由监察机关以谈话或者书面方式进行。以谈话方式进行的，应当制作记录。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百零二条 对违法的公职人员依法需要给予政务处分的，应当根据情节轻重作出警告、记过、记大过、降级、撤职、开除的政务处分决定，制作政务处分决定书。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百零三条 监察机关应当将政务处分决定书在作出后一个月以内送达被处分人和被处分人所在机关、单位，并依法履行宣布、书面告知程序。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　政务处分决定自作出之日起生效。有关机关、单位、组织应当依法及时执行处分决定，并将执行情况向监察机关报告。处分决定应当在作出之日起一个月以内执行完毕，特殊情况下经监察机关批准可以适当延长办理期限，最迟不得超过六个月。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百零四条 监察机关对不履行或者不正确履行职责造成严重后果或者恶劣影响的领导人员，可以按照管理权限采取通报、诫勉、政务处分等方式进行问责；提出组织处理的建议。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百零五条 监察机关依法向监察对象所在单位提出监察建议的，应当经审批制作监察建议书。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察建议书一般应当包括下列内容：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）监督调查情况；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）调查中发现的主要问题及其产生的原因；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）整改建议、要求和期限；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）向监察机关反馈整改情况的要求。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百零六条 监察机关经调查，对没有证据证明或者现有证据不足以证明被调查人存在违法犯罪行为的，应当依法撤销案件。省级以下监察机关撤销案件后，应当在七个工作日以内向上一级监察机关报送备案报告。上一级监察机关监督检查部门负责备案工作。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　省级以下监察机关拟撤销上级监察机关指定管辖或者交办案件的，应当将《撤销案件意见书》连同案卷材料，在法定调查期限到期七个工作日前报指定管辖或者交办案件的监察机关审查。对于重大、复杂案件，在法定调查期限到期十个工作日前报指定管辖或者交办案件的监察机关审查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　指定管辖或者交办案件的监察机关由监督检查部门负责审查工作。指定管辖或者交办案件的监察机关同意撤销案件的，下级监察机关应当作出撤销案件决定，制作《撤销案件决定书》；指定管辖或者交办案件的监察机关不同意撤销案件的，下级监察机关应当执行该决定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关对于撤销案件的决定应当向被调查人宣布，由其在《撤销案件决定书》上签名、捺指印，立即解除留置措施，并通知其所在机关、单位。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　撤销案件后又发现重要事实或者有充分证据，认为被调查人有违法犯罪事实需要追究法律责任的，应当重新立案调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百零七条 对于涉嫌行贿等犯罪的非监察对象，案件调查终结后依法移送起诉。综合考虑行为性质、手段、后果、时间节点、认罪悔罪态度等具体情况，对于情节较轻，经审批不予移送起诉的，应当采取批评教育、责令具结悔过等方式处置；应当给予行政处罚的，依法移送有关行政执法部门。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于有行贿行为的涉案单位和人员，按规定记入相关信息记录，可以作为信用评价的依据。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于涉案单位和人员通过行贿等非法手段取得的财物及孳息，应当依法予以没收、追缴或者责令退赔。对于违法取得的其他不正当利益，依照法律法规及有关规定予以纠正处理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百零八条 对查封、扣押、冻结的涉嫌职务犯罪所得财物及孳息应当妥善保管，并制作《移送司法机关涉案财物清单》随案移送人民检察院。对作为证据使用的实物应当随案移送；对不宜移送的，应当将清单、照片和其他证明文件随案移送。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于移送人民检察院的涉案财物，价值不明的，应当在移送起诉前委托进行价格认定。在价格认定过程中，需要对涉案财物先行作出真伪鉴定或者出具技术、质量检测报告的，应当委托有关鉴定机构或者检测机构进行真伪鉴定或者技术、质量检测。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对不属于犯罪所得但属于违法取得的财物及孳息，应当依法予以没收、追缴或者责令退赔，并出具有关法律文书。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对经认定不属于违法所得的财物及孳息，应当及时予以返还，并办理签收手续。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百零九条 监察机关经调查，对违法取得的财物及孳息决定追缴或者责令退赔的，可以依法要求公安、自然资源、住房城乡建设、市场监管、金融监管等部门以及银行等机构、单位予以协助。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　追缴涉案财物以追缴原物为原则，原物已经转化为其他财物的，应当追缴转化后的财物；有证据证明依法应当追缴、没收的涉案财物无法找到、被他人善意取得、价值灭失减损或者与其他合法财产混合且不可分割的，可以依法追缴、没收其他等值财产。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　追缴或者责令退赔应当自处置决定作出之日起一个月以内执行完毕。因被调查人的原因逾期执行的除外。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　人民检察院、人民法院依法将不认定为犯罪所得的相关涉案财物退回监察机关的，监察机关应当依法处理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百一十条 监察对象对监察机关作出的涉及本人的处理决定不服的，可以在收到处理决定之日起一个月以内，向作出决定的监察机关申请复审。复审机关应当依法受理，并在受理后一个月以内作出复审决定。监察对象对复审决定仍不服的，可以在收到复审决定之日起一个月以内，向上一级监察机关申请复核。复核机关应当依法受理，并在受理后二个月以内作出复核决定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　上一级监察机关的复核决定和国家监察委员会的复审、复核决定为最终决定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百一十一条 复审、复核机关承办部门应当成立工作组，调阅原案卷宗，必要时可以进行调查取证。承办部门应当集体研究，提出办理意见，经审批作出复审、复核决定。决定应当送达申请人，抄送相关单位，并在一定范围内宣布。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　复审、复核期间，不停止原处理决定的执行。复审、复核机关经审查认定处理决定有错误或者不当的，应当依法撤销、变更原处理决定，或者责令原处理机关及时予以纠正。复审、复核机关经审查认定处理决定事实清楚、适用法律正确的，应当予以维持。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　坚持复审复核与调查审理分离，原案调查、审理人员不得参与复审复核。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七节 移送审查起诉</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百一十二条 监察机关决定对涉嫌职务犯罪的被调查人移送起诉的，应当出具《起诉意见书》，连同案卷材料、证据等，一并移送同级人民检察院。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关案件审理部门负责与人民检察院审查起诉的衔接工作，调查、案件监督管理等部门应当予以协助。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　国家监察委员会派驻或者派出的监察机构、监察专员调查的职务犯罪案件，应当依法移送省级人民检察院审查起诉。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百一十三条 涉嫌职务犯罪的被调查人和涉案人员符合监察法第三十一条、第三十二条规定情形的，结合其案发前的一贯表现、违法犯罪行为的情节、后果和影响等因素，监察机关经综合研判和集体审议，报上一级监察机关批准，可以在移送人民检察院时依法提出从轻、减轻或者免除处罚等从宽处罚建议。报请批准时，应当一并提供主要证据材料、忏悔反思材料。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　上级监察机关相关监督检查部门负责审查工作，重点审核拟认定的从宽处罚情形、提出的从宽处罚建议，经审批在十五个工作日以内作出批复。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百一十四条 涉嫌职务犯罪的被调查人有下列情形之一，如实交代自己主要犯罪事实的，可以认定为监察法第三十一条第一项规定的自动投案，真诚悔罪悔过：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）职务犯罪问题未被监察机关掌握，向监察机关投案的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）在监察机关谈话、函询过程中，如实交代监察机关未掌握的涉嫌职务犯罪问题的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）在初步核实阶段，尚未受到监察机关谈话时投案的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）职务犯罪问题虽被监察机关立案，但尚未受到讯问或者采取留置措施，向监察机关投案的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）因伤病等客观原因无法前往投案，先委托他人代为表达投案意愿，或者以书信、网络、电话、传真等方式表达投案意愿，后到监察机关接受处理的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）涉嫌职务犯罪潜逃后又投案，包括在被通缉、抓捕过程中投案的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（七）经查实确已准备去投案，或者正在投案途中被有关机关抓获的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（八）经他人规劝或者在他人陪同下投案的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（九）虽未向监察机关投案，但向其所在党组织、单位或者有关负责人员投案，向有关巡视巡察机构投案，以及向公安机关、人民检察院、人民法院投案的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（十）具有其他应当视为自动投案的情形的。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　被调查人自动投案后不能如实交代自己的主要犯罪事实，或者自动投案并如实供述自己的罪行后又翻供的，不能适用前款规定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百一十五条 涉嫌职务犯罪的被调查人有下列情形之一的，可以认定为监察法第三十一条第二项规定的积极配合调查工作，如实供述监察机关还未掌握的违法犯罪行为：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）监察机关所掌握线索针对的犯罪事实不成立，在此范围外被调查人主动交代其他罪行的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）主动交代监察机关尚未掌握的犯罪事实，与监察机关已掌握的犯罪事实属不同种罪行的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）主动交代监察机关尚未掌握的犯罪事实，与监察机关已掌握的犯罪事实属同种罪行的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）监察机关掌握的证据不充分，被调查人如实交代有助于收集定案证据的。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　前款所称同种罪行和不同种罪行，一般以罪名区分。被调查人如实供述其他罪行的罪名与监察机关已掌握犯罪的罪名不同，但属选择性罪名或者在法律、事实上密切关联的，应当认定为同种罪行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百一十六条 涉嫌职务犯罪的被调查人有下列情形之一的，可以认定为监察法第三十一条第三项规定的积极退赃，减少损失：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）全额退赃的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）退赃能力不足，但被调查人及其亲友在监察机关追缴赃款赃物过程中积极配合，且大部分已追缴到位的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）犯罪后主动采取措施避免损失发生，或者积极采取有效措施减少、挽回大部分损失的。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百一十七条 涉嫌职务犯罪的被调查人有下列情形之一的，可以认定为监察法第三十一条第四项规定的具有重大立功表现：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）检举揭发他人重大犯罪行为且经查证属实的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）提供其他重大案件的重要线索且经查证属实的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）阻止他人重大犯罪活动的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）协助抓捕其他重大职务犯罪案件被调查人、重大犯罪嫌疑人（包括同案犯）的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）为国家挽回重大损失等对国家和社会有其他重大贡献的。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　前款所称重大犯罪一般是指依法可能被判处无期徒刑以上刑罚的犯罪行为；重大案件一般是指在本省、自治区、直辖市或者全国范围内有较大影响的案件；查证属实一般是指有关案件已被监察机关或者司法机关立案调查、侦查，被调查人、犯罪嫌疑人被监察机关采取留置措施或者被司法机关采取强制措施，或者被告人被人民法院作出有罪判决，并结合案件事实、证据进行判断。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察法第三十一条第四项规定的案件涉及国家重大利益，是指案件涉及国家主权和领土完整、国家安全、外交、社会稳定、经济发展等情形。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百一十八条 涉嫌行贿等犯罪的涉案人员有下列情形之一的，可以认定为监察法第三十二条规定的揭发有关被调查人职务违法犯罪行为，查证属实或者提供重要线索，有助于调查其他案件：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）揭发所涉案件以外的被调查人职务犯罪行为，经查证属实的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）提供的重要线索指向具体的职务犯罪事实，对调查其他案件起到实质性推动作用的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）提供的重要线索有助于加快其他案件办理进度，或者对其他案件固定关键证据、挽回损失、追逃追赃等起到积极作用的。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百一十九条 从宽处罚建议一般应当在移送起诉时作为《起诉意见书》内容一并提出，特殊情况下也可以在案件移送后、人民检察院提起公诉前，单独形成从宽处罚建议书移送人民检察院。对于从宽处罚建议所依据的证据材料，应当一并移送人民检察院。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关对于被调查人在调查阶段认罪认罚，但不符合监察法规定的提出从宽处罚建议条件，在移送起诉时没有提出从宽处罚建议的，应当在《起诉意见书》中写明其自愿认罪认罚的情况。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百二十条 监察机关一般应当在正式移送起诉十日前，向拟移送的人民检察院采取书面通知等方式预告移送事宜。对于已采取留置措施的案件，发现被调查人因身体等原因存在不适宜羁押等可能影响刑事强制措施执行情形的，应当通报人民检察院。对于未采取留置措施的案件，可以根据案件具体情况，向人民检察院提出对被调查人采取刑事强制措施的建议。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百二十一条 监察机关办理的职务犯罪案件移送起诉，需要指定起诉、审判管辖的，应当与同级人民检察院协商有关程序事宜。需要由同级人民检察院的上级人民检察院指定管辖的，应当商请同级人民检察院办理指定管辖事宜。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关一般应当在移送起诉二十日前，将商请指定管辖函送交同级人民检察院。商请指定管辖函应当附案件基本情况，对于被调查人已被其他机关立案侦查的犯罪认为需要并案审查起诉的，一并进行说明。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　派驻或者派出的监察机构、监察专员调查的职务犯罪案件需要指定起诉、审判管辖的，应当报派出机关办理指定管辖手续。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百二十二条 上级监察机关指定下级监察机关进行调查，移送起诉时需要人民检察院依法指定管辖的，应当在移送起诉前由上级监察机关与同级人民检察院协商有关程序事宜。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百二十三条 监察机关对已经移送起诉的职务犯罪案件，发现遗漏被调查人罪行需要补充移送起诉的，应当经审批出具《补充起诉意见书》，连同相关案卷材料、证据等一并移送同级人民检察院。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　对于经人民检察院指定管辖的案件需要补充移送起诉的，可以直接移送原受理移送起诉的人民检察院；需要追加犯罪嫌疑人、被告人的，应当再次商请人民检察院办理指定管辖手续。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百二十四条 对于涉嫌行贿犯罪、介绍贿赂犯罪或者共同职务犯罪等关联案件的涉案人员，移送起诉时一般应当随主案确定管辖。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　主案与关联案件由不同监察机关立案调查的，调查关联案件的监察机关在移送起诉前，应当报告或者通报调查主案的监察机关，由其统一协调案件管辖事宜。因特殊原因，关联案件不宜随主案确定管辖的，调查主案的监察机关应当及时通报和协调有关事项。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百二十五条 监察机关对于人民检察院在审查起诉中书面提出的下列要求应当予以配合：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）认为可能存在以非法方法收集证据情形，要求监察机关对证据收集的合法性作出说明或者提供相关证明材料的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）排除非法证据后，要求监察机关另行指派调查人员重新取证的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）对物证、书证、视听资料、电子数据及勘验检查、辨认、调查实验等笔录存在疑问，要求调查人员提供获取、制作的有关情况的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）要求监察机关对案件中某些专门性问题进行鉴定，或者对勘验检查进行复验、复查的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）认为主要犯罪事实已经查清，仍有部分证据需要补充完善，要求监察机关补充提供证据的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）人民检察院依法提出的其他工作要求。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百二十六条 监察机关对于人民检察院依法退回补充调查的案件，应当向主要负责人报告，并积极开展补充调查工作。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百二十七条 对人民检察院退回补充调查的案件，经审批分别作出下列处理：　</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）认定犯罪事实的证据不够充分的，应当在补充证据后，制作补充调查报告书，连同相关材料一并移送人民检察院审查，对无法补充完善的证据，应当作出书面情况说明，并加盖监察机关或者承办部门公章；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）在补充调查中发现新的同案犯或者增加、变更犯罪事实，需要追究刑事责任的，应当重新提出处理意见，移送人民检察院审查；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）犯罪事实的认定出现重大变化，认为不应当追究被调查人刑事责任的，应当重新提出处理意见，将处理结果书面通知人民检察院并说明理由；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）认为移送起诉的犯罪事实清楚，证据确实、充分的，应当说明理由，移送人民检察院依法审查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百二十八条 人民检察院在审查起诉过程中发现新的职务违法或者职务犯罪问题线索并移送监察机关的，监察机关应当依法处置。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百二十九条 在案件审判过程中，人民检察院书面要求监察机关补充提供证据，对证据进行补正、解释，或者协助人民检察院补充侦查的，监察机关应当予以配合。监察机关不能提供有关证据材料的，应当书面说明情况。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　人民法院在审判过程中就证据收集合法性问题要求有关调查人员出庭说明情况时，监察机关应当依法予以配合。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百三十条 监察机关认为人民检察院不起诉决定有错误的，应当在收到不起诉决定书后三十日以内，依法向其上一级人民检察院提请复议。监察机关应当将上述情况及时向上一级监察机关书面报告。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百三十一条 对于监察机关移送起诉的案件，人民检察院作出不起诉决定，人民法院作出无罪判决，或者监察机关经人民检察院退回补充调查后不再移送起诉，涉及对被调查人已生效政务处分事实认定的，监察机关应当依法对政务处分决定进行审核。认为原政务处分决定认定事实清楚、适用法律正确的，不再改变；认为原政务处分决定确有错误或者不当的，依法予以撤销或者变更。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百三十二条 对于贪污贿赂、失职渎职等职务犯罪案件，被调查人逃匿，在通缉一年后不能到案，或者被调查人死亡，依法应当追缴其违法所得及其他涉案财产的，承办部门在调查终结后应当依法移送审理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关应当经集体审议，出具《没收违法所得意见书》，连同案卷材料、证据等，一并移送人民检察院依法提出没收违法所得的申请。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关将《没收违法所得意见书》移送人民检察院后，在逃的被调查人自动投案或者被抓获的，监察机关应当及时通知人民检察院。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百三十三条 监察机关立案调查拟适用缺席审判程序的贪污贿赂犯罪案件，应当逐级报送国家监察委员会同意。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关承办部门认为在境外的被调查人犯罪事实已经查清，证据确实、充分，依法应当追究刑事责任的，应当依法移送审理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关应当经集体审议，出具《起诉意见书》，连同案卷材料、证据等，一并移送人民检察院审查起诉。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　在审查起诉或者缺席审判过程中，犯罪嫌疑人、被告人向监察机关自动投案或者被抓获的，监察机关应当立即通知人民检察院、人民法院。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>　　第六章 反腐败国际合作</span></strong><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一节 工作职责和领导体制</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百三十四条 国家监察委员会统筹协调与其他国家、地区、国际组织开展反腐败国际交流、合作。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　国家监察委员会组织《联合国反腐败公约》等反腐败国际条约的实施以及履约审议等工作，承担《联合国反腐败公约》司法协助中央机关有关工作。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　国家监察委员会组织协调有关单位建立集中统一、高效顺畅的反腐败国际追逃追赃和防逃协调机制，统筹协调、督促指导各级监察机关反腐败国际追逃追赃等涉外案件办理工作，具体履行下列职责：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）制定反腐败国际追逃追赃和防逃工作计划，研究工作中的重要问题；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）组织协调反腐败国际追逃追赃等重大涉外案件办理工作；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）办理由国家监察委员会管辖的涉外案件；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）指导地方各级监察机关依法开展涉外案件办理工作；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）汇总和通报全国职务犯罪外逃案件信息和追逃追赃工作信息；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）建立健全反腐败国际追逃追赃和防逃合作网络；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（七）承担监察机关开展国际刑事司法协助的主管机关职责；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（八）承担其他与反腐败国际追逃追赃等涉外案件办理工作相关的职责。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百三十五条 地方各级监察机关在国家监察委员会领导下，统筹协调、督促指导本地区反腐败国际追逃追赃等涉外案件办理工作，具体履行下列职责：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）落实上级监察机关关于反腐败国际追逃追赃和防逃工作部署，制定工作计划；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）按照管辖权限或者上级监察机关指定管辖，办理涉外案件；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）按照上级监察机关要求，协助配合其他监察机关开展涉外案件办理工作；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）汇总和通报本地区职务犯罪外逃案件信息和追逃追赃工作信息；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）承担本地区其他与反腐败国际追逃追赃等涉外案件办理工作相关的职责。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　省级监察委员会应当会同有关单位，建立健全本地区反腐败国际追逃追赃和防逃协调机制。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　国家监察委员会派驻或者派出的监察机构、监察专员统筹协调、督促指导本部门反腐败国际追逃追赃等涉外案件办理工作，参照第一款规定执行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百三十六条 国家监察委员会国际合作局归口管理监察机关反腐败国际追逃追赃等涉外案件办理工作。地方各级监察委员会应当明确专责部门，归口管理本地区涉外案件办理工作。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　国家监察委员会派驻或者派出的监察机构、监察专员和地方各级监察机关办理涉外案件中有关执法司法国际合作事项，应当逐级报送国家监察委员会审批。由国家监察委员会依法直接或者协调有关单位与有关国家（地区）相关机构沟通，以双方认可的方式实施。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百三十七条 监察机关应当建立追逃追赃和防逃工作内部联络机制。承办部门在调查过程中，发现被调查人或者重要涉案人员外逃、违法所得及其他涉案财产被转移到境外的，可以请追逃追赃部门提供工作协助。监察机关将案件移送人民检察院审查起诉后，仍有重要涉案人员外逃或者未追缴的违法所得及其他涉案财产的，应当由追逃追赃部门继续办理，或者由追逃追赃部门指定协调有关单位办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二节 国（境）内工作</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百三十八条 监察机关应当将防逃工作纳入日常监督内容，督促相关机关、单位建立健全防逃责任机制。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关在监督、调查工作中，应当根据情况制定对监察对象、重要涉案人员的防逃方案，防范人员外逃和资金外流风险。监察机关应当会同同级组织人事、外事、公安、移民管理等单位健全防逃预警机制，对存在外逃风险的监察对象早发现、早报告、早处置。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百三十九条 监察机关应当加强与同级人民银行、公安等单位的沟通协作，推动预防、打击利用离岸公司和地下钱庄等向境外转移违法所得及其他涉案财产，对涉及职务违法和职务犯罪的行为依法进行调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百四十条 国家监察委员会派驻或者派出的监察机构、监察专员和地方各级监察委员会发现监察对象出逃、失踪、出走，或者违法所得及其他涉案财产被转移至境外的，应当在二十四小时以内将有关信息逐级报送至国家监察委员会国际合作局，并迅速开展相关工作。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百四十一条 监察机关追逃追赃部门统一接收巡视巡察机构、审计机关、行政执法部门、司法机关等单位移交的外逃信息。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关对涉嫌职务违法和职务犯罪的外逃人员，应当明确承办部门，建立案件档案。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百四十二条 监察机关应当依法全面收集外逃人员涉嫌职务违法和职务犯罪证据。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百四十三条 开展反腐败国际追逃追赃等涉外案件办理工作，应当把思想教育贯穿始终，落实宽严相济刑事政策，依法适用认罪认罚从宽制度，促使外逃人员回国投案或者配合调查、主动退赃。开展相关工作，应当尊重所在国家（地区）的法律规定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百四十四条 外逃人员归案、违法所得及其他涉案财产被追缴后，承办案件的监察机关应当将情况逐级报送国家监察委员会国际合作局。监察机关应当依法对涉案人员和违法所得及其他涉案财产作出处置，或者请有关单位依法处置。对不需要继续采取相关措施的，应当及时解除或者撤销。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三节 对外合作</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百四十五条 监察机关对依法应当留置或者已经决定留置的外逃人员，需要申请发布国际刑警组织红色通报的，应当逐级报送国家监察委员会审核。国家监察委员会审核后，依法通过公安部向国际刑警组织提出申请。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　需要延期、暂停、撤销红色通报的，申请发布红色通报的监察机关应当逐级报送国家监察委员会审核，由国家监察委员会依法通过公安部联系国际刑警组织办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百四十六条 地方各级监察机关通过引渡方式办理相关涉外案件的，应当按照引渡法、相关双边及多边国际条约等规定准备引渡请求书及相关材料，逐级报送国家监察委员会审核。由国家监察委员会依法通过外交等渠道向外国提出引渡请求。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百四十七条 地方各级监察机关通过刑事司法协助方式办理相关涉外案件的，应当按照国际刑事司法协助法、相关双边及多边国际条约等规定准备刑事司法协助请求书及相关材料，逐级报送国家监察委员会审核。由国家监察委员会依法直接或者通过对外联系机关等渠道，向外国提出刑事司法协助请求。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　国家监察委员会收到外国提出的刑事司法协助请求书及所附材料，经审查认为符合有关规定的，作出决定并交由省级监察机关执行，或者转交其他有关主管机关。省级监察机关应当立即执行，或者交由下级监察机关执行，并将执行结果或者妨碍执行的情形及时报送国家监察委员会。在执行过程中，需要依法采取查询、调取、查封、扣押、冻结等措施或者需要返还涉案财物的，根据我国法律规定和国家监察委员会的执行决定办理有关法律手续。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百四十八条 地方各级监察机关通过执法合作方式办理相关涉外案件的，应当将合作事项及相关材料逐级报送国家监察委员会审核。由国家监察委员会依法直接或者协调有关单位，向有关国家（地区）相关机构提交并开展合作。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百四十九条 地方各级监察机关通过境外追诉方式办理相关涉外案件的，应当提供外逃人员相关违法线索和证据，逐级报送国家监察委员会审核。由国家监察委员会依法直接或者协调有关单位向有关国家（地区）相关机构提交，请其依法对外逃人员调查、起诉和审判，并商有关国家（地区）遣返外逃人员。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百五十条 监察机关对依法应当追缴的境外违法所得及其他涉案财产，应当责令涉案人员以合法方式退赔。涉案人员拒不退赔的，可以依法通过下列方式追缴：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）在开展引渡等追逃合作时，随附请求有关国家（地区）移交相关违法所得及其他涉案财产；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）依法启动违法所得没收程序，由人民法院对相关违法所得及其他涉案财产作出冻结、没收裁定，请有关国家（地区）承认和执行，并予以返还；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）请有关国家（地区）依法追缴相关违法所得及其他涉案财产，并予以返还；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）通过其他合法方式追缴。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>　　第七章 对监察机关和监察人员的监督</span></strong><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百五十一条 监察机关和监察人员必须自觉坚持党的领导，在党组织的管理、监督下开展工作，依法接受本级人民代表大会及其常务委员会的监督，接受民主监督、司法监督、社会监督、舆论监督，加强内部监督制约机制建设，确保权力受到严格的约束和监督。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百五十二条 各级监察委员会应当按照监察法第五十三条第二款规定，由主任在本级人民代表大会常务委员会全体会议上报告专项工作。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　在报告专项工作前，应当与本级人民代表大会有关专门委员会沟通协商，并配合开展调查研究等工作。各级人民代表大会常务委员会审议专项工作报告时，本级监察委员会应当根据要求派出领导成员列席相关会议，听取意见。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　各级监察委员会应当认真研究办理本级人民代表大会常务委员会反馈的审议意见，并按照要求书面报告办理情况。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百五十三条 各级监察委员会应当积极接受、配合本级人民代表大会常务委员会组织的执法检查。对本级人民代表大会常务委员会的执法检查报告，应当认真研究处理，并向其报告处理情况。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百五十四条 各级监察委员会在本级人民代表大会常务委员会会议审议与监察工作有关的议案和报告时，应当派相关负责人到会听取意见，回答询问。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关对依法交由监察机关答复的质询案应当按照要求进行答复。口头答复的，由监察机关主要负责人或者委派相关负责人到会答复。书面答复的，由监察机关主要负责人签署。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百五十五条 各级监察机关应当通过互联网政务媒体、报刊、广播、电视等途径，向社会及时准确公开下列监察工作信息：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）监察法规；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）依法应当向社会公开的案件调查信息；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）检举控告地址、电话、网站等信息；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）其他依法应当公开的信息。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百五十六条 各级监察机关可以根据工作需要，按程序选聘特约监察员履行监督、咨询等职责。特约监察员名单应当向社会公布。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关应当为特约监察员依法开展工作提供必要条件和便利。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百五十七条 监察机关实行严格的人员准入制度，严把政治关、品行关、能力关、作风关、廉洁关。监察人员必须忠诚坚定、担当尽责、遵纪守法、清正廉洁。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百五十八条 监察机关应当建立监督检查、调查、案件监督管理、案件审理等部门相互协调制约的工作机制。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监督检查和调查部门实行分工协作、相互制约。监督检查部门主要负责联系地区、部门、单位的日常监督检查和对涉嫌一般违法问题线索处置。调查部门主要负责对涉嫌严重职务违法和职务犯罪问题线索进行初步核实和立案调查。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　案件监督管理部门负责对监督检查、调查工作全过程进行监督管理，做好线索管理、组织协调、监督检查、督促办理、统计分析等工作。案件监督管理部门发现监察人员在监督检查、调查中有违规办案行为的，及时督促整改；涉嫌违纪违法的，根据管理权限移交相关部门处理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百五十九条 监察机关应当对监察权运行关键环节进行经常性监督检查，适时开展专项督查。案件监督管理、案件审理等部门应当按照各自职责，对问题线索处置、调查措施使用、涉案财物管理等进行监督检查，建立常态化、全覆盖的案件质量评查机制。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百六十条 监察机关应当加强对监察人员执行职务和遵纪守法情况的监督，按照管理权限依法对监察人员涉嫌违法犯罪问题进行调查处置。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百六十一条 监察机关及其监督检查、调查部门负责人应当定期检查调查期间的录音录像、谈话笔录、涉案财物登记资料，加强对调查全过程的监督，发现问题及时纠正并报告。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百六十二条 对监察人员打听案情、过问案件、说情干预的，办理监察事项的监察人员应当及时向上级负责人报告。有关情况应当登记备案。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　发现办理监察事项的监察人员未经批准接触被调查人、涉案人员及其特定关系人，或者存在交往情形的，知情的监察人员应当及时向上级负责人报告。有关情况应当登记备案。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百六十三条 办理监察事项的监察人员有监察法第五十八条所列情形之一的，应当自行提出回避；没有自行提出回避的，监察机关应当依法决定其回避，监察对象、检举人及其他有关人员也有权要求其回避。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　选用借调人员、看护人员、调查场所，应当严格执行回避制度。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百六十四条 监察人员自行提出回避，或者监察对象、检举人及其他有关人员要求监察人员回避的，应当书面或者口头提出，并说明理由。口头提出的，应当形成记录。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关主要负责人的回避，由上级监察机关主要负责人决定；其他监察人员的回避，由本级监察机关主要负责人决定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百六十五条 上级监察机关应当通过专项检查、业务考评、开展复查等方式，强化对下级监察机关及监察人员执行职务和遵纪守法情况的监督。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百六十六条 监察机关应当对监察人员有计划地进行政治、理论和业务培训。培训应当坚持理论联系实际、按需施教、讲求实效，突出政治机关特色，建设高素质专业化监察队伍。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百六十七条 监察机关应当严格执行保密制度，控制监察事项知悉范围和时间。监察人员不准私自留存、隐匿、查阅、摘抄、复制、携带问题线索和涉案资料，严禁泄露监察工作秘密。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关应当建立健全检举控告保密制度，对检举控告人的姓名（单位名称）、工作单位、住址、电话和邮箱等有关情况以及检举控告内容必须严格保密。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百六十八条 监察机关涉密人员离岗离职后，应当遵守脱密期管理规定，严格履行保密义务，不得泄露相关秘密。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百六十九条 监察人员离任三年以内，不得从事与监察和司法工作相关联且可能发生利益冲突的职业。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察人员离任后，不得担任原任职监察机关办理案件的诉讼代理人或者辩护人，但是作为当事人的监护人或者近亲属代理诉讼或者进行辩护的除外。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百七十条 监察人员应当严格遵守有关规范领导干部配偶、子女及其配偶经商办企业行为的规定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百七十一条 监察机关在履行职责过程中应当依法保护企业产权和自主经营权，严禁利用职权非法干扰企业生产经营。需要企业经营者协助调查的，应当依法保障其合法的人身、财产等权益，避免或者减少对涉案企业正常生产、经营活动的影响。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　查封企业厂房、机器设备等生产资料，企业继续使用对该财产价值无重大影响的，可以允许其使用。对于正在运营或者正在用于科技创新、产品研发的设备和技术资料等，一般不予查封、扣押，确需调取违法犯罪证据的，可以采取拍照、复制等方式。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百七十二条 被调查人及其近亲属认为监察机关及监察人员存在监察法第六十条第一款规定的有关情形，向监察机关提出申诉的，由监察机关案件监督管理部门依法受理，并按照法定的程序和时限办理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百七十三条 监察机关在维护监督执法调查工作纪律方面失职失责的，依法追究责任。监察人员涉嫌严重职务违法、职务犯罪或者对案件处置出现重大失误的，既应当追究直接责任，还应当严肃追究负有责任的领导人员责任。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关应当建立办案质量责任制，对滥用职权、失职失责造成严重后果的，实行终身责任追究。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>　　第八章 法律责任</span></strong><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百七十四条 有关单位拒不执行监察机关依法作出的下列处理决定的，应当由其主管部门、上级机关责令改正，对单位给予通报批评，对负有责任的领导人员和直接责任人员依法给予处理：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）政务处分决定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）问责决定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）谈话提醒、批评教育、责令检查，或者予以诫勉的决定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）采取调查措施的决定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）复审、复核决定；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）监察机关依法作出的其他处理决定。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百七十五条 监察对象对控告人、申诉人、批评人、检举人、证人、监察人员进行打击、压制等报复陷害的，监察机关应当依法给予政务处分。构成犯罪的，依法追究刑事责任。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百七十六条 控告人、检举人、证人采取捏造事实、伪造材料等方式诬告陷害的，监察机关应当依法给予政务处分，或者移送有关机关处理。构成犯罪的，依法追究刑事责任。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察人员因依法履行职责遭受不实举报、诬告陷害、侮辱诽谤，致使名誉受到损害的，监察机关应当会同有关部门及时澄清事实，消除不良影响，并依法追究相关单位或者个人的责任。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百七十七条 监察机关应当建立健全办案安全责任制。承办部门主要负责人和调查组组长是调查安全第一责任人。调查组应当指定专人担任安全员。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　地方各级监察机关履行管理、监督职责不力发生严重办案安全事故的，或者办案中存在严重违规违纪违法行为的，省级监察机关主要负责人应当向国家监察委员会作出检讨，并予以通报、严肃追责问责。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　案件监督管理部门应当对办案安全责任制落实情况组织经常性检查和不定期抽查，发现问题及时报告并督促整改。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百七十八条 监察人员在履行职责中有下列行为之一的，依法严肃处理；构成犯罪的，依法追究刑事责任：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）贪污贿赂、徇私舞弊的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）不履行或者不正确履行监督职责，应当发现的问题没有发现，或者发现问题不报告、不处置，造成严重影响的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）未经批准、授权处置问题线索，发现重大案情隐瞒不报，或者私自留存、处理涉案材料的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）利用职权或者职务上的影响干预调查工作的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）违法窃取、泄露调查工作信息，或者泄露举报事项、举报受理情况以及举报人信息的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）对被调查人或者涉案人员逼供、诱供，或者侮辱、打骂、虐待、体罚或者变相体罚的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（七）违反规定处置查封、扣押、冻结的财物的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（八）违反规定导致发生办案安全事故，或者发生安全事故后隐瞒不报、报告失实、处置不当的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（九）违反规定采取留置措施的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（十）违反规定限制他人出境，或者不按规定解除出境限制的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（十一）其他职务违法和职务犯罪行为。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百七十九条 对监察人员在履行职责中存在违法行为的，可以根据情节轻重，依法进行谈话提醒、批评教育、责令检查、诫勉，或者给予政务处分。构成犯罪的，依法追究刑事责任。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百八十条 监察机关及其工作人员在行使职权时，有下列情形之一的，受害人可以申请国家赔偿：</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）采取留置措施后，决定撤销案件的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）违法没收、追缴或者违法查封、扣押、冻结财物造成损害的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）违法行使职权，造成被调查人、涉案人员或者证人身体伤害或者死亡的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）非法剥夺他人人身自由的；</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）其他侵犯公民、法人和其他组织合法权益造成损害的。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　受害人死亡的，其继承人和其他有扶养关系的亲属有权要求赔偿；受害的法人或者其他组织终止的，其权利承受人有权要求赔偿。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百八十一条 监察机关及其工作人员违法行使职权侵犯公民、法人和其他组织的合法权益造成损害的，该机关为赔偿义务机关。申请赔偿应当向赔偿义务机关提出，由该机关负责复审复核工作的部门受理。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　赔偿以支付赔偿金为主要方式。能够返还财产或者恢复原状的，予以返还财产或者恢复原状。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>　　第九章 附 则</span></strong><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百八十二条 本条例所称监察机关，包括各级监察委员会及其派驻或者派出监察机构、监察专员。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百八十三条 本条例所称“近亲属”，是指夫、妻、父、母、子、女、同胞兄弟姊妹。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百八十四条 本条例所称以上、以下、以内，包括本级、本数。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百八十五条 期间以时、日、月、年计算，期间开始的时和日不算在期间以内。本条例另有规定的除外。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　按照年、月计算期间的，到期月的对应日为期间的最后一日；没有对应日的，月末日为期间的最后一日。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　期间的最后一日是法定休假日的，以法定休假日结束的次日为期间的最后一日。但被调查人留置期间应当至到期之日为止，不得因法定休假日而延长。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百八十六条 本条例由国家监察委员会负责解释。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"background: white; margin: 0cm 0cm 6.75pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二百八十七条 本条例自发布之日起施行。</span><span lang=\"EN-US\" style=\'color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2470','3294','1','','','（2020年6月20日第十三届全国人民代表大会常务委员会第十九次会议通过）　　目录　　第一章　总则　　第二章　政务处分的种类和适用　　第三章　违法行为及其适用的政务处分　　第四章　政务处分的程序　　第五章　复审、复核　　第六章　法律责任　　第七章　附则　　','172.16.1.2','172.16.1.2','1654658877','<p align=\"center\" style=\"margin: 0cm 0cm 6.8pt; text-align: center; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（<span lang=\"EN-US\">2020</span>年<span lang=\"EN-US\">6</span>月<span lang=\"EN-US\">20</span>日第十三届全国人民代表大会常务委员会第十九次会议通过）</span></strong><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　<strong><span style=\"mso-bidi-font-weight: bold;\">目录</span></strong></span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一章　总则</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二章　政务处分的种类和适用</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三章　违法行为及其适用的政务处分</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四章　政务处分的程序</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五章　复审、复核</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六章　法律责任</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七章　附则</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　<strong><span style=\"mso-bidi-font-weight: bold;\">第一章　总则</span></strong></span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第一条　为了规范政务处分，加强对所有行使公权力的公职人员的监督，促进公职人员依法履职、秉公用权、廉洁从政从业、坚持道德操守，根据《中华人民共和国监察法》，制定本法。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二条　本法适用于监察机关对违法的公职人员给予政务处分的活动。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　本法第二章、第三章适用于公职人员任免机关、单位对违法的公职人员给予处分。处分的程序、申诉等适用其他法律、行政法规、国务院部门规章和国家有关规定。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　本法所称公职人员，是指《中华人民共和国监察法》第十五条规定的人员。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三条　监察机关应当按照管理权限，加强对公职人员的监督，依法给予违法的公职人员政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　公职人员任免机关、单位应当按照管理权限，加强对公职人员的教育、管理、监督，依法给予违法的公职人员处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关发现公职人员任免机关、单位应当给予处分而未给予，或者给予的处分违法、不当的，应当及时提出监察建议。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四条　给予公职人员政务处分，坚持党管干部原则，集体讨论决定；坚持法律面前一律平等，以事实为根据，以法律为准绳，给予的政务处分与违法行为的性质、情节、危害程度相当；坚持惩戒与教育相结合，宽严相济。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五条　给予公职人员政务处分，应当事实清楚、证据确凿、定性准确、处理恰当、程序合法、手续完备。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六条　公职人员依法履行职责受法律保护，非因法定事由、非经法定程序，不受政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　<strong><span style=\"mso-bidi-font-weight: bold;\">第二章　政务处分的种类和适用</span></strong></span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第七条　政务处分的种类为：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）警告；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）记过；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）记大过；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）降级；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）撤职；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）开除。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第八条　政务处分的期间为：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）警告，六个月；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）记过，十二个月；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）记大过，十八个月；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）降级、撤职，二十四个月。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　政务处分决定自作出之日起生效，政务处分期自政务处分决定生效之日起计算。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第九条　公职人员二人以上共同违法，根据各自在违法行为中所起的作用和应当承担的法律责任，分别给予政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十条　有关机关、单位、组织集体作出的决定违法或者实施违法行为的，对负有责任的领导人员和直接责任人员中的公职人员依法给予政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十一条　公职人员有下列情形之一的，可以从轻或者减轻给予政务处分：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）主动交代本人应当受到政务处分的违法行为的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）配合调查，如实说明本人违法事实的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）检举他人违纪违法行为，经查证属实的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）主动采取措施，有效避免、挽回损失或者消除不良影响的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）在共同违法行为中起次要或者辅助作用的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）主动上交或者退赔违法所得的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（七）法律、法规规定的其他从轻或者减轻情节。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十二条　公职人员违法行为情节轻微，且具有本法第十一条规定的情形之一的，可以对其进行谈话提醒、批评教育、责令检查或者予以诫勉，免予或者不予政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　公职人员因不明真相被裹挟或者被胁迫参与违法活动，经批评教育后确有悔改表现的，可以减轻、免予或者不予政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十三条　公职人员有下列情形之一的，应当从重给予政务处分：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）在政务处分期内再次故意违法，应当受到政务处分的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）阻止他人检举、提供证据的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）串供或者伪造、隐匿、毁灭证据的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）包庇同案人员的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）胁迫、唆使他人实施违法行为的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）拒不上交或者退赔违法所得的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（七）法律、法规规定的其他从重情节。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十四条　公职人员犯罪，有下列情形之一的，予以开除：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）因故意犯罪被判处管制、拘役或者有期徒刑以上刑罚（含宣告缓刑）的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）因过失犯罪被判处有期徒刑，刑期超过三年的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）因犯罪被单处或者并处剥夺政治权利的。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　因过失犯罪被判处管制、拘役或者三年以下有期徒刑的，一般应当予以开除；案件情况特殊，予以撤职更为适当的，可以不予开除，但是应当报请上一级机关批准。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　公职人员因犯罪被单处罚金，或者犯罪情节轻微，人民检察院依法作出不起诉决定或者人民法院依法免予刑事处罚的，予以撤职；造成不良影响的，予以开除。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十五条　公职人员有两个以上违法行为的，应当分别确定政务处分。应当给予两种以上政务处分的，执行其中最重的政务处分；应当给予撤职以下多个相同政务处分的，可以在一个政务处分期以上、多个政务处分期之和以下确定政务处分期，但是最长不得超过四十八个月。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十六条　对公职人员的同一违法行为，监察机关和公职人员任免机关、单位不得重复给予政务处分和处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十七条　公职人员有违法行为，有关机关依照规定给予组织处理的，监察机关可以同时给予政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十八条　担任领导职务的公职人员有违法行为，被罢免、撤销、免去或者辞去领导职务的，监察机关可以同时给予政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第十九条　公务员以及参照《中华人民共和国公务员法》管理的人员在政务处分期内，不得晋升职务、职级、衔级和级别；其中，被记过、记大过、降级、撤职的，不得晋升工资档次。被撤职的，按照规定降低职务、职级、衔级和级别，同时降低工资和待遇。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十条　法律、法规授权或者受国家机关依法委托管理公共事务的组织中从事公务的人员，以及公办的教育、科研、文化、医疗卫生、体育等单位中从事管理的人员，在政务处分期内，不得晋升职务、岗位和职员等级、职称；其中，被记过、记大过、降级、撤职的，不得晋升薪酬待遇等级。被撤职的，降低职务、岗位或者职员等级，同时降低薪酬待遇。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十一条　国有企业管理人员在政务处分期内，不得晋升职务、岗位等级和职称；其中，被记过、记大过、降级、撤职的，不得晋升薪酬待遇等级。被撤职的，降低职务或者岗位等级，同时降低薪酬待遇。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十二条　基层群众性自治组织中从事管理的人员有违法行为的，监察机关可以予以警告、记过、记大过。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　基层群众性自治组织中从事管理的人员受到政务处分的，应当由县级或者乡镇人民政府根据具体情况减发或者扣发补贴、奖金。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十三条　《中华人民共和国监察法》第十五条第六项规定的人员有违法行为的，监察机关可以予以警告、记过、记大过。情节严重的，由所在单位直接给予或者监察机关建议有关机关、单位给予降低薪酬待遇、调离岗位、解除人事关系或者劳动关系等处理。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　《中华人民共和国监察法》第十五条第二项规定的人员，未担任公务员、参照《中华人民共和国公务员法》管理的人员、事业单位工作人员或者国有企业人员职务的，对其违法行为依照前款规定处理。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十四条　公职人员被开除，或者依照本法第二十三条规定，受到解除人事关系或者劳动关系处理的，不得录用为公务员以及参照《中华人民共和国公务员法》管理的人员。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十五条　公职人员违法取得的财物和用于违法行为的本人财物，除依法应当由其他机关没收、追缴或者责令退赔的，由监察机关没收、追缴或者责令退赔；应当退还原所有人或者原持有人的，依法予以退还；属于国家财产或者不应当退还以及无法退还的，上缴国库。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　公职人员因违法行为获得的职务、职级、衔级、级别、岗位和职员等级、职称、待遇、资格、学历、学位、荣誉、奖励等其他利益，监察机关应当建议有关机关、单位、组织按规定予以纠正。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十六条　公职人员被开除的，自政务处分决定生效之日起，应当解除其与所在机关、单位的人事关系或者劳动关系。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　公职人员受到开除以外的政务处分，在政务处分期内有悔改表现，并且没有再发生应当给予政务处分的违法行为的，政务处分期满后自动解除，晋升职务、职级、衔级、级别、岗位和职员等级、职称、薪酬待遇不再受原政务处分影响。但是，解除降级、撤职的，不恢复原职务、职级、衔级、级别、岗位和职员等级、职称、薪酬待遇。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十七条　已经退休的公职人员退休前或者退休后有违法行为的，不再给予政务处分，但是可以对其立案调查；依法应当予以降级、撤职、开除的，应当按照规定相应调整其享受的待遇，对其违法取得的财物和用于违法行为的本人财物依照本法第二十五条的规定处理。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　已经离职或者死亡的公职人员在履职期间有违法行为的，依照前款规定处理。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　<strong><span style=\"mso-bidi-font-weight: bold;\">第三章　违法行为及其适用的政务处分</span></strong></span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十八条　有下列行为之一的，予以记过或者记大过；情节较重的，予以降级或者撤职；情节严重的，予以开除：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）散布有损宪法权威、中国共产党领导和国家声誉的言论的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）参加旨在反对宪法、中国共产党领导和国家的集会、游行、示威等活动的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）拒不执行或者变相不执行中国共产党和国家的路线方针政策、重大决策部署的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）参加非法组织、非法活动的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）挑拨、破坏民族关系，或者参加民族分裂活动的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）利用宗教活动破坏民族团结和社会稳定的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（七）在对外交往中损害国家荣誉和利益的。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　有前款第二项、第四项、第五项和第六项行为之一的，对策划者、组织者和骨干分子，予以开除。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　公开发表反对宪法确立的国家指导思想，反对中国共产党领导，反对社会主义制度，反对改革开放的文章、演说、宣言、声明等的，予以开除。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第二十九条　不按照规定请示、报告重大事项，情节较重的，予以警告、记过或者记大过；情节严重的，予以降级或者撤职。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　违反个人有关事项报告规定，隐瞒不报，情节较重的，予以警告、记过或者记大过。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　篡改、伪造本人档案资料的，予以记过或者记大过；情节严重的，予以降级或者撤职。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十条　有下列行为之一的，予以警告、记过或者记大过；情节严重的，予以降级或者撤职：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）违反民主集中制原则，个人或者少数人决定重大事项，或者拒不执行、擅自改变集体作出的重大决定的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）拒不执行或者变相不执行、拖延执行上级依法作出的决定、命令的。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十一条　违反规定出境或者办理因私出境证件的，予以记过或者记大过；情节严重的，予以降级或者撤职。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　违反规定取得外国国籍或者获取境外永久居留资格、长期居留许可的，予以撤职或者开除。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十二条　有下列行为之一的，予以警告、记过或者记大过；情节较重的，予以降级或者撤职；情节严重的，予以开除：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）在选拔任用、录用、聘用、考核、晋升、评选等干部人事工作中违反有关规定的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）弄虚作假，骗取职务、职级、衔级、级别、岗位和职员等级、职称、待遇、资格、学历、学位、荣誉、奖励或者其他利益的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）对依法行使批评、申诉、控告、检举等权利的行为进行压制或者打击报复的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）诬告陷害，意图使他人受到名誉损害或者责任追究等不良影响的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）以暴力、威胁、贿赂、欺骗等手段破坏选举的。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十三条　有下列行为之一的，予以警告、记过或者记大过；情节较重的，予以降级或者撤职；情节严重的，予以开除：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）贪污贿赂的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）利用职权或者职务上的影响为本人或者他人谋取私利的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）纵容、默许特定关系人利用本人职权或者职务上的影响谋取私利的。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　拒不按照规定纠正特定关系人违规任职、兼职或者从事经营活动，且不服从职务调整的，予以撤职。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十四条　收受可能影响公正行使公权力的礼品、礼金、有价证券等财物的，予以警告、记过或者记大过；情节较重的，予以降级或者撤职；情节严重的，予以开除。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　向公职人员及其特定关系人赠送可能影响公正行使公权力的礼品、礼金、有价证券等财物，或者接受、提供可能影响公正行使公权力的宴请、旅游、健身、娱乐等活动安排，情节较重的，予以警告、记过或者记大过；情节严重的，予以降级或者撤职。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十五条　有下列行为之一，情节较重的，予以警告、记过或者记大过；情节严重的，予以降级或者撤职：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）违反规定设定、发放薪酬或者津贴、补贴、奖金的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）违反规定，在公务接待、公务交通、会议活动、办公用房以及其他工作生活保障等方面超标准、超范围的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）违反规定公款消费的。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十六条　违反规定从事或者参与营利性活动，或者违反规定兼任职务、领取报酬的，予以警告、记过或者记大过；情节较重的，予以降级或者撤职；情节严重的，予以开除。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十七条　利用宗族或者黑恶势力等欺压群众，或者纵容、包庇黑恶势力活动的，予以撤职；情节严重的，予以开除。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十八条　有下列行为之一，情节较重的，予以警告、记过或者记大过；情节严重的，予以降级或者撤职：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）违反规定向管理服务对象收取、摊派财物的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）在管理服务活动中故意刁难、吃拿卡要的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）在管理服务活动中态度恶劣粗暴，造成不良后果或者影响的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）不按照规定公开工作信息，侵犯管理服务对象知情权，造成不良后果或者影响的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）其他侵犯管理服务对象利益的行为，造成不良后果或者影响的。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　有前款第一项、第二项和第五项行为，情节特别严重的，予以开除。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第三十九条　有下列行为之一，造成不良后果或者影响的，予以警告、记过或者记大过；情节较重的，予以降级或者撤职；情节严重的，予以开除：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）滥用职权，危害国家利益、社会公共利益或者侵害公民、法人、其他组织合法权益的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）不履行或者不正确履行职责，玩忽职守，贻误工作的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）工作中有形式主义、官僚主义行为的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）工作中有弄虚作假，误导、欺骗行为的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）泄露国家秘密、工作秘密，或者泄露因履行职责掌握的商业秘密、个人隐私的。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十条　有下列行为之一的，予以警告、记过或者记大过；情节较重的，予以降级或者撤职；情节严重的，予以开除：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）违背社会公序良俗，在公共场所有不当行为，造成不良影响的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）参与或者支持迷信活动，造成不良影响的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）参与赌博的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）拒不承担赡养、抚养、扶养义务的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）实施家庭暴力，虐待、遗弃家庭成员的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）其他严重违反家庭美德、社会公德的行为。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　吸食、注射毒品，组织赌博，组织、支持、参与卖淫、嫖娼、色情淫乱活动的，予以撤职或者开除。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十一条　公职人员有其他违法行为，影响公职人员形象，损害国家和人民利益的，可以根据情节轻重给予相应政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　<strong><span style=\"mso-bidi-font-weight: bold;\">第四章　政务处分的程序</span></strong></span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十二条　监察机关对涉嫌违法的公职人员进行调查，应当由二名以上工作人员进行。监察机关进行调查时，有权依法向有关单位和个人了解情况，收集、调取证据。有关单位和个人应当如实提供情况。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　严禁以威胁、引诱、欺骗及其他非法方式收集证据。以非法方式收集的证据不得作为给予政务处分的依据。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十三条　作出政务处分决定前，监察机关应当将调查认定的违法事实及拟给予政务处分的依据告知被调查人，听取被调查人的陈述和申辩，并对其陈述的事实、理由和证据进行核实，记录在案。被调查人提出的事实、理由和证据成立的，应予采纳。不得因被调查人的申辩而加重政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十四条　调查终结后，监察机关应当根据下列不同情况，分别作出处理：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）确有应受政务处分的违法行为的，根据情节轻重，按照政务处分决定权限，履行规定的审批手续后，作出政务处分决定；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）违法事实不能成立的，撤销案件；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）符合免予、不予政务处分条件的，作出免予、不予政务处分决定；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）被调查人涉嫌其他违法或者犯罪行为的，依法移送主管机关处理。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十五条　决定给予政务处分的，应当制作政务处分决定书。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　政务处分决定书应当载明下列事项：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）被处分人的姓名、工作单位和职务；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）违法事实和证据；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）政务处分的种类和依据；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）不服政务处分决定，申请复审、复核的途径和期限；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）作出政务处分决定的机关名称和日期。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　政务处分决定书应当盖有作出决定的监察机关的印章。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十六条　政务处分决定书应当及时送达被处分人和被处分人所在机关、单位，并在一定范围内宣布。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　作出政务处分决定后，监察机关应当根据被处分人的具体身份书面告知相关的机关、单位。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十七条　参与公职人员违法案件调查、处理的人员有下列情形之一的，应当自行回避，被调查人、检举人及其他有关人员也有权要求其回避：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）是被调查人或者检举人的近亲属的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）担任过本案的证人的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）本人或者其近亲属与调查的案件有利害关系的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）可能影响案件公正调查、处理的其他情形。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十八条　监察机关负责人的回避，由上级监察机关决定；其他参与违法案件调查、处理人员的回避，由监察机关负责人决定。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关或者上级监察机关发现参与违法案件调查、处理人员有应当回避情形的，可以直接决定该人员回避。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第四十九条　公职人员依法受到刑事责任追究的，监察机关应当根据司法机关的生效判决、裁定、决定及其认定的事实和情节，依照本法规定给予政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　公职人员依法受到行政处罚，应当给予政务处分的，监察机关可以根据行政处罚决定认定的事实和情节，经立案调查核实后，依照本法给予政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关根据本条第一款、第二款的规定作出政务处分后，司法机关、行政机关依法改变原生效判决、裁定、决定等，对原政务处分决定产生影响的，监察机关应当根据改变后的判决、裁定、决定等重新作出相应处理。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十条　监察机关对经各级人民代表大会、县级以上各级人民代表大会常务委员会选举或者决定任命的公职人员予以撤职、开除的，应当先依法罢免、撤销或者免去其职务，再依法作出政务处分决定。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关对经中国人民政治协商会议各级委员会全体会议或者其常务委员会选举或者决定任命的公职人员予以撤职、开除的，应当先依章程免去其职务，再依法作出政务处分决定。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关对各级人民代表大会代表、中国人民政治协商会议各级委员会委员给予政务处分的，应当向有关的人民代表大会常务委员会，乡、民族乡、镇的人民代表大会主席团或者中国人民政治协商会议委员会常务委员会通报。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十一条　下级监察机关根据上级监察机关的指定管辖决定进行调查的案件，调查终结后，对不属于本监察机关管辖范围内的监察对象，应当交有管理权限的监察机关依法作出政务处分决定。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十二条　公职人员涉嫌违法，已经被立案调查，不宜继续履行职责的，公职人员任免机关、单位可以决定暂停其履行职务。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　公职人员在被立案调查期间，未经监察机关同意，不得出境、辞去公职；被调查公职人员所在机关、单位及上级机关、单位不得对其交流、晋升、奖励、处分或者办理退休手续。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十三条　监察机关在调查中发现公职人员受到不实检举、控告或者诬告陷害，造成不良影响的，应当按照规定及时澄清事实，恢复名誉，消除不良影响。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十四条　公职人员受到政务处分的，应当将政务处分决定书存入其本人档案。对于受到降级以上政务处分的，应当由人事部门按照管理权限在作出政务处分决定后一个月内办理职务、工资及其他有关待遇等的变更手续；特殊情况下，经批准可以适当延长办理期限，但是最长不得超过六个月。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　<strong><span style=\"mso-bidi-font-weight: bold;\">第五章　复审、复核</span></strong></span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十五条　公职人员对监察机关作出的涉及本人的政务处分决定不服的，可以依法向作出决定的监察机关申请复审；公职人员对复审决定仍不服的，可以向上一级监察机关申请复核。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　监察机关发现本机关或者下级监察机关作出的政务处分决定确有错误的，应当及时予以纠正或者责令下级监察机关及时予以纠正。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十六条　复审、复核期间，不停止原政务处分决定的执行。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　公职人员不因提出复审、复核而被加重政务处分。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十七条　有下列情形之一的，复审、复核机关应当撤销原政务处分决定，重新作出决定或者责令原作出决定的监察机关重新作出决定：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）政务处分所依据的违法事实不清或者证据不足的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）违反法定程序，影响案件公正处理的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）超越职权或者滥用职权作出政务处分决定的。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十八条　有下列情形之一的，复审、复核机关应当变更原政务处分决定，或者责令原作出决定的监察机关予以变更：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）适用法律、法规确有错误的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）对违法行为的情节认定确有错误的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）政务处分不当的。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第五十九条　复审、复核机关认为政务处分决定认定事实清楚，适用法律正确的，应当予以维持。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十条　公职人员的政务处分决定被变更，需要调整该公职人员的职务、职级、衔级、级别、岗位和职员等级或者薪酬待遇等的，应当按照规定予以调整。政务处分决定被撤销的，应当恢复该公职人员的级别、薪酬待遇，按照原职务、职级、衔级、岗位和职员等级安排相应的职务、职级、衔级、岗位和职员等级，并在原政务处分决定公布范围内为其恢复名誉。没收、追缴财物错误的，应当依法予以返还、赔偿。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　公职人员因有本法第五十七条、第五十八条规定的情形被撤销政务处分或者减轻政务处分的，应当对其薪酬待遇受到的损失予以补偿。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　<strong><span style=\"mso-bidi-font-weight: bold;\">第六章　法律责任</span></strong></span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十一条　有关机关、单位无正当理由拒不采纳监察建议的，由其上级机关、主管部门责令改正，对该机关、单位给予通报批评，对负有责任的领导人员和直接责任人员依法给予处理。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十二条　有关机关、单位、组织或者人员有下列情形之一的，由其上级机关，主管部门，任免机关、单位或者监察机关责令改正，依法给予处理：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）拒不执行政务处分决定的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）拒不配合或者阻碍调查的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）对检举人、证人或者调查人员进行打击报复的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）诬告陷害公职人员的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）其他违反本法规定的情形。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十三条　监察机关及其工作人员有下列情形之一的，对负有责任的领导人员和直接责任人员依法给予处理：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（一）违反规定处置问题线索的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（二）窃取、泄露调查工作信息，或者泄露检举事项、检举受理情况以及检举人信息的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（三）对被调查人或者涉案人员逼供、诱供，或者侮辱、打骂、虐待、体罚或者变相体罚的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（四）收受被调查人或者涉案人员的财物以及其他利益的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（五）违反规定处置涉案财物的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（六）违反规定采取调查措施的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（七）利用职权或者职务上的影响干预调查工作、以案谋私的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（八）违反规定发生办案安全事故，或者发生安全事故后隐瞒不报、报告失实、处置不当的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（九）违反回避等程序规定，造成不良影响的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（十）不依法受理和处理公职人员复审、复核的；</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　（十一）其他滥用职权、玩忽职守、徇私舞弊的行为。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十四条　违反本法规定，构成犯罪的，依法追究刑事责任。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　<strong><span style=\"mso-bidi-font-weight: bold;\">第七章　附则</span></strong></span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十五条　国务院及其相关主管部门根据本法的原则和精神，结合事业单位、国有企业等的实际情况，对事业单位、国有企业等的违法的公职人员处分事宜作出具体规定。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十六条　中央军事委员会可以根据本法制定相关具体规定。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十七条　本法施行前，已结案的案件如果需要复审、复核，适用当时的规定。尚未结案的案件，如果行为发生时的规定不认为是违法的，适用当时的规定；如果行为发生时的规定认为是违法的，依照当时的规定处理，但是如果本法不认为是违法或者根据本法处理较轻的，适用本法。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>　　第六十八条　本法自<span lang=\"EN-US\">2020</span>年<span lang=\"EN-US\">7</span>月<span lang=\"EN-US\">1</span>日起施行。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p align=\"right\" style=\"margin: 0cm 0cm 6.8pt; text-align: right; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'background: white; color: rgb(85, 85, 85); font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>（转自中国人大网）</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2471','3295','1','','','近日，中共中央办公厅印发了《关于加强新时代廉洁文化建设的意见》（以下简称《意见》），并发出通知，要求各地区各部门结合实际认真贯彻落实。《意见》指出，党中央高度重视廉洁文化建设，强调反对腐败、建设廉洁政治，是我们党一贯坚持的鲜明政治立场，是党自我革命必','172.16.1.2','172.16.1.2','1654658930','<p class=\"MsoNormal\" style=\"text-indent: 21pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">近日，中共中央办公厅印发了《关于加强新时代廉洁文化建设的意见》（以下简称《意见》），并发出通知，要求各地区各部门结合实际认真贯彻落实。</span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 21pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">《意见》指出，党中央高度重视廉洁文化建设，强调反对腐败、建设廉洁政治，是我们党一贯坚持的鲜明政治立场，是党自我革命必须长期抓好的重大政治任务。全面从严治党，既要靠治标，猛药去疴，重典治乱；也要靠治本，正心修身，涵养文化，守住为政之本。必须站在勇于自我革命、保持党的先进性和纯洁性的高度，把加强廉洁文化建设作为一体推进不敢腐、不能腐、不想腐的基础性工程抓紧抓实抓好，为推进全面从严治党向纵深发展提供重要支撑。</span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 21pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">《意见》强调，加强新时代廉洁文化建设，要坚持以习近平新时代中国特色社会主义思想为指导，全面贯彻党的十九大和十九届历次全会精神，增强“四个意识”、坚定“四个自信”、做到“两个维护”，不忘初心、牢记使命，坚持思想建党和制度治党同向发力，坚持依法治国和以德治国相结合，以理想信念强基固本，以先进文化启智润心，以高尚道德砥砺品格，惩治震慑、制度约束、提高觉悟一体发力，推动廉洁文化建设实起来、强起来，不断实现干部清正、政府清廉、政治清明、社会清朗。</span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 21pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">《意见》指出，要夯实清正廉洁思想根基，强化理论武装，增强政治定力抵腐定力；坚定信仰信念信心，筑牢拒腐防变思想防线；发展积极健康党内政治文化，引领廉洁文化建设。要厚植廉洁奉公文化基础，用革命文化淬炼公而忘私、甘于奉献的高尚品格，用社会主义先进文化培育为政清廉、秉公用权的文化土壤，用中华优秀传统文化涵养克己奉公、清廉自守的精神境界。要培养廉洁自律道德操守，引导领导干部明大德、守公德、严私德，把廉洁要求贯穿日常教育管理监督之中，把家风建设作为领导干部作风建设重要内容。要发挥廉洁教育基础作用，强化形势教育、纪法意识、警示震慑、示范引领。要弘扬崇廉拒腐社会风尚，运用新媒体新技术传播廉洁文化，丰富廉洁文化优质产品和服务供给，拓展利用廉洁文化资源。</span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 21pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">《意见》要求，各地区各部门要担负起廉洁文化建设的政治责任，把廉洁文化建设纳入党风廉政建设和反腐败工作布局进行谋划，建立廉洁文化建设统筹协调机制，久久为功抓好落实，推动新时代廉洁文化建设深入开展。</span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 21pt; mso-char-indent-count: 2.0;\"><span lang=\"EN-US\">&nbsp;</span></p>\r\n<p align=\"right\" class=\"MsoNormal\" style=\"text-align: right; text-indent: 21pt; mso-char-indent-count: 2.0;\"><span style=\"font-family: 宋体; mso-ascii-font-family: Calibri; mso-hansi-font-family: Calibri;\">（转自中央纪委国家监委网站）</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2472','3296','1','','','国卫医函〔2021〕169号各省、自治区、直辖市及新疆生产建设兵团卫生健康委、中医药管理局：为进一步加强行业作风建设，提升医疗机构及其工作人员廉洁从业水平，国家卫生健康委、国家中医药局制定了《全国医疗机构及其工作人员廉洁从业行动计划（2021-2024年）》。现印发','172.16.1.2','172.16.1.2','1654659023','<p style=\"margin: 0cm 0cm 6.8pt; text-align: center; line-height: 18pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; font-size: 13.5pt; mso-bidi-font-family: \"Microsoft YaHei UI\";\'><span style=\'font-family: \"Microsoft YaHei UI\"; font-size: 15pt; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA;\'>国卫医函〔<span lang=\"EN-US\">2021</span>〕<span lang=\"EN-US\">169</span>号</span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 18pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; font-size: 13.5pt; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>各省、自治区、直辖市及新疆生产建设兵团卫生健康委、中医药管理局：</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 18pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; font-size: 13.5pt; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>为进一步加强行业作风建设，提升医疗机构及其工作人员廉洁从业水平，国家卫生健康委、国家中医药局制定了《全国医疗机构及其工作人员廉洁从业行动计划（<span lang=\"EN-US\">2021-2024</span>年）》。现印发给你们，请结合实际认真贯彻执行。</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 18pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; font-size: 13.5pt; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>&ensp;</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p align=\"right\" style=\"margin: 0cm 0cm 6.8pt; text-align: right; line-height: 18pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; font-size: 13.5pt; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>&ensp;国家卫生健康委&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;国家中医药局</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p align=\"right\" style=\"margin: 0cm 0cm 6.8pt; text-align: right; line-height: 18pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; font-size: 13.5pt; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>2021</span><span style=\'font-family: \"Microsoft YaHei UI\"; font-size: 13.5pt; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>年<span lang=\"EN-US\">8</span>月<span lang=\"EN-US\">6</span>日</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 18pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; font-size: 13.5pt; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>（信息公开形式：主动公开）</span><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p align=\"center\" style=\"margin: 0cm 0cm 6.8pt; text-align: center; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>&ensp;<span lang=\"EN-US\"></span></span></p>\r\n<p align=\"center\" style=\"margin: 0cm 0cm 6.8pt; text-align: center; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; font-size: 15pt; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>全国医疗机构及其工作人员廉洁从业行动计划</span></strong><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p align=\"center\" style=\"margin: 0cm 0cm 6.8pt; text-align: center; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; font-size: 15pt; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（<span lang=\"EN-US\">2021-2024</span>年）</span></strong><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; text-indent: 21pt; mso-line-height-alt: 1.35pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>&ensp;</span></strong><span lang=\"EN-US\" style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>医德医风事关行业形象，事关群众切身利益，事关医疗卫生事业发展。全面贯彻党中央、国务院关于党风廉政建设的有关要求，根据新形势下医疗机构及其工作人员行风问题总体情况，进一步加强行业作风建设、提升廉洁从业水平、创新行风监管治理举措，对于促进医疗卫生事业高质量发展、落实党史学习教育要求、让人民群众切实感受到医疗卫生事业发展成效、提高患者满意度等具有重要意义。为持续净化行业风气，维护风清气正的良好医疗环境，确保卫生健康工作“十四五”开好局、起好步，制定本行动计划。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>一、总体要求<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>以习近平新时代中国特色社会主义思想为指导，深入学习贯彻习近平总书记在十九届中央纪委五次全体会议上的重要讲话精神，认真落实党中央、国务院关于党风廉政工作的决策部署，强化底线思维和红线意识，围绕影响群众看病就医感受的突出行风问题，坚持“管行业必须管行风”、“谁主管谁负责”，坚持标本兼治、源头治理，坚持系统施策、综合监管，切实解决群众身边的“烦心事”，持续纠治医疗领域的不正之风，维护医疗行业公平正义，推动新时代行风建设工作不断取得新成效。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>二、工作目标&ensp;<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>自<span lang=\"EN-US\">2021</span>年至<span lang=\"EN-US\">2024</span>年，集中开展整治“红包”、回扣专项行动，加大监督检查、执纪执法力度，对违反行业纪律的医务人员，批评教育一批、通报处理一批、严肃清理一批，对涉嫌利益输送的各类机构，严肃惩处、移送线索、行业禁入，持续保持对“红包”、回扣行为的高压打击态势。建立健全医疗机构内行风建设工作体系，完善院内管理制度、提升行风管理软硬件水平，构建打击“红包”、回扣等行风问题的长效机制，巩固拓展作风建设成效，提升群众看病就医的获得感，为医疗卫生行业高质量发展提供切实保障。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>三、加强学习教育，提高思想认识<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（一）深入开展行业作风教育。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>地方各级卫生健康行政部门（含中医药主管部门，下同）认真组织广大干部职工和医务人员深入学习领会习近平总书记关于党风廉政建设的重要论述、对卫生健康领域的重要指示批示和“敬佑生命，救死扶伤，甘于奉献，大爱无疆”的新时期职业精神，大力弘扬伟大抗疫精神，弘扬“大医精诚”传统医德医风，有的放矢地进行职业道德教育，不断提高职业道德素质，强化新时期卫生健康工作宗旨观念。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（二）重视党对行风工作的引领作用。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>充分发挥党员干部的带头引领作用，以党风引导行风，以干部带领群众。充分运用好开展党史学习教育的有利契机，以党史教育指引行风建设。不断总结运用好党风廉政工作中的优秀经验，选树先进典型，宣传优秀事迹，引导广大医务人员见贤思齐，增强自觉抵制行业不正之风的思想定力。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（三）开展集中学习。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>医疗机构要根据此次行动计划要求，结合本地区本单位实际情况，确定学习教育的具体内容，综合运用主题教育和警示教育形式，注重“身边事教育身边人”。制定学习培训计划，每月至少安排一次专题学习，主要负责同志要带头开展行风教育工作，组织安排重点岗位工作人员进行重点教育，确保此次行风教育实现<span lang=\"EN-US\">100%</span>覆盖。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（四）营造浓厚氛围。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>各级地方卫生健康行政部门要综合运用各类应用程序、网络等多种媒介形式，推送教育内容，广泛开展学习活动。医疗机构要充分运用网站首页置顶、院内电子屏滚动播放、显著位置张贴海报、组织院内和科室内线上线下学习等形式深入开展学习教育工作，进一步强化廉洁文化建设，推进廉洁从业要求全面落实，形成风清气正的医疗机构内部文化氛围。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>四、完善管理体系，落实主体责任<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（五）进一步完善行风管理体系。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>进一步建立健全卫生健康行政部门牵头的纠正医药购销领域和医疗服务中不正之风工作机制，优化卫生健康行政部门内的行风工作协调管理机制。医疗机构要完善行风管理队伍，按照工作需要全面配齐本单位负责行风建设工作专（兼）职工作人员，选配人员应当具备相应的知识储备、行业背景和工作能力。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（六）优化医疗机构行风管理架构。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>各级各类医疗机构要坚决落实行风建设的主体责任，进一步明确医疗机构主要负责同志是本单位行风管理的第一责任人、是此次工作的第一承办人，建立由主要负责同志担任组长、分管负责同志担任副组长、办公室设在行风管理经办部门机构内的专项工作机制，办公室承担宏观指导、统筹协调职责，负责机构内行风管理政策制定、具体工作任务安排，院本级纪检监察、人事、财务、信息、质控、护理、药剂、设备、医保等相关部门要按职责分工，全程参与、切实履行行风管理职责。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（七）构建打击“红包”、回扣长效机制。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>各级地方卫生健康行政部门要持续完善对“红包”、回扣行为的投诉举报、调查处理、督导检查体系，要构建与有关部门的线索移交、核实处置、跟踪反馈机制。医疗机构要建立完善重点岗位、重点人员、重点医疗行为、重点药品耗材等关键节点的监测预警体系，形成具有可行性的“红包”、回扣主动上缴、线索反映、调查核实、处置上报等管理制度。利用多种形式开展随访，借鉴患者满意度测评的做法和经验，在门诊挂号结算或者办理出入院手续时开展即时行风满意度评价，搜集工作线索。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>五、优化内部管理，遏制“红包”行为<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（八）细化明确“红包”内容。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>各级地方卫生健康行政部门、医疗机构应当结合实际情况，对医务人员在从事诊疗活动过程中，患者及其亲友的礼品、礼金、消费卡和有价证券、股权、其他金融产品等财物，其安排、组织或者支付费用的宴请或者旅游、健身、娱乐等活动安排均应认定为“红包”，应全部纳入此次专项行动整治范围，并按照各地的具体情况，不断细化完善“红包”范畴、明确处罚红线。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（九）落实纠治“红包”责任。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>医疗机构要落实纠治收受“红包”行为的主体责任。加强本单位医务人员教育，建立健全奖惩机制，对查实的违规人员要坚决予以严肃处罚。对不知情或不可抗“红包”应当建立上缴登记制度。要健全医患双方不收不送“红包”告知制度，在医疗机构内显著位置公布医务人员收受“红包”的本单位和上级卫生健康行政部门举报途径。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（十）清除“红包”产生空间。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>对门诊等候、预约诊疗、床位安排、特殊检查、特殊治疗和择期手术的患者，应当通过网络、公众号、院内电子屏等途径向患者充分告知医院诊疗资源分布信息，提供提示服务。通过网络预约、扫码预约、线下预约等多种形式有效提高医疗资源利用率，增加医疗资源信息的公开透明程度，减少患者排队次数，缩短排队等待时间。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（十一）完善“红包”防控措施。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>建立完善社会监督员制度，采取明查暗访、聘请第三方机构评估、媒体监督等办法加大对本单位医务人员收受“红包”线索的发现力度。提升防范“红包”的硬件设施水平，在院内容易产生“红包”行为的重点场所应当加强监管措施，消除“红包”行为高发场合的监控死角。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>六、加大惩治力度，清除回扣乱象<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（十二）严防各类形式回扣。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>建立清正廉洁的新型医商关系，依法与利益相关企业交往，严禁收受医疗器械、药品、试剂等生产、经营企业或人员以各种名义、形式给予的回扣、提成，严禁参与或接受影响医疗行为公正性的宴请、礼品、旅游、学习、考察或其他休闲社交活动，不得参加以某医药产品的推荐、采购、供应或使用为交换条件的推广活动。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（十三）严守各项招采纪律。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>遵守国家采购政策，严格落实医疗卫生机构各项内控制度，严禁违反规定干预和插手药品、医疗器械采购和基本建设等工作。严格按照国家有关药品、耗材集中采购的政策规定在省级医药集中采购平台采购所需的药品和耗材，优先采购、使用集中带量采购中选产品，并对使用情况进行监测。严禁医疗卫生人员违反规定私自采购、销售、使用药品、医疗器械、医疗卫生材料。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（十四）严控药品耗材使用。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>医疗机构应当落实管理职责，提高管理能力，承担管理责任。规范医疗运行管理秩序，针对重点岗位、重点人员、重点药品耗材要细化管理措施，以电子病历为基础，对医疗重点环节开展监控，严格审批程序，分类甄别、及时预警，要充分运用同学科横向比较手段，对医务人员药品耗材使用情况排名靠前且无正当理由的，要根据行为性质，进行约谈、调岗、核减绩效或暂停执业。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（十五）严惩违规违法人员。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>完善医药代表院内拜访医务人员的管理制度，参照“定时定点定人”“有预约有流程有记录”（“三定”“三有”）的方式，拟定细化可执行的院内制度，对违规出现在诊疗场所且与诊疗活动无关的人员要及时驱离，对核实的输送回扣行为要及时上报，对查实收受回扣的医务人员要根据金额从严处罚，涉嫌犯罪的要移送司法机关。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>七、落实部门职责，营造良好环境<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（十六）强化组织领导。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>各级地方卫生健康行政部门要成立由主要负责同志牵头负责的临时性工作机构，专职落实此次专项行动有关要求，明确部门职责、拟定工作计划、实施督导检查并及时上报阶段性进展。要按照专项行动要求，细化、量化、硬化工作目标，分解到具体部门、具体岗位、具体人员。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（十七）发挥机制作用。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>依托纠正医药购销领域和医疗服务中不正之风机制开展联合整治，加强部门间联动，对于经查实存在给付回扣、捆绑销售药品耗材等商业贿赂违法行为的企业信息要及时通报机制成员单位，依托医药价格和招采信用评级制度，对涉案医药企业依据裁量基准采取相应措施。由市场监管、公安等部门对其可能涉及的其他违法经营活动开展调查，税务部门对相关部门移送的涉税违法线索开展核查检查。各级地方卫生健康部门还应充分发挥本级医改领导小组秘书处作用，强化“三医联动”，推动各部门对“红包”、回扣等行风问题一体研究、一体部署、一体推进，努力形成行风整治的强大合力。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（十八）加强宣传引领。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>加强廉洁从业行动计划和工作成效的宣传，注重运用多种媒介，多层次多角度宣传推广好工作成效，鼓励和引导广大群众积极参与、支持整治“红包”、回扣专项行动，营造良好舆论氛围，形成社会共治的良好局面。要注意舆情信息收集、归纳分析，通过社会评价检验工作成效，引导医疗机构落实好行动计划各项要求。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（十九）开展典型带动。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>地方各级卫生健康行政部门要发掘、树立先进典型作为样本医院，通过典型带动、先行先试、因院制宜，逐步推动辖区内各级各类医疗机构落实此次专项行动的各项要求，提升整体廉洁从业水平。国家卫生健康委将根据各地推荐，遴选出一批全国优秀样本&ensp;&ensp;&ensp;&ensp;医院并组织系列宣传报道，供各地交流学习。<span lang=\"EN-US\"></span></span></p>\r\n<p style=\"margin: 0cm 0cm 6.8pt; line-height: 30pt; text-indent: 21pt; mso-pagination: widow-orphan;\"><strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-bidi-font-weight: bold;\'>（二十）严肃处罚问责。</span></strong><span style=\'font-family: \"Microsoft YaHei UI\"; mso-bidi-font-family: \"Microsoft YaHei UI\";\'>地方各级卫生健康行政部门要加强对本辖区医疗卫生机构开展专项整治行动计划的监管，综合运用通报、约谈、警示、曝光等行政措施，加强督促检查，并将专项行动开展情况纳入医院巡查的重要内容，对“红包”、回扣问题查处情况要在辖区内进行通报。对专项行动工作不负责、不作为，分工责任不落实的，要严肃问责、追查到底。<span lang=\"EN-US\"></span></span></p>\r\n<span style=\'font-family: \"Microsoft YaHei UI\"; font-size: 12pt; mso-bidi-font-family: \"Microsoft YaHei UI\"; mso-font-kerning: 1.0pt; mso-ansi-language: EN-US; mso-fareast-language: ZH-CN; mso-bidi-language: AR-SA;\'>（转自国家卫健委官网）</span>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2473','3297','1','','/ewebeditor/uploadfile/image/2022/06/09/20220609143554279001.png','为进一步增强党员干部职工廉洁自律意识，根据市纪委监委、市卫健委及医院“510”廉政教育活动部署，5月27日，盐城市一院组织党员干部90余人赴东台参观廉政文化教育专线，接受廉政实境教育。在黄海森林公园党性教育基地，全体党员同志重温入党誓词，唱响《歌唱祖国》，切','172.16.1.2','172.16.1.2','1654756345','<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><span style=\"font-size: 15pt;\">为进一步增强党员干部职工廉洁自律意识，根据市纪委监委、市卫健委及医院</span><span style=\"font-size: 15pt;\">“</span><span style=\"font-size: 15pt;\">510</span><span style=\"font-size: 15pt;\">”</span><span style=\"font-size: 15pt;\">廉政教育活动部署，</span><span style=\"font-size: 15pt;\">5</span><span style=\"font-size: 15pt;\">月</span><span style=\"font-size: 15pt;\">27</span><span style=\"font-size: 15pt;\">日，盐城市一院组织党员干部</span><span style=\"font-size: 15pt;\">90</span><span style=\"font-size: 15pt;\">余人赴东台参观廉政文化教育专线，接受廉政实境教育。</span></span></p>\r\n<span style=\"font-size: 15pt;\">\r\n</span>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 15pt;\">在黄海森林公园党性教育基地，全体党员同志重温入党誓词，唱响《歌唱祖国》，切身感受生命激情，重温峥嵘岁月，提高党性修养。</span></p>\r\n<span style=\"font-size: 15pt;\">\r\n</span>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><span style=\"font-size: 15pt;\">在黄海森林公园党风廉政教育基地，通过图文和实物相结合的方式，聆听廉政故事，让大家现场感悟</span><span style=\"font-size: 15pt;\">“</span><span style=\"font-size: 15pt;\">艰苦奋斗、科学求真、守正创新、绿色发展</span><span style=\"font-size: 15pt;\">”</span><span style=\"font-size: 15pt;\">的黄海林工精神。</span></span></p>\r\n<span style=\"font-size: 15pt;\">\r\n</span>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 15pt;\">此次廉政文化实境教育，是一次深刻的学习、是一次心灵的洗礼、更是一次思想的熏陶。大家纷纷表示，在今后工作、学习、生活中，将不断加强自身党性锻炼，遵守党章党纪，廉洁行医，认真履职尽责服务好患者，做一名德技双馨的医务人员。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: center; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><img width=\"553\" height=\"312\" align=\"center\" src=\"/ewebeditor/uploadfile/image/2022/06/09/20220609143554279001.png\"></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: center; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 15pt;\"><br>\r\n</span></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2481','3305','1','','<!--#p8_attach#-->/cms/item/2022_06/10_10/78349c16eec95086.jpg.thumb.jpg','2021年，我们守正创新、锐意进取、合作共赢，2021年，我们在变与不变中探索医院高质量发展之路，那些难忘的瞬间凝聚成我们不断前行的动力，凡是过往皆为序章，所有将来皆为可盼......','172.16.1.2','172.16.1.2','1641004490','<span>2021年，我们守正创新、锐意进取、合作共赢，</span><span>2021年，我们在变与不变中探索医院高质量发展之路，那些难忘的瞬间凝聚成我们不断前行的动力，凡是过往皆为序章，所有将来皆为可盼......</span>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2482','3306','1','','/ewebeditor/uploadfile/image/2022/06/10/20220610105214855001.png','为进一步增强党员干部职工廉洁自律意识，根据市纪委监委、市卫健委及医院“510”廉政教育活动部署，5月27日，盐城市一院组织党员干部90余人赴东台参观廉政文化教育专线，接受廉政实境教育。在黄海森林公园党性教育基地，全体党员同志重温入党誓词，唱响《歌唱祖国》，切','172.16.1.2','172.16.1.2','1654012800','<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">为进一步增强党员干部职工廉洁自律意识，根据市纪委监委、市卫健委及医院“<span>510</span>”廉政教育活动部署，<span>5</span>月<span>27</span>日，盐城市一院组织党员干部<span>90</span>余人赴东台参观廉政文化教育专线，接受廉政实境教育。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">在黄海森林公园党性教育基地，全体党员同志重温入党誓词，唱响《歌唱祖国》，切身感受生命激情，重温峥嵘岁月，提高党性修养。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">在黄海森林公园党风廉政教育基地，通过图文和实物相结合的方式，聆听廉政故事，让大家现场感悟“艰苦奋斗、科学求真、守正创新、绿色发展”的黄海林工精神。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">此次廉政文化实境教育，是一次深刻的学习、是一次心灵的洗礼、更是一次思想的熏陶。大家纷纷表示，在今后工作、学习、生活中，将不断加强自身党性锻炼，遵守党章党纪，廉洁行医，认真履职尽责服务好患者，做一名德技双馨的医务人员。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><br>\r\n</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: center; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><img width=\"553\" height=\"311\" align=\"center\" src=\"/ewebeditor/uploadfile/image/2022/06/10/20220610105214855001.png\"></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">&nbsp;</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">&nbsp;</span></p>\r\n<p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2485','3309','1','','','盐城市第一人民医院为满足临床使用需要，更好地为患者提供医疗服务，经医院院研究同意，拟对医院《出生医学证明》档案整理服务项目进行招标，具体要求以招标文件为准。欢迎符合资格要求的供应商前来报名参与。一、招标内容《出生医学证明》档案整理服务项目，具体情况详','172.16.1.2','172.16.1.2','1604419200','<p><br /></p><p><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">盐城市第一人民医院为满足临床使用需要，更好地为患者提供医疗服务，经医院院研究同意，拟对医院《出生医学证明》档案整理服务项目进行招标，具体要求以招标文件为准。欢迎符合资格要求的供应商前来报名参与。<br />一、招标内容<br />《出生医学证明》档案整理服务项目，具体情况详见“项目需求”等招标人要求。<br />二、资质要求<br />报名单位必须具备《中华人民共和国政府采购法》第22条所规定的条件外，还须具备如下条件：<br />1、在国内市场监督管理部门注册，具有承担本次招标内容能力的独立的法人资格（以营业执照或市场监督管理部门备案材料为准）；<br />2、投标人至少四名项目成员具有档案资格上岗证（提供近半年社保证明，参与该项目人员需与投标文件提供人员保持一致）；<br />3、投标人具有综合档案馆或同类项目案例；<br />4、参加政府采购活动前三年内，没有重大违法记录；<br />5、本项目不接受联合体投标。<br />三、报名时间<br />2020年11月4日至2020年11月10日；<br />北京时间：上午08:30 — 11:30，下午14:30 — 18:00。<br />四、报名地点<br />盐城市青年西路8号805室（江苏仁禾中衡工程咨询房地产估价有限公司）。<br />五、报名需提交材料<br />1、企业营业执照(副本)的复印件；<br />2、投标人提供至少四名项目成员档案资格上岗证及近半年社保证明（参与该项目人员需与投标文件提供人员保持一致）；<br />3、投标人提供综合档案馆或同类项目案例资料，如合同复印件等；<br />4、法定代表人直接参与报名的，需提供法定代表人身份证原件，同时提供复印件；委托代理人参与报名的，需提供法定代表人授权委托书、委托代理人身份证原件，同时提供法定代表人以及委托代理人身份证复印件；<br />5、提供信用查询记录（登录信用中国：www.creditchina.gov.cn，查询信用记录并截图）；<br />6、封面注明投标公司名称、投标人电话、邮箱。<br />注：以上报名材料请加盖投标单位红章、按顺序装订成册（概不退还）。<br />六、招标文件的获取<br />在报名后的3个工作日内，将招标文件通过邮件发送给投标报名人，注意查收（不另行电话通知），报名单位根据其做好标书等待开标。<br />七、开标时间、地点：另行通知。<br />八、联系方式<br />联系科室：盐城市第一人民医院主院区（南区）门诊4楼招标采购中心<br />联系人：张老师<br />联系电话：0515-66696855<br />联系地址：盐城市人民南路66号<br />邮编：224000<br /><br />招标代理公司：江苏仁禾中衡工程咨询房地产估价有限公司<br />联系人：胡女士<br />联系电话：13815581495<br />联系地址：盐城市青年西路8号805室<br />邮编：224006<br /><br />盐城市第一人民医院<br />2020年11月4日<br /><br /></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2486','3310','1','','','一、盐城市第一人民医院广济医院UPS采购及安装项目已具备招标条件，诚邀潜在的投标人参加本项目的投标工作。二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。三、项目概况3.1项目名称：盐城市第一人民医院广济医院UPS采购及安装3.2建','172.16.1.2','172.16.1.2','1604419200','<p><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">一、盐城市第一人民医院广济医院UPS采购及安装项目已具备招标条件，诚邀潜在的投标人参加本项目的投标工作。<br />二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。<br />三、项目概况<br />3.1项目名称：盐城市第一人民医院广济医院UPS采购及安装<br />3.2建设地点：盐城市大庆中路52号。<br />3.3招标范围：盐城市第一人民医院广济医院UPS采购、安装及调试，具体内容详见招标人提供的设计图纸和工程量清单，招标人保留对上述招标范围进行适当调整的权利。<br />3.4工期要求：15日历天，具体开工时间以招标人书面通知为准。<br />3.5质量标准：符合国家“合格”标准，并通过相关部门验收合格。<br />四、投标申请人应当具备的主要资格条件：<br />4.1投标人具有建设行政主管部门核发的电子与智能化工程二级及以上专业承包资质，同时具备独立法人资格和有效的安全生产许证。<br />4.2项目负责人资格要求<br />项目负责人具有建筑工程或机电安装工程专业二级及以上建造师资格（投标时须提供建造师注册证书或临时执业证书）和持有安全生产考核合格证（B类）。<br />4.4财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<br />4.5信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。<br />4.6委托代理人为投标人本单位正式职工，从投标截止日当月向前连续6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2020年10月*日，则为2020年4-9月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。<br />五、投标保证金：<br />本项目投标保证金为人民币贰仟圆整，具体交纳方式详见招标文件约定。<br />六、本项目资格审查采用“资格后审”办法。<br />七、本项目评标办法采用“合理低价法”。<br />八、招标文件领取时间和地点<br />请符合要求的潜在投标人于2020年11月4日至2020年11月8日（每日上午8：30时至12：00时，下午14：30时至18：00时）携带单位介绍信原件（或授权委托书原件，注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号805室）报名并领取招标文件。 &nbsp;<br />招标人：盐城市第一人民医院<br />地址：盐城市毓龙西路166号<br />联系人：金仁甫<br />联系电话：66698027<br /><br />招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司<br />地址：盐城市青年西路8号<br />联系人：刘永杰、胡荣艳<br />联系电话：0515-69057406、13626222271<br />日期：2020年11月4日<br /><br /></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2487','3311','1','','','一、盐城市儿童医院配电房改造工程已具备招标条件，诚邀潜在的投标人参加本项目的投标工作。二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。三、项目概况3.1项目名称：盐城市儿童医院配电房改造工程3.2建设地点：盐城市毓龙西路6号','172.16.1.2','172.16.1.2','1604419200','<p style=\"text-indent: 2em;\"><br /></p><p><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">一、盐城市儿童医院配电房改造工程已具备招标条件，诚邀潜在的投标人参加本项目的投标工作。<br />二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。<br />三、项目概况<br />3.1项目名称：盐城市儿童医院配电房改造工程<br />3.2建设地点：盐城市毓龙西路6号。<br />3.3招标范围：盐城市儿童医院高压配电房搬迁改造工程施工，具体内容详见招标人提供的设计图纸和工程量清单，招标人保留对上述招标范围进行适当调整的权利。<br />3.4工期要求：15日历天，具体开工时间以招标人书面通知为准。<br />3.5质量标准：符合国家“合格”标准。<br />四、投标申请人应当具备的主要资格条件：<br />4.1投标人须是具有建设行政主管部门颁发的建筑工程施工总承包三级及以上施工资质的独立法人，同时具有有效的安全生产许可证。<br />4.2项目负责人资格要求<br />项目负责人具有建筑工程专业二级及以上建造师资格,并持有安全生产考核合格证（B类）。<br />4.3财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<br />4.4信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。<br />4.5委托代理人为投标人本单位正式职工，从投标截止日当月向前连续6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2020年10月*日，则为2020年4-9月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。<br />4.6本次招标工程招标人一律不接受联合体投标报名。<br />五、投标保证金：具体金额及交纳方式详见招标文件约定。<br />六、本项目资格审查采用“资格后审”办法。<br />七、本项目评标办法采用“合理低价法”。<br />八、招标文件领取时间和地点<br />请符合要求的潜在投标人于2020年11月4日至2020年11月8日（每日上午8：30时至12：00时，下午14：30时至18：00时）携带单位介绍信原件（或授权委托书原件，注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号805室）报名并领取招标文件。 &nbsp;<br />&nbsp;<br />招标人：盐城市第一人民医院<br />地址：盐城市毓龙西路6号<br />联系人：金仁甫<br />联系电话：0515-66698027<br />&nbsp;<br />招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司<br />地址：盐城市青年西路8号<br />联系人：刘永杰、胡荣艳<br />联系电话：0515-69057406、13626222271<br />日期：2020年11月4日</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2488','3312','1','','','一、招标条件本招标项目 盐城市儿童医院一期工程 已经相关部门批准建设，招标人为 盐城市第一人民医院，工程所需资金来源 国有资金，100% 。项目已具备招标条件，现对该项目的工程结算审核及零星工程招标代理服务 进行招标。二、项目概况1、项目名称：盐城市儿童医院一','172.16.1.2','172.16.1.2','1604073600','<p><br /></p><p><br /><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">一、招标条件<br />本招标项目 盐城市儿童医院一期工程 已经相关部门批准建设，招标人为 盐城市第一人民医院，工程所需资金来源 国有资金，100% 。项目已具备招标条件，现对该项目的工程结算审核及零星工程招标代理服务 进行招标。<br />二、项目概况<br />1、项目名称：盐城市儿童医院一期工程结算审核及零星工程招标代理服务。<br />2、服务地点：盐城市第一人民医院、盐城市儿童医院。<br />3、工程规模：本项目工程总造价约4000万元。<br />4、标段划分及招标内容<br />本次招标项目为一个标段，招标内容为盐城市儿童医院一期工程结算审核及零星工程招标代理服务。 内容如下：<br />4.1 工程结算审核服务：工程竣工结算的编制与审核；与工程造价业务有关的咨询服务等。<br />4.2招标代理服务的主要工作内容（包含但不限于）：向招标人提供工程策划和代理咨询建议；负责拟定发包方案，并报业主认可；发布招标公告（发出投标邀请书）；编制资格预审文件，组织接收投标人报名，审查潜在投标人资格，协助业主确定潜在投标人（如有资格预审项目）；编制招标文件(含编制招标工程量清单、标底、招标控制价)并对其合法性、准确性和严密性负责；组织现场踏勘和答疑；组织开标、评标；处理招标过程中出现的质疑、投诉等事项；草拟工程合同；编制招投标情况书面报告，招投标资料整理归档；及时、主动、有效、稳妥的跟踪解决招标后可能存在的各种问题，减少投诉。<br />三、投标人资格要求<br />1、投标人必须为具有工程造价咨询甲级资质的独立法人，近三年未因业务质量问题和违法违规行为受到相关部门处理处罚。<br />2、项目组拟派人员的要求：<br />2.1项目组拟派人员至少包括（但不限于）以下人员：<br />①项目负责人1名，必须是本单位在岗在职的注册造价工程师，年龄不超过55周岁，并具备工程建设类中级及以上职称。<br />②土建、安装专业的造价工程师各1名，必须是本单位在岗在职的注册造价工程师。<br />③除上述①、②项人员外，其他人员必须是投标人本单位在岗在职的员工，人员数量、专业根据项目服务需要配备。<br />④工程审计服务与招标代理服务人员互不兼职。<br />2.2 项目组组成人员驻场要求：<br />项目负责人必须常驻现场，须接受招标人的日常考核和值班安排。<br />3、投标人须保证委托代理人及投标项目负责人、投标项目组成员均为本单位的正式职工，并确保从2020 年1 月开始至投标截止之日当月至少连续6个月均已在本单位缴纳养老保险（例：如开标日期为2020年10月的某一天，则投标单位需保证以上人员2020年2月以后任意连续6月养老保险均在本单位缴纳）。投标时无需提供养老保险证明材料。<br />4、招标人不接受联合体投标。<br />5、招标人坚决谢绝挂靠单位参与本工程的投标。如在招投标过程中发现以他人名义投标的，没收其全部投标保证金，如中标的将取消其中标资格并按相关法律法规予以处罚。<br />6、财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<br />7、信誉要求：<br />7.1 投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。<br />7.2 投标人不得存在：近3年内有行贿犯罪行为且被记录，或者法定代表人有行贿犯罪记录且自记录之日起未超过5年的情形。<br />7.3 投标人被人民法院列为失信被执行人的信息在“信用中国”、“信用江苏”网站上被公布的，自载明的发布之日开始，直至撤销或者更正信息中载明的发布时间的期间，本项目的招标人拒绝其投标，资格审查不予通过，不得被确定为中标候选人、中标人。在一次招投标活动中，资格预审申请人、投标人或者中标候选人因正被列为失信被执行人，导致其资格预审不通过或者被取消中标候选人资格、中标人资格的，不因其之后失信信息被撤销或更正而改变已经作出的决定。 <br />7.4 投标人作为失信联合惩戒对象，被采取依法限制参与建设工程招投标惩戒措施的，被有关部门推送在“信用中国”、“信用江苏”、“信用盐城”相关网站公示且在有效期内的，本项目的招标人拒绝其投标，资格审查不予通过，不得被确定为中标候选人、中标人。在一次招投标活动中，资格预审申请人、投标人或者中标候选人因正被列为联合惩戒对象，导致其资格预审不通过或者被取消中标候选人资格、中标人资格的，不因其之后失信信息被撤销或更正而改变已经作出的决定。<br />7.5 投标人应保证符合上述信誉要求，如被证明违反，除按照招标文件处理，还将被视为不诚信行为，由相关部门按规定处理。<br />8、根据不相容岗位职责分离原则，“盐城市儿童医院一期招标代理服务单位”的服务单位不得参与本次投标。<br />四、评标办法<br />本项目资格审查方法采用“资格后审”合格制；本项目评标办法采用“综合评估法”。 具体详见招标文件。<br />五、招标文件的获取<br />1、请符合资格条件的投标人于2020年10月31日至2020年11月4日，上午8：30时至12：00时，下午14：30时至18：00时，携带投标申请人单位介绍信或授权委托书原件（注明联系人、联系方式及邮箱）及本人身份证原件购买并领取招标文件等材料,逾期领取的不予接收。<br />2、领取地点：江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）。<br />3、联系电话： 刘永杰，0515-69057406；手机：13626222271 。<br />六、本工程严禁挂靠、转包，一经核实挂靠、转包的，将被取消投标、中标资格，并按相关规定进行处罚，直至建议有关部门吊销资质证书。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招标人：盐城市第一人民医院&nbsp; &nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：2020年10月31日 &nbsp;<br /><br /></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2489','3313','1','','','1、盐城市第一人民医院主院区3#DSA手术室改造工程，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院主院区3#DSA手术室改造工程。2.2项目地点：盐城市第一人民医院主院区','172.16.1.2','172.16.1.2','1604073600','<p style=\"text-indent: 0em;\" dir=\"ltr\"><span style=\"font-size: 18px; font-family: 宋体, SimSun;\"><br /></span></p><p style=\"text-indent: 0em;\" dir=\"ltr\"><span style=\"font-size: 18px; font-family: 宋体, SimSun;\">1、盐城市第一人民医院主院区3#DSA手术室改造工程，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。<br />2、项目概况与招标内容<br />2.1项目名称：盐城市第一人民医院主院区3#DSA手术室改造工程。<br />2.2项目地点：盐城市第一人民医院主院区（人民南路66号）。<br />2.3招标内容：盐城市第一人民医院主院区3#DSA手术室改造工程,具体内容详见招标人提供的工程量清单。<br />2.4发包方式：包工包料。<br />3、投标人资格要求<br />3.1投标人须是同时具有建筑机电安装工程专业承包三级及以上资质和建筑智能化工程专业承包三级及以上资质（或建筑智能化工程设计与施工二级及以上资质）的独立法人，并具有有效的安全生产许可证。<br />3.2拟派的项目经理必须具有贰级及以注册建造师证书（专业为机电安装工程），并具有安全生产B类证，且从本工程投标文件递交截止之日起无在建工程。（提供项目经理建造师证书、安全生产考核合格证B类）。<br />3.3项目经理、委托代理人为投标人本单位正式职工，从投标截止日当月向前连续6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2020年10月*日，则为2020年4-9月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。<br />4、投标报名及招标文件的获取<br />请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话及邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投标报名，投标报名时间2020年10月31日8时至2020年11月4日18时。联系人：刘永杰，0515-69057406；手机：13626222271。<br />5、本项目采用竞争性谈判方式。<br />6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。<br />7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。<br />其余内容详见招标文件。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; 招标人：盐城市第一人民医院<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：2020年10月31日</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2490','3314','1','','','因医院实际工作需要，于2020年10月22日进行招标的项目，经评标小组评审，中标结果如下：一、盐城市第一人民医院北区6号楼2F西侧病房呼叫系统更换等项目中标单位：江苏中灿机电工程有限公司中标价：49500元二、盐城市第一人民医院北区发热门诊CT机房改造项目中标单位：宜','172.16.1.2','172.16.1.2','1603296000','<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">因医院实际工作需要，于2020年10月22日进行招标的项目，经评标小组评审，中标结果如下：<br />一、盐城市第一人民医院北区6号楼2F西侧病房呼叫系统更换等项目<br />中标单位：江苏中灿机电工程有限公司<br />中标价：49500元<br />二、盐城市第一人民医院北区发热门诊CT机房改造项目<br />中标单位：宜兴市日森防辐射设备有限公司<br />中标价：183000元<br />三、盐城市第一人民医院北区放疗楼等处中央空调管道维修更换等项目<br />中标单位：江苏诚锐暖通设备有限公司<br />中标价：44000元<br />四、盐城市第一人民医院发电机组维保项目<br />中标单位：泰州东康机电工程有限公司<br />中标价：124800元<br />为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监察室联系，电话0515-66696810。<br />&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; 盐城市第一人民医院 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2020年10月22日</span><br /><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2491','3315','1','','','各医疗设备生产企业、经营企业：盐城市儿童医院由盐城市第一人民医院代为建设，拟采购一批医疗设备（设备清单见附件1），项目资金已落实，将于近期开展市场调研，欢迎符合条件的医疗设备生产企业、经营企业前来参与。有意愿参与本次市场调研的医疗设备生产企业、经营企','172.16.1.2','172.16.1.2','1603296000','<p style=\"line-height: 16px;\"><br /></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">各医疗设备生产企业、经营企业：</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">盐城市儿童医院由盐城市第一人民医院代</span><span style=\"color:#333333; font-family:\">为</span><span style=\"color:#333333; font-family:\">建</span><span style=\"color:#333333; font-family:\">设</span><span style=\"color:#333333; font-family:\">，拟采购一批医疗设备（设备清单见附件</span><span style=\"color:#333333; font-family:\">1</span><span style=\"color:#333333; font-family:\">），项目资金已落实，将于近期开展市场调研，欢迎符合条件的医疗设备生产企业、经营企业前来参与。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">有意愿参与本次市场调研的医疗设备生产企业、经营企业请于20</span><span style=\"color:#333333; font-family:\">20</span><span style=\"color:#333333; font-family:\">年10月 </span><span style=\"color:#333333; font-family:\">31</span><span style=\"color:#333333; font-family:\">日前</span><span style=\"color:#333333; font-family:\">将相关材料发送至邮箱：</span><a href=\"mailto:ycyysbc@qq.com。\"><span style=\"color:#0000ff; font-family:\">ycyysbc@qq.com。</span></a></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">现场咨询会时间另行通知。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24.1pt; \"><span style=\"color:#333333; font-family:\">报名须知：</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">1.仅允许产品生产厂家或取得生产厂家授权期限不少于一年的区域级代理经销商参与本</span><span style=\"color:#333333; font-family:\">次</span><span style=\"color:#333333; font-family:\">设备采购的咨询、调研、招标等活动。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">2.具有独立承担民事责任的能力。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">3.具有履行合同所必须具备的设备和专业知识。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">4.具有良好的商业信誉。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">5.近3年内在经营活动中无严重不良记录。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">6.按要求填写《盐城市第一人民医院设备咨询报名表》（见附件2），并上传医疗器械注册证、医疗器械经营许可证、销售代表授权书、产品销售授权书原件扫描件。</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#333333; font-family:\">联系人：李老师</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">&nbsp;</span><span style=\"color:#333333; font-family:\">联系电话：0515-88508946</span></p><p style=\"margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; widows:0; \"><span style=\"font-family:\">&nbsp;</span></p><p style=\"line-height: 16px;\"><a style=\"font-size:12px; color:#0066cc;\" href=\"http://localhost/upload/files/2020/10/a3e0ecdacfd9708e.xls\" title=\"附件1.xls\">附件1.xls</a></p><p style=\"line-height: 16px;\"><a style=\"font-size:12px; color:#0066cc;\" href=\"http://localhost/upload/files/2020/10/27678a6cd7df89e9.docx\" title=\"附件2.docx\">附件2.docx</a></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2492','3316','1','','','因医院实际工作需要，于2020年10月19日进行招标的项目，经评标小组评审，中标结果如下：一、盐城市儿童医院传染科病房医用气体和呼叫系统安装工程中标单位：江苏航宇环境科技有限公司中标价：112930元二、盐城市儿童医院传染病和战备房改造项目中标单位：江苏省春晖高空','172.16.1.2','172.16.1.2','1603123200','<p style=\"text-align: left;\" dir=\"ltr\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">因医院实际工作需要，于2020年10月19日进行招标的项目，经评标小组评审，中标结果如下：<br />一、盐城市儿童医院传染科病房医用气体和呼叫系统安装工程<br />中标单位：江苏航宇环境科技有限公司<br />中标价：112930元<br />二、盐城市儿童医院传染病和战备房改造项目<br />中标单位：江苏省春晖高空建安防腐有限公司<br />中标价：183925元<br />三、盐城市第一人民医院北区污水处理站流量计更换等项目<br />中标单位：南京优格环保科技有限公司<br />中标价：26000元<br />为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监察室联系，电话0515-66696810。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 盐城市第一人民医院<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; 2020年10月20日<br /></span><br /><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2493','3317','1','','','因医院实际工作需要，于2020年10月15日进行招标的项目，经评标小组评审，中标结果如下：一、盐城市儿童医院手术室改造项目中标单位：联合体牵头人:淮安美华洁净技术有限公司 联合体成员：苏州理想建设工程有限公司中标价：198310元为体现公开、公平、公正的原则，特此公','172.16.1.2','172.16.1.2','1602691200','<p style=\";text-indent: 28px;padding: 0;line-height: 42px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 16px;\">因医院实际工作需要，于2020年10月15日进行招标的项目，经评标小组评审，中标结果如下：</span></p><p style=\"margin-top: 0;margin-right: 0;margin-bottom: 0;text-indent: 27px;padding: 0;line-height: 42px\"><span style=\"font-size: 16px;\"><strong><span style=\"color: rgb(51, 51, 51); font-size: 16px; font-family: 宋体;\">一</span></strong><strong><span style=\"color: rgb(51, 51, 51); font-size: 16px; font-family: 宋体;\">、盐城市儿童医院手术室改造项目</span></strong><strong><span style=\"color: rgb(51, 51, 51); font-size: 16px; font-family: 宋体;\"></span></strong></span></p><p style=\"margin-top: 0;margin-right: 0;margin-bottom: 0;text-indent: 27px;padding: 0;line-height: 42px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 16px;\">中标单位：联合体牵头人:淮安美华洁净技术有限公司 联合体成员：苏州理想建设工程有限公司</span></p><p style=\";text-indent: 28px;padding: 0;line-height: 42px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 16px;\">中标价：198310元</span></p><p style=\";text-indent: 28px;padding: 0;line-height: 42px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 16px;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监察室联系，电话0515-66696810。</span></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:28px;padding:0 0 0 0 ;text-align:right;line-height:42px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;盐城市第一人民医院</span></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:28px;padding:0 0 0 0 ;text-align:right;line-height:42px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2020年10月15日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2494','3318','1','','','1、盐城市第一人民医院主院区3#DSA手术室改造工程，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院主院区3#DSA手术室改造工程。2.2项目地点：盐城市第一人民医院主院区','172.16.1.2','172.16.1.2','1602691200','<p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">1、盐城市第一人民医院主院区3#DSA手术室改造工程，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">2、项目概况与招标内容</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">2.1项目名称：盐城市第一人民医院主院区3#DSA手术室改造工程。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">2.2项目地点：盐城市第一人民医院主院区（人民南路66号）。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">2.3招标内容：盐城市第一人民医院主院区3#DSA手术室改造工程,具体内容详见招标人提供的工程量清单。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">2.4发包方式：包工包料。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">3、投标人资格要求</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">3.1投标人须是同时具有建筑机电安装工程专业承包三级及以上资质和建筑智能化工程专业承包三级及以上资质（或建筑智能化工程设计与施工二级及以上资质）的独立法人，并具有有效的安全生产许可证；</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">3.2拟派的项目经理必须具有贰级及以注册建造师证书（专业为机电安装工程），并具有安全生产B类证，且从本工程投标文件递交截止之日起无在建工程。（提供项目经理建造师证书、安全生产考核合格证B类）。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">3.3项目</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">经理</span></span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">、委托代理人为投标人本单位正式职工，从投标截止日当月向前连续</span>6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为20</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">20</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">年</span></span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">10</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">月</span>*日，则为20</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">20</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">年</span></span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">4</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">-</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">9</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">4、投标报名及招标文件的获取</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路</span>8号8楼810室）投标报名并领取招标文件，投标报名时间2020年10月</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">15</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">日</span>8时至2020年10月</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">19</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">日</span>1</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">7</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">时。联系人：刘永杰，</span>0515-69057406；手机：13626222271。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">5、本项目采用竞争性谈判方式。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 27px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">其余内容详见招标文件。</span></span></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:56px;padding:0 0 0 0 ;text-align:right;line-height:33px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招标人：盐城市第一人民医院</span></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:56px;padding:0 0 0 0 ;text-align:right;line-height:28px\"><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">时间：</span>2020年10月</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\">15</span><span style=\"font-family: 宋体;color: rgb(51, 51, 51);letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">日</span></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2495','3319','1','','','一、盐城市儿童医院传染科病房医用气体和呼叫系统安装工程已具备招标条件，现邀请合格的潜在投标人参加本工程的投标工作。二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。三、项目概况3.1工程名称：盐城市儿童医院传染科病房医用气','172.16.1.2','172.16.1.2','1602691200','<p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">一、盐城市儿童医院传染科病房医用气体和呼叫系统安装工程已具备招标条件，现邀请合格的潜在投标人参加本工程的投标工作。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">三、项目概况</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">3.1工程名称：盐城市儿童医院传染科病房医用气体和呼叫系统安装工程。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">3.2工程地点：盐城市毓龙西路6号。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">3.3招标范围：盐城市儿童医院传染科病房医用气体和呼叫系统安装，具体以招标人提供的设计图纸、技术要求和工程量清单为准。招标人保留对招标范围调整的权利。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">3.4工期要求：25日历天，并满足盐城市儿童医院医疗要求。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">3.5质量标准：国家合格标准，并通过招标人及相关部门验收合格。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">四、投标申请人应当具备的主要资格条件：</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">4.1投标人资质条件：投标人须同时具有建设行政主管部门核发的建筑机电安装工程专业承包三级及以上资质和建筑智能化三级及以上资质，并取得安全生产许可证。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">投标人还须同时具有下列四种有效的证书：（1）《特种设备安装改造维修许可证》；（2）《医疗器械经营许可证》（或《医疗器械生产许可证》；（3）《医疗器械注册证（中心供氧、中心吸引系统）》。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">4.2项目负责人资格要求：具有机电安装专业二级及以上注册建造师资格，并具有安全生产考核证（B类）。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">4.3业绩要求：自2017年1月1日以来（以施工合同签订日期为准），投标人须承担过一项单项合同金额达10万及以上的医用气体安装等类似施工业绩（投标时须提供施工合同）。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">4.4财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">4.5信誉要求：投标申请人近三年内没有被司法部门或行业管理部门查处；或虽有违反法律法规行为、被依法取消投标资格但期限已满的；或因招标投标活动中有违法违规等不良行为的、被招标投标管理部门公示限制但限制期限已满的；没有处于被责令停业、财产被接管、冻结、破产等状态。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 14px;\">4.6项目负责人、委托代理人为投标人本单位正式职工，从投标截止日当月向前连续6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2020<span style=\"font-size: 16px; font-family: 宋体;\">年</span>10<span style=\"font-size: 16px; font-family: 宋体;\">月</span>*日，则为2020<span style=\"font-size: 16px; font-family: 宋体;\">年</span>4-9<span style=\"font-size: 16px; font-family: 宋体;\">月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">4.7本工程施工项目经理部主要管理人员包括项目负责人、技术负责人、施工员、安全员、质量员。投标时，仅需提供项目负责人相关资料，其他现场人员在中标签订施工合同备案时按苏建建管〔2017〕236号文要求配备到位。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 14px;\">4.8本次招标工程招标人接受联合体投标报名。</span></strong></span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">五、投标保证金：</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">本项目投标保证金为人民币3000元整，具体交纳方式详见招标文件约定。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">六、本项目资格审查采用资格后审办法。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">七、本工程评标办法采用“合理低价法”。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 14px;\"><span style=\"font-size: 16px; font-family: 宋体;\">八、请具备主要资格条件的潜在投标人于</span>2020年10<span style=\"font-size: 16px; font-family: 宋体;\">月</span>15<span style=\"font-size: 16px; font-family: 宋体;\">日至</span>2020年10<span style=\"font-size: 16px; font-family: 宋体;\">月</span>19<span style=\"font-size: 16px; font-family: 宋体;\">日（每日上午</span>8：30时至12：00时，下午14：30时至17:00时）携带单位介绍信（或授权委托书，注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号805室）报名并领取招标文件。&nbsp;&nbsp;</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">招标人：盐城市第一人民医院</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">地址：盐城市毓龙西路6号</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">联系人：金仁甫</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">联系电话：0515-66698027</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">&nbsp;</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">地址：盐城市青年西路8号</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">联系人：刘永杰、胡荣艳</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 16px;\">联系电话：0515-69057406、13626222271</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; letter-spacing: 0px; font-size: 14px;\"><span style=\"font-size: 16px; font-family: 宋体;\">日期：</span>2020年10<span style=\"font-size: 16px; font-family: 宋体;\">月</span>15<span style=\"font-size: 16px; font-family: 宋体;\">日</span></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2496','3320','1','','','一、盐城市儿童医院传染病和战备病房改造项目已具备招标条件，诚邀潜在的投标人参加本项目的投标工作。二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。三、项目概况3.1项目名称：盐城市儿童医院传染病和战备病房改造项目3.2建设地点','172.16.1.2','172.16.1.2','1602691200','<p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">一、盐城市儿童医院传染病和战备病房改造项目已具备招标条件，诚邀潜在的投标人参加本项目的投标工作。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">二、江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托具体负责本项目的招标事宜。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">三、项目概况</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">3.1项目名称：盐城市儿童医院传染病和战备病房改造项目</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">3.2建设地点：盐城市毓龙西路6号。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">3.3招标范围：盐城市儿童医院传染病和战备病房改造，具体内容详见招标人提供的设计图纸和工程量清单，招标人保留对上述招标范围进行适当调整的权利。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">3.4工期要求：30日历天，具体开工时间以招标人书面通知为准。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">3.5质量标准：符合国家“合格”标准，并通过相关部门验收合格。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">四、投标申请人应当具备的主要资格条件：</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">4.1投标人须是具有建设行政主管部门颁发的建筑装修装饰工程专业承包二级及以上（或在有效期内的建筑装饰装修工程设计与施工一体化二级及以上）的独立法人，并持有安全生产许可证。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">4.2项目负责人资格要求</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">项目负责人具有建筑工程专业二级及以上建造师资格,并持有安全生产考核合格证（B类）。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">4.3财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">4.4信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 14px;\">4.5<span style=\"font-size: 16px; font-family: 宋体;\">项目负责人和委托代理人为投标人本单位正式职工，从投标截止日当月向前连续</span>6个月均在投标人本单位缴纳养老保险（例：如投标截止日期为2020年10月*日，则为2020年4-9月养老保险均在投标人本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">4.6本次招标工程招标人不接受联合体投标报名。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">五、投标保证金：</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">本项目投标保证金为人民币陆仟圆整，具体交纳方式详见招标文件约定。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">六、本项目资格审查采用“资格后审”办法。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">七、本项目评标办法采用“合理低价法”。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">八、招标文件领取时间和地点</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 14px;\"><span style=\"font-size: 16px; font-family: 宋体;\">请符合要求的潜在投标人于</span>2020年10<span style=\"font-size: 16px; font-family: 宋体;\">月</span>15<span style=\"font-size: 16px; font-family: 宋体;\">日至</span>2020年10月19<span style=\"font-size: 16px; font-family: 宋体;\">日（每日上午</span>8：30时至12：00时，下午14：30时至17<span style=\"font-size: 16px; font-family: 宋体;\">：</span>00时）携带单位介绍信原件（或授权委托书原件，注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号805室）报名并领取招标文件。&nbsp;&nbsp;</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">招标人：盐城市第一人民医院</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">地址：盐城市毓龙西路6号</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">联系人：金仁甫</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">联系电话：0515-66698027</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">&nbsp;</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">地址：盐城市青年西路8号</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">联系人：刘永杰、胡荣艳</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 16px;\">联系电话：0515-69057406、13626222271</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 32px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); letter-spacing: 0px; font-size: 14px;\"><span style=\"font-size: 16px; font-family: 宋体;\">日期：</span>2020年10<span style=\"font-size: 16px; font-family: 宋体;\">月</span>15<span style=\"font-size: 16px; font-family: 宋体;\">日</span></span></p><p style=\"text-autospace:ideograph-numeric;line-height:32px\"><span style=\";font-family:Calibri;font-size:14px\">&nbsp;</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2497','3321','1','','','1、盐城市第一人民医院北区污水处理站流量计更换等项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院北区污水处理站流量计更换等项目。2.2项目地点：盐城市第一人民','172.16.1.2','172.16.1.2','1602691200','<p style=\"margin-top:0;margin-bottom:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:32px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\">1、盐城市第一人民医院</span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">北区污水处理站流量计更换等</span></span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></span></p><p style=\"margin-top:0;margin-bottom:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:32px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\">2、项目概况与招标内容</span></p><p style=\"margin-top:0;margin-bottom:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:32px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\">2.1项目名称：盐城市第一人民医院</span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">北区污水处理站流量计更换等</span></span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">项目。</span></span></p><p style=\"margin-top:0;margin-bottom:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:32px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\">2.2项目地点：盐城市第一人民医院北区</span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">（毓龙西路</span>166号）、五官科医院（南城河路12号）。</span></p><p style=\"margin-top:0;margin-bottom:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:32px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\">2.3招标内容：北区污水处理站管道流量计、污泥泵</span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">更换</span></span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">、五官科医院污水处理站曝气机</span></span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">维修、增加流量计</span></span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">等</span></span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">，</span></span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">具体内容详见招标人提供的工程量清单。</span></span></p><p style=\"margin-top:0;margin-bottom:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:32px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\">2.4发包方式：包工包料。</span></p><p style=\"margin-top:0;margin-bottom:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:32px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\">3、投标人资格要求</span></p><p style=\"margin-top: 0;margin-bottom: 0;margin-left: 0;text-indent: 32px;padding: 0;line-height: 32px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">投标人营业执照经营范围包含污水处理或机电设备安装等相关内容的独立法人资格。</span></span></p><p style=\"margin-top: 0;margin-bottom: 0;margin-left: 0;text-indent: 32px;padding: 0;line-height: 32px;background: rgb(255, 255, 255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\">4、投标报名及招标文件的获取</span></p><p style=\