INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2733','3557','1','','','为方便盐城市第一人民医院南、北两区人员流动，更好地为职工与病员提供便捷的交通服务，我院决定面向社会对院际班车运营进行招租，现将有关事项公告如下：一、公司资质及条件：投标租车公司应有运营资质，具有一定规模，实力相对雄厚（提供营业执照等复印件）。二、具体','172.16.1.2','172.16.1.2','1554048000','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为方便盐城市第一人民医院南、北两区人员流动，更好地为职工与病员提供便捷的交通服务，我院决定面向社会对院际班车运营进行招租，现将有关事项公告如下：&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">一、公司资质及条件：投标租车公司应有运营资质，具有一定规模，实力相对雄厚（提供营业执照等复印件）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">二、具体要求：具有自主产权的大型客车（座位50客以上，10立方行李箱）不少于10辆，其中使用年限不超过三年的不少于5辆，运行状况良好的品牌客车（提供车辆登记证、行驶证等证明，以证件为准）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">三、招租事项：租赁车辆为3辆（座位50客以上，10立方行李箱），用于医院南北两区人员、物资接送（毓龙西路166号?西环路高架?人民南路66号），具体运行安排为：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"></span></p><table cellspacing=\"0\" cellpadding=\"0\"><tbody><tr class=\"firstRow\"><td colspan=\"6\" valign=\"top\" style=\"border:solid black 1px;padding:0 7px 0 7px\"><p style=\"text-align:center;line-height:35px\"><span style=\"font-family: 宋体; color: rgb(89, 93, 96); font-size: 20px;\">医院北区发车时间</span></p></td></tr><tr style=\";height:74px\"><td valign=\"top\" style=\"border-right: 1px solid black; border-bottom: 1px solid black; border-left: 1px solid black; border-image: initial; border-top: none; padding: 0px 7px;\" height=\"74\"><p style=\"text-align:center;line-height:35px\"><span style=\"font-family: 宋体; color: rgb(89, 93, 96); font-size: 20px;\">07:20</span></p><p style=\"text-align:center;line-he