INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2745','3569','1','','','1、盐城市第一人民医院北区立体停车库维护保养项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2.项目概况与招标内容2.1项目名称：盐城市第一人民医院北区立体停车库维护保养项目。2.2项目地点：盐城市第一人民医院北区2','172.16.1.2','172.16.1.2','1548691200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、盐城市第一人民医院北区立体停车库维护保养项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.项目概况与招标内容</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.1项目名称：盐城市第一人民医院北区立体停车库维护保养项目。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.2项目地点：盐城市第一人民医院北区</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3招标内容：北区立体停车库维护保养项目，具体详见招标文件及工作量清单。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.&nbsp;投标人资格要求</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.1投标人具备独立法人资格，营业执照经营范围包含电梯或立体停车库安装和维护保养；并取得电梯或立体停车库等特种设备维护保养企业资质。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.2投标时须授权单位项目经理以上人员参加。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.&nbsp;投标报名及招标文件的获取</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2019年1月30日至2019年2月4日。联系人：胡荣艳，0515-68037830；手机：13815581495。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5. 本项目采用综合评估法。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;招标人：盐城市第一人民医院</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 时间：2019年1月29日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2746','3570','1','','http://localhost/upload/images/2019/10/d55cb4af6d17fce9.png','一、询价要求：参加报价的供应商（公司）需在江苏省行政审批中心或盐城市行政审批中心中介服务网上备案。本次招标范围为盐城市第一人民医院个人剂量监测（2019年度-2021年度），含每年4个季度的放射工作人员个人剂量监测，并于下个季度的第一个月底前出具上个季度的监测','172.16.1.2','172.16.1.2','1547654400','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、询价要求：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">参加报价的供应商（公司）需在江苏省行政审批中心或盐城市行政审批中心中介服务网上备案。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本次招标范围为盐城市第一人民医院个人剂量监测（2019年度-2021年度），含每</span></p><p></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">年4个季度的放射工作人员个人剂量监测，并于下个季度的第一个月底前出具上个季度</span></p><p></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">的监测报告。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、投标人资质条件：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.投标人是在中华人民共和国境内登记注册的具有独立法人资格的单位，营业执照须具有相关经营范围，能独立承担民事责任；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.具备职业卫生技术服务机构资质；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.具备计量认证CMA证书及附件；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、询价报价文件的组成及份数：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）营业执照正、副本复印件；（2）公司资质（卫生）（3）询价采购报价单；（4）询价报价文件正本一份，副本二份。</span></p><p></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、询价报价文件的递交：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、询价报价文件应密封，在密封袋上注明报价单位、所报项目名称及联系人姓名和联系号码，并于1月&nbsp;24&nbsp;日&nbsp;17&nbsp;时前送达或邮寄到</span><a href=\"http://www.dtry.cn/\" target=\"_blank\" title=\"东台市人民医院\" style=\"text-decoration: underline; font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院</span></a><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">预防保健处。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、采购单位于1月&nbsp;25日&nbsp;15时开标，按照符合采购需求、质量和服务相等且报价最低的原则确定成交供应商，并将询价结果进行公示。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、成交结果公示后，成交供应商须在5个工作日内与采购单位签定合同。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、付款方式：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">甲方按期收到全年纸质监测报告后，一个月内支付按实际监测人数应付合同款。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、联系方式：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系电话：18932281211；预防保健处：周公平</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系地址：盐城市人民南路166号</span></p><p></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">采购单位：盐城市第一人民医院</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年1月17日</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院个人剂量监测</span></p><p></p><p></p><p style=\"text-align: center;\"><img src=\"http://localhost/upload/images/2019/10/d55cb4af6d17fce9.png\" title=\"image.png\" alt=\"image.png\" /></p>','','','','','');
