INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2780','3604','1','','','一、投标要求：参加报价的供应商（公司）需在江苏省行政审批中心或盐城市行政审批中心中介服务网上备案。二、投标人资质条件：1.投标人是在中华人民共和国境内登记注册的具有独立法人资格的单位，营业执照须具有相关经营范围，能独立承担民事责任；2.具备职业卫生技术服','172.16.1.2','172.16.1.2','1540137600','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、投标要求：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">参加报价的供应商（公司）需在江苏省行政审批中心或盐城市行政审批中心中介服务网上备案。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、投标人资质条件：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.投标人是在中华人民共和国境内登记注册的具有独立法人资格的单位，营业执照须具有相关经营范围，能独立承担民事责任；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.具备职业卫生技术服务机构和国家环保部颁发的建设项目辐射环境影响评价资质；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.具备计量认证CMA证书及附件；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、投标文件的组成及份数：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）营业执照副本复印件；（2）公司资质（卫生及环保）（3）报价单。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">投标报价文件正本一份，副本二份。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、投标文件的递交：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、投标文件应密封，在密封袋上注明报价单位、所报项目名称及联系人姓名和联系号码，并于10 月&nbsp;30日 17&nbsp;时前送达或邮寄到盐城市第一人民医院预防保健处。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、采购单位定于10 月31日 15：30时开标。开标地点：盐城市第一人民医院（南院区）会议室；投标单位负责人（公司法人或授权委托代理人）需准时入场，如迟到按自动废标处理。采购单位按照符合采购需求、质量和服务相等且报价最低的原则确定成交供应商，现场公布开标结果。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、成交结果确定后，成交供应商须在3个工作日内与采购单位签定合同。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、付款方式：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">甲方在收到竣工验收报告后，两周内一次性付清合同款。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、报价：（格式自拟）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本次报价含1台X光机、125I粒子植入(分别在DSA、PET-CT工作场所内）项目环评备案、预评价、控制效果评价、防护及性能检测，辐射安全许可证及放射诊疗许可证变更等。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">七、联系方式：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系电话：13382609191&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;；预防保健处：严福余</span></p><p></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 招标单位：盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp;2018年10月22日</span></p><p><br /></p>','','','','','');
