INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2793','3617','1','','','盐城市第一人民医院零星被服采购项目(招标公告二次)1、盐城市第一人民医院零星被服采购项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2.项目概况与招标内容2.1项目名称：盐城市第一人民医院零星被服采购项目。2.2项目','172.16.1.2','172.16.1.2','1535904000','<p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"text-align: center; background-color: rgb(255, 255, 255); font-family: 宋体;\"><span style=\"font-family: 宋体, SimSun;\">盐城市第一人民医院零星被服采购项目&nbsp;</span><span style=\"font-family: 宋体, SimSun;\">(</span><span style=\"font-family: 宋体, SimSun;\">招标公告</span></span><span style=\"font-family: 宋体;\">二次</span><span style=\"font-family: 宋体;\">)</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、盐城市第一人民医院零星被服采购项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.项目概况与招标内容</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.1项目名称：盐城市第一人民医院零星被服采购项目。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.2项目地点：盐城市毓龙西路166号</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3招标内容：零星被服，具体内容详见招标人的要求和采购清单</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.投标人资格要求</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.1符合《中华人民共和国政府采购法》第二十二条规定，是中国境内注册的独立法人企业。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.2供应商营业执照经营范围须包含本次招标的主要内容。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.投标报名</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到盐城市第一人民医院（急诊六楼）招标采购中心投标报名并领取招标文件，投标报名时间2018年9&nbsp;&nbsp;月&nbsp;3日至2018年9月&nbsp;&nbsp;7&nbsp;&nbsp;日；联系人：王主任，联系方式：0515-88507786。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5.&nbsp;本项目采用竞争性谈判。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;招标人：盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">时间：2018年&nbsp;&nbsp;9月&nbsp;&nbsp;3&nbsp;&nbsp;日</span></p><p><br /></p>','','','','','');
