INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2873','3697','1','','','盐城市第一人民医院消防零星整改项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。1、工程概况 1.1项目名称：盐城市第一人民医院消防零星整改项目1.2项目地点：盐城市第一人民医院1.3招标内容：盐城市第一人民医院消防零星','172.16.1.2','172.16.1.2','1658825243','<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院消防零星整改项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">1</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、工程概况<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目名称：盐城市第一人民医院消防零星整改项目</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目地点：盐城市第一人民医院</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标内容：</span><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院消防零星整改项目施工</span><span style=\"font-family: 宋体; font-size: 12pt;\">。具体内容详见招标人提供的工程量清单。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">质量标准：符合国家“合格”标准。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">工期要求：<span>15</span>日历天，具体开工日期以招标人的通知。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标规模：工程造价约<span>8.93</span>万元。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.7</span><span style=\"font-family: 宋体; font-size: 12pt;\">发包方式：包工包料。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2 </span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">投标人应当具备的主要资格条件：</span></b></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 22pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人须具有有效的建筑装修装饰工程专业承包二级及以上施工资质的独立法人，并具有有效的安全生产许可证。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">、项目负责人资格类别和等级<span>:</span>项目负责人须提供建筑工程专业二级及以上建造师注册证书和安全生产考核合格证（<span>B</span>类）。项目负责人从本工程投标文件递交截止之日起必须满足下列条件：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">（<span>1</span>）项目负责人不得同时在两个或者两个以上单位受聘或者执业特指以下三种情形：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">① 同时在两个及以上单位签订劳动合同或缴纳社会保险；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">② 将本人执（职）业资格证书同时注册在两个及以上单位；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">③ 项目负责人担任其他公司法定代表人。 </span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">（<span>2</span>）项目负责人是非变更后无在建工程，或项目负责人是变更后无在建工程（必须原合同工期已满且变更备案之日已满<span>6</span>个月），或因非承包方原因致使工程项目停工或因故不能按期开工、且已办理了项目负责人解锁手续，或项目负责人有在建工程，但该在建工程与本次招标的工程属于同一工程项目、同一项目批文、同一施工地点分段发包或分期施工的情况且总的工程规模在项目负责人执业范围之内。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">项目负责人投标截止时不得同时在其他建设工程项目中担任现场管理人员、不在本项目兼任本项目的其它岗位。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">在建工程：处于中标结果公告（直接发包的项目以网上合同备案或归集为准）到合同约定的工程全部完成且竣工验收合格期间的工程。竣工验收证明是指由建设单位（或监理）组织工程建设各方验收合格，并签署相应的单位工程质量竣工验收记录或者分部工程质量验收记录等验收文件。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 26pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人须保证项目负责人及代理人均为本单位的正式职工，并确保从投标截止之日当月向前连续<span>6</span>个月均已在本单位缴纳养老保险（例：如开标日期为<span>2020</span>年<span>7</span>月的某一天，则投标单位需保证以上人员<span>2020</span>年<span>1-6</span>月养老保险均在本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.4.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span>&nbsp;</span></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.4.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">信誉要求：①投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.5 </span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标不接受联合体投标。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 21pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">3</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white;\">请符合上述资质条件的投标人携带单位介绍信到江苏双清工程造价咨询有限公司投标报名并领取招标文件，投标报名时间</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">7</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">26</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">时</span><span style=\"background: white; font-family: 宋体;\">30</span><span style=\"background: white;\">分至</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">1</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">18</span><span style=\"background: white;\">时。联系人：张旭良；手机：</span><span style=\"background: white; font-family: 宋体;\">0515-88200706,13515130964</span>，逾期不再受理<span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">4</span><span style=\"background: white;\">、</span><b>本项目采用“资格后审”，“综合评估法”</b><span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">6</span><span style=\"background: white;\">、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话</span><span style=\"background: white; font-family: 宋体;\">0515-66696810</span><span style=\"background: white;\">。</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">地<span>&nbsp; </span>址：人民南路<span>66</span>号</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2874','3698','1','','','盐城市第一人民医院变配电设备维修保养、试验检测等项目已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：一、盐城市招标代理有限公司受招标人委托具体负责本项目的招标事宜。二、符','172.16.1.2','172.16.1.2','1658825344','<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">盐城市第一人民医院变配电设备维修保养、试验检测等项目</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">一、</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">盐城市招标代理有限公司</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">受招标人委托具体负责本项目的招标事宜。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">二、符合本项目招标公告资格要求的投标的申请人均可参加该项目投标。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><b><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">三、项目概况：</span></b></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">1</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">、工程名称：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">盐城市第一人民医院变配电设备维修保养、试验检测等项目</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">2</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">、工程地点：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">盐城市第一人民医院主院区（人民南路<span>66</span>号）、北区（毓龙西路<span>166</span>号）、儿童医院（毓龙西路<span>6</span>号）、五官科医院（南城河路<span>12</span>号）</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">3</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">、招标规模：工程造价约<span>28.5</span>万元。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">4</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">、招标范围及内容：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">医院四个院区高低压变配电设备维修保养、试验检测等项目。具体内容详见招标人的要求和工程量清单</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">，招标人保留对上述工程规模进行适当调整的权利。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">5</span><span style=\"font-family: 宋体;\">、工期要求：</span><span style=\"font-family: 宋体;\">30</span><span style=\"font-family: 宋体;\">日历天，具体开工日期以招标人的通知</span><span style=\"font-family: 宋体;\">。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">6</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">、质量标准：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">符合国家及行业合格标准，确保医院变配电设备安全稳定运行。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><b><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">四、投标人资格要求：</span></b></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; vertical-align: baseline; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">1</span><span style=\"font-family: 宋体;\">、投标人具有建设行政主管部门核发的电力工程施工总承包或输（送）变电工程专业承包三级及以上资质的独立法人，并持有有效的安全生产许可证。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; vertical-align: baseline; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">2</span><span style=\"font-family: 宋体;\">、投标人具有国家电监会派出机构颁发的承装（试、修）类均为五级及以上的电力设施许可证。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">3</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">、本次招标不接受联合体投标。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">4</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">、财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span> </span></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">5</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">、信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">6</span><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">、投标申请人须保证项目负责人及代理人均为本单位的正式职工，并确保从投标截止之日当月向前连续<span>6</span>个月均已在本单位缴纳养老保险（例：如开标日期为<span>2020</span>年<span>7</span>月的某一天，则投标单位需保证以上人员<span>2020</span>年<span>1-6</span>月养老保险均在本单位缴纳），投标时无需提供缴费记录证明，中标候选人公示前、异议（投诉）提出相关单位需提供缴费记录给招标人核验。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><b><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">五、招标文件获取：</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">1</span><span style=\"font-family: 宋体;\">、获取时间：<span>2022</span>年<span>7</span>月<span>26</span>日至<span>2022</span>年<span>8</span>月<span>1</span>日，每日<span>8:30 </span>时至<span>18</span>：<span>00</span>时（北京时间<span>,</span>下同）。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">2</span><span style=\"font-family: 宋体;\">、获取方法：凡有意参加本次招标的投标人，须在获取招标文件截止时间前，携带单位介绍信（加盖公章、注明联系人、联系方式及邮箱）至盐城市招标代理有限公司行政服务部报名，领取招标文件。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">3</span><span style=\"font-family: 宋体;\">、招标文件获取联系人：张工，联系号码：<span>0515-89803566</span>、<span>13921809379</span>。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><b><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">六、投标保证金</span></b></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">本项目投标保证金为人民币<span>3000</span>元，具体详见招标文件。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">七、本项目资格审查办法采用<b>资格后审</b>；评标办法采用<b>综合评估法</b>，具体详见招标文件。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"color: black; font-family: 宋体; font-size: 10.5pt;\">八、本项目严禁挂靠，一经核实挂靠，将被取消投标、中标资格，并按相关规定进行处罚，直至建议有关部门吊销资质证书。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><b><span style=\"font-family: 宋体; font-size: 10.5pt;\">九、联系方式</span></b></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">招标人：盐城市第一人民医院</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">地<span>&nbsp; </span>址：盐城市人民南路<span>66</span>号</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">联系人：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">费工</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">电<span>&nbsp; </span>话：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">0515-83578933</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">招标代理机构：</span><span style=\"font-family: 宋体; font-size: 10.5pt;\">盐城市招标代理有限公司<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">地址：盐城市青年路钱江财富广场西区<span>A</span>座<span>4</span>楼<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">联系人：吴工<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 12pt; -ms-layout-grid-mode: char; -ms-text-autospace:; -ms-text-justify: inter-ideograph; punctuation-wrap: simple;\'><span style=\"font-family: 宋体; font-size: 10.5pt;\">电话：<span>0515-89803566&nbsp; 15950298580&nbsp;&nbsp;\r\n</span></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2875','3699','1','','','盐城市第一人民医院主院区净化维保服务项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。1、工程概况 1.1项目名称：盐城市第一人民医院主院区净化维保服务项目。1.2服务地点：盐城市第一人民医院主院区（人民南路66号）。','172.16.1.2','172.16.1.2','1658825375','<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院主院区净化维保服务项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">1</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、工程概况<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目名称：盐城市第一人民医院主院区净化维保服务项目。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">服务地点：盐城市第一人民医院主院区（人民南路<span>66</span>号）。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标内容：盐城市第一人民医院主院区净化维保服务项目</span><span style=\"font-family: 宋体; font-size: 12pt;\">，包括门诊手术室、急诊手术室、中心手术室、<span>DSA</span>、静脉配置中心、产房、<span>EICU</span>、<span>ICU</span>、<span>CCU</span>、<span>NICU</span>、中心供应室、<span>PCR</span>实验室、中心实验室、发热门诊等及其相关区域，主要包含但不限于：净化空调系统（风系统、水系统、净化机组、排风机组等）、强电系统（净化空调强电系统、照明、插座、配电柜、隔离变压器等）、弱电系统（<span>DDC</span>控制器、上位机、各类传感器、保护装置、执行器、数模转换器以及显示操作面板等）、监控系统（监控主机、电源、摄像头、显示器、线路等）、呼叫系统（电源、主机、喇叭、分机、麦克风等）、建筑装饰（顶板、墙面、地板、防撞带、踢脚线、检修口等）、各类门（自动门、手推移门、手拉门、门禁等）；医用气体系统（设备带、气体吊塔等）以及洁净室内的保温、保冷库、器械柜、药品柜、写字板、传递窗、观察窗、洗手盆、洗手池、盐水挂构、水龙头等与净化工程相关范围内的一切工作的日常巡检、维修、维护保养等工作，具体内容详见招标人要求。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">合同期限：一年，具体进场日期以招标人通知为准，进场时按照招标人要求进行设备验收、移交。第一年维保合同期满后，经院方年度考核平均得分≥<span>90</span>分可续签下一年维保合同，否则甲方有权终止合同或重新组织招标，中标人须无条件服从。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">质量标准：符合国家及行业合格标准，确保招标范围内的净化空调正常运行，并满足《医院洁净手术部建筑技术规范》<span>GB50333-2013</span>、《医院空气净化管理规范》<span>WS/T368-2012</span>等规范、标准及甲方要求</span><span style=\"font-family: 宋体; font-size: 12pt;\">，</span><span style=\"font-family: 宋体; font-size: 12pt;\">满足净化空调相关规范及实际工作需要。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">中标人维保范围内设备在维保期内发生的所有故障由中标人负责修复，因此产生的相关费用（包含但不限于人工费、材料费等所有费用）均由中标人承担。上述费用由投标人考虑到投标报价中，招标人不另支付上述费用。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标规模：工程造价约<span> 29 </span>万元。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2 </span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">投标人应当具备的主要资格条件：</span></b></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人具备建设行政主管部门核发的机电工程施工总承包三级及以上或建筑机电安装工程专业承包三级及以上资质，同时具有电子与智能化工程专业承包二级及以上资质，且有安全生产许可证的独立法人企业。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目负责人的资格要求：具有机电工程专业二级及以上注册建造师资格，同时具有有效的安全生产考核合格证（<span>B</span>类）。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目组其他技术人员要求：暖通工程师、特种作业操作证（低压）电工、智能建筑弱电工程师人员各一人，须<span>24</span>小时驻点值班，年龄要求在法定退休年龄内，身体健康、能吃苦耐劳、体态良好。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">业绩要求：投标人须承担过医院净化空调系统（或设备）工程项目（未发生过安全事故）运行维护保养服务项目业绩。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标申请人须保证项目负责人、代理人及项目组其他技术人员均为本单位的正式职工，并确保从投标截止之日当月向前连续<span>6</span>个月均已在本单位缴纳养老保险（例：如开标日期为<span>2018</span>年<span>7</span>月的某一天，则投标人需保证以上人员<span>2018</span>年<span>1-6</span>月养老保险均在本单位缴纳）；投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求<span><br>\r\n&nbsp;&nbsp;&nbsp; 2.6.1</span>财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span>&nbsp;<br>\r\n&nbsp;&nbsp;&nbsp; 2.6.2</span>信誉要求：①投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.7 </span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标接受联合体投标。</span><span style=\"font-family: 宋体; font-size: 12pt;\">如投标人不同时具备上述施工资质的，可组成联合体参与本项目投标，组成联合体投标的，须满足以下要求：</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.7.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">联合体最多只能由<span>2</span>家施工企业组成；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.7.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">联合体成员应共同签署联合体投标协议，并且明确各方的权利与义务，提供联合体协议书原件并装订在投标文件内；\r\n</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.7.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">联合体各方必须指定施工企业为联合体牵头人 ，由牵头人的法定代表人或其授权委托人为联合体授权代表执行联合体有关的决议，以联合体名义处理与本项目相关的事务；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.7.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">联合体投标的，联合体牵头人报名即可，投标文件只需联合体牵头人按照招标文件要求签字盖章（联合体协议书除外）；</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.7.5</span><span style=\"font-family: 宋体; font-size: 12pt;\">参加联合体的各成员单位对本项目不得再以自己名义单独投标，也不得同时参加两个或两个以上联合体的投标。如果违反，与此有关的联合体的投标将全部被拒绝。\r\n</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.7.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">联合体中标后，其成员不得变更。 </span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 21pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">3</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">请有意参与投标的申请人携带单位介绍信或授权委托书（自行注明联系方式和邮箱）于<b><span style=\"color: blue;\">2022</span><span style=\"color: blue;\">年<span>7</span>月<span>26</span>日<span>-2022</span>年<span>8</span>月<span>1</span>日<span>18</span>：<span>00</span>时</span></b>前到江苏双清工程造价咨询有限公司财务部（联系电话：<span>0515-88200706</span>、<span>18705110288</span>（微信同步）；地址：盐城市紫薇广场<span>C</span>区<span>7</span>楼）购买招标文件，售后不退，逾期不再受理。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">4</span><span style=\"background: white;\">、</span><b>本项目采用“资格后审”，“综合评估法”</b><span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">6</span><span style=\"background: white;\">、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话</span><span style=\"background: white; font-family: 宋体;\">0515-66696810</span><span style=\"background: white;\">。</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0cm; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0cm; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">地<span>&nbsp; </span>址：人民南路<span>66</span>号</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0cm; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">联系人：沈剑</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0cm; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">电话：<span>18662081713</span></span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0cm; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">招标代理机构：江苏双清工程造价咨询有限公司\r\n</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">地址：盐城市紫薇广场<span>C</span>区<span>7</span>楼<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">联系人：张旭良<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">电话：<span>0515-88200706</span>，<span>13515130964&nbsp;&nbsp; </span></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2876','3700','1','','','盐城市第一人民医院主院区生活水泵、空调水泵维保项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。1、项目概况 1.1项目名称：盐城市第一人民医院主院区生活水泵、空调水泵维保项目。1.2服务地点：盐城市第一人民医院主院','172.16.1.2','172.16.1.2','1658825413','<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院主院区生活水泵、空调水泵维保项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">1</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、项目概况<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目名称：盐城市第一人民医院主院区生活水泵、空调水泵维保项目。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">服务地点：盐城市第一人民医院主院区（人民南路<span>66</span>号）。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标内容：盐城市第一人民医院主院区生活水泵、空调水泵维保项目</span><span style=\"font-family: 宋体; font-size: 12pt;\">，具体内容详见招标人要求和工程量清单。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">质量标准：符合国家及行业合格标准，满足相关规范及实际工作需要。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">中标人维保范围内设备在质保期内发生的所有故障由中标人负责修复，因此产生的相关费用（包含但不限于人工费、材料费等所有费用）均由中标人承担。上述费用由投标人考虑到投标报价中，无须单独列项，招标人不另支付上述费用。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.5 </span><span style=\"font-family: 宋体; font-size: 12pt;\">合同期限：二年，具体进场日期以招标人通知为准，进场时按照招标人要求进行设备验收、移交。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标规模：工程造价约<span> 12 </span>万元。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2 </span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">投标人应当具备的主要资格条件：</span></b></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人具备建设行政主管部门核发的机电工程施工总承包或建筑机电安装工程专业承包三级（含）以上施工资质的独立法人，并具有有效的安全生产许可证。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span>&nbsp;</span></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">信誉要求：①投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.3 </span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标不接受联合体投标。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 21pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">3</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'>请有意参与投标的申请人携带单位介绍信或授权委托书（自行注明联系方式和邮箱）于<b><span style=\"font-family: 宋体;\">2022</span></b><b>年</b><b><span style=\"font-family: 宋体;\">7</span></b><b>月</b><b><span style=\"font-family: 宋体;\">26</span></b><b>日</b><b><span style=\"font-family: 宋体;\">-2022</span></b><b>年</b><b><span style=\"font-family: 宋体;\">8</span></b><b>月</b><b><span style=\"font-family: 宋体;\">1</span></b><b>日</b><b><span style=\"font-family: 宋体;\">18</span></b><b>：</b><b><span style=\"font-family: 宋体;\">00</span></b><b>时</b>前到江苏双清工程造价咨询有限公司财务部（联系电话：<span style=\"font-family: 宋体;\">0515-88200706</span>、<span style=\"font-family: 宋体;\">18705110288</span>（微信同步）；地址：盐城市紫薇广场<span style=\"font-family: 宋体;\">C</span>区<span style=\"font-family: 宋体;\">7</span>楼）购买招标文件，售后不退，逾期不再受理。</p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">4</span><span style=\"background: white;\">、</span><b>本项目采用“资格后审”，“综合评估法”</b><span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">6</span><span style=\"background: white;\">、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话</span><span style=\"background: white; font-family: 宋体;\">0515-66696810</span><span style=\"background: white;\">。</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">地<span>&nbsp; </span>址：人民南路<span>66</span>号</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">联系人：沈剑</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">电<span>&nbsp; </span>话：<span>18662081713</span></span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">招标代理机构：江苏双清工程造价咨询有限公司\r\n</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">地址：盐城市紫薇广场<span>C</span>区<span>7</span>楼 <span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">联系人：张旭良</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">电话：<span>0515-88200706</span>，<span>13515130964&nbsp;&nbsp; </span></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2877','3701','1','','','盐城市第一人民医院2022年6月零星维修项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。1、工程概况 1.1项目名称：盐城市第一人民医院2022年6月零星维修项目。1.2项目地点：盐城市第一人民医院主院区（人民南路66号）、北区','172.16.1.2','172.16.1.2','1658825444','<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院<span>2022</span>年<span>6</span>月零星维修项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">1</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、工程概况<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目名称：盐城市第一人民医院<span>2022</span>年<span>6</span>月零星维修项目。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目地点：盐城市第一人民医院主院区（人民南路<span>66</span>号）、北区（毓龙西路<span>166</span>号）。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标内容：</span><span style=\"font-family: 宋体; font-size: 12pt;\">盐城市第一人民医院主院区产科等病房反碱处理</span><span style=\"font-family: 宋体; font-size: 12pt;\">、主院区负二层护理会议室通道墙面铺设纤维板、主院区供应室蒸汽锅管屋顶道井防水处理、主院区感染一铝合金门维修、主院区血液科实验室搬迁改造、主院区骨科一防火门更换；北区老年四科<span>15-16</span>床更换卫生间台盆；五官科医院手术室男女淋浴间漏水维修等项目。具体内容详见招标人提供的工程量清单。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">质量标准：符合国家“合格”标准，质保期<span>1</span>年，防水工程质保期<span>5</span>年。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.5 </span><span style=\"font-family: 宋体; font-size: 12pt;\">工期要求：<span>15</span>日历天，具体开工日期以招标人的通知。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.6</span><span style=\"font-family: 宋体; font-size: 12pt;\">招标规模：工程造价约<span>6.8</span>万元。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">1.7</span><span style=\"font-family: 宋体; font-size: 12pt;\">发包方式：包工包料。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2 </span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">投标人应当具备的主要资格条件：</span></b></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人须具有有效的建筑装修装饰工程专业承包二级及以上施工资质的独立法人，并具有有效的安全生产许可证。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">2.2</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">截止<span>2022</span>年<span>4</span>月在盐城市第一人民医院存在已中标工程项目，且投标人未按合同工期完成并通过招标人验收的，本次不得报名参与本单位招标项目。</span></b></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">&nbsp;&nbsp; 2.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求<span><br>\r\n&nbsp;&nbsp; 2.3.1</span>财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span>&nbsp;<br>\r\n&nbsp;&nbsp; 2.3.2</span>信誉要求：①投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 24pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">2.4 </span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标不接受联合体投标。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 21pt; text-indent: 24.1pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><b><span style=\"font-family: 宋体; font-size: 12pt;\">3</span></b><b><span style=\"font-family: 宋体; font-size: 12pt;\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white;\">请符合上述资质条件的投标人携带单位介绍信到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">号</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">楼</span><span style=\"background: white; font-family: 宋体;\">805</span><span style=\"background: white;\">室）投标报名并领取招标文件，投标报名时间</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">7</span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">26</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">8</span><span style=\"background: white;\">时</span><span style=\"background: white; font-family: 宋体;\">30</span><span style=\"background: white;\">分至</span><span style=\"background: white; font-family: 宋体;\">2022</span><span style=\"background: white;\">年</span><span style=\"background: white; font-family: 宋体;\">\r\n<span>8</span></span><span style=\"background: white;\">月</span><span style=\"background: white; font-family: 宋体;\">1</span><span style=\"background: white;\">日</span><span style=\"background: white; font-family: 宋体;\">18</span><span style=\"background: white;\">时。联系人：刘工，</span><span style=\"background: white; font-family: 宋体;\">0515-69057406</span><span style=\"background: white;\">；手机：</span><span style=\"background: white; font-family: 宋体;\">13626222271</span><span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">4</span><span style=\"background: white;\">、</span><b>本项目采用“资格后审”，“综合评估法”</b><span style=\"background: white;\">。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">5</span><span style=\"background: white;\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\'background: white; margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt;\'><span style=\"background: white; font-family: 宋体;\">6</span><span style=\"background: white;\">、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话</span><span style=\"background: white; font-family: 宋体;\">0515-66696810</span><span style=\"background: white;\">。</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">招标人：盐城市第一人民医院</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">地<span>&nbsp; </span>址：人民南路<span>66</span>号</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">联系人：沈工</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">电<span>&nbsp; </span>话：</span><span style=\"font-family: 宋体;\">0515-66696857</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司\r\n</span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">地址：盐城市青年西路<span>8</span>号<span>805</span>室<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\'margin: 0cm -6pt 0pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; font-family: \"嫤疓B2312\",\"serif\"; font-size: 12pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">联系人：刘工<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></p>\r\n<p style=\'margin: 0cm 0cm 0pt; text-align: justify; line-height: 25pt; text-indent: 24pt; font-family: \"Times New Roman\",\"serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体; font-size: 12pt;\">电话：<span>0515-69057406&nbsp;&nbsp;\r\n</span></span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2878','3702','1','','','盐城市第一人民医院北区门诊服务台采购安装项目已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：一、盐城市招标代理有限公司受招标人委托具体负责本项目的招标事宜。二、符合本项目','172.16.1.2','172.16.1.2','1658995030','<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">盐城市第一人民医院北区门诊服务台采购安装项目已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">一、盐城市招标代理有限公司受招标人委托具体负责本项目的招标事宜。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">二、符合本项目招标公告资格要求的投标的申请人均可参加该项目投标。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: black; font-family: 宋体;\">三、项目概况</span></b><span style=\"color: black; font-family: 宋体;\">：</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">1</span><span style=\"color: black; font-family: 宋体;\">、工程名称：盐城市第一人民医院北区门诊服务台采购安装项目。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">2</span><span style=\"color: black; font-family: 宋体;\">、工程地点：盐城市毓龙西路<span>166</span>号。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">3</span><span style=\"color: black; font-family: 宋体;\">、招标规模：本项目造价约<span>5</span>万元。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">4</span><span style=\"color: black; font-family: 宋体;\">、招标范围及内容：</span><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">盐城市第一人民医院北区门诊服务台的采购、供应、运输、安装及就位、售前、售后等相关伴随服务，具体名称、数量、规格及技术参数等以招标文件、工程量清单及其它技术资料为准，招标人保留对上述招标范围进行适当调整的权利。<span>&nbsp;&nbsp;</span></span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">5</span><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">、供货期要求：确保在合同签<span style=\"background: white;\">订后<span>15</span>日历天内完成本项目的生产、制作、供应、</span>运输、安装并确保使用。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">6</span><span style=\"color: black; font-family: 宋体;\">、质量标准：</span><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">符合招标文件中技术需求、国家现行的相关规范标准及有关部门最新颁布的相应的最新标准。<span>&nbsp;</span></span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">7</span><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">、质保期：产品实行“三包”，质保期<span>2</span>年，自最终验收合格、交付使用之日起计算。在家具保修期内，一旦发生质量问题，投标人保证在接到通知<span>24</span>小时内响应，<span>2</span>天内赶到现场进行修理、更换或退货，费用由投标人负责。如投标人在接到通知后<span>24</span>小时内没有答复，则视为投标人承认质量问题并承担由此发生的一切费用。保修期间家具的一切质量问题，更换部件及家具本身质量原因造成的直接经济损失应全部由投标人负责。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: black; font-family: 宋体;\">四、投标人资格要求</span></b><span style=\"color: black; font-family: 宋体;\">：</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; vertical-align: baseline; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">1</span><span style=\"color: black; font-family: 宋体;\">、投标人资格：投标人必须是在中国境内注册的家具生产厂家或代理商。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; vertical-align: baseline; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">2</span><span style=\"color: black; font-family: 宋体;\">、本次招标不接受联合体投标。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; vertical-align: baseline; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">3</span><span style=\"color: black; font-family: 宋体;\">、财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; vertical-align: baseline; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">4</span><span style=\"color: black; font-family: 宋体;\">、信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: black; font-family: 宋体;\">5</span><span style=\"color: black; font-family: 宋体;\">、投标申请人须保证委托代理人为本单位的正式职工，并确保从投标截止之日当月向前连续<span>6</span>个月均已在本单位缴纳养老保险（例：如开标日期为<span>2020</span>年<span>7</span>月的某一天，则投标单位需保证以上人员<span>2020</span>年<span>1-6</span>月养老保险均在本单位缴纳），投标时无需提供缴费记录证明，中标候选人公示前、异议（投诉）提出相关单位需提供缴费记录给招标人核验。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: black; font-family: 宋体;\">五、招标文件获取：</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">1</span><span style=\"font-family: 宋体;\">、获取时间：<span>2022</span>年<span>7</span>月<span>28</span>日至<span>2022</span>年<span>8</span>月<span>2</span>日，每日<span>8:30 </span>时至<span>18</span>：<span>00</span>时（北京时间<span>,</span>下同）。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">2</span><span style=\"font-family: 宋体;\">、获取方法：凡有意参加本次招标的投标人，须在获取招标文件截止时间前，携带单位介绍信（加盖公章、注明联系人、联系方式及邮箱）至盐城市招标代理有限公司行政服务部报名，领取招标文件。</span></p>\r\n<p align=\"left\" style=\'margin: 0cm 0cm 0pt; text-align: left; line-height: 25pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-layout-grid-mode: char; -ms-text-justify: inter-ideograph;\'><span style=\"font-family: 宋体;\">3</span><span style=\"font-family: 宋体;\">、招标文件获取联系人：张工，联系号码：<span>0515-89803566</span>、<span>13921809379</span>。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">六、投标保证金</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">本项目投标保证金为人民币<span>800</span>元，具体详见招标文件。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">七、本项目资格审查办法采用<b>资格后审</b>；评标办法采用<b>综合评估法</b>，具体详见招标文件。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">八、本项目严禁挂靠，一经核实挂靠，将被取消投标、中标资格，并按相关规定进行处罚，直至建议有关部门吊销资质证书。</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21.1pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><b><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">九、联系方式</span></b></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">招标人：盐城市第一人民医院</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">地址：盐城市人民南路<span>66</span>号</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">联系人：</span><span style=\"font-family: 宋体;\">郭工</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">电话：</span><span style=\"font-family: 宋体;\">0515-83578933</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">招标代理机构：盐城市招标代理有限公司</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all; -ms-text-justify: inter-ideograph;\'><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">地址：盐城市青年路钱江财富广场西区</span><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">A</span><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">座</span><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">4</span><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">楼</span><span style=\"color: rgb(68, 68, 68); font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p align=\"left\" style=\'background: white; margin: 0cm 0cm 0pt; text-align: left; line-height: 23pt; text-indent: 21pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-word-break: break-all;