INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2880','3704','1','','/ewebeditor/uploadfile/image/2022/08/02/20220802163027922_s.jpg','在“八一”建军节来临之际，盐城市第一人民医院党委书记查文章率队前往盐城消防支队特勤中队开展走访慰问活动，向在酷暑高温天气坚守岗位的消防战员们送去慰问品以及节日的问候和祝福。座谈会上市一院党委书记查文章代表盐城市一院向盐城消防支队特勤中队全体人员致以崇','172.16.1.2','172.16.1.2','1659342590','<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">在“八一”建军节来临之际，盐城市第一人民医院党委书记查文章率队前往盐城消防支队特勤中队开展走访慰问活动，向在酷暑高温天气坚守岗位的消防战员们送去慰问品以及节日的问候和祝福。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">座谈会上市一院党委书记查文章代表盐城市一院向盐城消防支队特勤中队全体人员致以崇高的敬意。盐城消防支队特勤中队始终以忠诚为魂、以执着为念，特别是在防火灭火、抢险救灾、应急处突及为群众解难纾困等工作中不辱使命、扎实工作，无私奉献，充分发扬了政治坚定，顽强拼搏的工作精神。盐城市第一人民医院将认真学习消防救援官兵对党忠诚、奉献担当、竭诚为民的精神，以更加饱满的热情投入到工作中去，为盐阜人民健康作出新贡献。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<a href=\"/ewebeditor/uploadfile/image/2022/08/02/20220802163027922.jpg\" target=\"_blank\"><img src=\"/ewebeditor/uploadfile/image/2022/08/02/20220802163027922_s.jpg\" border=\"0\"></a><br>\r\n</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-