INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2971','3796','1','','<!--#p8_attach#-->/cms/item/2022_10/12_10/b13c60a1cda24457.jpg.thumb.jpg','日常生活中，我们难免会有吃不完的剩饭菜。对于这些剩饭菜，再吃的时候，一般都会经过加热后食用。但是你知道吗，有些食物不适合二次加热了吃，吃不完最好扔掉！否则可能给身体埋下很多隐患！究竟哪些食物不能二次加热呢？我们又应该如何处理剩饭菜呢？一起来看看吧。1','172.16.1.2','172.16.1.2','1665543554','<p style=\"background: white; text-align: justify; margin-bottom: 18pt; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">日常生活中，我们难免会有吃不完的剩饭菜。对于这些剩饭菜，再吃的时候，一般都会经过加热后食用。</span><span lang=\"EN-US\"></span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"background: white; text-align: justify; margin-bottom: 18pt; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">但是你知道吗，有些食物不适合二次加热了吃，吃不完最好扔掉！否则可能给身体埋下很多隐患！究竟哪些食物不能二次加热呢？我们又应该如何处理剩饭菜呢？一起来看看吧。</span><span lang=\"EN-US\" style=\"color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p align=\"center\" style=\"text-align: center; line-height: 16.8pt; mso-pagination: widow-orphan;\"><strong><span lang=\"EN-US\" style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">1</span></strong><span lang=\"EN-US\" style=\"color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<div align=\"center\" class=\"MsoNormal\" style=\"text-align: center; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">\r\n</span><hr width=\"100%\" size=\"2\" align=\"center\" style=\"color: rgb(62, 62, 62);\" noshade=\"\">\r\n<span style=\"font-size: 12pt;\">\r\n</span></span></div>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p class=\"MsoNormal\" style=\"background: white; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1; mso-font-kerning: 0pt; mso-bidi-language: AR;\">这些食物最好不要二次加热</span></strong><span lang=\"EN-US\" style=\"color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">一、菌类食物</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">提到菌类，第一个想到的就是蘑菇，蘑菇经过高温烹饪之后会产生大量的硝酸盐，放在冰箱中低温保存，第二天加热后继续吃的话，进入人体内的亚硝酸盐含量会增加。吃二次加热后的蘑菇可能出现腹痛、腹泻等症状，长期这样吃会增加罹患消化系统肿瘤的概率，所以不建议蘑菇二次加热后继续吃，</span><strong><span style=\"font-size: 12pt;\">尽量一次性吃完，吃不完扔掉。</span></strong></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">二、海鲜食物</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">虾、蟹、花甲等海鲜类食物不适宜二次加热，没吃完的海鲜如果放置太久，容易</span><strong><span style=\"font-size: 12pt;\">滋生细菌，产生毒素，</span></strong><span style=\"font-size: 12pt;\">增加肝的解毒负担，最好现做现吃。</span></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">三、菠菜</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">菠菜草酸含量高，需要焯水之后食用，再加上菠菜中还含有大量的硝酸盐，如果二次加热的话，菠菜会因为再次受热转化为亚硝酸盐，从而对人体有害。</span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">四、牛奶</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">牛奶</span><strong><span style=\"font-size: 12pt;\">不能反复加热。</span></strong><span style=\"font-size: 12pt;\">时间长了牛奶就会变质，容易造成腹泻。另外，反复加热牛奶，会使牛奶中的蛋白质变性，导致营养的缺失。所以，正常的牛奶煮透了就给孩子喝，不要反复加热。</span></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">五、银耳汤</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">因银耳含有较多的硝酸盐类，煮熟的银耳汤，如果放置时间过久，硝酸盐会分解还原成亚硝酸盐。亚硝酸盐会使血液里的血红蛋白丧失携带氧气的能力，从而破坏人体正常造血功能，影响健康。所以银耳汤最好</span><strong><span style=\"font-size: 12pt;\">现做现喝，不要隔夜。</span></strong></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">六、溏心鸡蛋</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">溏心鸡蛋主要是指没有煮熟的鸡蛋，这样煮出来的鸡蛋无法彻底杀灭鸡蛋中的沙门氏菌。如果再放置一晚，增加了细菌繁殖和产生毒素的机会。</span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">平时</span><strong><span style=\"font-size: 12pt;\">偶尔少量吃溏心鸡蛋，</span></strong><span style=\"font-size: 12pt;\">可能不会给人体健康带来明显的危害。但是如果过量食用或者经常食用溏心鸡蛋，可能会导致胃肠炎，出现</span><strong><span style=\"font-size: 12pt;\">腹痛、腹泻、恶心</span></strong><span style=\"font-size: 12pt;\">等症状，严重时还可能会导致食物中毒。</span></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">鸡蛋最好还是吃</span><strong><span style=\"font-size: 12pt;\">全熟</span></strong><span style=\"font-size: 12pt;\">的，这样才能将里面的细菌全部消灭。</span></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p align=\"center\" style=\"text-align: center; line-height: 16.8pt; mso-pagination: widow-orphan;\"><strong><span lang=\"EN-US\" style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">2</span></strong><span lang=\"EN-US\" style=\"color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<div align=\"center\" class=\"MsoNormal\" style=\"text-align: center; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">\r\n</span><hr width=\"100%\" size=\"2\" align=\"center\" style=\"color: rgb(62, 62, 62);\" noshade=\"\">\r\n<span style=\"font-size: 12pt;\">\r\n</span></span></div>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p align=\"left\" class=\"MsoNormal\" style=\"text-align: left; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1; mso-font-kerning: 0pt; mso-bidi-language: AR;\">如何正确对待家里的剩饭菜？</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">一、对待剩饭菜，处理态度要“严谨”</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">熟食在室温下不得存放</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">2</span><span style=\"font-size: 12pt;\">小时以上，</span></span></strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">应该</span><strong><span style=\"font-size: 12pt;\">及时冷藏</span></strong><span style=\"font-size: 12pt;\">（最好在</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">5</span><span style=\"font-size: 12pt;\">℃以下），</span><strong><span style=\"font-size: 12pt;\">不要在冰箱当中存放超过</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">3</span><span style=\"font-size: 12pt;\">天，</span></strong><span style=\"font-size: 12pt;\">剩饭菜加热的次数不应该超过</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">1</span><span style=\"font-size: 12pt;\">次。</span></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">二、剩食物要“聪明”，最好“只剩肉不剩菜”</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">相比肉类食品，蔬菜在存放的过程中会产生更多的</span><strong><span style=\"font-size: 12pt;\">亚硝酸盐</span></strong><span style=\"font-size: 12pt;\">，而且还会有较大一部分营养流失（维生素</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">C</span><span style=\"font-size: 12pt;\">等）。综合来看，建议大家“实在要剩，</span><strong><span style=\"font-size: 12pt;\">尽量剩肉不剩菜”。</span></strong></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">另外，</span><strong><span style=\"font-size: 12pt;\">不建议剩凉菜。</span></strong><span style=\"font-size: 12pt;\">凉菜一般不经过加热，更容易滋生细菌等有害物质，从而诱发</span><strong><span style=\"font-size: 12pt;\">腹泻、腹痛</span></strong><span style=\"font-size: 12pt;\">等食物中毒问题。</span></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">三、存放剩饭菜要“科学”</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">为了保证剩饭、剩菜的“卫生和安全”，建议避免对其进行“不必要的操作”。</span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">（</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">1</span><span style=\"font-size: 12pt;\">）应</span><strong><span style=\"font-size: 12pt;\">减少筷子等器具对剩饭菜的翻动，</span></strong><span style=\"font-size: 12pt;\">用材质安全、靠谱的保鲜盒盛装、存放剩饭菜，确保其密封性。</span></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">（</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">2</span><span style=\"font-size: 12pt;\">）剩饭菜在存放的过程中应放于</span><strong><span style=\"font-size: 12pt;\">“冰箱低温区域”，</span></strong><span style=\"font-size: 12pt;\">尽早吃完，避免细菌等微生物的大量繁殖。</span></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">（</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">3</span><span style=\"font-size: 12pt;\">）值得注意的是，剩饭菜需要</span><strong><span style=\"font-size: 12pt;\">“分类包装＋存放”</span></strong><span style=\"font-size: 12pt;\">，避免出现交叉污染和串味。</span></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p align=\"center\" style=\"text-align: center; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">四、剩饭菜在吃之前必须要“热透”</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">虽然剩饭菜可以吃，但在吃之前一定要进行“完全加热”。也就是说，应该将剩饭菜</span><strong><span style=\"font-size: 12pt;\">加热至</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">100</span><span style=\"font-size: 12pt;\">℃，</span></strong><span style=\"font-size: 12pt;\">而且至少要保持</span><strong><span style=\"font-size: 12pt;\">沸腾</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">5</span><span style=\"font-size: 12pt;\">分钟以上。</span></strong></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">&nbsp;</span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><strong><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\">五、吃多少，热多少，请勿“反复加热”</span></strong><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p align=\"left\" class=\"MsoNormal\" style=\"text-align: left; mso-pagination: widow-orphan;\"><span lang=\"EN-US\" style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1; mso-font-kerning: 0pt; mso-bidi-language: AR;\"><br>\r\n</span><span style=\"background: white; color: black; letter-spacing: 0.4pt; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1; mso-font-kerning: 0pt; mso-bidi-language: AR;\"><span style=\"font-size: 12pt;\">为了降低微生物的侵袭风险，避免发生食物中毒事件，对于剩饭和剩菜，都不建议反复多次加热。</span><strong><span style=\"font-size: 12pt;\">能吃多少，每次就热多少，</span></strong><span style=\"font-size: 12pt;\">剩下的食物继续储存于冰箱，并抓紧吃完。</span></span><span lang=\"EN-US\" style=\"color: black; font-family: 宋体; font-size: 12pt; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p style=\"background: white; text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.25pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\"><br>\r\n</span></span></p>\r\n<p style=\"background: white; text-align: justify; -ms-text-justify: inter-ideograph; mso-pagination: widow-orphan;\"><span style=\"background: white; color: black; letter-spacing: 0.25pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"><span style=\"font-size: 12pt;\">资料来源：北京健康教育，</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">CCTV</span><span style=\"font-size: 12pt;\">生活圈，科普中国</span></span><span lang=\"EN-US\" style=\"color: black; letter-spacing: 0.4pt; font-family: 宋体; mso-ascii-theme-font: major-fareast; mso-fareast-theme-font: major-fareast; mso-hansi-theme-font: major-fareast; mso-bidi-font-family: 宋体; mso-bidi-theme-font: major-fareast; mso-themecolor: text1;\"></span></p>','','','','','');
