INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3047','3872','1','','','“高质量发展是全面建设社会主义现代化国家的首要任务。”习近平总书记5日在参加他所在的十四届全国人大一次会议江苏代表团审议时发表重要讲话，强调“必须完整、准确、全面贯彻新发展理念”“必须更好统筹质的有效提升和量的合理增长”“必须坚定不移深化改革开放、深','172.16.1.2','172.16.1.2','1680763250','<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">“高质量发展是全面建设社会主义现代化国家的首要任务。”习近平总书记<span>5</span>日在参加他所在的十四届全国人大一次会议江苏代表团审议时发表重要讲话，强调“必须完整、准确、全面贯彻新发展理念”“必须更好统筹质的有效提升和量的合理增长”“必须坚定不移深化改革开放、深入转变发展方式”“必须以满足人民日益增长的美好生活需要为出发点和落脚点”，为推动高质量发展进一步指明了前进方向、提供了重要遵循。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">发展是党执政兴国的第一要务，是解决我国一切问题的基础和关键。新征程上，推动高质量发展，必须完整、准确、全面贯彻新发展理念，始终以创新、协调、绿色、开放、共享的内在统一来把握发展、衡量发展、推动发展。坚持创新发展、协调发展、绿色发展、开放发展、共享发展是关系我国发展全局的一场深刻变革，必须坚持系统观念，加强前瞻性思考、全局性谋划、战略性布局、整体性推进。要深刻认识到加快实现高水平科技自立自强是推动高质量发展的必由之路，加快实施创新驱动发展战略，努力突破关键核心技术难题，在重点领域、关键环节实现自主可控。要牢牢把握加快构建新发展格局这个推动高质量发展的战略基点，把实施扩大内需战略同深化供给侧结构性改革有机结合起来，加快建设现代化产业体系，坚持把发展经济的着力点放在实体经济上，着力推动产业链供应链优化升级，以更好适应我国发展新阶段要求、塑造国际合作和竞争新优势。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">推动高质量发展，必须更好统筹质的有效提升和量的合理增长，做好“质”“量”统一的大文章。当前，我国已进入高质量发展阶段，不能简单以国内生产总值增长率论英雄。只有始终坚持质量第一、效益优先，大力增强质量意识，视质量为生命，以高质量为追求，实现有效益、有质量、可持续的发展，才能切实把推动发展的立足点转到提高质量和效益上来，为经济社会持续健康发展夯实基础。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">推动高质量发展，必须坚定不移深化改革开放、深入转变发展方式，以效率变革、动力变革促进质量变革，加快形成可持续的高质量发展体制机制。要按照构建高水平社会主义市场经济体制、推进高水平对外开放的要求，深入推进重点领域改革，统筹推进现代化基础设施体系和高标准市场体系建设，稳步扩大制度型开放，通过深化改革创新增添动力、激发活力，形成共同推动高质量发展的强大合力。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">“治国有常，而利民为本。”人民幸福安康是推动高质量发展的最终目的。必须以满足人民日益增长的美好生活需要为出发点和落脚点，牢固树立和践行以人民为中心的发展思想。基层治理和民生保障事关人民群众切身利益，是促进共同富裕、打造高品质生活的基础性工程。强基才能固本，本固方能邦宁。要健全基层党组织领导的基层群众自治机制，为群众提供家门口的优质服务和精细管理，坚持和发展新时代“枫桥经验”，及时化解矛盾纠纷，紧紧抓住人民群众“急难愁盼”问题，采取更多惠民生、暖民心举措，把发展成果不断转化为生活品质，不断增强人民群众的获得感、幸福感、安全感。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">办好中国的事情，关键在党。推动高质量发展，必须坚持和加强党的全面领导、坚定不移全面从严治党。党要管党才能管好党，从严治党才能治好党。各级党委要牢牢扛起全面从严治党主体责任，切实加强党的二十大精神学习宣传贯彻工作，深入开展学习贯彻习近平新时代中国特色社会主义思想主题教育，加强干部队伍建设和各领域党建工作，为奋进新征程、建功新时代提供坚强有力的政治引领和政治保障。（新华社评论员）</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3048','3873','1','','','3月5日下午，习近平总书记在参加江苏代表团审议时强调，高质量发展是全面建设社会主义现代化国家的首要任务。必须完整、准确、全面贯彻新发展理念，始终以创新、协调、绿色、开放、共享的内在统一来把握发展、衡量发展、推动发展；必须更好统筹质的有效提升和量的合理增','172.16.1.2','172.16.1.2','1680763299','<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">3</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">月<span>5</span>日下午，习近平总书记在参加江苏代表团审议时强调，高质量发展是全面建设社会主义现代化国家的首要任务。必须完整、准确、全面贯彻新发展理念，始终以创新、协调、绿色、开放、共享的内在统一来把握发展、衡量发展、推动发展；必须更好统筹质的有效提升和量的合理增长，始终坚持质量第一、效益优先，大力增强质量意识，视质量为生命，以高质量为追求；必须坚定不移深化改革开放、深入转变发展方式，以效率变革、动力变革促进质量变革，加快形成可持续的高质量发展体制机制；必须以满足人民日益增长的美好生活需要为出发点和落脚点，把发展成果不断转化为生活品质，不断增强人民群众的获得感、幸福感、安全感。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">习近平总书记的重要讲话深刻洞察发展形势、准确把握时代潮流，进一步全面深刻阐释了高质量发展的丰富内涵，科学回答了事关发展的一系列方向性、战略性重大问题，为我们在新征程推动高质量发展指明了前进方向、提供了根本遵循。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">发展是党执政兴国的第一要务，是解决我国一切问题的基础和关键。党的十八大以来，以习近平同志为核心的党中央围绕我国经济社会发展面临的重大和紧迫问题，回应人民对美好生活的向往，把推进高质量发展摆在更为突出的位置，作出一系列全局性的重大决策部署。我国从经济实力实现大幅跃升，到成功跻身创新型国家行列；从城乡区域发展协调性增强，到形成对外开放新格局；从生态环境保护发生历史性、转折性、全局性变化，到人民生活全方位改善……新时代中国经济高质量发展取得历史性伟大成就。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">我们要清醒认识到，百年变局之下，我国发展面临的环境更加错综复杂，深入推进高质量发展刻不容缓。只有坚定不移走高质量发展之路，转变发展方式，创新发展思路，在质量和效益上下功夫，才能赢得优势、赢得主动、赢得未来。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">知之愈明，则行之愈笃。习近平总书记强调，加快实现高水平科技自立自强，是推动高质量发展的必由之路；加快构建新发展格局，是推动高质量发展的战略基点；推进农业现代化是实现高质量发展的必然要求；人民幸福安康是推动高质量发展的最终目的。习近平总书记对推动高质量发展实现路径这一全面、系统、深刻的阐释，进一步深化了全党全社会对发展规律的认识，为我国经济转向高质量发展注入了信心和动力。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">我们要深入贯彻落实习近平总书记关于贯彻新发展理念、加快构建新发展格局、实现高质量发展的一系列重要讲话精神，要坚持“四个面向”，努力突破关键核心技术难题，在重点领域、关键环节实现自主可控，强化企业主体地位，深化科技体制改革，健全科技评价体系和激励机制。要把实施扩大内需战略同深化供给侧结构性改革有机结合起来，加快建设现代化产业体系。坚持把发展经济的着力点放在实体经济上，深入推进重点领域改革，稳步扩大制度型开放。要严守耕地红线，把产业振兴作为乡村振兴的重中之重，优化镇村布局规划，深化农村土地制度改革。要着力解决人民群众“急难愁盼”问题，切实做好重点群体就业帮扶、收入分配调节、健全社会保障体系、强化“一老一幼”服务等工作。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt;\r\nline-height:24.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">蹄疾步稳谋发展，御风而行向未来。各地区各部门要结合实际情况，因地制宜、扬长补短，坚定发展自信心，打好发展主动仗，为全面建设社会主义现代化国家开好局起好步，推动“中国号”巨轮沿着高质量发展航向扬帆远航、行稳致远。</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3049','3874','1','','','盐城市第一人民医院2022年11月零星维修项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。1、工程概况 1.1项目名称：盐城市第一人民医院2022年十一月零星维修项目。1.2项目地点：盐城市第一人民医院主院区（人民南路66号）、','172.16.1.2','172.16.1.2','1680832561','<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:12.0pt;\r\nfont-family:宋体\">盐城市第一人民医院<span>2022</span>年<span>11</span>月零星维修项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.1pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><b><span style=\"font-size:\r\n12.0pt;font-family:宋体\">1</span></b><b><span style=\"font-size:12.0pt;font-family:\r\n宋体\">、工程概况<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">1.1</span><span style=\"font-size:12.0pt;font-family:\r\n宋体\">项目名称：盐城市第一人民医院<span>2022</span>年十一月零星维修项目。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">1.2</span><span style=\"font-size:12.0pt;font-family:\r\n宋体\">项目地点：盐城市第一人民医院主院区（人民南路<span>66</span>号）、北区（毓龙西路<span>166</span>号）、儿童医院（毓龙西路<span>34</span>号）。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><span style=\"font-size:\r\n12.0pt;font-family:宋体\">1.3</span><span style=\"font-size:12.0pt;font-family:\r\n宋体\">招标内容：</span><span style=\"font-size:12.0pt;font-family:宋体\">盐城市第一人民医院<span>2022</span>年十一月零星维修项目包含但不限于：主院区五层屋面、连廊防水、北区发热门诊地面硬化；主院区发电机房更换防火门；主院区设备机房安装格栅式防火门；主院区水泵房吸音板拆除；主院区急诊外科、<span>ICU2</span>病房晒衣架、拖把架；北区<span>9</span>号楼负一楼消防改造等内容，</span><span style=\"font-size:12.0pt;font-family:宋体\">具体内容详见招标人提供的工程量清单。</span><span style=\"font-family:宋体\">招标人保留对上述招标范围进行适当调整的权利。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">1.4</span><span style=\"font-size:12.0pt;font-family:宋体\">质量标准：符合国家“合格”标准，质保期<span>1</span>年，防水工程质保期<span>5</span>年。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">1.5 </span><span style=\"font-size:12.0pt;font-family:宋体\">工期要求：<span>15</span>日历天，具体开工日期以招标人的通知。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">1.6</span><span style=\"font-size:12.0pt;font-family:宋体\">最高限价：<span>15</span>万元。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.1pt;\r\nline-height:24.0pt;layout-grid-mode:char\" align=\"left\"><b><span style=\"font-size:\r\n12.0pt;font-family:宋体\">2 </span></b><b><span style=\"font-size:12.0pt;\r\nfont-family:宋体\">投标人应当具备的主要资格条件：</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">2.1</span><span style=\"font-size:12.0pt;font-family:宋体\">投标人须具有建设行政主管部门核发的建筑装修装饰工程专业承包二级及以上施工资质的独立法人，并取得有效的安全生产许可证。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">2.2</span><span style=\"font-size:12.0pt;font-family:宋体\">项目负责人的资格要求：项目负责人须具有建筑工程专业二级及以上建造师注册证书；同时具有《建筑施工企业项目负责人安全生产考核合格证》（<span>B</span>类）。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';line-height:24.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">&nbsp;&nbsp; 2.3</span><span style=\"font-size:12.0pt;font-family:宋体\">其他要求<span><br>\r\n&nbsp;&nbsp; 2.3.1</span>财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span>&nbsp;<br>\r\n&nbsp;&nbsp; 2.3.2</span>信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">2.4 </span><span style=\"font-size:12.0pt;font-family:宋体\">本次招标不接受联合体投标。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.1pt;\r\nline-height:21.0pt;layout-grid-mode:char\" align=\"left\"><b><span style=\"font-size:\r\n12.0pt;font-family:宋体\">3</span></b><b><span style=\"font-size:12.0pt;font-family:\r\n宋体\">、投标报名及招标文件的获取</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;font-size:12.0pt;font-family:\'嫤疓B2312\',\'serif\';text-indent:24.0pt;line-height:24.0pt;background:white\"><span style=\"background:white\">请符合上述资质条件的投标人携带单位介绍信到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路</span><span style=\"font-family:宋体;background:white\">8</span><span style=\"background:white\">号</span><span style=