INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3127','3952','1','','<!--#p8_attach#-->/cms/item/2023_07/04_16/b042dbc9f5930a18.jpg.cthumb.jpg','7月1日，农工党盐城市一院基层委员会赴大丰三院开展大型义诊、教学查房和学术讲座活动。基层委主委王军，委员任向东、秦玲、蔡鼎及专家党员参加，农工党党员、大丰区卫健委副主任王文宜应邀参加。本次义诊涵盖神经内科、呼吸内科、心血管内科、内分泌内科、泌尿外科、普','10.10.10.10','10.10.10.10','1688459202','<p style=\"text-align: left; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(62, 62, 62); letter-spacing: 2px; background: white;\">7</span><span style=\"color: rgb(62, 62, 62); letter-spacing: 2px; background: white;\">月<span>1</span>日，农工党盐城市一院基层委员会赴大丰三院开展大型义诊、教学查房和学术讲座活动。基层委主委王军，委员任向东、秦玲、蔡鼎及专家党员参加，农工党党员、大丰区卫健委副主任王文宜应邀参加。</span></p><p style=\"text-align: left; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(62, 62, 62); letter-spacing: 2px; background: white;\">本次义诊涵盖神经内科、呼吸内科、心血管内科、内分泌内科、泌尿外科、普外科及护理等科室，现场慕名前来咨询的群众络绎不绝。期间，专家党员们耐心、细致地为前来咨询的群众分析病情，给予专业的治疗建议和科学的健康指导。</span></p><p style=\"text-align: left; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(62, 62, 62); letter-spacing: 2px; background: white;\">专家党员还深入临床一线进行了科室调研、技术指导、经验交流、教学查房、病例讨论等活动。</span></p><p style=\"text-align: left; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(62, 62, 62); letter-spacing: 2px; background: white;\">汤琪春主任、刘忠祥主任分别就《等级医院创建标准解读》、《肺结节诊断与治疗》进行了学术讲座。</span></p><p style=\"text-align: left; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(62, 62, 62); letter-spacing: 2px; background: white;\">王军主委表示，此次活动既是农工党市一院基层委落实与大丰三院战略合作协议的具体行动，也是积极响应“优质医疗资源下沉”的号召、 推进“区域医疗中心”建设、服务基层百姓健康需求的重要活动，今后基层委将持续发挥党员专业力量，为推动盐城医疗体系优质发展做出积极贡献！</span></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2023_07/04_16/b042dbc9f5930a18.jpg.cthumb.jpg\" style=\"\" /></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2023_07/04_16/6f6231787d2b9188.jpg.cthumb.jpg\" style=\"\" /></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2023_07/04_16/ff1e9c14fc0ca583.jpg.cthumb.jpg\" style=\"\" /></p><p><br /></p>','','','','','');
