INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3147','3972','1','','<!--#p8_attach#-->/cms/item/2023_08/02_16/b186e451132dff5e.jpg.cthumb.jpg','2023年7月27日上午，民建盐城市第一人民医院基层委员会换届大会在主院区交班会议室举行。市一院党委书记查文章代表院党委向大会的召开表示祝贺，向全体民建会员致以诚挚的问候，对民建盐城市一院基层委员会自成立以来，围绕地方经济社会发展、医院工作等方面充分发挥专','10.10.10.10','10.10.10.10','1690964315','<p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">2023年<span>7</span>月<span>27</span>日上午，民建盐城市第一人民医院基层委员会换届大会在主院区交班会议室举行。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">市一院党委书记查文章代表院党委向大会的召开表示祝贺，向全体民建会员致以诚挚的问候，对民建盐城市一院基层委员会自成立以来，围绕地方经济社会发展、医院工作等方面充分发挥专业特长、积极参政议政所作出的贡献表示肯定。他对新一届委员会提出了三点希望：一要坚定正确的政治方向，坚持中国共产党的领导，引导广大会员切实增进政治认同、思想认同、理论认同、情感认同，坚定不移跟党走、携手奋进新征程；二要积极履行参政党职能，深入调查研究，积极建言献策，为盐城建设作出新的更大贡献；三要切实加强自身建设，增强组织的凝聚力和向心力，进一步提升良好的市一院民建形象。</p><p><span style=\"letter-spacing: 1px;\">&nbsp; &nbsp; 大会宣读了</span>民建盐城市第一人民医院基层委员会选举结果，卞伟钢当选民建盐城市一院基层委员会主委，索艳君、季丹、吉亚军当选副主委，民建盐城市第一人民医院基层委员<span>32</span>名会员代表参加大会。</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2023_08/02_16/b186e451132dff5e.jpg.cthumb.jpg\" title=\"66cc1db30e113b230939f3cc721cb86.jpg\" alt=\"66cc1db30e113b230939f3cc721cb86.jpg\" /></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2023_08/02_16/f3619b3f18153718.jpg.cthumb.jpg\" title=\"ede0ebefb8c5cf2ce9dc173f9bf4d3d.jpg\" alt=\"ede0ebefb8c5cf2ce9dc173f9bf4d3d.jpg\" /></p>','','','','','');
