INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('12','1926','1','','','为改善盐城市第一人民医院五官科医院门诊就诊环境，经研究决定，自2021年4月8日起对五官科医院门诊楼分步装修改造，工期五个月。装修期间急诊输液暂停，其他诊疗业务均正常开展。装修改造给群众造成不便，敬请谅解。盐城市第一人民医院','192.168.162.110','192.168.162.110','1626143254','<p>为改善盐城市第一人民医院五官科医院门诊就诊环境，经研究决定，自2021年4月8日起对五官科医院门诊楼分步装修改造，工期五个月。装修期间急诊输液暂停，其他诊疗业务均正常开展。装修改造给群众造成不便，敬请谅解。</p>\r\n\r\n<p style=\"text-align: right;\">盐城市第一人民医院</p>\r\n\r\n<p>&nbsp;</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('13','1927','1','','','1、门诊开放时间：周一至周五全天，周六上午；周六下午和周日开放业余门诊服务。上午：8:00&amp;amp;mdash;11:30，下午：2:00&amp;amp;mdash;5:30（春秋）、1:30&amp;amp;mdash;5:00（冬）、2:30&amp;amp;mdash;5:30（夏）。上午提前半小时挂号。2、各院区门诊一楼大厅&amp;amp','192.168.162.110','192.168.162.110','1626143281','<p>1、门诊开放时间：周一至周五全天，周六上午；周六下午和周日开放业余门诊服务。上午：8:00&mdash;11:30，下午：2:00&mdash;5:30（春秋）、1:30&mdash;5:00（冬）、2:30&mdash;5:30（夏）。上午提前半小时挂号。</p>\r\n\r\n<p>2、各院区门诊一楼大厅&nbsp;&ldquo;一站式服务中心&rdquo;为您提供咨询、审批、预约、轮椅、发放报告单等多项便民服务。</p>\r\n\r\n<p>3、就诊及诊疗实行实名制，初诊时请携带患者身份证、医保卡等有效身份证件并于挂号时主动出示，留联系方式；复诊时请出示就诊卡、医保卡。</p>\r\n\r\n<p>4、挂号、就诊、检查请自觉排队；初诊、回诊请在诊区先签到再候诊；每位患者仅限一名家属陪同进入诊室。</p>\r\n\r\n<p>5、医院在各楼层设立自助服务区，请根据需要进行自助挂号、交费、打印门诊病历及检验单。</p>\r\n\r\n<p>6、为节约时间，医院提供现场、网上、电话、诊间、出院患者复诊、微信等预约挂号方式，详见预约挂号指南。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('14','1928','1','','http://www.ycsyy.com/upload/images/2019/11/a4b8eb1493661927.png','','192.168.162.110','192.168.162.110','1626143338','<p><img alt=\"image.png\" border=\"0\" height=\"558\" src=\"http://www.ycsyy.com/upload/images/2019/11/a4b8eb1493661927.png\" title=\"image.png\" vspace=\"0\" width=\"857\" /></p>\r\n\r\n<p><img alt=\"image.png\" border=\"0\" height=\"561\" src=\"http://www.ycsyy.com/upload/images/2019/11/59aec7f3f148fc5a.png\" title=\"image.png\" vspace=\"0\" width=\"857\" /></p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('15','1929','1','','','一、预约挂号方式及具体操作方法1、现场预约 在北区、南区或五官科医院门诊大厅一站式服务台预约二周内专家或专科门诊。2、电话预约 拨打0515&amp;amp;mdash;88508990、0515&amp;amp;mdash;66696879预约二周内专家或专科门诊。3、网上预约 ①登录盐城市第一人民医院网站https://www.ycsy','192.168.162.110','192.168.162.110','1626143372','<p><strong>一、预约挂号方式及具体操作方法</strong></p>\r\n\r\n<p>1、现场预约</p>\r\n\r\n<p>&nbsp; &nbsp;在北区、南区或五官科医院门诊大厅一站式服务台预约二周内专家或专科门诊。</p>\r\n\r\n<p>2、电话预约</p>\r\n\r\n<p>&nbsp; 拨打0515&mdash;88508990、0515&mdash;66696879预约二周内专家或专科门诊。</p>\r\n\r\n<p>3、网上预约</p>\r\n\r\n<p>&nbsp; ①登录盐城市第一人民医院网站https://<a href=\"http://www.ycyy.com/\">www.ycsyy.com</a>，点击&ldquo;预约挂号&rdquo;。</p>\r\n\r\n<p>&nbsp; ②登录&ldquo;微医网&rdquo;https://www.guahao.com&nbsp;，搜索&ldquo;盐城市第一人民医院&rdquo;。</p>\r\n\r\n<p>4、诊间预约</p>\r\n\r\n<p>&nbsp;&nbsp;患者本次就诊结束后，如有需要可由门诊医师进行复诊预约。</p>\r\n\r\n<p>5、出院患者复诊预约</p>\r\n\r\n<p>&nbsp; 住院患者出院后可由病区医师进行出院后门诊复诊预约。</p>\r\n\r\n<p>6、微信预约</p>\r\n\r\n<p>①关注&ldquo;盐城一院&rdquo;公众号（ycsdyrmyy），进入&ldquo;掌上一院&rdquo;进行预约。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('16','1930','1','','','医保患者门诊就医须知一、参保人员持本人医保IC卡和医保病历到医保挂号处挂号。二、参保人员持挂号单、专用病历到相关科室就诊。三、持专用处方、检查单到医保交费窗口用IC卡刷卡支付费用，IC卡中资金不足时，不足部分由本人现金支付。四、参保人员持交费单据进行检查、','192.168.162.110','192.168.162.110','1626143432','<p>医保患者门诊就医须知</p>\r\n\r\n<p>一、参保人员持本人医保IC卡和医保病历到医保挂号处挂号。</p>\r\n\r\n<p>二、参保人员持挂号单、专用病历到相关科室就诊。</p>\r\n\r\n<p>三、持专用处方、检查单到医保交费窗口用IC卡刷卡支付费用，IC卡中资金不足时，不足部分由本人现金支付。</p>\r\n\r\n<p>四、参保人员持交费单据进行检查、治疗或取药。</p>\r\n\r\n<p>五、药品处方限量管理的规定：急性病3-5天、慢性病7-10天、需长期服药的慢性病30天、同类药品不超过2种。</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>医保患者住院须知</p>\r\n\r\n<p>一、符合住院指征的参保人员持门诊接诊医师开具的入院通知单，单位或社区卫生服务站介绍信（仅城南新区及开发区参保人员需要介绍信）到门诊一站式服务台换取本院入院介绍信。</p>\r\n\r\n<p>二、请携带入院介绍信（仅城南新区及开发区参保人员需要介绍信）、医保IC卡和医保病历到住院处办理住院手续。</p>\r\n\r\n<p>三、住院时请配合病区医保管理人员核对人、卡、证。</p>\r\n\r\n<p>四、严禁冒名住院、挂床住院套取医保基金等不规范行为。</p>\r\n\r\n<p>五、住院检查、用药坚持因病施治，做到&ldquo;三合理&rdquo;使用原则。</p>\r\n\r\n<p>六、患者出院时不得带与本次住院诊断无关的药品，口服药一般限3-7天量，最多不超过14天，不得外带静脉注射药及检查治疗项目。</p>\r\n\r\n<p>七、根据患者病情需用特殊内置材料的，要求医师填写内置材料审批表到我院医保办审批、相应医保中心备案后才能使用；急症病人可以先使用后审批，术后三个工作日内补办。</p>\r\n\r\n<p>八、领取并详细认真阅读盐城市第一人民医院&laquo;医疗保险患者住院知情同意书&raquo;。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('17','1931','1','','','医保患者入院流程1、经门、急诊医师诊治，开具住院通知单2、凭住院通知单、社区或医保中心介绍信(仅开发区和城南新区参保患者需要介绍信)、医保卡到一站式服务台办理登记签章手续（急诊可先办理住院手续，三个工作日内补办医保手续）3、持盖章住院通知单、医保卡到住院','192.168.162.110','192.168.162.110','1626143472','<p><strong>医保患者入院流程</strong></p>\r\n\r\n<p>1、经门、急诊医师诊治，开具住院通知单</p>\r\n\r\n<p>2、凭住院通知单、社区或医保中心介绍信(仅开发区和城南新区参保患者需要介绍信)、医保卡到一站式服务台办理登记签章手续（急诊可先办理住院手续，三个工作日内补办医保手续）&nbsp;</p>\r\n\r\n<p>3、持盖章住院通知单、医保卡到住院处刷卡住院、预缴住院费用、住院处开具预交金收据</p>\r\n\r\n<p>4、持住院通知单、门诊病历到所在病区护士站</p>\r\n\r\n<p>5、办理相关入院手续</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>医保患者出院流程</strong></p>\r\n\r\n<p>1、床位医生出具出院医嘱，确定出院时间</p>\r\n\r\n<p>2、护士站开具出院通知单、电脑关帐</p>\r\n\r\n<p>3、床位医生提供出院小结、出院诊断书</p>\r\n\r\n<p>4、凭出院通知单及预交金收据到住院处办理出院手续</p>\r\n\r\n<p>5、住院处结算、开具住院收费票据、提供住院费用清单。出院小结到一站式服务台盖医务公章</p>\r\n\r\n<p>6、出院</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('18','1932','1','','','新型农村合作患者入院流程　　1、经门、急诊医师诊治，开具住院通知单　　2、凭住院通知单到住院处预缴住院费用、住院处开具预交金收据　　3、凭住院通知单、预交金收据、转诊单（所在地农合办办理）、身份证明（居民身份证或户口簿）复印件、新农合参合证卡复印件到新','192.168.162.110','192.168.162.110','1626143497','<p>新型农村合作患者入院流程</p>\r\n\r\n<p>　　1、经门、急诊医师诊治，开具住院通知单</p>\r\n\r\n<p>　　2、凭住院通知单到住院处预缴住院费用、住院处开具预交金收据</p>\r\n\r\n<p>　　3、凭住院通知单、预交金收据、转诊单（所在地农合办办理）、身份证明（居民身份证或户口簿）复印件、新农合参合证卡复印件到新农合现场结报中心（住院处西第一间办公室）办理登记签章手续</p>\r\n\r\n<p>　　4、持住院通知单、门诊病历到所在病区护士站</p>\r\n\r\n<p>　　5、办理相关入院手续</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　新型农村合作患者出院流程</p>\r\n\r\n<p>　　1、床位医生出具出院医嘱，确定出院时间</p>\r\n\r\n<p>　　2、护士站开具出院通知单、电脑关帐</p>\r\n\r\n<p>　　3、床位医生提供出院小结（两份）、出院诊断书</p>\r\n\r\n<p>　　4、凭出院通知单、预交金收据及出院小结到住院处办理出院手续</p>\r\n\r\n<p>　　5、出院</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('19','1933','1','','','　亭湖区新型农村合作患者住院须知　　一、患者持入院通知书、新型农村合作医疗证书、身份证、所在县新型农村合作医疗办公室转诊介绍信到住院处办理住院。　　二、入院后要协助病区管理人员核对病人及农村合作医疗证书。　　三、在诊疗过程中，要做到合理检查、合理治疗','192.168.162.110','192.168.162.110','1626143525','<p>　亭湖区新型农村合作患者住院须知</p>\r\n\r\n<p>　　一、患者持入院通知书、新型农村合作医疗证书、身份证、所在县新型农村合作医疗办公室转诊介绍信到住院处办理住院。</p>\r\n\r\n<p>　　二、入院后要协助病区管理人员核对病人及农村合作医疗证书。</p>\r\n\r\n<p>　　三、在诊疗过程中，要做到合理检查、合理治疗、合理用药、合理收费。严格执行《江苏省新型农村合作医疗基本药物目录》，使用目录内药品比例不低于80%，如病情需要必须使用自费药品者，必须征得您同意并签字方可使用。</p>\r\n\r\n<p>　　四、因工伤、交通事故，打架斗殴等第三者责任事故和自杀、自残、吸毒、犯罪行为及从事危险操作而未作防护而至伤的不在新农合补偿之列。</p>\r\n\r\n<p>　　五、患者出院时，医生为患者提供出院小结，诊断证明，患者凭新型农村合作医疗证、出院小结、转诊证明、住院发票、费用清单、领钱人身份证到住院处新农合结算窗口进行现场结算、报销。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('20','1934','1','','http://www.ycsyy.com/upload/images/2019/10/9e94926577dfcf22.jpeg','一、参保人员住院医疗待遇　　凡符合基本医疗保险药品目录、诊疗目录何服务设施范围的住院医疗费用，个人负担乙类项目自负费用后再按比例报销。　　1、城镇职工基本医疗保险。个人负担住院起付线，再按下列比例补偿：　　　　　　　　2、城镇居民基本医疗保险。参保时间','192.168.162.110','192.168.162.110','1626143575','<p>一、参保人员住院医疗待遇</p>\r\n\r\n<p>　　凡符合基本医疗保险药品目录、诊疗目录何服务设施范围的住院医疗费用，个人负担乙类项目自负费用后再按比例报销。</p>\r\n\r\n<p>　　1、城镇职工基本医疗保险。个人负担住院起付线，再按下列比例补偿：</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　<img alt=\"01\" border=\"0\" height=\"88\" src=\"http://www.ycsyy.com/upload/images/2019/10/9e94926577dfcf22.jpeg\" width=\"552\" /></p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　2、城镇居民基本医疗保险。参保时间不满6个月的，医疗费用不予以补偿，参保时间满6个月不满1年的，补偿待遇限额20000元。同时实行补偿待遇与连续缴费年限挂钩，每年增加15000元，最高限额为200000元。具体比例为：</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　<img alt=\"02\" border=\"0\" height=\"54\" src=\"http://www.ycsyy.com/upload/images/2019/10/b0ee70a2530733cf.jpeg\" width=\"552\" /></p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('21','1935','1','','','急诊留观病人：依病情需要但无床，或病情不需要住院只需短期留观的病人，不办住院手续，需到收费处交纳所需费用。　　1、急诊观察室的护士接到门、急诊医生关于病人需留观的电话后准备床单及用品。　　2、留观病人持写有&amp;amp;ldquo;收留观&amp;amp;rdquo;字样的门、急诊病历或就诊卡','192.168.162.110','192.168.162.110','1626143621','<p>急诊留观病人：依病情需要但无床，或病情不需要住院只需短期留观的病人，不办住院手续，需到收费处交纳所需费用。</p>\r\n\r\n<p>　　1、急诊观察室的护士接到门、急诊医生关于病人需留观的电话后准备床单及用品。</p>\r\n\r\n<p>　　2、留观病人持写有&ldquo;收留观&rdquo;字样的门、急诊病历或就诊卡进入急诊病房护士站。</p>\r\n\r\n<p>　　3、护士安排床位，填留观登记，通知医生查看病人。</p>\r\n\r\n<p>　　4、护士评估病人基本情况，测量生命体征。</p>\r\n\r\n<p>　　5、医生查看病人后开出病人检查、化验、用药及各种费用，病人到急诊收费处交费后到急诊药房取药。</p>\r\n\r\n<p>　　6、病人将取回的药品及处方 （一式三份） 交到护士站，护士按底方查收药品，按医嘱配液；病人保存好另一张底方及相关的收费凭证。</p>\r\n\r\n<p>　　7、留观病人以后每日的取药程序同第一日的取药流程。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('22','1936','1','','','1、值班医生通知留观病人出院，根据病情恢复情况开出带药医嘱。　　2、病人凭处方自行去急诊药房取药。　　3、医生或护士告知病人出院后的注意事项。　　4、病人离院。','192.168.162.110','192.168.162.110','1626143653','<p>1、值班医生通知留观病人出院，根据病情恢复情况开出带药医嘱。</p>\r\n\r\n<p>　　2、病人凭处方自行去急诊药房取药。</p>\r\n\r\n<p>　　3、医生或护士告知病人出院后的注意事项。</p>\r\n\r\n<p>　　4、病人离院。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('23','1937','1','','','　　一、凡因我院技术或设备条件限制不能解决的疑难病例，需转至外院诊治的患者，在病情允许转送的前提下，由经管主治医师提出，上级医师和科主任同意，上报分管院长审批并备案。　　二、转院必须严格掌握指征，生命体征不稳定的患者不予转院，应暂留院处理，待病情稳定','192.168.162.110','192.168.162.110','1626143685','<p>　　一、凡因我院技术或设备条件限制不能解决的疑难病例，需转至外院诊治的患者，在病情允许转送的前提下，由经管主治医师提出，上级医师和科主任同意，上报分管院长审批并备案。</p>\r\n\r\n<p>　　二、转院必须严格掌握指征，生命体征不稳定的患者不予转院，应暂留院处理，待病情稳定后再转院。</p>\r\n\r\n<p>　　三、转院的费用结算及手续与出院相同。</p>\r\n\r\n<p>　　四、患者转院时可按照有关规定复印并带走部分病历资料，如检查化验单、病情摘要、出院小结以及转院介绍信等。</p>\r\n\r\n<p>　　五、患者转院时由原经管科室酌情派医护人员随救护车护送。</p>\r\n\r\n<p>　　六、因各种原因主动要求转院的患者，应由患者本人和家属在病程记录中写明&ldquo;要求自动出院&rdquo;字样并签名及按手印。转院手续由其本人、家属或单位自行联系解决，按自动出院处理。</p>\r\n\r\n<p>　　七、医保患者的转院或转外地诊治问题按医院相关规定办理相关手续。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('24','1938','1','','','为切实保障患者权益，积极有效对患者合并疾病进行治疗，特制定以下规定：　　一、患者住院期间出现他科病情或确诊为其他专业疾病或合并其他专业疾病比本专业疾病更为紧急时，经转入科医师会诊并同意接收后，由原经治医生开转科医嘱。　　二、接到转科医嘱后，护士处理医','192.168.162.110','192.168.162.110','1626143709','<p>为切实保障患者权益，积极有效对患者合并疾病进行治疗，特制定以下规定：</p>\r\n\r\n<p>　　一、患者住院期间出现他科病情或确诊为其他专业疾病或合并其他专业疾病比本专业疾病更为紧急时，经转入科医师会诊并同意接收后，由原经治医生开转科医嘱。</p>\r\n\r\n<p>　　二、接到转科医嘱后，护士处理医嘱，核对医嘱、收费项目，核对剩余药物，与转入科室联系转入时间，办理退药、整理病历等转科手续。</p>\r\n\r\n<p>　　三、经治医师写好转科记录，按联系时间转科，转出科需派人陪同到转入科，与该科护士交接病历、药物，并向值班医师交代病情：转入科医师及时检查处理患者，书写转入记录。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('27','1941','1','','','★若服用抗凝剂至少停用7天方可做活检。　　★胃镜：检查前一晚宜进食清淡，10点后不能吃东西和水。　　★结肠镜：　　①检查前2-3天少渣饮食（低纤维饮食），检查前一晚进流质（如豆浆、汤、果汁等，忌食西瓜、番茄、蔬菜）。　　②检查前6-8小时开始口服泻药之后饮水2','192.168.162.110','192.168.162.110','1626143888','<p>★若服用抗凝剂至少停用7天方可做活检。</p>\r\n\r\n<p>　　★胃镜：检查前一晚宜进食清淡，10点后不能吃东西和水。</p>\r\n\r\n<p>　　★结肠镜：</p>\r\n\r\n<p>　　①检查前2-3天少渣饮食（低纤维饮食），检查前一晚进流质（如豆浆、汤、果汁等，忌食西瓜、番茄、蔬菜）。</p>\r\n\r\n<p>　　②检查前6-8小时开始口服泻药之后饮水2000ml（大约2茶瓶）解清大便，检查前4小时不能吃东西和水。</p>\r\n\r\n<p>　　③至检查前排泄＜4次或半次排清水样便。为保证检查质量与病人安全，请重新就诊预约检查时间。</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>取报告时间：一般检查后20分钟内出内镜彩图报告，病理报告于3-5个工作日后至内镜中心服务台取报告。</p>\r\n	</li>\r\n</ul>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('26','1940','1','','http://www.ycsyy.com/upload/images/2019/10/db5d94921d9da003.jpeg','影像检查流程　　付费&amp;amp;rarr;登记&amp;amp;rarr;预约&amp;amp;rarr;检查&amp;amp;rarr;取报告影像检查相关科室平面图　　　　　　　　　　取报告时间　　一、摄片（DR）　　1、门诊检查：一般常规影像检查，正常情况下，检查结束30分钟后取报告，特殊检查半日后取报告。　　2、住院检查：上午检查','192.168.162.110','192.168.162.110','1626143852','<p>影像检查流程</p>\r\n\r\n<p>　　付费&rarr;登记&rarr;预约&rarr;检查&rarr;取报告</p>\r\n\r\n<p>影像检查相关科室平面图</p>\r\n\r\n<p>　　<img alt=\"\" border=\"0\" height=\"417\" src=\"http://www.ycsyy.com/upload/images/2019/10/db5d94921d9da003.jpeg\" width=\"454\" /><img alt=\"\" border=\"0\" height=\"677\" src=\"http://www.ycsyy.com/upload/images/2019/10/d7c61c6e01db759f.jpeg\" width=\"452\" /></p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　取报告时间</p>\r\n\r\n<p>　　一、摄片（DR）</p>\r\n\r\n<p>　　1、门诊检查：一般常规影像检查，正常情况下，检查结束30分钟后取报告，特殊检查半日后取报告。</p>\r\n\r\n<p>　　2、住院检查：上午检查的于当日下午、下午检查的于次日上午送至病房护士站。</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　二、CT</p>\r\n\r\n<p>　　1、门诊检查：上午检查当天下午4:30后取报告；下午检查次日上午10:30后取报告；</p>\r\n\r\n<p>　　2、病房检查：于检查24小时后送至病房护士站。</p>\r\n\r\n<p>　　3、特殊检查： CTA、CTU、CTPA、灌注成像等，门诊病人于检查结束2天后下午4点取报告；住院病人于检查结束2天后送至病房护士站。</p>\r\n\r\n<p>　　4、急诊检查：检查结束后30分钟取急诊临时报告，取正式报告时间参照以上1-3项内容。</p>\r\n\r\n<p>　　&nbsp;　&nbsp;</p>\r\n\r\n<p>　　三、磁共振（MRI）</p>\r\n\r\n<p>　　1、门诊检查：12点前检查当天下午4点后取报告、12点后检查次日上午9点后取报告。</p>\r\n\r\n<p>　　2、病房检查：上午检查的于当日下午、下午检查的于次日上午送至病房护士站。</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　四、超声科</p>\r\n\r\n<p>　　一般检查30分钟后取报告，特殊检查次日取报告。</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　注： 1、住院病人如急需，请自行到登记处取片。</p>\r\n\r\n<p>　　&nbsp; &nbsp;2、各类检查须知请详询各检查科室。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('28','1942','1','','','　★凭就诊卡或缴费发票到门诊一楼采血中心登记处打印条形码。　　★需空腹抽血的检查项目：生化全套、肝功能、肾功能、血脂分析、血糖、血液流变学等。　　★取报告时间：　　①肝功能、肾功能、血脂分析、乙肝两对半、风湿全套、丙肝、艾滋病、梅毒血清检查、血型、抗','192.168.162.110','192.168.162.110','1626143914','<p>　★凭就诊卡或缴费发票到门诊一楼采血中心登记处打印条形码。</p>\r\n\r\n<p>　　★需空腹抽血的检查项目：生化全套、肝功能、肾功能、血脂分析、血糖、血液流变学等。</p>\r\n\r\n<p>　　★取报告时间：</p>\r\n\r\n<p>　　①肝功能、肾功能、血脂分析、乙肝两对半、风湿全套、丙肝、艾滋病、梅毒血清检查、血型、抗结核抗体、幽门螺旋杆菌抗体等项目，每日上午9：30前抽血者，当日下午上班后取报告；每日上午9：30后抽血者，次日下午上班后取报告。</p>\r\n\r\n<p>　　②生化全套、血糖、心肌酶谱、电解质分析、血液流变学、免疫球蛋白全套、糖化血红蛋白等项目，每日上午9：30前抽血者，当日下午上班后取报告。（注：此类标本不能放置太久，故9:30后不再抽血。）</p>\r\n\r\n<p>　　③肿瘤标志物、甲状腺功能、性激素全套、HCG、孕酮、唐氏筛查等项目，每日上午10：30前抽血者，当日下午4:00后取报告；每日上午10:30后抽血者，次日下午4:00后取报告（注：唐氏筛查10:30后不抽血）。</p>\r\n\r\n<p>　　④急诊生化（干式生化全套，干式肝功能，干式肾功能，干式电解质，干式血糖，干式血脂，干式心肌酶谱，心肌标志物）类项目，24小时抽血，抽血后2小时取报告。</p>\r\n\r\n<p>　　⑤血、尿、便常规，接受标本后30分钟取报告。</p>\r\n\r\n<p>　　★凭就诊卡或缴费发票到自助打印机打印报告，如遇困难请到一站式服务中心打印。</p>\r\n\r\n<p>　　★特殊项目检验，请到门诊一楼采血中心窗口咨询或电话0515-88508729询问。</p>\r\n\r\n<p>　　★如遇项目更改或其他情况，请以门诊一楼采血中心告知为准。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('29','1943','1','','','　检查前：　　★由于检查药物的特殊性，希望受检者不要随意更改预约好的检查时间，如有特殊情况不能如期检查，请至少于约定时间的前一天下午4点前通知PET/CT工作人员，否则药费损失由受检者承担；　　★孕妇、哺乳妇女务必提前告知；　　★请于受检前一天禁酒、禁做剧','192.168.162.110','192.168.162.110','1626143949','<p>　检查前：</p>\r\n\r\n<p>　　★由于检查药物的特殊性，希望受检者不要随意更改预约好的检查时间，如有特殊情况不能如期检查，请至少于约定时间的前一天下午4点前通知PET/CT工作人员，否则药费损失由受检者承担；</p>\r\n\r\n<p>　　★孕妇、哺乳妇女务必提前告知；</p>\r\n\r\n<p>　　★请于受检前一天禁酒、禁做剧烈或长时间的运动；</p>\r\n\r\n<p>　　★请于受检前6小时开始禁食，禁饮含糖饮料、禁静脉滴注葡萄糖，可饮少量清水；糖尿病患者正常服用降糖药物，心脏受检患者在检查当日晨6时口服速效降脂药（具体可电话咨询盐城市一院PET/CT中心）；</p>\r\n\r\n<p>　　★建议检查当日着宽松、保暖衣服，在检查前取出身上的金属饰品及手机等；</p>\r\n\r\n<p>　　★来PET/CT中心检查时，需带齐有关资料(所有既往病史、B超报告、CT、X线片、病理结果、MRI和DSA等)；</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　检查时：</p>\r\n\r\n<p>　　★在注射显像药物前后都须保持安静、少走动，尽可能避免与人交谈，并按规定饮用为您预备的水或甘露醇；</p>\r\n\r\n<p>　　★开始显像检查前，受检者需要到卫生间排净尿液；</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　检查后：</p>\r\n\r\n<p>　　★检查后需征得检查医师的同意，确认无需延迟扫描，方可离开；</p>\r\n\r\n<p>　　★检查后需要多喝水以利于放射性核素的代谢并排出体外；</p>\r\n\r\n<p>　　★24小时内不要接触孕妇及儿童。</p>\r\n\r\n<p>　　&nbsp;</p>\r\n\r\n<p>　　盐城市一院PET/CT中心</p>\r\n\r\n<p>　　电话：0515-88507122</p>\r\n\r\n<p>　　地址：盐城市第一人民医院（盐城市毓龙西路166号）北院区9号楼一楼西侧</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('30','1944','1','','','1、服务协调：及时协调、解决患者诊疗过程中出现的各种问题；对南、北及五官科院区患者的检查、住院及治疗等进行院内协调。2、导医导诊：实行门前及分楼层服务；提供路线指引、名医、专科特色等就医信息的介绍；疾病预检分诊；老弱病残和行动不便者的陪检陪送。3、优先','192.168.162.110','192.168.162.110','1626144004','<p>&nbsp; 1、服务协调：及时协调、解决患者诊疗过程中出现的各种问题；对南、北及五官科院区患者的检查、住院及治疗等进行院内协调。</p>\r\n\r\n<p>&nbsp;&nbsp;2、导医导诊：实行门前及分楼层服务；提供路线指引、名医、专科特色等就医信息的介绍；疾病预检分诊；老弱病残和行动不便者的陪检陪送。</p>\r\n\r\n<p>&nbsp;&nbsp;3、优先服务：为急危重症、重度残疾、久病体弱、重度疼痛病人及军人、消防救援人员、离休干部、老年人提供优先诊疗和门诊预约挂号病人优先就诊的审核、证明服务。</p>\r\n\r\n<p>&nbsp;&nbsp;4、审批服务：假日期间疾病诊断证明审批及出院小结盖章。</p>\r\n\r\n<p>&nbsp;&nbsp;5、咨询服务：提供医学咨询、健康咨询服务及检验结果的电话、短信咨询。</p>\r\n\r\n<p>&nbsp;&nbsp;6、预约服务：接受专家、专科挂号的现场及电话预约；门诊特殊病人或病人有特殊要求检查项目的预约服务。</p>\r\n\r\n<p>&nbsp;&nbsp;7、便民服务：为空腹抽血病人提供免费早餐、免费提供开水、纸杯、信纸、信封、针线、雨伞、老花镜、写字板、健康教育处方，失物招领、代寄检验单、提供意见薄和投诉意见箱，提供共享轮椅及平车。</p>\r\n\r\n<p>&nbsp;&nbsp;8、发放报告：自助打印和服务台人工打印、发放各类检验报告及病理报告单。</p>\r\n\r\n<p>&nbsp;&nbsp;9、一站式服务中心电话：</p>\r\n\r\n<p>　　南院区（0515）66696879</p>\r\n\r\n<p>　　北院区（0515）88508990</p>\r\n\r\n<p>　　五官科院区（0515）88358506。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('31','1945','1','','http://www.ycsyy.com/upload/images/2021/4/1bf5c91f70bb3aeb.png','','192.168.162.110','192.168.162.110','1626144148','<img alt=\"男基本.png\" src=\"http://www.ycsyy.com/upload/images/2021/4/1bf5c91f70bb3aeb.png\" title=\"男基本.png\" /><img alt=\"女基本.png\" src=\"http://www.ycsyy.com/upload/images/2021/4/db4964eed8c27630.png\" title=\"女基本.png\" /><img alt=\"男高端.png\" src=\"http://www.ycsyy.com/upload/images/2021/4/4601c0c0d23a73fc.png\" title=\"男高端.png\" /><img alt=\"女高端.png\" src=\"http://www.ycsyy.com/upload/images/2021/4/d80783266760877f.png\" title=\"女高端.png\" />','','','','','');
INSERT INTO `p8_cms_item_page_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('32','1946','1','','','盐城市第一人民医院体检中心中心创建于2006年，位于市中心南城河路12号盐城市第一人民医院五官科医院内，建筑面积约2000多平方米，主要承担市直机关干部和事业单位人员体检、全市专家体检、公务员入职体检、军转干部体检等指令性任务和全市各企事业单位的职工体检、市民','192.168.162.110','192.168.162.110','1626144175','<p>&nbsp;&nbsp;&nbsp;&nbsp;盐城市第一人民医院体检中心中心创建于2006年，位于市中心南城河路12号盐城市第一人民医院五官科医院内，建筑面积约2000多平方米，主要承担市直机关干部和事业单位人员体检、全市专家体检、公务员入职体检、军转干部体检等指令性任务和全市各企事业单位的职工体检、市民个人体检等，获得社会各界一致好评。经过十多年的发展，体检中心具有一定的规模，目前是盐城市业务量最大的体检中心，中心是盐城市医学会健康管理分会、盐城市医院协会健康管理专业委员会主任成员单位，江苏省医学会健康管理分会成员单位，江苏省医院协会疾病与健康管理专业委员会常委单位，同时被中国健康促进会授予&ldquo;大肠癌筛查中心&rdquo;、&ldquo;胃癌筛查中心&rdquo;&ldquo;江苏省体重与肥胖管理中心盐城分中心&rdquo;等称号，2018年接待各类体检8万多人次，业务收入达3500万元。 2017年根据市委市政府要求设立盐城市第一人民医院体检中心中心南区分中心，二个中心实行一体化管理，依托盐城市第一人民医院平台，逐步由单纯的健康体检向健康管理过度，对体检过程中发现的阳性指针进行全程跟踪并提供必要的就医绿色通道；中心科室设置齐全，仪器设备先进，大型医技检查如各种检验、胸部DR、CT、磁共振、胃肠镜检查等共享医院资源；中心现有主任医师、副主任医师、全科医师各一名，承担体检报告的总检并提供必要的健康咨询。每年组织医院专家到各体检单位举办健康讲座和健康咨询十余场次，取得较好的社会效益。</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;南院区体检中心：地址：盐城市人民南路66号，电话：0515-66696533&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;五官科医院体检中心：地址：盐城市南城河路12号，电话：0515-88365756</p>\r\n','','','','','');
