INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('8121','9','cms','item','update','3944','1028','','宣传处','修改内容 | 盐城市第一人民医院招收2023年度社会化/单位委培住院医师规范化培','/admin.php/cms/item-update','10.10.10.10','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?model=article&id=3944&verified=1\',\n  \'cid\' => \'1028\',\n  \'title\' => \'盐城市第一人民医院招收2023年度社会化/单位委培住院医师规范化培训学员的公告\',\n  \'timestamp\' => \'2023-06-14 16:20:17\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'author_x\' => \'\',\n  \'author_y\' => \'\',\n  \'author_z\' => \'\',\n  \'editer\' => \'\',\n  \'source\' => \'教育处\',\n  \'frame\' => \'/attachment/cms/item/2023_06/14_16/9cc0c2330ba251af.png.cthumb.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'盐城市第一人民医院创建于1948年，经过七十余年的发展，现已建设成为一所集医疗、教学、科研、康复、急救为一体的三级甲等综合性医院。医院是首批国家级“住院医师规范化培训基地”、“全科医生临床培养建设基地”，承担南通大学、徐州医学院本科与研究生教学任务，及江\',\n  \'url\' => \'\',\n  \'views\' => 0,\n  \'#field_content_posted\' => \'\',\n  \'#field_starttime_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'starttime\' => \'\',\n    \'endtime\' => \'\',\n    \'content\' => \'<p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">盐城市第一人民医院创建于<span>1948</span>年，经过七十余年的发展，现已建设成为一所集医疗、教学、科研、康复、急救为一体的三级甲等综合性医院。医院</span><span style=\"line-height: 150%;\">是首批国家级“住院医师规范化培训基地”、“全科医生临床培养建设基地”，承担南通大学、徐州医学院本科与研究生教学任务，及江苏医药职业学院等十余所高校实习生带教工作<span>,</span>并于</span><span style=\"line-height: 150%;\">2019</span><span style=\"line-height: 150%;\">年正式挂牌南京大学医学院附属盐城第一人民医院。</span><span style=\"line-height: 150%;\">医院共设有<span>4</span>个院区：主院区、北院区、五官科院区、儿童医院。</span><span style=\"line-height: 150%;\">医院重视学科建设，建成了一批特色鲜明的重点专科，其中呼吸内科、骨科、普外科、心内科、检验科、影像科、肿瘤科、神经内科等<span>20</span>个科室被评为江苏省临床重点专科；市级临床重点专科<span>15</span>个；市医学重点学科<span>18</span>个。</span></p><p style=\"text-align:left;text-indent:32px;line-height:150%\"><span style=\"line-height: 150%;\">&nbsp;</span></p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\"></p><p style=\"text-align: center;\"><img src=\"/attachment/cms/item/2023_06/14_16/9cc0c2330ba251af.png.cthumb.jpg\" title=\"微信图片_20230614084514.png\" alt=\"微信图片_20230614084514.png\"/></p><p></p><p style=\"text-align:left;text-indent:32px;line-height:150%\"><span style=\"line-height: 150%;\">&nbsp;</span></p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">为了更好地选拔优秀人才，培养合格临床医师，我培训基地从即日起面向社会及各单位招收<span>2023</span>年度住院医师规范化培训社会化<span>/</span>单位委培养学员，现将有关事项公告如下：</span></p><p style=\"text-align:left;text-indent:32px;line-height:150%\"><span style=\"line-height: 150%;\">&nbsp;</span></p><p style=\"text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"line-height: 150%;\">一、基本条件：</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px; margin-left: 28px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"line-height: 150%;\"><span>1.<span style=\"font-style: normal; font-variant: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-weight: normal; font-stretch: normal; line-height: normal;\">&nbsp;&nbsp; </span></span></span><span style=\"line-height: 150%;\">坚持四项基本原则，</span><span style=\"line-height: 150%;\">拥护党的路线、方针、政策，</span><span style=\"line-height: 150%;\">遵纪守法，品行端正，无违法违纪行为。</span></p><p style=\"margin-t