INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('8157','9','cms','item','update','3982','860','','宣传处','修改内容 | 关于公布盐城市第一人民医院医药领域腐败问题集中整治工作举报方式的公','/admin.php/cms/item-update','10.10.10.10','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?id=3982&model=article&verified=1\',\n  \'cid\' => \'860\',\n  \'title\' => \'关于公布盐城市第一人民医院医药领域腐败问题集中整治工作举报方式的公告\',\n  \'timestamp\' => \'2023-08-30 09:21:40\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'author_x\' => \'\',\n  \'author_y\' => \'\',\n  \'author_z\' => \'\',\n  \'editer\' => \'\',\n  \'source\' => \'监督审查室\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'url\' => \'\',\n  \'views\' => 3,\n  \'#field_content_posted\' => \'\',\n  \'#field_starttime_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'starttime\' => \'\',\n    \'endtime\' => \'\',\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">为推进我院医药领域腐败问题集中整治工作，加强医药领域违纪违法线索收集，现向社会公布盐城市第一人民医院集中整治协作机制举报方式及相关事项。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>一、整治时间</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2023年8月至2024年6月。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>二、举报方式</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">电话：0515-66696055</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">电子邮箱：<a href=\"mailto:ycyyjw@126.com%EF%BC%9B\"><span style=\"color: rgb(0, 0, 0);\">ycyyjw@126.com</span></a></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">邮寄地址：盐城市人民南路66号盐城市第一人民医院监督审查室，邮政编码：224008</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（电话受理时间：工作日8:30—11:30，14:30—17:30）。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>三、相关要求</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（一）投诉举报须提供明确的时间、地点、人员、具体事项等内容，并附相关旁证材料，以便核查。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（二）鼓励实名举报，举报人对投诉举报内容的真实性负责，不得虚构和夸大事实，不得故意捏造和诬陷他人。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（三）受理部门对举报人信息、举报内容严格保密。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">感谢社会公众对盐城市第一人民医院医药领域腐败问题集中整治工作的关心支持和协助配合。</p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">盐城市第一人民医院</p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">医药领域腐败问题集中整治工作协作机制工作专班</p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">2023年8月30日</p><p><br/></p>\',\n  ),\n  \'#field_endtime_posted\' => \'\',\n  \'addon_title\' => \'\',\n  \'list_order\' => \'2023-08-31 09:21:40\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'custom_a\' => \'\',\n  \'level_time\' => \'权重有效期\',\n  \'level\' => \'\',\n  \'template\' => \'\',\n  \'attachment_pdf\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'title_color\' => \'\',\n  \'keywords\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'1\',\n  \'id\' => \'3982\',\n  \'iid\' => \'3982\',\n  \'page\' => \'1\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/core-main\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'update\',\n  \'attachment_hash\' => \'9cb4744d1f71d327\',\n  \'verifier\' => \'宣传处\',\n)','1694684240');
