INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('8161','9','cms','item','add','0','859','','宣传处','添加内容 | 关于检验科部分室间质评传染性标志物新增质控品的遴选公告','/admin.php/cms/item-add','10.10.10.10','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'关于检验科部分室间质评传染性标志物新增质控品的遴选公告\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'author_x\' => \'\',\n  \'author_y\' => \'\',\n  \'author_z\' => \'\',\n  \'editer\' => \'\',\n  \'source\' => \'设备处\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'url\' => \'\',\n  \'views\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'#field_starttime_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'starttime\' => \'\',\n    \'endtime\' => \'\',\n    \'content\' => \'<p style=\"line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">各医疗器械生产企业、经营企业：</span></p><p style=\"text-indent:32px;line-height:27px;background:rgb(255,255,255)\"><span style=\"color: rgb(68, 68, 68);\">&nbsp;</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(68, 68, 68);\">为</span><span style=\"color: rgb(68, 68, 68);\">贯彻落实《江苏省消除艾滋、梅毒和乙肝母婴传播计划实施方案（2023-2025）》要求</span><span style=\"color: rgb(68, 68, 68);\">，经医院研究决定，需对</span><span style=\"color: rgb(68, 68, 68);\">检验科所需</span>包含<strong>人免疫缺陷病毒抗体、梅毒螺旋体特异性抗体和梅毒螺旋体非特异性抗体的</strong><strong>复合质控品</strong><span style=\"color: rgb(68, 68, 68);\">进行院内遴选，</span><span style=\"color: rgb(51, 51, 51);\">欢迎符合条件的生产企业、经营企业前来参与。</span></p><p style=\"text-indent:32px;line-height:27px;background:rgb(255,255,255)\"><span style=\"color: rgb(51, 51, 51);\">&nbsp;</span></p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-weight: bold;\">1、</span><strong><span style=\"line-height: 150%;\">参数要求</span></strong></p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">&nbsp;&nbsp;&nbsp;&nbsp;包含</span><span style=\"line-height: 150%;\">人免疫缺陷病毒抗体、梅毒螺旋体特异性抗体和梅毒螺旋体非特异性抗体的</span><span style=\"line-height: 150%;\">复合质控品，3个浓度水平，每支量</span><span style=\"line-height: 150%;\">800μl</span><span style=\"line-height: 150%;\">以上</span><span style=\"line-height: 150%;\">。</span></p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: center; margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-weight: bold;\">2、</span><strong><span style=\"line-height: 150%;\">浓度要求</span></strong></p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: center; margin-top: 15px; margin-bottom: 15px;\"><img src=\"/attachment/cms/item/2023_09/15_16/dc9275caa7398f4c.png.cthumb.jpg\" title=\"1.png\" _src=\"/attachment/cms/item/2023_09/15_16/dc9275caa7398f4c.png.cthumb.jpg\" alt=\"1.png\"/></p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-weight: bold;\">3、</span><strong><span style=\"line-height: 150%;\">数量要求</span></strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">300套（每套包含3个水平）。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"color: rgb(51, 51, 51);\">报名须知：</span></strong></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">1.所供试剂需在江苏省药品(医用耗材)阳光采购和综合监管平台。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">2.专用设备的配置及性能。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">3.具有独立承担民事责任的能力。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">4.具有履行合同所必须具备的体外诊断试剂专业知识。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">5.具有良好的商业信誉</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">6.近三年内在经营活动