INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('8326','9','cms','item','update','4010','859','','宣传处','修改内容 | 中标结果公示','/admin.php/cms/item-update','10.10.10.10','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?model=article&id=4010&verified=1\',\n  \'cid\' => \'859\',\n  \'title\' => \'中标结果公示\',\n  \'timestamp\' => \'2023-10-19 17:36:30\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'author_x\' => \'\',\n  \'author_y\' => \'\',\n  \'author_z\' => \'\',\n  \'editer\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'中标结果公示因医院实际工作需要，于2023年10月19日进行公开招标，经评审小组评审，中标结果如下：一、项目名称：盐城市第一人民医院主院区配电房运行值守项目中标单位：江苏新宇工程有限公司中标价：488000.00元项目负责人：纪龙娣为体现公开、公平、公正的原则，特此\',\n  \'url\' => \'\',\n  \'views\' => 0,\n  \'#field_content_posted\' => \'\',\n  \'#field_starttime_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'starttime\' => \'\',\n    \'endtime\' => \'\',\n    \'content\' => \'<p style=\"text-align: center; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; margin-top: 15px; margin-bottom: 15px;\"><br/></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">因医院实际工作需要，于2023年10月19日进行公开招标，经评审小组评审，中标结果如下：</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">项目名称：盐城市第一人民医院主院区配电房运行值守项目</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">中标单位：江苏新宇工程有限公司</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">中标价：488000.00元</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">项目负责人：纪龙娣</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监督审查室联系，电话0515-66696810。</p><p style=\"text-indent:37px;text-autospace:ideograph-numeric;line-height:35px;background:rgb(255,255,255)\"><span style=\"color: rgb(68, 68, 68);\">&nbsp;</span></p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">盐城市第一人民医院</p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">2023年10月19日</p><p><br/></p>\',\n  ),\n  \'#field_endtime_posted\' => \'\',\n  \'addon_title\' => \'\',\n  \'list_order\' => \'2023-10-19 17:36:30\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'custom_a\' => \'\',\n  \'level_time\' => \'权重有效期\',\n  \'level\' => \'\',\n  \'template\' => \'\',\n  \'attachment_pdf\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'title_color\' => \'\',\n  \'keywords\' => \'\',\n  \'model\