INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`attachment_pdf`,`url`,`attributes`,`summary`,`source`,`author`,`author_x`,`author_y`,`author_z`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`,`starttime`,`endtime`) VALUES ('2081','article','945','2','admin5','姜中华','','0','副主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_10/197ad8f69416ecf9.JPG','','','','6','医学博士，副主任医师，毕业于南京医科大学江苏省消化内镜青年委员，江苏省“科技强卫工程”医学青年人才，盐城市“科教兴卫战略工程”医学青年人才。先后在第二军医大学附属长海医院、江苏省人民医院、苏州大学附属第一医院学习消化临床及消化内镜下的各种治疗，熟练掌握消化科常见病及疑难危重病例的诊治，对内镜下胃肠道息肉、消化道出血、狭窄、逆行胰胆管造影诊治胆胰疾病（内镜下胰胆管取石术，胆道支架置入.','','','','','','','','','1','admin5','0','1626229335','1626229335','1626229335','1626229335','1','','','8','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','超声内镜引导下胆胰占位性病变的细胞学和组织学活检术（EUS-FNA+FNB），超声内镜引导下胰腺癌放射性离子、H101植入术 ，胰腺癌晚期顽固性疼痛腹腔神经节阻滞术（EUS-CPN），胰腺假性囊肿的支架引流术。','','','','','','');
INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`attachment_pdf`,`url`,`attributes`,`summary`,`source`,`author`,`author_x`,`author_y`,`author_z`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`,`starttime`,`endtime`) VALUES ('2082','article','945','2','admin5','王正江','','0','副主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_10/107a300cbea2e1dc.JPG','','','','6','男，副主任医师，本科学历。对消化系统常见病、多发病、疑难病诊治，肝、胆、胰疾病的内镜微创介入诊治，内镜逆行胰胆管造影、超声内镜诊治、内镜下黏膜切除、内镜下息肉摘除等，急、慢性胰腺炎、消化道狭窄和各种胆总管结石、阻塞性黄疸的介入和内科治疗有丰富的经验。在省级及中华期刊发表多篇论文；获盐城市卫生局新技术引进一等奖，获盐城市科技进步奖1项。','','','','','','','','','1','admin5','0','1626229381','1626229381','1626229381','1626229381','1','','','0','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','消化道狭窄、胆总管结石及梗阻性黄疸内镜微创诊疗、重症急性胰腺炎的救治及超声胃镜诊治。','','','','','','');
