INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1195','2007','1','','<!--#p8_attach#-->/cms/item/2021_07/13_17/4237624c4528b687.jpg.thumb.jpg','女，新生儿科主任，主任医师，医学硕士，江苏省研究型医院医学会新生儿医学专业委员会委员，江苏省妇幼保健协会新生儿分会委员，江苏省盐城市医学会儿科学分会委员，江苏省第二届医疗鉴定损害专家库成员。擅长危重新生儿的监护和抢救，尤其擅长有创及无创机械通气呼吸支持技术、静脉营养支持技术的应用，以及超低出生体重儿、极低出生体重儿、新生儿呼吸窘迫综合征、新生儿坏死性小肠结肠炎、新生儿持续肺动脉高压等危重症的救治。','192.168.162.110','172.16.70.1','1626168414','<p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">女，新生儿科主任，主任医师，医学硕士，江苏省研究型医院医学会新生儿医学专业委员会委员，江苏省妇幼保健协会新生儿分会委员，江苏省盐城市医学会儿科学分会委员，江苏省第二届医疗鉴定损害专家库成员。毕业于南京医科大学，在北京儿童医院新生儿重症监护病房和儿童重症监护病房进修一年。熟练诊治儿科常见病、多发病及疑难病。擅长危重新生儿的监护和抢救，尤其擅长有创及无创机械通气呼吸支持技术、静脉营养支持技术的应用，以及超低出生体重儿、极低出生体重儿、新生儿呼吸窘迫综合征、新生儿坏死性小肠结肠炎、新生儿持续肺动脉高压等危重症的救治。在国家级、省级儿科专业杂志上发表论文二十余篇，主持的科研项目获盐城市新技术引进二等奖。</p><p><br /></p>','','','','','');
