INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1212','2024','1','','<!--#p8_attach#-->/cms/item/2021_07/13_17/e9a5563423a38832.JPG','肿瘤内科副主任医师，医学硕士。从事肿瘤内科工作二十余年，有丰富的临床经验。1995年毕业于南京中医药大学，获医学学士学位；2004年毕业于昆明医学院肿瘤学专业，获医学硕士学位，2005年主持的乳腺癌腋淋巴转移的多因素分析与微转移检测技术获盐城市医学新技术引进奖一等奖，2007年至2009年于江苏省肿瘤医院进修。擅长各种肿瘤的中西医康复治疗。发表核心期刊论文十','192.168.162.110','192.168.162.110','1626168517','肿瘤内科副主任医师，医学硕士。从事肿瘤内科工作二十余年，有丰富的临床经验。1995年毕业于南京中医药大学，获医学学士学位；2004年毕业于昆明医学院肿瘤学专业，获医学硕士学位，2005年主持的乳腺癌腋淋巴转移的多因素分析与微转移检测技术获盐城市医学新技术引进奖一等奖，2007年至2009年于江苏省肿瘤医院进修。擅长各种肿瘤的中西医康复治疗。发表核心期刊论文十余篇。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1213','2025','1','','<!--#p8_attach#-->/cms/item/2021_07/13_17/68afad545db74404.jpg.thumb.jpg','女，妇产科主任医师，研究生学历，盐城市医学会妇产科分会委员。先后在苏北人民医院、江苏省人民医院、复旦大学附属妇产医院进修学习生殖医学、妇科内分泌专业。从事生殖医学十几年，在省级以上期刊发表论文8篇，主持市级科研课题2项，参与市级科研课题2项，获得市级科技进步奖3项（二等奖1项，三等奖2项）。擅长不孕不育的规范化诊治、生殖内分泌疾病的诊治、促排卵治疗、B超监测','192.168.162.110','192.168.162.110','1626168522','<p>女，妇产科主任医师，研究生学历，盐城市医学会妇产科分会委员。先后在苏北人民医院、江苏省人民医院、复旦大学附属妇产医院进修学习生殖医学、妇科内分泌专业。从事生殖医学十几年，在省级以上期刊发表论文8篇，主持市级科研课题2项，参与市级科研课题2项，获得市级科技进步奖3项（二等奖1项，三等奖2项）。擅长不孕不育的规范化诊治、生殖内分泌疾病的诊治、促排卵治疗、B超监测卵泡发育及排卵预测、复发性流产的病因筛查及治疗、人工授精、试管婴儿相关临床操作技术、早孕期系统化监护、不孕症相关宫腔镜手术。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1214','2026','1','','<!--#p8_attach#-->/cms/item/2021_07/13_17/dc59f5f2046559c9.JPG','医学博士，肿瘤内科副主任医师。从事肿瘤科工作多年，擅长食管癌、乳腺癌、肺癌、胃癌、直肠癌等多个肿瘤疾病的诊治，熟练掌握肿瘤科各项操作。在省级以上杂志发表多篇学术论文。','192.168.162.110','192.168.162.110','1626168578','医学博士，肿瘤内科副主任医师。从事肿瘤科工作多年，擅长食管癌、乳腺癌、肺癌、胃癌、直肠癌等多个肿瘤疾病的诊治，熟练掌握肿瘤科各项操作。在省级以上杂志发表多篇学术论文。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1215','2027','1','','<!--#p8_attach#-->/cms/item/2022_04/04_10/90d45866af32b0fc.jpg.thumb.jpg','女，医学博士，主任医师，硕士研究生导师，盐城市第一人民医院妇产科副主任。','192.168.162.110','172.16.1.2','1634541960','<p></p><p>妇产科学博士，盐城市第一人民医院妇产科学科带头人，妇产科副主任，研究生导师，主任医师。江苏省333工程高层次培养对象兼考核优秀人才，江苏省医学会妇产科分会委员，江苏省医学会妇科内镜委员会委员，盐城市135工程医学重点人才，盐城市医学会妇产科分会副主任委员。美国纽约凯瑟琳癌症中心，澳大利亚墨尔本MERCY妇产医院，台北荣民总医院访问学者。获江苏省科技厅课题一项，盐城市科技局课题5项，相关研究成果曾获华夏医学科技奖，淮海科技进步二等奖，盐城市新技术引进一等奖等，以第一作者于SCI收录杂志发表论文7篇，中华妇产科杂志等国内核心期刊发表论文30余篇。从事妇产科临床20余年，擅长达芬奇机器人手术，妇科子宫内膜异位症，子宫肌瘤，腺肌症，宫颈病变，疤痕妊娠，子宫内膜占位息肉等腹腔镜宫腔镜微创手术，各类妇科恶性肿瘤如卵巢癌，宫颈癌，子宫内膜癌彻底的分期手术及化疗，靶向等综合治疗。&nbsp;&nbsp;</p><p></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1217','2029','1','','<!--#p8_attach#-->/cms/item/2021_07/13_17/75c23db227a887e6.jpg','女，妇科主任，主任医师。从事妇产科临床三十余年，擅长：宫颈癌、卵巢癌、子宫内膜癌等妇科恶性肿瘤的手术和化疗；常规开展妇科微创(宫腔镜、腹腔镜)手术，开展子宫肌瘤、子宫腺肌瘤腺肌症、卵巢巧克力囊肿、卵巢畸胎瘤等妇科微创手术，擅长子宫内膜病变、宫腔息肉、子宫粘膜下肌瘤、宫腔黏黏等宫腔镜手术；擅长压力性尿失禁子宫脱垂等盆底修复手术；开展达芬奇机器人复杂子宫切除、子宫内膜异位症、妇科恶性肿瘤的分期手术等二百例；常规开展妇科内分泌及不孕不育治疗。临床尤其擅长单孔腹腔镜手术，做到无痕美观，造福广大女性患者。','192.168.162.110','172.16.70.1','1626168642','<p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">女，妇科主任，<span style=\"text-indent: 2em;\">主任医师。中国医药教育协会更年期教育专业委员会委员，中国老年保健协会更年期与妇科内分泌分会委员，世界内镜医师协会江苏省妇科内镜专家委员会成员，江苏省中西医结合协会妇产科分会委员，江苏省医学会妇产科分会宫颈学组秘书，江苏省妇幼保健协会科学生育分会副主任委员，江苏省抗癌协会第五届妇科肿瘤专业委员会整合康复学组委员，盐城市医学会妇产科分会常委。江苏省第23期援非医疗队妇产科专家，援助非洲坦桑尼亚-桑给巴尔-纳兹莫嘉医院工作2年。曾赴加拿大Sunnybrook science center研修妇科肿瘤；参加美国Holycross Hospital surgicalonline training。从事妇产科临床三十余年，擅长：宫颈癌、卵巢癌、子宫内膜癌等妇科恶性肿瘤的手术和化疗；常规开展妇科微创(宫腔镜、腹腔镜)手术，开展子宫肌瘤、子宫腺肌瘤腺肌症、卵巢巧克力囊肿、卵巢畸胎瘤等妇科微创手术，擅长子宫内膜病变、宫腔息肉、子宫粘膜下肌瘤、宫腔黏黏等宫腔镜手术；擅长压力性尿失禁子宫脱垂等盆底修复手术；开展达芬奇机器人复杂子宫切除、子宫内膜异位症、妇科恶性肿瘤的分期手术等二百例；常规开展妇科内分泌及不孕不育治疗。临床尤其擅长单孔腹腔镜手术，做到无痕美观，造福广大女性患者。</span><span style=\"text-indent: 2em;\">先后发表SCI论文3篇，参与主编论著一篇，发表省级及以上期刊论文数十篇，主持完成市级课题一项，参与市级课题多项，2003年获得盐城市新技术引进奖二等奖。</span></p><p>&nbsp;</p><p><br /></p>','','','','','');
