INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1286','2098','1','','<!--#p8_attach#-->/cms/item/2021_07/14_10/2fab6d30a7f80fd7.JPG','男，血液科主任、主任医师，本科学历。系江苏省医学会血液学分会委员、江苏省医师协会血液科分会委员、江苏省抗癌协会淋巴瘤专业委员会委员、江苏省抗癌协会血液肿瘤专业委员会委员、江苏省研究型医院学会淋巴瘤专委会常委、江苏省研究型医院学会白血病和MDS专委会委员、盐城市医学会内科学分会委员、盐城市医学会血液学分会主任委员、盐城市血友病诊疗专家组组长。擅长造血系统肿瘤（','192.168.162.110','192.168.162.110','1626230408','男，血液科主任、主任医师，本科学历。系江苏省医学会血液学分会委员、江苏省医师协会血液科分会委员、江苏省抗癌协会淋巴瘤专业委员会委员、江苏省抗癌协会血液肿瘤专业委员会委员、江苏省研究型医院学会淋巴瘤专委会常委、江苏省研究型医院学会白血病和MDS专委会委员、盐城市医学会内科学分会委员、盐城市医学会血液学分会主任委员、盐城市血友病诊疗专家组组长。擅长造血系统肿瘤（白血病、骨髓增生异常综合征、淋巴瘤、多发性骨髓瘤）、再生障碍性贫血、免疫性血小板减少症等现代诊断和治疗及造血干细胞移植技术，在血液病诊疗方面有较高的专业技术水平和丰富的临床经验。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1287','2099','1','','<!--#p8_attach#-->/cms/item/2021_07/14_10/ece0ae93aaf2f4f1.JPG','男，风湿免疫科主任，主任医师，本科学历。江苏省风湿病委员，江苏省中西医风湿病学会委员，盐城市风湿分会主任委员。1984年毕业于苏州医学院医疗系，对各种风湿病有独特见解和治疗方法，目前主攻风湿学科的类风湿关节炎和强直性脊柱炎。','192.168.162.110','192.168.162.110','1626230967','男，风湿免疫科主任，主任医师，本科学历。江苏省风湿病委员，江苏省中西医风湿病学会委员，盐城市风湿分会主任委员。1984年毕业于苏州医学院医疗系，对各种风湿病有独特见解和治疗方法，目前主攻风湿学科的类风湿关节炎和强直性脊柱炎。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1288','2100','1','','<!--#p8_attach#-->/cms/item/2021_07/14_10/bf9121865ebbd27c.JPG','男，感染病科副主任，硕士，主任医师。毕业于徐州医科大学研究生院，医学硕士，曾在上海瑞金医院感染科进修学习。江苏省医学会临床流行病学分会委员，盐城医学会感染病学会委员。临床经验丰富，擅长各种肝病，如慢性乙型肝炎、慢性丙型肝炎、肝硬化、酒精性肝病、自身免疫性肝病诊断和治疗，对不明原因发热，不明原因肝损，败血症，重症肾综合征出血热，重型肝炎及其并发症的救治均有丰富的临床经验。发表专业论文多篇，曾多次获...','192.168.162.110','10.10.10.10','1626231340','<p>男，感染病科副主任，硕士，主任医师。毕业于徐州医科大学研究生院，医学硕士，曾在上海瑞金医院感染科进修学习。江苏省医学会临床流行病学分会委员，盐城医学会感染病学会委员。临床经验丰富，擅长各种肝病，如慢性乙型肝炎、慢性丙型肝炎、肝硬化、酒精性肝病、自身免疫性肝病诊断和治疗，对不明原因发热，不明原因肝损，败血症，重症肾综合征出血热，重型肝炎及其并发症的救治均有丰富的临床经验。发表专业论文多篇，曾多次获得盐城市科技进步奖、新技术引进奖，参与多项研究课题。</p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1289','2101','1','','<!--#p8_attach#-->/cms/item/2021_07/14_10/f95be52ea92fb1d8.JPG','男，主任医师，南通大学医学院兼职教授，本科学历。从事感染科专业三十多年，熟练掌握感染性疾病的诊治，具有丰富临床经验，擅长慢性肝炎抗病毒治疗及耐药的处理研究，擅长慢性肝炎，肝硬化，肝癌，各种肝脏功能异常的诊疗。对发热待查，腹水待查，出血热等疑难杂症有深入研究。掌握人工肝腹水浓缩回输等技术。发表20多篇医学论文，其中国家级杂志2篇，SCI论文1篇。','192.168.162.110','172.16.1.2','1626231098','男，主任医师，南通大学医学院兼职教授，本科学历。从事感染科专业三十多年，熟练掌握感染性疾病的诊治，具有丰富临床经验，擅长慢性肝炎抗病毒治疗及耐药的处理研究，擅长慢性肝炎，肝硬化，肝癌，各种肝脏功能异常的诊疗。对发热待查，腹水待查，出血热等疑难杂症有深入研究。掌握人工肝腹水浓缩回输等技术。发表20多篇医学论文，其中国家级杂志2篇，SCI论文1篇。','','','','','');
