INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1337','2149','1','','<!--#p8_attach#-->/cms/item/2021_07/14_14/075f95ee17630a74.JPG','男，骨科副主任医师，硕士研究生。对于创伤骨科、小儿骨科有深入的研究，对骨科常见疾病及疑难病症有独特的见解，有丰富的临床经验。','192.168.162.110','192.168.162.110','1626242958','男，骨科副主任医师，硕士研究生。对于创伤骨科、小儿骨科有深入的研究，对骨科常见疾病及疑难病症有独特的见解，有丰富的临床经验。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1338','2150','1','','<!--#p8_attach#-->/cms/item/2021_07/14_14/17a369ca057da5d0.JPG','男，副主任医师，博士研究生。中国医药教育协会脊柱微创学组委员，中国医师协会脊柱内镜委员会委员，美国脊柱微创协会（SMISS）会员，北美脊柱外科协会(NASS)会员，国家公派留学美国，师从SMISS主席Larry Khoo教授，NASS主席和AO Spine全球主席Jeffery Wang教授，ISASS前任主席、椎间孔镜YESS技术创始人Anthony Ye','192.168.162.110','192.168.162.110','1626243041','<p>男，副主任医师，博士研究生。中国医药教育协会脊柱微创学组委员，中国医师协会脊柱内镜委员会委员，美国脊柱微创协会（SMISS）会员，北美脊柱外科协会(NASS)会员，国家公派留学美国，师从SMISS主席Larry Khoo教授，NASS主席和AO Spine全球主席Jeffery Wang教授，ISASS前任主席、椎间孔镜YESS技术创始人Anthony Yeung教授，学习了国际上先进的脊柱微创外科技术理念。熟练掌握相关临床诊治及手术操作，对颈椎病、腰椎间盘突出、腰椎管狭窄、腰椎滑脱、脊柱肿瘤等脊柱疾病有独特的见解，在国内外核心期刊发表论文十余篇，其中SCI期刊收录6篇，为国际期刊的审稿人之一，主持国家自然科学基金项目1项，曾荣获&ldquo;江苏省优秀青年志愿者&rdquo;称号。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1339','2151','1','','<!--#p8_attach#-->/cms/item/2021_07/14_14/7a7cad80874ac93b.JPG','男，骨科副主任医师，硕士研究生。对于骨科相关疾患的诊断和治疗积累了丰富的临床工作经验。对于四肢骨关节疾病最新的内固定技术及骨病的保守和手术治疗有着自己独到的见解。擅长各类创伤、骨关节疾病的相关治疗。获得院先进个人四次；获得师科技进步三等奖1次；在国家级核心期刊发表论文3篇。','192.168.162.110','192.168.162.110','1626243087','男，骨科副主任医师，硕士研究生。对于骨科相关疾患的诊断和治疗积累了丰富的临床工作经验。对于四肢骨关节疾病最新的内固定技术及骨病的保守和手术治疗有着自己独到的见解。擅长各类创伤、骨关节疾病的相关治疗。获得院先进个人四次；获得师科技进步三等奖1次；在国家级核心期刊发表论文3篇。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1340','2152','1','','<!--#p8_attach#-->/cms/item/2021_07/14_14/de6399a4a50ac931.JPG','男，医学博士，主任医师。从事骨科临床工作二十余年，熟练掌握骨科常见病、多发病的诊治与手术。擅长脊柱退变性疾病（椎间盘突出症、椎管狭窄症、椎体失稳滑脱症、韧带骨化症）、脊柱（四肢）骨折、炎症、结核、肿瘤、侧弯后凸畸形等疾患的诊治与手术；特别是微创治疗椎间盘突出症、椎管狭窄症（脊柱内镜下侧、后路椎管减压/髓核摘除术）、脊柱病理性骨折（椎体成形术，PVP/PKP）','192.168.162.110','172.16.100.2','1626243121','男，医学博士，主任医师。从事骨科临床工作二十余年，熟练掌握骨科常见病、多发病的诊治与手术。擅长脊柱退变性疾病（椎间盘突出症、椎管狭窄症、椎体失稳滑脱症、韧带骨化症）、脊柱（四肢）骨折、炎症、结核、肿瘤、侧弯后凸畸形等疾患的诊治与手术；特别是微创治疗椎间盘突出症、椎管狭窄症（脊柱内镜下侧、后路椎管减压/髓核摘除术）、脊柱病理性骨折（椎体成形术，PVP/PKP）积累了丰富的临床经验；曾先后在江苏省人民医院、上海长征医院脊柱外科、河南省直三院脊柱内镜中心进修学习；在国家级专业期刊以第一作者发表学术论文数篇（SCI数据库收录1篇；中国知网收录7篇）。先后多次被市直卫生系统评为“文明职工、先进个人”。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1341','2153','1','','<!--#p8_attach#-->/cms/item/2021_07/14_14/36e8e4ef4847f489.JPG','男，骨科硕士，副主任医师，SICOT中国部江苏省数字骨科分会委员，江苏省医学会创伤分会青年委员，盐城市骨矿盐与骨质疏松委员会委员。能熟练做好骨科病人围手术期处理，将运动损伤手术与康复治疗有效结合，使患者恢复更佳，尽快恢复运动与工作。开展骨科的常见病和多发病手术，擅长创伤外科疾病，常见四肢骨折及运动损伤，颈肩痛，腰腿痛等骨科疾病。从事骨科临床工作10年余，发表','192.168.162.110','192.168.162.110','1626243180','男，骨科硕士，副主任医师，SICOT中国部江苏省数字骨科分会委员，江苏省医学会创伤分会青年委员，盐城市骨矿盐与骨质疏松委员会委员。能熟练做好骨科病人围手术期处理，将运动损伤手术与康复治疗有效结合，使患者恢复更佳，尽快恢复运动与工作。开展骨科的常见病和多发病手术，擅长创伤外科疾病，常见四肢骨折及运动损伤，颈肩痛，腰腿痛等骨科疾病。从事骨科临床工作10年余，发表论文10余篇，参与主持省级课题研究1项，市级课题研究2项。','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1342','2154','1','','<!--#p8_attach#-->/cms/item/2021_07/14_14/1101a19281a7ccd1.JPG','男，副主任医师，硕士学历，国际AO创伤协会会员，亚太足踝协会委员，中国数字骨科江苏省分会委员，江苏省骨质疏松协会骨折学组委员，盐城市医学会骨科分会创伤学组秘书，擅长骨关节疾病诊治，多发复杂创伤急救创伤控制及修复重建，四肢长骨骨折微创修复重建，肩、肘、腕、髋、膝、踝关节复杂骨折手术治疗，对骨盆髋臼重大创伤有深厚造诣，擅长畸形矫形，肢体缺血性疾病(糖尿病足、闭塞','192.168.162.110','192.168.162.110','1626243228','<p>男，副主任医师，硕士学历，国际AO创伤协会会员，亚太足踝协会委员，中国数字骨科江苏省分会委员，江苏省骨质疏松协会骨折学组委员，盐城市医学会骨科分会创伤学组秘书，擅长骨关节疾病诊治，多发复杂创伤急救创伤控制及修复重建，四肢长骨骨折微创修复重建，肩、肘、腕、髋、膝、踝关节复杂骨折手术治疗，对骨盆髋臼重大创伤有深厚造诣，擅长畸形矫形，肢体缺血性疾病(糖尿病足、闭塞性脉管炎)保肢手术治疗，复杂足踝疾患诊治，在国内外著名学术期刊发表多篇论著。</p>\r\n','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1343','2155','1','','<!--#p8_attach#-->/cms/item/2021_07/14_14/090558e4c633fed5.JPG','殷俊，主任医师,医学博士。江苏省医师协会骨科医师分会微创学组委员，中国医药教育协会肩肘运动医学规范化培训中心委员，江苏省医学会骨科学分会微创学组成员，盐城市医学会骨科学分会关节学组委员，盐城市医学会运动医疗分会委员。','192.168.162.110','172.16.100.2','1626243269','<p style=\"margin: 0cm 0cm 0.0001pt; font-size: 10.5pt; text-align: left;\" align=\"left\"></p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;mso-layout-grid-align:\r\nnone;text-autospace:none\"><span style=\"font-size: 14pt;\"><span style=\"font-size: 12pt;\">个人简历</span><span lang=\"EN-US\" style=\"\"></span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-align:left;mso-layout-grid-align:\r\nnone;text-autospace:none\"><span style=\"font-size: 14pt;\"><span style=\"font-size: 12pt;\">&ensp;&ensp;&ensp;殷俊，主任医师</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">,</span><span style=\"font-size: 12pt;\">医学博士。江苏省医师协会骨科医师分会微创学组委员，中国医药教育协会肩肘运动医学规范化培训中心委员，江苏省医学会骨科学分会微创学组成员，盐城市医学会骨科学分会关节学组委员，盐城市医学会运动医疗分会委员。</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">2002</span><span style=\"font-size: 12pt;\">年本科毕业于天津医科大学，</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">2008</span><span style=\"font-size: 12pt;\">年毕业于南通大学，获硕士学位；</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">2016</span><span style=\"font-size: 12pt;\">年毕业于苏州大学，获博士学位；</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">2014</span><span style=\"font-size: 12pt;\">年到上海华山医院运动医学科进修学习膝关节镜技术，</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">2017</span><span style=\"font-size: 12pt;\">年到北医三院运动医学科进修肩、踝关节镜技术，</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">2019</span><span style=\"font-size: 12pt;\">年到德国帕德博恩进修关节及运动损伤微创修复技术。从事骨科医疗、科研工作近二十年，对于骨科相关疾患的诊断和治疗积累了丰富的临床工作经验。熟练掌握各类创伤、骨关节疾病及运动损伤疾患的诊治</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">,</span><span style=\"font-size: 12pt;\">特别是对肩、膝及踝关节疾病的微创治疗。曾获得淮海科技进步三等奖</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">1</span><span style=\"font-size: 12pt;\">次；已发表</span><span lang=\"EN-US\" style=\"font-size: 12pt;\">SCI</span><span style=\"font-size: 12pt;\">文章二篇及多篇省级以上核心期刊文章。</span><span lang=\"EN-US\" style=\"font-family: 仿宋;\"></span></span></p>\r\n<span style=\"font-size: 12pt;\">\r\n</span>','','','','','');
