INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1369','2181','1','','<!--#p8_attach#-->/cms/item/2021_07/14_14/8c70182c87042d15.JPG','男，主任医师，普外科医学博士，硕士生导师，兼职副教授，盐城市重点医学人才，江苏省333工程重点培养人才，江苏省普外科学组青年委员，2014年赴德国学习研修。研究方向为肝胆外科，对普外科急危重、疑难病例的处理有丰富的临床经验。目前在SCI收录期刊发表医学论文7篇，另在核心期刊、省级期刊发表医学论文20余篇。主持盐城市科研课题三项。有较强的临床工作能力和科研、教','192.168.162.110','192.168.162.110','1626245558','男，主任医师，普外科医学博士，硕士生导师，兼职副教授，盐城市重点医学人才，江苏省333工程重点培养人才，江苏省普外科学组青年委员，2014年赴德国学习研修。研究方向为肝胆外科，对普外科急危重、疑难病例的处理有丰富的临床经验。目前在SCI收录期刊发表医学论文7篇，另在核心期刊、省级期刊发表医学论文20余篇。主持盐城市科研课题三项。有较强的临床工作能力和科研、教学能力。','','','','','');
