INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1952','2772','1','','','一、盐城市第一人民医院五官科医院门诊楼空调改造工程已经相关部门批准建设。工程所需资金自筹，已落实。项目已具备招标条件，现对该本工程进行公开招标。本次招标对投标报名人的资格审查采用资格后审方法确定合格的投标人。二、盐城市招标代理有限公司受招标人委托具体','192.168.162.110','192.168.162.110','1620921600','<p style=\"text-indent: 2em;\">一、盐城市第一人民医院五官科医院门诊楼空调改造工程已经相关部门批准建设。工程所需资金自筹，已落实。项目已具备招标条件，现对该本工程进行公开招标。本次招标对投标报名人的资格审查采用资格后审方法确定合格的投标人。</p><p style=\"text-indent: 2em;\">二、盐城市招标代理有限公司受招标人委托具体负责本工程的招标事宜。</p><p style=\"text-indent: 2em;\">三、项目概况</p><p style=\"text-indent: 2em;\">1、项目名称：盐城市第一人民医院五官科医院门诊楼空调改造工程。</p><p style=\"text-indent: 2em;\">2、工程地点：盐城市南城河路12号。</p><p style=\"text-indent: 2em;\">3、工程规模：本工程造价约29.9万元。</p><p style=\"text-indent: 2em;\">4、质量标准：国家“合格”标准。并符合招标文件中技术需求、国家现行的相关规范标准及有关部门最新颁布的相应的最新标准。</p><p style=\"text-indent: 2em;\">5、工期要求：120日历天。</p><p style=\"text-indent: 2em;\">四、标段划分及招标内容</p><p style=\"text-indent: 2em;\">1、标段划分：本工程为一个标段。</p><p style=\"text-indent: 2em;\">2、招标内容：盐城市第一人民医院五官科医院门诊楼空调改造工程的施工，包括原吸顶式空调的拆除、新增设备材料的采购（包括室外机、室内机及所有配套管道、线缆、风口、风阀等）、供应、运输、安装、相关开堵洞、塞填缝、调试、验收交付、售前售后等伴随服务。具体详见设计图纸及工程量清单，招标人保留对上述招标范围进行适当调整的权利。</p><p style=\"text-indent: 2em;\">五、投标人主要资格要求</p><p style=\"text-indent: 2em;\">1、投标人资质要求：投标人具备独立法人资格，且营业执照经营范围须包含空调生产（或销售）或家电销售相关内容。</p><p style=\"text-indent: 2em;\">2、项目负责人的资格要求：项目负责人必须是投标人本单位正式职工。</p><p style=\"text-indent: 2em;\">3、本次招标不接受联合体投标。</p><p style=\"text-indent: 2em;\">4、财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</p><p style=\"text-indent: 2em;\">5、信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</p><p style=\"text-indent: 2em;\">6、投标申请人须保证项目负责人及代理人均为本单位的正式职工，并确保从投标截止之日当月向前连续6个月均已在本单位缴纳养老保险（例：如开标日期为2020年7月的某一天，则投标单位需保证以上人员2020年1-6月养老保险均在本单位缴纳）。</p><p style=\"text-indent: 2em;\">六、设备品质要求：投标设备必须是质量好、性能优的知名品牌产品，其中，吸顶式空调招标人所选参考品牌为美的、格力、海尔。</p><p style=\"text-indent: 2em;\">七、投标保证金：</p><p style=\"text-indent: 2em;\">本项目投标保证金为人民币伍仟元整，具体交纳方式详见招标文件约定。</p><p style=\"text-indent: 2em;\">八、本项目评标办法采用“合理低价法”。</p><p style=\"text-indent: 2em;\">九、投标报名及招标文件领取时间和地点</p><p style=\"text-indent: 2em;\">请符合要求的潜在投标人于2021年5月15日至2021年5月20日（每日上午8：30时至12：00时，下午14：30时至18：00时）携带单位介绍信原件（或授权委托书原件，注明联系人电话及邮箱）至盐城市招标代理有限公司盐城市钱江财富广场A座4F</p><p style=\"text-indent: 2em;\">十、联系方式</p><p style=\"text-indent: 2em;\">建设单位（盖章）：盐城市第一人民医院 </p><p style=\"text-indent: 2em;\">联系人：张春鹏</p><p style=\"text-indent: 2em;\">联系电话：13305104996</p><p style=\"text-indent: 2em;\">地址：盐城市人民南路66号</p><p style=\"text-indent: 2em;\"></p><p style=\"text-indent: 2em;\">招标代理机构（盖章）：盐城市招标代理有限公司</p><p style=\"text-indent: 2em;\">联系人：吴满霞</p><p style=\"text-indent: 2em;\">联系电话：0515-89803566 15950298580</p><p style=\"text-indent: 2em;\">地 址：盐城市钱江财富广场A座4F</p><p style=\"text-indent: 2em;\"></p><p><br /></p>','','','','','');
