INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1958','2778','1','','','盐城市第一人民医院主院区净化空调系统运行及维保服务项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。1、项目概况与招标内容1.1项目名称：盐城市第一人民医院主院区净化空调系统运行及维保服务项目。1.2项目地点：盐城','192.168.162.110','192.168.162.110','1619625600','<p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><a name=\"_Hlk60902728\"></a><span style=\"background:white\">盐城市第一人民医院主院区净化空调系统运行及维保服务项目</span><span style=\"background:white\">，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">1、项目概况与招标内容</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">1.1项目名称：盐城市第一人民医院主院区净化空调系统运行及维保服务项目。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">1.2项目地点：盐城市第一人民医院主院区（人民南路66号）</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">1.3项目简要说明：盐城市第一人民医院主院区净化空调系统运行及维保服务项目，主要包含但不限于：（1）净化空调系统（风系统、水系统、净化机组、排风机组等）；强电系统（净化空调强电系统、照明、插座、配电柜、隔离变压器等）；弱电系统（净化空调弱电控制系统：包括DDC控制器、上位机、各类传感器、保护装置、执行器、数模转换器以及显示操作面板等；监控系统：包括监控主机、电源、摄像头、显示器、线路等；呼叫系统：包括电源、主机、喇叭、分机、麦克风等）。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">（2）建筑装饰（顶板、墙面、地板、防撞带、踢脚线、检修口等）、各类门（自动门、手推移门、手拉门、门禁等）；医用气体系统（设备带、气体吊塔等）以及洁净室内的保温、保冷库、器械柜、药品柜、写字板、传递窗、观察窗、洗手盆、洗手池、盐水挂构、水龙头等与净化工程相关范围内的一切工作的日常巡检，维修、维护保养工作。具体内容详见招标人提供的服务要求。</span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-family:宋体\">1.4<span style=\"background:white\">服务期：1年。</span></span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-family:宋体;background:white\">1.5服务地点：盐城市第一人民医院主院区。</span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-family:宋体;background:white\">1.6本项目设定最高限价为<span style=\"text-decoration:underline;\">28</span>万元。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">2、投标人资格要求</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">2.1投标人须是具备建筑机电安装<a name=\"_GoBack\"></a>工程专业承包三级及以上资质及电子与智能化工程专业承包二级及以上资质的独立法人。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">2.2</span><span style=\"font-family:\'&amp\">未被</span>“<span style=\"font-family:\'&amp\">信用中国</span>”<span style=\"font-family:\'&amp\">网站（www.creditchina.gov.cn）列入失信被执行人、重大税收违法案件当事人名单、政府采购严重失信行为记录名单</span>。</p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\">2.3本项目<span style=\"text-decoration:underline;\">不接受</span>联合体参与磋商。</p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:32px;margin-bottom:0;line-height:33px;background:white\"><span style=\"background:white\">3、投标报名及招标文件的获取</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话及邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投标报名，投标报名时间2021年</span><span style=\";background:white\">4</span><span style=\"background:white\">月</span><span style=\";background:white\">29</span><span style=\"background:white\">日8时至2021年</span><span style=\";background:white\">5</span><span style=\"background:white\">月</span><span style=\";background:white\">8</span><span style=\"background:white\">日18时。联系人：刘永杰，0515-69057406；手机：13626222271。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\";background:white\">4</span><span style=\"background:white\">、本项目采用竞争性</span><span style=\";background:white\">磋商</span><span style=\"background:white\">方式。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\";background:white\">5</span><span style=\"background:white\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\";background:white\">6</span><span style=\"background:white\">、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联</span></p><p style=\";margin-bottom:0;line-height:33px;background:white\"><span style=\"background:white\">系电话0515-66696810。</span></p><p></p><p></p><p></p><p style=\"text-align:right\"><span style=\"font-family: 宋体\">盐城市第一人民医院</span></p><p style=\"text-align:right\">2021<span style=\"font-family:宋体\">年</span>4<span style=\"font-family: 宋体\">月</span>29<span style=\"font-family: 宋体\">日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1959','2779','1','','','盐城市第一人民医院主院区排污泵维保项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。1.项目概况与招标内容1.1项目名称：盐城市第一人民医院主院区排污泵维保项目1.2招标内容：主院区排污泵维保项目，主要为排污泵清理、定','192.168.162.110','192.168.162.110','1619625600','<p style=\"text-align:left;text-indent:32px;line-height:28px\"><span style=\"font-size:16px;font-family:宋体\">盐城市第一人民医院主院区排污泵维保项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</span></p><p style=\"margin-left:28px;text-align:left;line-height:28px\"><span style=\"font-size:16px;font-family:宋体\">1.</span><span style=\"font-size:16px;font-family: 宋体\">项目概况与招标内容</span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-size:16px;font-family:宋体\">1.1</span><span style=\"font-size:16px;font-family:宋体\">项目名称：盐城市第一人民医院主院区排污泵维保项目</span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-size:16px;font-family:宋体\">1.2</span><span style=\"font-size:16px;font-family:宋体\">招标内容：主院区排污泵维保项目，主要为排污泵清理、定子绕组维修、轴承更换、风机叶片更换、叶轮维修、机械密封、水位控制装置的维修、支架更换维修、电气控制配件的更换、及相关配件的更换、电机保养，配电箱巡查等以及无法维修的水泵免费换新等<span style=\"background:white\">。具体内容详见招标人提供的工程量清单。</span></span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-size:16px;font-family:宋体\">1.3</span><span style=\"font-size:16px;font-family:宋体\">服务地点：<span style=\"background:white\">盐城市第一人民医院主院区（人民南路66号）。</span></span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-size:16px;font-family:宋体\">1.4</span><span style=\"font-size:16px;font-family:宋体\">质量要求：符合国家“合格”标准，维保期1年，维保期内若清单内排污泵发生故障，由中标人负责修复；无法修复的由中标人免费换新，所有费用均由中标人承担。维保期满经院方考核合格后可根据相关法律法规续签合同。</span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-size:16px;font-family:宋体\">1.5</span><span style=\"font-size:16px;font-family:宋体\">发包方式：包工包料。</span></p><p style=\"text-align:left;text-indent:32px;line-height:28px\"><strong><span style=\"font-size:16px;font-family:宋体\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、投标人应当具备的主要资格条件：</span></strong></p><p style=\";margin-bottom:0;text-indent:32px;line-height:28px;background:white\"><span style=\"font-family:宋体\">投标人具有独立法人资格，营业执照经营范围包含机电设备或电机、水泵安装或维修等内容且具备履行合同所必需的设备和专业技术能力。</span></p><p style=\"text-align:left;text-indent:32px;line-height:28px\"><strong><span style=\"font-size:16px;font-family:宋体\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、投标报名及招标文件的获取</span></strong></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;background:white\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投标报名并领取招标文件，投标报名时间2021年4月29日8时至2021年5月8日18时。联系人：刘永杰，0515-69057406；手机：13626222271。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;background:white\">4</span><span style=\"font-family:宋体;background:white\">、本项目采用竞争性谈判方式。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;background:white\">5</span><span style=\"font-family:宋体;background:white\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;background:white\">6</span><span style=\"font-family:宋体;background:white\">、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;background:white\">其余内容详见招标文件。</span></p><p style=\"text-align:right;background: white\"><span style=\"font-family:宋体;background:white\"> </span><span style=\"font-family:宋体;background:white\">招标人：盐城市第一人民医院</span></p><p style=\"text-align:right;background: white\"><span style=\"font-family:宋体;background:white\"> </span><span style=\"font-family:宋体;background:white\">时间：2021年4月29日 </span></p><p><br /></p>','','','','','');
