INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1982','2802','1','','','因医院实际工作需要，于2021年3月30日进行招标的项目，经评标小组评审，中标结果如下：一、盐城市第一人民医院电工材料采购项目第一名：盐城市亭湖区城南文之睿灯具商行中标价：16.9583万元第二名：盐城市新都商贸城光之源灯具经营部中标价：23.63万元第三名：盐城市铭','192.168.162.110','192.168.162.110','1617292800','<p style=\";margin-bottom:0;text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">因医院实际工作需要，于2021年3月30日进行招标的项目，经评标小组评审，中标结果如下：</span></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:28px;margin-bottom:0;text-indent:0;line-height:37px\"><strong><span style=\"font-family:宋体;color:#333333\">一、</span></strong><strong><span style=\"font-family:宋体;color:#333333\">盐城市第一人民医院电工材料采购项目</span></strong></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">第一名：盐城市亭湖区城南文之睿灯具商行</span></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">中标价：16.9583万元</span></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">第二名：盐城市新都商贸城光之源灯具经营部</span></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">中标价：23.63万元</span></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">第三名：盐城市铭一鑫商贸有限公司</span></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">中标价：24.699万元</span></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:28px;margin-bottom:0;text-indent:0;line-height:37px\"><strong><span style=\"font-family:宋体;color:#333333\">二、</span></strong><strong><span style=\"font-family:宋体;color:#333333\">盐城市第一人民医院水工材料采购项目 </span></strong></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">第一名：盐城市涛盈建材有限公司</span></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">中标价：31.1381万元</span></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">第二名：盐城市乐开卫浴有限公司</span></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">中标价：32.31476万元</span></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">第三名：盐城装饰装潢材料市场洁利来卫浴经营部</span></p><p style=\"text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">中标价：35.12369万元</span></p><p style=\"margin-left:28px;text-indent: 0;line-height:37px\"><strong><span style=\"font-family:宋体;color:#333333\">三、</span></strong><strong><span style=\"font-family:宋体;color:#333333\">盐城市第一人民医院零星布类物品采购项目</span></strong></p><p style=\"margin-left:28px;text-indent: 0;line-height:37px\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; text-indent: 32px;\">盐城市第一人民医院零星布类物品采购项目经评标小组复议，投标人提供的样品不符合采购文件工程量清单的要求，按相关规定作流标处理。</span></p><p style=\"margin-left:28px;text-indent: 0;line-height:37px\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; text-indent: 32px;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监督审查室联系，电话0515-66696810。  </span></p><p style=\";margin-bottom:0;text-align:right;text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\">盐城市第一人民医院</span></p><p style=\";margin-bottom:0;text-align:right;text-indent:32px;line-height:37px\"><span style=\"font-family:宋体;color:#333333\"> 2021</span><span style=\"font-family: 宋体;color:#333333\">年4月2日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1983','2803','1','','','盐城市第一人民医院主院区净化空调系统运行及维保服务项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。1、项目概况与招标内容1.1项目名称：盐城市第一人民医院主院区净化空调系统运行及维保服务项目。1.2项目地点：盐城','192.168.162.110','192.168.162.110','1616515200','<p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><a name=\"_Hlk60902728\"></a><span style=\"color:black;background:white\">盐城市第一人民医院主院区净化空调系统运行及维保服务项目</span><span style=\"color:black;background:white\">，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"color:black;background:white\">1、项目概况与招标内容</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"color:black;background:white\">1.1项目名称：盐城市第一人民医院主院区净化空调系统运行及维保服务项目。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"color:black;background:white\">1.2项目地点：</span><span style=\"background:white\">盐城市第一人民医院主院区（人民南路66号）</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"color:black;background:white\">1.3项目简要说明：盐城市第一人民医院主院区净化空调系统运行及维保服务项目，主要包含但不限于：（1）净化空调系统（风系统、水系统、净化机组、排风机组等）；强电系统（净化空调强电系统、照明、插座、配电柜、隔离变压器等）；弱电系统（净化空调弱电控制系统：包括DDC控制器、上位机、各类传感器、保护装置、执行器、数模转换器以及显示操作面板等；监控系统：包括监控主机、电源、摄像头、显示器、线路等；呼叫系统：包括电源、主机、喇叭、分机、麦克风等）。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"color:black;background:white\">（2）建筑装饰（顶板、墙面、地板、防撞带、踢脚线、检修口等）、各类门（自动门、手推移门、手拉门、门禁等）；医用气体系统（设备带、气体吊塔等）以及洁净室内的保温、保冷库、器械柜、药品柜、写字板、传递窗、观察窗、洗手盆、洗手池、盐水挂构、水龙头等与净化工程相关范围内的一切工作的日常巡检，维修、维护保养工作。具体内容详见招标人提供的服务要求。</span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-family:宋体\">1.4<span style=\"color:black;background:white\">服务期：1年。</span></span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-family:宋体;color:black;background:white\">1.5服务地点：盐城市第一人民医院主院区。</span></p><p style=\"text-indent:32px;line-height:28px\"><span style=\"font-family:宋体;color:black;background:white\">1.6本项目设定最高限价为<span style=\"text-decoration:underline;\">28</span>万元。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"color:black;background:white\">2、投标人资格要求</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"color:black;background:white\">2.1投标人须是具备建筑机电安装工程专业承包三级及以上资质及电子与智能化工程专业承包二级及以上资质的独立法人。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"color:black;background:white\">2.2</span><span style=\"font-family:\'&color:black\">未被</span><span style=\"color:black\">“</span><span style=\"font-family:\'&color:black\">信用中国</span><span style=\"color:black\">”</span><span style=\"font-family:\'&color:black\">网站（www.creditchina.gov.cn）列入失信被执行人、重大税收违法案件当事人名单、政府采购严重失信行为记录名单</span><span style=\"color:black\">。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"color:black\">2.3本项目<span style=\"text-decoration:underline;\">不接受</span>联合体参与磋商。</span></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:32px;margin-bottom:0;line-height:33px;background:white\"><span style=\"background:white\">3、投标报名及招标文件的获取</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话及邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投<a name=\"_GoBack\"></a>标报名，投标报名时间2021年3月24日8时至2021年3月29日18时。联系人：刘永杰，0515-69057406；手机：13626222271。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">5、本项目采用竞争性</span><span style=\";background:white\">磋商</span><span style=\"background:white\">方式。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:33px;background:white\"><span style=\"background:white\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联</span></p><p style=\";margin-bottom:0;line-height:33px;background:white\"><span style=\"background:white\">系电话0515-66696810。</span></p><p></p><p></p><p></p><p style=\"text-align:right\"><span style=\"font-family: 宋体\">盐城市第一人民医院</span></p><p style=\"text-align:right\"><span style=\"font-family:宋体\">2021</span><span style=\"font-family:宋体\">年3月24日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('1984','2804','1','','','1、盐城市第一人民医院五官科医院膜结构雨棚制作安装项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院五官科医院膜结构雨棚制作安装项目。2.2项目地点：盐城市第一','192.168.162.110','192.168.162.110','1616515200','<p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">1</span><span style=\"font-family:宋体;color:black;background:white\">、盐城市第一人民医院五官科医院膜结构雨棚制作安装项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2</span><span style=\"font-family:宋体;color:black;background:white\">、项目概况与招标内容</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2.1</span><span style=\"font-family:宋体;color:black;background:white\">项目名称：盐城市第一人民医院五官科医院膜结构雨棚制作安装项目。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2.2</span><span style=\"font-family:宋体;color:black;background:white\">项目地点：盐城市第一人民医院</span><span style=\"font-family: 宋体;background:white\">五官科医院（南城河路12号）。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2.3</span><span style=\"font-family:宋体;color:black;background:white\">招标内容：五官科医院体检中心北侧制作安装膜结构非机动车雨棚。具体内容详见招标人提供的工程量清单。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2.4</span><span style=\"font-family:宋体;color:black;background:white\">发包方式：包工包料。</span></p><