INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2025','2845','1','','','根据2019年11月7日发布的《盐城市卫生健康委员会部分直属事业单位2019年下半年赴外公开招聘医学人才公告》（盐人社事招公告核〔2019〕9号），盐城市第一人民医院部分招聘岗位（上海及南京招聘点）未达成开考条件或未完成招聘计划。因工作需要，经研究决定，对上述公告中','192.168.162.110','192.168.162.110','1576252800','<p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\"><span style=\"font-family: 宋体;\">根据</span>2019<span style=\"font-family: 宋体;\">年</span>11<span style=\"font-family: 宋体;\">月</span>7<span style=\"font-family: 宋体;\">日发布的《盐城市卫生健康委员会部分直属事业单位</span>2019<span style=\"font-family: 宋体;\">年下半年赴外公开招聘医学人才公告》（盐人社事招公告核〔</span>2019<span style=\"font-family: 宋体;\">〕</span>9<span style=\"font-family: 宋体;\">号），盐城市第一人民医院部分招聘岗位（上海及南京招聘点）未达成开考条件或未完成招聘计划。因工作需要，经研究决定，对上述公告中肿瘤内科等</span>63<span style=\"font-family: 宋体;\">个岗位重新组织公开招聘，现发布补充公告如下：</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">1. <span style=\"font-size: 20px; font-family: 宋体;\">补报名时间：</span>2019<span style=\"font-size: 20px; font-family: 宋体;\">年</span>12<span style=\"font-size: 20px; font-family: 宋体;\">月</span>26<span style=\"font-size: 20px; font-family: 宋体;\">日至</span>12<span style=\"font-size: 20px; font-family: 宋体;\">月</span>28<span style=\"font-size: 20px; font-family: 宋体;\">日（周六正常接受报名）；</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">2. <span style=\"font-size: 20px; font-family: 宋体;\">报名地点：盐城市第一人民医院主院区（人民南路</span>166<span style=\"font-size: 20px; font-family: 宋体;\">号，门诊楼四楼人事处）；</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">3. <span style=\"font-size: 20px; font-family: 宋体;\">联系电话：</span>0515-66696806<span style=\"font-size: 20px; font-family: 宋体;\">；</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">4. <span style=\"font-size: 20px; font-family: 宋体;\">笔试、面试时间及地点将由盐城市第一人民医院另行通知；</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">5. <span style=\"font-size: 20px; font-family: 宋体;\">调整后招聘岗位表，详见附件。</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">6. <span style=\"font-size: 20px; font-family: 宋体;\">《盐城市卫生健康委员会部分直属事业单位</span>2019<span style=\"font-size: 20px; font-family: 宋体;\">年下半年赴外公开招聘医学人才公告》（盐人社事招公告核〔</span>2019<span style=\"font-size: 20px; font-family: 宋体;\">〕</span>9<span style=\"font-size: 20px; font-family: 宋体;\">号）中的其他条件保持不变。</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">特此公告。</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">附件：</span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">1<span style=\"font-size: 20px; font-family: 宋体;\">、</span><a href=\"http://wsj.yancheng.gov.cn/module/download/downfile.jsp?classid=0&amp;filename=8cb79e644915433da492867f67156c2d.xls\"><span style=\"font-size: 20px; font-family: 微软雅黑, sans-serif; color: rgb(51, 51, 51); background: white;\">盐城市第一人民医院2019年下半年公开招聘医学人才岗位表（补充）</span></a></span></p><p style=\"text-align: left; text-indent: 40px; line-height: 3em;\"><span style=\"font-size: 20px;\">2<span style=\"font-size: 20px; font-family: 宋体;\">、</span>2019<span style=\"font-size: 20px; font-family: 宋体;\">年</span>11<span style=\"font-size: 20px; font-family: 宋体;\">月</span>7<span style=\"font-size: 20px; font-family: 宋体;\">日盐城市人力资源和社会保障局发布的《盐城市卫生健康委员会部分直属事业单位</span>2019<span style=\"font-size: 20px; font-family: 宋体;\">年下半年赴外公开招聘医学人才公告》（盐人社事招公告核〔</span>2019<span style=\"font-size: 20px; font-family: 宋体;\">〕</span>9<span style=\"font-size: 20px; font-family: 宋体;\">号）原文链接</span></span></p><p style=\"text-align: left; line-height: 3em;\"><span style=\"font-size: 20px;\">http://jsychrss.yancheng.gov.cn/art/2019/11/7/art_3431_3288951.html</span></p><p style=\"text-align: left; text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\"></span></p><p style=\"text-align: left; text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\"></span></p><p style=\"text-align: right; text-indent: 43px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">盐城市卫生健康委员会</span></p><p style=\"text-align: right; text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">2019<span style=\"font-size: 20px; font-family: 宋体;\">年</span>12<span style=\"font-size: 20px; font-family: 宋体;\">月</span>13<span style=\"font-size: 20px; font-family: 宋体;\">日</span></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2026','2846','1','','http://www1.ycsyy.com/upload/images/2019/12/b1b902adba26bdb1.png','根据《2019年下半年公开招聘编外专业技术人员公告》，我院于11月30日进行了招聘笔试和面试，现将进入体检、考察人员名单予以公布，根据体检和考察结果，确定拟聘用人员名单。 对本公布名单如有异议可向医院纪委监察室反映。时间12月4日-12月10日。监督电话：0515-666968','192.168.162.110','192.168.162.110','1575475200','<p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">根据《2019年下半年公开招聘编外专业技术人员公告》，我院于11月30日进行了招聘笔试和面试，现将进入体检、考察人员名单予以公布，<span style=\"font-family: 宋体, SimSun; color: black; background: white;\">根据体检和考察结果，确定拟聘用人员名单。 </span></span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">对本公布名单如有异</span><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">议可向医院纪委监察室反映。时间12月4日-12月10日。</span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">监督电话：0515-66696810(市第一人民医院纪检监察室)；</span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">咨</span><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\"></span><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">询电话：0515-66696806（市第一人民医院人事处）</span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/12/b1b902adba26bdb1.png\" title=\"image.png\" alt=\"image.png\" /></span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><strong style=\"font-family: 宋体, SimSun; font-size: 20px; text-align: center;\"><span style=\"font-size: 21px; font-family: 宋体; color: rgb(51, 51, 51);\"></span></strong></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2027','2847','1','','','根据《盐城市第一人民医院2019年公开招聘编外护理专业技术人员公告（第二批）》公告要求，在盐城市卫生健康委员会指导下，盐城市第一人民医院依次组织了2019年第二批编外护理人员招聘报名、复审、笔试、面试、实践操作考核、体检、政审等招聘工作环节，现根据公告将拟录','192.168.162.110','192.168.162.110','1574784000','<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">根据《盐城市第一人民医院2019年公开招聘编外护理专业技术人员公告（第二批）》公告要求，在盐城市卫生健康委员会指导下，盐城市第一人民医院依次组织了2019年第二批编外护理人员招聘报名、复审、笔试、面试、实践操作考核、体检、政审等招聘工作环节，现根据公告将拟录用的58名护理人员名单面向社会公示。</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 0em;\">一、</span><span style=\"font-family: 宋体; font-size: 20px; text-indent: 0em;\">应届生本科</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">蔡银婷 陈荣誉 陈俞静 季倩 吕国跃 秦吕敏 徐榕梓 杨健峰</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">张进 庄朋朋</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\"><br /></span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">二、应届生大专</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">卞玉霞 卜雪 陈<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>枫 陈晓梦 丁<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>洁 杲禹香 葛梦琦 谷建娇</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">胡<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>婧 花璐皇甫立胜吉<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>琳 李青青 李<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>庆 李少荣 李伟唯</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">沈树萍 史洋洋 王慧林 王健国 王<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>盛 韦<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>鑫 吴玉秋 邢冬梅</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">徐盼盼 严<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>悦 虞梦琦 张红秀 赵<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>茵 周倩霞 </span></p><p style=\"margin-left: 28px; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"> </span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、成熟型护理人才</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">何<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>晨 金晓雨 李<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>慧 李<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>鹂 李赛男 梁<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>益 刘孝佐 孙<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>超</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">孙<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>江 王<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>宏 王<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>瑄 徐晓婷 徐<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>玉 薛媛媛 於晓晨 于<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>潇</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">朱<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>婷 朱惟惟 </span></p><p style=\"margin-left: 28px; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"> </span></p><p style=\"margin-left: 28px; text-align: right; text-indent: 0em;\" dir=\"ltr\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">公示时间：2019年11月25日—2019年12月1日</span></p><p style=\"margin-left: 28px; text-align: right; text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">监督电话：0515-66696806（市一院人事处</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">）</span></p><p style=\"margin-left: 28px; text-align: right; text-indent: 0em;\" dir=\"ltr\"><span style=\"font-family: 宋体; font-size: 20px;\">0515-66696810（市一院监察室）</span></p><p style=\"margin-left: 28px; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2028','2848','1','','','为加强卫生健康系统人才队伍建设，更好地选拔优秀适岗人才，优化人员结构，盐城市第一人民医院现面向社会公开招聘专业技术人员。根据《江苏省事业单位公开招聘人员办法》（苏办发〔2011〕46号）等规定，并经盐城市人力资源和社会保障局核准，现将有关事项公告如下：一、','192.168.162.110','192.168.162.110','1573488000','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为加强卫生健康系统人才队伍建设，更好地选拔优秀适岗人才，优化人员结构，盐城市第一人民医院现面向社会公开招聘专业技术人员。根据《江苏省事业单位公开招聘人员办法》（苏办发〔2011〕46号）等规定，并经盐城市人力资源和社会保障局核准，现将有关事项公告如下：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、报考条件</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1．具有中华人民共和国国籍，享有公民的政治权利；坚持四项基本原则，拥护党的路线、方针、政策；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2．遵纪守法，品行端正，团结同志，廉洁奉公；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3．适应岗位要求的身体条件；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4．年龄在35周岁以下（1983年11月21日及以后出生）。对年龄有特殊要求的，以岗位表中注明的为准；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5．具备报考岗位要求的其他资格条件（详见《盐城市卫生健康委员会部分直属事业单位2019年下半年公开招聘专业技术人员岗位表》）；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6．取得祖国大陆全日制普通高校学历的台湾学生和取得祖国大陆承认学历的其他台湾居民应聘时按国家和江苏省的有关规定执行；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">7．盐城市范围内医疗卫生单位在编人员不得报考。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、报名</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1．报名时间、方式、地点</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">报名时间：2019年11月21日至11月23日。上午8：30～11：30，下午2：30～5：30。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">报名方式：现场报名。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">报名地点：盐城市第一人民医院主院区（人民南路166号，门诊楼四楼人事处）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2．报名注意事项及资格审查</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）每位报考者限报考一个岗位。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（2）报考者报名时须提交下列材料：①本人近期一寸免冠正面彩照3张；②《盐城市卫生健康委部分直属事业单位2019年下半年公开招聘专业技术人员报名表》；③身份证；④学历、学位证书【含第一学历，在国（境）外取得的学历学位须取得教育部中国留学服务中心的认证证明】、专业方向证明等；⑤其它材料：岗位报名需要的执业资格证书、专业技术资格证书等。（③、④、⑤均提供复印件同时备原件核查，报名时由用人单位收取相关复印件，资格复审时必须提供原件）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">报考岗位要求具备工作经历的，需提供所在单位出具的工作经历证明，工作经历计算的基准时间为2019年11月30日。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（3）报考者在规定时间内到报名地点报名，市卫生健康委和用人单位负责资格审核。研究生、副高级及中级职称人员岗位设1：2开考比例，其他岗位设1：3开考比例，招聘岗位数与通过资格审核人数达不到开考比例的，相应核减岗位招聘计划数，直至取消该岗位招聘计划。报考被取消岗位的人员，可在规定时间内改报其他符合条件的岗位。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（4）应聘人员对所提交材料的真实性、准确性、完整性、有效性负责。凡有弄虚作假者，一经查实，取消应聘资格。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、考试科目、时间和实施办法</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">考试由市卫生健康委和用人单位共同组织实施。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1．研究生、副高级职称人员岗位</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">考试采取结构化面试和专业化面试相结合的形式进行，主要考核招聘岗位必备的专业知识和应聘者的综合素质能力。面试总分为100分，设60分合格线，低于合格线者不予录用。面试成绩当场公布。面试具体时间、地点另行通知。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2．其他岗位</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">考试采取笔试、面试相结合的形式。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）笔试：应聘人员参加专业理论考试，笔试卷面总成绩的50%为笔试合格分数线。笔试具体时间、地点另行通知。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（2）资格复审：面试前，用人单位及市卫生健康委对进入面试人选进行资格复核。凡考生提供的证明材料影响资格复核结果的，取消其面试资格，并在报考同岗位的笔试合格人员中从高分到低分依次递补面试人选。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（3）面试：面试采取结构化面试的形式进行，在笔试成绩合格人员中，按照招聘岗位1：3的比例从高分到低分确定参加面试的人员（含末位同分者），不足1：3的按照实际人数确定面试人选。面试总分100分，设60分合格线。面试成绩当场公布。面试具体时间、地点另行通知。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3．总成绩计算方式</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">考试结束后，直接面试岗位面试成绩即为总成绩；有笔试岗位，按笔试成绩（百分制折算）50%、面试成绩50%的比例计算考试总成绩。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、聘用</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1．体检。考试结束后，根据考试总成绩，按招聘岗位拟招聘人数1：1的比例从高分到低分确定参加体检人选。总成绩相同时，取面试成绩高者；总成绩、面试成绩均相同，加试决出名次（加试时间、方式、地点另行通知）。体检标准参照《国家公务员录用体检通用标准（试行）》执行。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2．考察。对体检合格人员由市卫生健康委和用人单位进行考察（考察参照江苏省公务员录用考察标准执行）。因个人放弃、体检考察不合格等原因出现招聘岗位空缺时，在该岗位的考试合格人员中，按总成绩从高分到低分依次递补（成绩相同取舍办法同前）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3．公示。根据体检和考察结果，确定拟聘用人员名单，并在市人力资源和社会保障网站、市卫生健康委网站上公示7个工作日。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4．聘用。对公示无异议人员，办理有关聘用手续，聘用审批后不再递补，试用期及工资福利待遇按照有关规定执行。聘用人员实行6个月试用期（2019届毕业生试用期延长至12个月），试用期满考核合格，予以定职定级。考核不合格者，取消聘用资格，终止聘用关系。2019届毕业生岗位连续两次未能通过考试取得岗位执业资格的，自动中止聘用关系。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019届毕业生于2019年12月31日前提供毕业证书、学位证书，逾期不能提供的，取消聘用资格。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、纪律监督</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1．招聘工作坚持公开、公平、公正的原则，全过程接受纪检、监察部门的监督。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2．本次招聘按《江苏省事业单位公开招聘人员办法》（苏办发〔2011〕46号）第七章第三十八条规定实行回避。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">监督电话：0515-80501632（盐城市纪委监委第十五派驻纪检监察组）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">咨询电话：0515-88336054（盐城市卫健委组织人事处） </span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">0515-80500736（盐城市人社局事管处）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; text-decoration: underline;\"><a href=\"http://jsychrss.yancheng.gov.cn/module/download/downfile.jsp?classid=0&amp;filename=be3607feaaa040078689c66510610194.xls\" style=\"text-decoration: underline; font-family: 宋体, SimSun; font-size: 20px;\">附件1：</a></span></span><a href=\"http://www1.ycsyy.com/upload/files/2019/11/e122f71267c2e36f.xls\" title=\"2019年下半年公开招聘硕士岗位表.xls\" style=\"color: rgb(0, 102, 204); text-decoration: underline; font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2019年下半年公开招聘硕士岗位表.xls</span></a></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"text-decoration: underline; font-family: 宋体, SimSun; font-size: 20px;\"><a href=\"http://jsychrss.yancheng.gov.cn/module/download/downfile.jsp?classid=0&amp;filename=c9cf0ee45cfe4d3a8ed6866bedb85898.doc\" style=\"text-decoration: underline; font-family: 宋体, SimSun; font-size: 20px;\">附件2：</a></span></span><a href=\"http://www1.ycsyy.com/upload/files/2019/11/66a1d6cbe6e49541.doc\" title=\"2019年下半年公开招聘专业技术人员报名表.doc\" style=\"color: rgb(0, 102, 204); text-decoration: underline; font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2019年下半年公开招聘专业技术人员报名表.doc</span></a></p><p style=\"line-height: 3em; text-indent: 2em;\"><a href=\"http://jsychrss.yancheng.gov.cn/module/download/downfile.jsp?classid=0&amp;filename=c9cf0ee45cfe4d3a8ed6866bedb85898.doc\" style=\"text-decoration: underline; font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></a><br /></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市卫生健康委员会</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年11月12日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2029','2849','1','','','为加强医院专业技术人员队伍建设，市第一人民医院现面向社会公开招聘临床、医技专业编外专业技术人员20名。现将有关事项公告如下：一、招聘条件1.具有中华人民共和国国籍，享有公民的政治权利；坚持四项基本原则，拥护党的路线、方针、政策；2.遵纪守法，品行端正，团结','192.168.162.110','192.168.162.110','1573142400','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为加强医院专业技术人员队伍建设，市第一人民医院现面向社会公开招聘临床、医技专业编外专业技术人员20名。现将有关事项公告如下：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、招聘条件</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.具有中华人民共和国国籍，享有公民的政治权利；坚持四项基本原则，拥护党的路线、方针、政策；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.遵纪守法，品行端正，团结同志，廉洁奉公；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.适应岗位要求的身体条件；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.招聘对象为国家计划内招收的全日制普通高校大专及以上学历毕业生，年龄在30周岁及以下（1988年11月21日及以后出生）。具体报考岗位要求的资格条件详见《盐城市第一人民医院2019年下半年公开招聘编外专业技术人员岗位表》；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5.取得祖国大陆全日制普通高校学历的台湾学生和取得祖国大陆承认学历的其他台湾居民应聘时按国家和江苏省的有关规定执行。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、报名</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（一）报名时间、方式、地点</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">报名时间：2019年11月20日至11月21日上午8:00-11:30，下午2:00-5:30。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">报名方式：现场报名。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">报名地点：盐城市第一人民医院主院区人事处（人民南路66号门诊楼四楼），联系电话：0515-66696806。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（二）报名注意事项及资格审查</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.每位报考者限报考一个岗位。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.报考者报名时须提交下列材料：①本人近期一寸免冠正面彩照3张；②《盐城市第一人民医院2019年下半年公开招聘编外专业技术人员报名登记表》；③身份证；④毕业证书【在国（境）外取得的学历学位须取得教育部中国留学服务中心的认证证明】；⑤其它材料：岗位报名需要的执业资格证书、相关工作经历证明（工作经历截止时间为2019年11月30日）等；⑥外省院校毕业生还需提供国家计划内统招证明材料，如录取通知书、省教委验证的录用花名册等。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">上述材料需提供原件和复印件各一份，报名时由医院人事处会同监察室进行资格审核并收取相关材料。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.报考者在规定时间内到报名地点报名。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.应聘人员对所提交材料的真实性、准确性、完整性、有效性负责。凡有弄虚作假者，一经查实，取消应聘资格。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5.通过资格审查的报名人员现场缴纳报名费100元。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、考试方式</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">考试采取笔试、面试相结合的形式。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">1.笔试：笔试内容为应聘岗位所需专业知识，笔试总分100分，设50分合格线。笔试具体时间、地点另行通知。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.资格复审：面试前，医院人事处会同监察室对进入面试人选进行资格复核。凡考生提供的证明材料影响资格复核结果的，取消其面试资格，并在报考同岗位的笔试合格人员中从高分到低分依次递补面试人选。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.面试：采取结构化面试方式进行。在笔试成绩合格人员中，按照招聘岗位1：3的比例从高分到低分确定参加面试的人员（含末位同分者），不足1：3的按照实际人数确定面试人选。面试总分100分，设60分合格线，低于合格线者不予录用。面试成绩当场公布。面试时间、地点另行通知。<br /> 4.总成绩计算方式：考试结束后，按笔试占50%、面试占50%的比例计算考试总成绩。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">用人单位根据招聘报名等情况，制定招聘工作方案报市卫健委备案后，由用人单位具体组织实施。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、聘用</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.根据考试总成绩，按招聘岗位拟招聘人数1:1的比例从高分到低分确定参加体检人选（总成绩分数相同时，取面试成绩高者；总成绩、面试成绩均相同，加笔试决出名次，加试时间、地点另行通知）。体检标准参照《国家公务员录用体检通用标准（试行）》执行。对体检合格人员由用人单位组织考察，并根据体检和考察结果，确定拟聘用人员名单。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.因个人原因放弃体检考察资格或体检考察不合格等情况出现招聘岗位空缺时，在该岗位考试合格人员中，按总成绩从高分到低分依次递补（成绩相同取舍办法同前）。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.根据体检和考察结果确定拟聘用人员名单，并在医院网站和医院事务公示栏内公示不少于7个工作日，接受社会监督。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.对公示无异议人员，用人单位按规定为其办理就业手续，并与其签订劳动合同。聘用人员如试用期间考核不合格或连续两年内未能取得相应的执业资格或专业技术资格则予以解聘。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、纪律监督</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招聘工作坚持“公开公正、竞争择优”的原则，严格执行有关纪律，秉公办事，阳光操作，并自觉接受纪检监察部门和社会的监督。如发现弄虚作假或有违规、违纪行为，将取消相关应聘人员的录用资格，并对有关当事人给予严肃的处理。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本次招聘按《江苏省事业单位公开招聘人员办法》（苏办发[2011]46号）第七章第三十八条规定实行回避。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">监督电话：0515-66696810、88508906(市第一人民医院纪检监察室)；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">咨询电话：0515-66696806、88508907（市第一人民医院人事处）。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">附件：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"text-indent: 2em; font-family: 宋体, SimSun; font-size: 20px;\">1.</span><a href=\"http://www1.ycsyy.com/upload/files/2019/11/2472610f8d8044a2.xls\" title=\"2019年下半年公开招聘编外专业技术人员岗位表.xls\" style=\"text-indent: 2em; color: rgb(0, 102, 204); font-family: 宋体, SimSun; font-size: 20px;\">2019年下半年公开招聘编外专业技术人员岗位表.xls</a></p><p style=\"line-height: 16px; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.</span><a style=\"color: rgb(0, 102, 204); text-decoration: underline; font-family: 宋体, SimSun; font-size: 20px;\" href=\"http://www1.ycsyy.com/upload/files/2019/11/c635cc14d3982f2e.doc\" title=\"2019年下半年公开招聘编外专业技术人员报名登记表.doc\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年下半年公开招聘编外专业技术人员报名登记表.doc</span></a><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\"></span></p><p style=\"line-height: 3em; text-align: right; text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年11月8日</span></p><p><br /></p>','','','','','');
