INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2624','3448','1','','','1、盐城市第一人民医院主院区、北区零星维修项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院主院区、北区零星维修项目。2.2项目地点：盐城市第一人民医院主院区（','172.16.1.2','172.16.1.2','1578499200','<p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">1、盐城市第一人民医院主院区、北区零星维修项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2、项目概况与招标内容</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2.1项目名称：盐城市第一人民医院主院区、北区零星维修项目。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体; color: black; background: white;\">2.2项目地点：盐城市第一人民医院主院区（人民南路66号）、北区</span><span style=\"font-size: 20px; font-family: 宋体; background: white;\">（毓龙西路166号）。</span></span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2.3招标内容：北区新做一辆医疗废物转运车，北区空调班负压吸引储气罐、管道、9号楼太阳能大水箱槽钢支架除锈粉刷，北区血液净化中心四楼玻璃门更换指纹密码锁，主院区护理部实训室增加隔断、窗帘，咖啡厅、影像科玻璃更换，屋面机房玻璃门改造，主院区住院部26号电梯轿厢地板修补等项目。具体内容详见招标人提供的工程量清单。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2.4发包方式：包工包料。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">3、投标人资格要求</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">投标人须具有有效的建筑装修装饰工程专业承包二级及以上施工资质的独立法人，并具有有效的安全生产许可证。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">4、投标报名及招标文件的获取</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2020年1月9日8时至2020年1月12日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">5、本项目采用竞争性谈判方式。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"text-align: right; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招标人：盐城市第一人民医院</span></p><p style=\"text-align: right; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：2020年1月9日&nbsp; </span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2625','3449','1','','','1、盐城市第一人民医院北区污水处理站进水闸阀维修改造等项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院北区污水处理站进水闸阀维修改造等项目。2.2项目地点：盐','172.16.1.2','172.16.1.2','1578499200','<p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1、盐城市第一人民医院北区污水处理站进水闸阀维修改造等项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2、项目概况与招标内容</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.1项目名称：盐城市第一人民医院北区污水处理站进水闸阀维修改造等项目。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.2项目地点：盐城市第一人民医院主院区（人民南路66号）、北区（毓龙西路166号）、五官科医院（南城河路12号）。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.3招标内容：北区污水处理站进水闸阀维修改造，主院区锅炉房蒸汽减压阀执行器更换，北区9号楼太阳能4台热水循环泵、1台补水泵更新维修，北区血液净化中心专用水泵维修、阀门更换等项目。具体内容详见招标人提供的工程量清单。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.4发包方式：包工包料。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">3、投标人资格要求</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">投标人具备独立法人资格，且营业执照经营范围包含机电设备或通用设备安装或维修等内容。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">4、投标报名及招标文件的获取</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2020年<span style=\"font-size: 16px; font-family: 宋体, SimSun; text-decoration: underline;\">1</span>月<span style=\"font-size: 16px; font-family: 宋体, SimSun; text-decoration: underline;\">9</span>日<span style=\"font-size: 16px; font-family: 宋体, SimSun; text-decoration: underline;\">8</span>时至2020年<span style=\"font-size: 16px; font-family: 宋体, SimSun; text-decoration: underline;\">1</span>月<span style=\"font-size: 16px; font-family: 宋体, SimSun; text-decoration: underline;\">12</span>日<span style=\"font-size: 16px; font-family: 宋体, SimSun; text-decoration: underline;\">18</span>时。联系人：胡荣艳，0515-68037830；手机：13815581495。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">5、本项目采用竞争性谈判方式。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"text-align: right; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招标人：盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：2020年1月9日&nbsp;</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2626','3450','1','','','盐城市第一人民医院垃圾房工程已具备招标条件，现拟对该工程进行公开招标，择优选择施工单位。1、工程概况 1.1 工程名称：盐城市第一人民医院垃圾房工程；1.2建设地点：盐城市第一人民医院南区（盐城市人民南路66号）；1.3招标内容：盐城市第一人民医院垃圾房工程施工，','172.16.1.2','172.16.1.2','1577980800','<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院垃圾房工程已具备招标条件，现拟对该工程进行公开招标，择优选择施工单位。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1、工程概况&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.1 工程名称：盐城市第一人民医院垃圾房工程；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.2建设地点：盐城市第一人民医院南区（盐城市人民南路66号）；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.3招标内容：盐城市第一人民医院垃圾房工程施工，具体内容详见招标人提供的设计图纸和工程量清单。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.4发包方式：包工包料。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.5质量标准：符合国家“合格”标准。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.6工期：20日历天。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2、投标人资格要求</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.1投标人具备独立企业法人资格；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.2投标人须是具有建设主管部门核发的建筑工程总承包三级及以上资质的独立法人，并具有有效的安全生产许可证；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.3项目负责人须具有建筑工程专业二级及以上注册建造师资格证书和有效的安全生产考核合格证（B类）。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">3、投标报名</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话和邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名，投标报名时间2020年1月3日8时至2020年1月9日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。逾期不再接收报名。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">4、本项目采用竞争性谈判方式。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">5、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">6、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">具体内容详见招标文件。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: right; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标人:盐城市第一人民医院</span></p><p style=\"text-align: right; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2020年1月2日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2627','3451','1','','','盐城市第一人民医院保健基地（主院区住院部19楼）装修工程设计服务项目已具备招标条件，拟对该项目进行公开招标，择优选择设计单位。1、项目概况1.1 项目名称：盐城市第一人民医院保健基地（主院区住院部19楼）装修工程设计服务项目；1.2建设地点：盐城市人民南路66号；','172.16.1.2','172.16.1.2','1577980800','<p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院保健基地（主院区住院部19楼）装修工程设计服务项目已具备招标条件，拟对该项目进行公开招标，择优选择设计单位。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、项目概况</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.1 项目名称：盐城市第一人民医院保健基地（主院区住院部19楼）装修工程设计服务项目；</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.2建设地点：盐城市人民南路66号；</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.3工程规模：装修建筑面积约1600m<sup>2</sup>，工程投资约300万元。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.4招标内容：盐城市第一人民医院保健基地（主院区住院部19楼）装修工程设计服务，包含但不限于方案设计、施工图设计以及施工和保修等阶段设计咨询服务等，具体以招标人的书面通知为准。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.5质量标准：符合国家、省及市关于施工图设计深度的规范要求，并满足《建筑工程设计文件编制深度规定(2016年版)》，最终设计成果须确保通过相关部门审查。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.6设计时限：满足招标人要求。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、投标人资格要求</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.1投标人具备独立企业法人资格；</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.2投标人须是具有建设主管部门核发的建筑装饰工程设计专项乙级及以上或工程设计综合甲级资质的独立法人；</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3项目负责人须是本单位在职职工，并具有工程类高级及以上职称。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.4本次招标不接受联合体投标。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、投标报名</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话及邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名，投标报名时间2020年1月3日8时至2020年1月9日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。逾期不再接收报名。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"color: blue; font-family: 宋体, SimSun; font-size: 20px;\">4、本项目采用竞争性谈判方式。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">具体内容详见招标文件。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"text-align: right; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;招标人：盐城市第一人民医院</span></p><p style=\"text-align: right; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;时间：2020年1月3日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2628','3452','1','','','因我院现危化品供应商的《危险化学品经营许可证》即将到期，经医院领导研究决定，将以江苏省医用耗材阳光采购平台目录为基础，按照“公开、公平、公正”的原则，对部分危化品进行招标采购，欢迎进入江苏省阳光采购平台的生产企业和取得生产企业授权的配送企业参加，现将','172.16.1.2','172.16.1.2','1577721600','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">因我院现危化品供应商的《危险化学品经营许可证》即将到期，经医院领导研究决定，将以江苏省医用耗材阳光采购平台目录为基础，按照“公开、公平、公正”的原则，对部分危化品进行招标采购，欢迎进入江苏省阳光采购平台的生产企业和取得生产企业授权的配送企业参加，现将有关事项公告如下：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、本次招标采购项目名称</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1</span><span style=\"font-size: 19px; font-family: 宋体;\">、</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">75%</span><span style=\"font-size: 19px; font-family: 宋体;\">酒精</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2</span><span style=\"font-size: 19px; font-family: 宋体;\">、</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">95%</span><span style=\"font-size: 19px; font-family: 宋体;\">酒精</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">3</span><span style=\"font-size: 19px; font-family: 宋体;\">、过氧乙酸</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">4</span><span style=\"font-size: 19px; font-family: 宋体;\">、松节油</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">5</span><span style=\"font-size: 19px; font-family: 宋体;\">、无水乙醇</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 19px; font-family: 宋体;\">备注：其中</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1</span><span style=\"font-size: 19px; font-family: 宋体;\">、</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2</span><span style=\"font-size: 19px; font-family: 宋体;\">、</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">3</span><span style=\"font-size: 19px; font-family: 宋体;\">项产品必须入围省网平台并提供相应的平台编号。</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、供应商资格要求</span></p><p style=\"margin-left: 0px; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1、</span><span style=\"font-size: 19px; font-family: 宋体;\">具备独立法人的资格。</span></span></p><p style=\"margin-left: 0px; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2、</span><span style=\"font-size: 19px; font-family: 宋体;\">具有良好的商业信誉。</span></span></p><p style=\"margin-left: 0px; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">3、</span><span style=\"font-size: 19px; font-family: 宋体;\">具有独立承担民事责任的能力。</span></span></p><p style=\"margin-left: 0px; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">4、</span><span style=\"font-size: 19px; font-family: 宋体;\">具有履行合同所必需的设备和专业技术能力。</span></span></p><p style=\"margin-left: 0px; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">5、</span><span style=\"font-size: 19px; font-family: 宋体;\">近三年内在经营活动中无严重不良记录。</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、报名</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1</span><span style=\"font-size: 19px; font-family: 宋体;\">、报名方式：现场报名</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2</span><span style=\"font-size: 19px; font-family: 宋体;\">、报名时间</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">:2019</span><span style=\"font-size: 19px; font-family: 宋体;\">年</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">12</span><span style=\"font-size: 19px; font-family: 宋体;\">月</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">31</span><span style=\"font-size: 19px; font-family: 宋体;\">日</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">--2020</span><span style=\"font-size: 19px; font-family: 宋体;\">年</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1</span><span style=\"font-size: 19px; font-family: 宋体;\">月</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">7</span><span style=\"font-size: 19px; font-family: 宋体;\">日</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 19px; font-family: 宋体;\">上午：</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">8</span><span style=\"font-size: 19px; font-family: 宋体;\">：</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">00--11:30&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 19px; font-family: 宋体;\">下午：</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">13:30--17:00</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">法定假日休息</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">3</span><span style=\"font-size: 19px; font-family: 宋体;\">、报名地点：盐城市第一人民医院北区招标采购中心办公室（盐城市亭湖区毓龙路</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">166</span><span style=\"font-size: 19px; font-family: 宋体;\">号）</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、报名须知：凡具备资格要求的供应商报名时，须携带企业营业执照副本、资质证书、法定代表人身份证明、平台中标的相关资料、取得生产企业授权的委托书等复印件，上述文件必须加盖单位公章。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 19px; font-family: 宋体;\">五、公告公示截止时间：</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2020</span><span style=\"font-size: 19px; font-family: 宋体;\">年</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1</span><span style=\"font-size: 19px; font-family: 宋体;\">月</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">7</span><span style=\"font-size: 19px; font-family: 宋体;\">日</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 19px; font-family: 宋体;\">六、本次招标项目联系人：张琳琳</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 19px; font-family: 宋体;\">联系电话：</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">0515-88507786</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">&nbsp;</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">附件：</span><span style=\"color: rgb(0, 102, 204); text-decoration: underline; font-size: 20px; font-family: 宋体, SimSun;\"><a style=\"color: rgb(0, 102, 204); text-decoration: underline; font-size: 20px; font-family: 宋体, SimSun;\" href=\"http://localhost/upload/files/2019/12/6c8027631abd87a8.xls\" title=\"危化品招标目录.xls\">危化品招标目录.xls</a></span></p><p style=\"line-height: 16px; text-indent: 2em;\"><br /></p><p style=\"white-space: normal; text-indent: 2em; text-align: right; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院招标采购中心</span></p><p style=\"white-space: normal; text-indent: 2em; text-align: right; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 19px;\">2019</span><span style=\"font-size: 19px; font-family: 宋体;\">年</span><span style=\"font-size: 19px;\">12</span><span style=\"font-size: 19px; font-family: 宋体;\">月</span><span style=\"font-size: 19px;\">31</span><span style=\"font-size: 19px; font-family: 宋体;\">日</span></span></p><p><br /></p>','','','','','');
