INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2639','3463','1','','','为了提高医院医疗诊疗水平，满足临床科室的诊疗需要，根据《医疗机构医用耗材管理办法》等政策要求，以江苏省阳光采购平台目录为基础，按照“公平，公正，公开”的原则，对部分特需医用耗材进行二次遴选，欢迎进入阳光采购平台的生产企业或取得生产企业授权的配送企业报','172.16.1.2','172.16.1.2','1574179200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为了提高医院医疗诊疗水平，满足临床科室的诊疗需要，根据《医疗机构医用耗材管理办法》等政策要求，以江苏省阳光采购平台目录为基础，按照“公平，公正，公开”的原则，对部分特需医用耗材进行二次遴选，欢迎进入阳光采购平台的生产企业或取得生产企业授权的配送企业报名参加，现将有关事项公告如下：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一．本次遴选目录</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1. 一次性使用热活检钳&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （内镜中心专用）</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2. 一次性使用圈套器（椭圆）&nbsp;&nbsp;&nbsp; （内镜中心专用）</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3. 一次性手术介入包&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （介入科专用）</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4. 宫腔用交联透明质酸钠凝胶&nbsp;&nbsp;&nbsp; （妇产科专用）</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5. 间隙性导尿包&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （康复科专用）</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6. 等离子刀头&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; （耳鼻喉科专用）</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二．供应商资格要求</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.具备独立法人的资格</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.具有良好的商业信誉</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.具有独立承担民事责任的能力</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4.具有履行合同所必需的设备和专业技术能力</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5.近三年内在经营活动中无严重不良记录</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三．报名</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.报名方式：现场报名</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.报名时间：2019年11月20日-11月25日 上午8：00 -11：30&nbsp;&nbsp; &nbsp;下午14：00-17：30</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.报名地点：盐城一院北区设备处</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四．报名须知</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">凡具备资格要求的供应商报名时，须携带企业营业执照副本、资质证书、法定代表人身份证明、平台中标的相关资料、取得生产企业授权的委托书等复印件，上述文件必须加盖单位公章并装订成册。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五．本次招标项目联系人：韩辉&nbsp;&nbsp;&nbsp;&nbsp; 联系电话：0515-88508946&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 0515-83578946</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2640','3464','1','','','因医院实际工作需要，于2019年11月14日下午5:00时进行招标的项目，经评标小组评审，中标结果如下：一、南、北区零星维修项目中标单位：江苏力邦装饰有限公司中 标 价：4.85万元二、南、北区水泵维保等项目中标单位：盐城市杰迅机电设备有限公司中 标 价：4.6万元三、北','172.16.1.2','172.16.1.2','1573747200','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">因医院实际工作需要，于2019年11月14日下午5:00时进行招标的项目，经评标小组评审，中标结果如下：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><strong><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">一、南、北区零星维修项目</span></strong></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">中标单位：江苏力邦装饰有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">中 标 价：4.85万元</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><strong><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">二、南、北区水泵维保等项目</span></strong></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">中标单位：盐城市杰迅机电设备有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">中 标 价：4.6万元</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><strong><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">三、北区急诊配液室净化空调主机采购及安装</span></strong></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">中标单位：江苏诚锐暖通设备有限公司 </span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">中 标 价：3.2万元&nbsp;&nbsp;&nbsp; </span></p><p style=\"line-height: 3em; text-indent: 2em;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、北区医疗区下水道疏通</span></strong><br /></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">中标单位：江苏省天悦建设工程有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">中 标 价：14万元&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监察室联系，电话0515-88508906。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2019年11月15日</span></p><p style=\"line-height: 3em; text-indent: 2em; text-align: right;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2641','3465','1','','','1、盐城市第一人民医院灭火器采购及检测充装项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院灭火器采购及检测充装项目等项目。2.2项目地点：盐城市第一人民医院南','172.16.1.2','172.16.1.2','1573574400','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、盐城市第一人民医院灭火器采购及检测充装项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、项目概况与招标内容</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.1项目名称：盐城市第一人民医院灭火器采购及检测充装项目等项目。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.2项目地点：盐城市第一人民医院南区（人民南路66号）、北区（毓龙西路166号）</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3招标内容：盐城市第一人民医院灭火器采购及检测充装项目等。具体内容详见招标人提供的工程量清单。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、投标人资格要求</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.1投标人经营范围包含：营业执照经营范围包含消防设备维修、保养或灭火器维修等内容；并具备江苏省消防技术服务机构资质证书，其消防技术服务类别：手提式ABC干粉灭火器维修。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.2投标时须授权本单位缴纳3-6个月以上社保的工作人员参加本项目谈判，不得授权本单位以外人员，一经查实，将取消投标和中标资格。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4、投标报名及招标文件的获取</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2019年11月13日 8时至2019年 11 月18日 18 时。联系人：胡荣艳，0515-68037830；手机：13815581495。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5、本项目采用竞争性谈判方式。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标人：盐城市第一人民医院</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">时间：2019年11月13日</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2642','3466','1','','','1、盐城市第一人民医院南北区水泵维保等项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。2、项目概况与招标内容2.1项目名称：盐城市第一人民医院南北区水泵维保等项目。2.2项目地点：盐城市第一人民医院南区（人民南路66','172.16.1.2','172.16.1.2','1573056000','<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">1、盐城市第一人民医院南北区水泵维保等项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">2、项目概况与招标内容</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">2.1项目名称：盐城市第一人民医院南北区水泵维保等项目。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">2.2项目地点：盐城市第一人民医院南区（人民南路66号）、北区（毓龙西路166号）。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">2.3招标内容：南区西区空调机房30KW供暖水泵维保、东区水泵房供水水泵维保；北区空调机房37KW热媒泵维修、污水处理站罗茨风机新购过滤网及保养、加药搅拌机更换项目等项目。具体内容详见招标人提供的工程量清单。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">2.4发包方式：包工包料。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">3、投标人资格要求</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">投标人须具有有效的建筑机电安装工程专业承包三级及以上资质的独立法人，并具有有效的安全生产许可证；或营业执照经营范围包含电机、水泵安装、维修相关内容。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">4、投标报名及招标文件的获取</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间<span style=\"font-family: 宋体; font-size: 20px; text-decoration: underline;\">2019</span>年<span style=\"font-family: 宋体; font-size: 20px; text-decoration: underline;\">11</span>月<span style=\"font-family: 宋体; font-size: 20px; text-decoration: underline;\">7</span>日<span style=\"font-family: 宋体; font-size: 20px; text-decoration: underline;\"> 8 </span>时至<span style=\"font-family: 宋体; font-size: 20px; text-decoration: underline;\">2019</span>年<span style=\"font-family: 宋体; font-size: 20px; text-decoration: underline;\">11</span>月<span style=\"font-family: 宋体; font-size: 20px; text-decoration: underline;\">12</span>日<span style=\"font-family: 宋体; font-size: 20px; text-decoration: underline;\">18 </span>时。联系人：胡荣艳，13815581495；李纯东18796550337。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">5、本项目采用竞争性谈判方式。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">其余内容详见招标文件。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"text-align: right; text-indent: 0em; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">&nbsp; 招标人：盐城市第一人民医院</span></p><p style=\"text-align: right; line-height: 3em;\"><span style=\"font-size: 19px; font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：2019年11月5日&nbsp;</span></p><p><br /></p>','','','','','');
