INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2647','3471','1','','','为了提高医院医疗诊断水平，检验科计划开展部分新的检验项目。根据《医疗机构医用耗材管理办法》等政策要求，以江苏省阳光采购平台目录为基础，按照“公平，公正，公开”的原则，对新增检验项目所用试剂及耗材进行招标采购，欢迎进入阳光采购平台的生产企业或取得生产企','172.16.1.2','172.16.1.2','1572883200','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为了提高医院医疗诊断水平，检验科计划开展部分新的检验项目。根据《医疗机构医用耗材管理办法》等政策要求，以江苏省阳光采购平台目录为基础，按照“公平，公正，公开”的原则，对新增检验项目所用试剂及耗材进行招标采购，欢迎进入阳光采购平台的生产企业或取得生产企业授权的配送企业报名参加，现将有关事项公告如下：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">一．本次招标采购目录</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">1.异常凝血酶原</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2. ANCA（免疫荧光法）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3. TPPA(梅毒诊断)梅毒螺旋体颗粒凝集实验</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">4.甲戊肝（发光法）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">5. IgE检测</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">6. 内毒素检测</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">7. G实验&nbsp;&nbsp; 真菌/细菌动态监测仪</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp; G实验&nbsp;&nbsp; 真菌（1-3）-B-D葡聚糖检测试剂盒</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">8. 血栓四项</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">9. 粪钙卫蛋白</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">10.肺支抗体</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">11.幽门螺旋杆菌抗体（可区分有毒株和无毒株）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">12.呼吸道抗原筛查（金标法）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">13.抗磷脂综合征化学发光法血清血检测</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">二．</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">供应商资格要求</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">1.具备独立法人的资格</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2.具有良好的商业信誉</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3.具有独立承担民事责任的能力</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">4.具有履行合同所必需的设备和专业技术能力</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">5.近三年内在经营活动中无严重不良记录</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">三．报名</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">1.报名方式：现场报名</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2.报名时间：2019年11月5日-11月9日 上午8：00 -11：30&nbsp;&nbsp;&nbsp; 下午14：00-17：30</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3.报名地点：盐城一院北区设备处</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">四．报名须知</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">凡具备资格要求的供应商报名时，须携带企业营业执照副本、资质证书、法定代表人身份证明、平台中标的相关资料、取得生产企业授权的委托书等复印件，上述文件必须加盖单位公章并装订成册。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">五．本次招标项目联系人：唐志明&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp; &nbsp;联系电话：0515-88508946&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 0515-83578946</span></p><p><br /></p>','','','','','');
