INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('9816','9','cms','item','add','0','869','','宣传处','添加内容 | 【自身建设】严明纪律规矩 提升责任意识 ——邓义军主委约谈医卫界别','/admin.php/cms/item-add','172.16.70.1','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'869\',\n  \'title\' => \'【自身建设】严明纪律规矩 提升责任意识 ——邓义军主委约谈医卫界别党员\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'author_x\' => \'\',\n  \'author_y\' => \'\',\n  \'author_z\' => \'\',\n  \'editer\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'url\' => \'\',\n  \'views\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'#field_starttime_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'starttime\' => \'\',\n    \'endtime\' => \'\',\n    \'content\' => \'<p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">7月9日下午，根据农工党盐城市委会纪律学习教育部署，市委会主委、市一院院长邓义军约谈农工党市一院基层委全体委员，并根据委员各自工作岗位差异，进行个别谈话提醒。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">邓义军指出，纪律学习，从我做起。全体委员要深刻认识加强纪律学习教育的重要性和紧迫性，仔细研读《中国农工民主党纪律处分办法（试行）》及《农工党盐城市委会开展纪律学习教育实施方案》，把严守政治纪律和政治规矩、遵守各项纪律要求等作为对照检查、述职述廉的重点内容，深入进行自我剖析，认真开展批评和自我批评，切实抓好整改落实，进一步严明纪律规矩，提升基层委委员纪律意识和规矩意识，营造风清气正的良好氛围。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: center; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><img src=\"/attachment/cms/item/2024_07/15_14/bee1444f3c4e6219.jpg.cthumb.jpg\" title=\"微信图片_20240715145549.jpg\" _src=\"/attachment/cms/item/2024_07/15_14/bee1444f3c4e6219.jpg.cthumb.jpg\" alt=\"微信图片_20240715145549.jpg\"/></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">后期，市委会还将进一步落实领导班子谈心谈话制度，有针对性地对各基层组织及界别党员开展谈心交流，发挥“关键少数”和榜样示范作用，引导全市农工党党员守牢廉洁底线、纪法红线。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><br/></p><p>&nbsp;</p><p><br/></p>\',\n  ),\n  \'#field_endtime_posted\' => \'\',\n  \'addon_title\' => \'\',\n  \'list_order\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'custom_a\' => \'\',\n  \'level_time\' => \'权重有效期\',\n  \'level\' => \'\',\n  \'template\' => \'\',\n  \'attachment_pdf\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'title_color\' => \'\',\n  \'keywords\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'2ac25dd312a30d54\',\n  \'verifier\' => \'宣传处\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1721026720');
