INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('9860','9','cms','item','add','0','859','','宣传处','添加内容 | 关于部分药物浓度测定试剂的遴选公告（二次）','/admin.php/cms/item-add','172.16.70.1','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=859&model=article&type=2\',\n  \'cid\' => \'859\',\n  \'title\' => \'关于部分药物浓度测定试剂的遴选公告（二次）\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'author_x\' => \'\',\n  \'author_y\' => \'\',\n  \'author_z\' => \'\',\n  \'editer\' => \'\',\n  \'source\' => \'设备处\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'url\' => \'\',\n  \'views\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'#field_starttime_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'starttime\' => \'\',\n    \'endtime\' => \'\',\n    \'content\' => \'<p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">各经营企业：</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">根据我院实际工作的需要，经医院领导研究同意，拟为临床药学室开展部分药物浓度测定试剂进行遴选，欢迎经营企业前来参与。</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">一．需求项目</span></strong><strong><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">（仅作为参考名称，不作为最终中选名称）</span></span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">1.美罗培南药物浓度测定</span></span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">2.万古霉素药物浓度测定</span></span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">3.甲氨蝶呤血样浓度定量检测及用药指导</span></span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">4.环孢霉素药物浓度测定</span></span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">5.霉芬酸类药物（吗替麦考酚酯）药物浓度测定</span></span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">6.利伐沙班药物浓度测定</span></span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">7.奥希替尼血样浓度定量检测及用药指导</span></span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">8.</span></strong><strong><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">安罗替尼血样浓度定量检测及用药指导</span></span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">9.阿帕替尼血样浓度定量检测及用药指导</span></span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">1.1主要配置要求：</span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">1.定量检测（ng/ml），可用于能力验证，实验室内质量控制。</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">2.可用于二维高效液相色谱平台（MLC-2801)</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 40px;background: rgb(255, 255, 255)\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">&nbsp;</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">二．报名须知</span></strong></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">1.报名资格：</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">　（1）</span><span style=\"color: rgb(255, 0, 0); letter-spacing: 0px;\">生产厂家或生产厂家委托参与遴选报名的委托人。</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">　（2）营业执照、医疗器械生产许可证、医疗器械经营许可证、医疗器械注册证等证件齐全且在有效期内。</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">&nbsp; &nbsp; （3）</span><span style=\"color: rgb(255, 0, 0); letter-spacing: 0px;\">报名检验检测试剂及耗材需为江苏省阳光采购平台中标品种。</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 40px;background: rgb(255, 255, 255)\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">&nbsp;</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">2.报名方式：邮箱报名。</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">3.报名时间：即日起到2024年</span><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">8</span><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">月</span><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">1</span><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">日17：00前。</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">4.报名所需材料</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">（1）生产企业资质（营业执照、医疗器械生产许可证）、医疗器械注册证；</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">（2）报名企业资质：营业执照及符合销售报名产品所应具备的医疗器械经营许可证/第二类医疗器械经营备案凭证、生产厂家委托书和所投产品代理证明（如非生产厂家报名需提供，代理证明注明级别）、报名企业法人授权委托书（如报名人员为法人可不提供）、法人和被授权人的身份证复印件。</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">（3）产品技术特点及优势、产品说明书。</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">（4）依据江苏省药品和医用耗材招采管理系统格式提供产品信息及价格表。</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">　5.报名邮箱：以上报名材料发送至邮箱。</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">6.遴选时间：另行通知</span></p><p style=\";text-indent: 32px;padding: 0;line-height: 40px;background: rgb(255, 255, 255)\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">&nbsp;</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">项目联系人</span></span></strong><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">：唐老师 &nbsp;联系方式：18921892789&nbsp;邮箱：</span><a href=\"mailto:772909836@qq.com\"><span style=\"text-decoration:underline;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px;\">772909836@qq.com</span></span></a></p><p style=\";text-indent: 32px;padding: 0;line-height: 40px;background: rgb(255, 255, 255)\"><span style=\"color: rgb(51, 5