INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2834','3658','1','','','经研究决定，我院拟对部分总务报废物资处理进行招标，诚邀潜在的投标人参加投标。一、 项目概况项目名称：盐城市第一人民医院广济医院总务报废物资。报废物资内容：广济分院总务报废物资（家具、电器等），具体详见招标人提供的物资报价清单。二、投标申请人资质要求投','172.16.1.2','172.16.1.2','1528819200','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">经研究决定，我院拟对部分总务报废物资处理进行招标，诚邀潜在的投标人参加投标。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、 项目概况</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">项目名称：盐城市第一人民医院广济医院总务报废物资。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">报废物资内容：广济分院总务报废物资（家具、电器等），具体详见招标人提供的物资报价清单。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、投标申请人资质要求</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">投标申请人须具有独立法人资格，且营业执照经营范围涵盖废旧物资回收等相关内容。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、评标方法</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本次评标采用有效最高报价法，在确定各投标人对招标做到实质性响应后，即营业执照经营范围符合要求、报价清单经评审通过，报价清单无错项、缺漏项的有效投标报价最高的投标人为中标候选人。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、报名及招标文件领取</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">报名时间：2018年6月13日上午11时到2018年6月21日上午11时；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">地点：盐城市青年西路8号809室；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">联系人：胡荣艳，联系电话:0515-88351862。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、招投标监督电话</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　盐城市第一人民医院纪委监察室，0515-88508906。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">具体内容详见招标文件</span></p><p></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;盐城市第一人民医院广济分院</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　　　　　　　　　　　　　　　　　2018年6月13</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">日</span></p><p><br /></p>','','','','','');
