INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2838','3662','1','','http://222.188.122.162:8090/upload/images/2018/5/2585232696.jpg','5月13日-19日是第四届全民营养周，今年全民营养周宣传主题为 “吃动平衡，健康体重”，宣传口号为“慧吃慧动、健康体重”。为迎接全民营养周的到来，盐城市第一人民医院营养科积极响应上级营养学会的号召，在院领导和组宣处、团委、信息处等部门支持下，营养科分别走进','172.16.1.2','172.16.1.2','1527177600','<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">5月13日-19日是第四届全民营养周，今年全民营养周宣传主题为 “吃动平衡，健康体重”，宣传口号为“慧吃慧动、健康体重”。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为迎接全民营养周的到来，盐城市第一人民医院营养科积极响应上级营养学会的号召，在院领导和组宣处、团委、信息处等部门支持下，营养科分别走进越河路社区、敬贤路实验学校、东河小学开展了一系列丰富多彩、形式多样的科普宣传活动，并且在医院的门诊大厅和病区也开展了营养知识宣传讲座。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">通过一系列活动的开展，让大家充分了解食物搭配，提升了食物营养与膳食平衡的理念，促进广大群众树立了健康的饮食习惯和生活方式。</span></p><p style=\"text-align: center;\"><img src=\"http://222.188.122.162:8090/upload/images/2018/5/2585232696.jpg\" /><img src=\"http://222.188.122.162:8090/upload/images/2018/5/2585232816.JPG\" /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('2839','3663','1','','','一、为控制病媒有害生物，达到国家卫生检查标准，我院3院区须进行控虫灭鼠除四害服务，经请示，采用招标方式择优选择服务单位。江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托负责本工程招标事宜。二、项目概况1、项目名称：盐城市第一人民医院“除四害”服务。2','172.16.1.2','172.16.1.2','1526832000','<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、为控制病媒有害生物，达到国家卫生检查标准，我院3院区须进行控虫灭鼠除四害服务，经请示，采用招标方式择优选择服务单位。江苏仁禾中衡工程咨询房地产估价有限公司受招标人委托负责本工程招标事宜。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、项目概况</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、项目名称：盐城市第一人民医院“除四害”服务。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、服务地点：盐城市第一人民医院南北区及五官科院区3个院区整体区域及外围。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、服务内容：蚊、蝇、蟑螂、老鼠的灭杀及孳生控制技术服务，达到国家“除四害”管理规定执行的标准。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">4、服务周期：服务期限为一年，从合同签订之日起计算。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、投标申请人应具备的资格条件</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、具有法人资格和独立承担民事责任的能力；具有依法缴纳税收和社会保障资金的良好记录；没有行贿或者偷税漏税等欺诈行为。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、投标人必须具有履行合同所必需设备和专业技术能力，投标时须授权技术人员参加本项目谈判。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、投标人须具备有效的爱卫机构颁发的“除四害”专业服务技术质量认定证书或城市</span><span style=\"font-family: 宋体