INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3091','3916','1','','','盐城市第一人民医院北院区病理科三楼布局改造及五官科医院增加岩棉板机房项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。1、项目概况与招标内容1.1项目名称：盐城市第一人民医院北院区病理科三楼布局改造及五官科医院增','172.16.1.2','172.16.1.2','1685954929','<p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly;\"><span style=\"color: black;\"><font face=\"宋体\">盐城市第一人民医院北院区病理科三楼</font><a name=\"_GoBack\"></a><font face=\"宋体\">布局改造及五官科医院增加岩棉板机房项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</font></span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly;\"><font face=\"宋体\"><span style=\"color: black;\">1</span><span style=\"color: black;\">、项目概况与招标内容</span></font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly;\"><font face=\"宋体\"><span style=\"color: black;\">1.1</span><span style=\"color: black;\">项目名称：盐城市第一人民医院北院区病理科三楼布局改造及五官科医院增加岩棉板机房项目。</span></font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly;\"><font face=\"宋体\"><span style=\"color: black;\">1.2</span><span style=\"color: black;\">项目地点：盐城市第一人民医院北区（毓龙西路<span>166</span>号）、五官科医院（南城河路<span>12</span>号）。</span></font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly;\"><span style=\"color: black; font-family: 宋体; font-size: 12pt; mso-bidi-font-family: 宋体;\">1.3</span><span style=\"color: black; font-family: 宋体; font-size: 12pt; mso-bidi-font-family: 宋体;\">招标内容：盐城市第一人民医院北院区病理科三楼布局改造及五官科医院增加岩棉板机房项目，具体详见招标人提供的工程量清单。</span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly;\"><span style=\"color: black; font-family: 宋体; font-size: 12pt; mso-bidi-font-family: 宋体;\">1.4</span><span style=\"color: black; font-family: 宋体; font-size: 12pt; mso-bidi-font-family: 宋体;\">工期要求：<span>15</span>日历天，具体开工日期以招标人的通知。</span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly;\"><span style=\"color: black; font-family: 宋体; font-size: 12pt; mso-bidi-font-family: 宋体;\">1.5</span><span style=\"color: black; font-family: 宋体; font-size: 12pt; mso-bidi-font-family: 宋体;\">质保期：<span>2</span>年。</span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly;\"><span style=\"color: black; font-family: 宋体; font-size: 12pt; mso-bidi-font-family: 宋体;\">1.6</span><span style=\"color: black; font-family: 宋体; font-size: 12pt; mso-bidi-font-family: 宋体;\">最高限价：<span>6.3</span>万元</span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly;\"><font face=\"宋体\"><span style=\"color: black;\">2</span><span style=\"color: black;\">、投标人资格要求</span></font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly; tab-stops: 45.0pt;\"><span style=\"font-family: 宋体; font-size: 12pt;\">2.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">投标人须具有建设行政主管部门核发的施工总承包三级及以上施工资质的独立法人，并取得有效的安全生产许可证。</span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly; tab-stops: 45.0pt;\"><span style=\"font-family: 宋体; font-size: 12pt;\">2.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">项目负责人的资格要求：项目负责人须具有建筑工程专业二级及以上建造师注册证书；同时具有《建筑施工企业项目负责人安全生产考核合格证》（<span>B</span>类）。</span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly; tab-stops: 45.0pt;\"><span style=\"font-family: 宋体; font-size: 12pt;\">2.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求</span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly; tab-stops: 45.0pt;\"><span style=\"font-family: 宋体; font-size: 12pt;\">2.3.1</span><span style=\"font-family: 宋体; font-size: 12pt;\">财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly; tab-stops: 45.0pt;\"><span style=\"font-family: 宋体; font-size: 12pt;\">2.3.2</span><span style=\"font-family: 宋体; font-size: 12pt;\">信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly; tab-stops: 45.0pt;\"><span style=\"font-family: 宋体; font-size: 12pt;\">2.4 </span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标不接受联合体投标。</span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly; tab-stops: 45.0pt;\"><span style=\"font-family: 宋体; font-size: 12pt;\">3.3</span><span style=\"font-family: 宋体; font-size: 12pt;\">其他要求<span><br /><span style=\"mso-spacerun: yes;\">&nbsp;&nbsp; </span>3.3.1</span>财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span>&nbsp;<br /><span style=\"mso-spacerun: yes;\">&nbsp; </span>3.3.2</span>信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 24pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly; tab-stops: 45.0pt;\"><span style=\"font-family: 宋体; font-size: 12pt;\">3.4</span><span style=\"font-family: 宋体; font-size: 12pt;\">本次招标不接受联合体投标。</span></p><p><font face=\"宋体\"></font></p><p style=\"background: white; margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 12pt; mso-char-indent-count: 1.0; mso-line-height-rule: exactly;\"><font face=\"宋体\"><span style=\"color: black; mso-bidi-language: AR;\">4</span><span style=\"color: black; mso-bidi-language: AR;\">、投标报名及招标文件的获取</span></font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly;\"><span style=\"background: white; color: black;\"><font face=\"宋体\">请符合上述资质条件的投标人携带单位介绍信到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路<span>8</span>号<span>8</span>楼<span>805</span>室）投标报名并领取招标文件，投标报名时间<span>2023</span>年<span>6</span>月<span>5</span>日<span>8</span>时<span>30</span>分至<span>2023</span>年<span>6</span>月<span>10</span>日<span>18</span>时。联系人：刘工，<span>0515-69057406</span>；手机：<span>13626222271</span>。</font></span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 12pt; mso-char-indent-count: 1.0; mso-line-height-rule: exactly;\"><font face=\"宋体\"><span>5</span>、<b>评标办法：采用“经评审的最低投标价法”。</b></font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 12pt; mso-char-indent-count: 1.0; mso-line-height-rule: exactly;\"><font face=\"宋体\"><span style=\"color: black;\">6</span><span style=\"color: black;\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm 0cm 0pt; line-height: 27pt; text-indent: 12pt; mso-char-indent-count: 1.0; mso-line-height-rule: exactly;\"><font face=\"宋体\"><span style=\"color: black;\">7</span><span style=\"color: black;\">、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话<span>0515-66696810</span>。</span></font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm -6pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; -ms-text-justify: inter-ideograph;\"><span><font face=\"宋体\">&nbsp;</font></span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm -6pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; -ms-text-justify: inter-ideograph;\"><font face=\"宋体\">招标人：盐城市第一人民医院</font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm -6pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; -ms-text-justify: inter-ideograph;\"><font face=\"宋体\">地址：盐城市人民南路<span>66</span>号</font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm -6pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; -ms-text-justify: inter-ideograph;\"><font face=\"宋体\">联系人：张女士</font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm -6pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; -ms-text-justify: inter-ideograph;\"><font face=\"宋体\">联系电话：<span>0515-66696855 </span></font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm -6pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; -ms-text-justify: inter-ideograph;\"><span><font face=\"宋体\">&nbsp;</font></span></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm -6pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; -ms-text-justify: inter-ideograph;\"><font face=\"宋体\">招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司 </font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm -6pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; -ms-text-justify: inter-ideograph;\"><font face=\"宋体\">地址：盐城市青年西路<span>8</span>号<span>805</span>室</font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm -6pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; -ms-text-justify: inter-ideograph;\"><font face=\"宋体\">联系人：刘工</font></p><p><font face=\"宋体\"></font></p><p style=\"margin: 0cm -6pt 0pt; text-align: justify; line-height: 23pt; text-indent: 24pt; -ms-text-justify: inter-ideograph;\"><font face=\"宋体\">电话：<span>0515-69057406</span></font></p><p><font face=\"宋体\"></font></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3092','3917','1','','','我院伦理审查委员会隶属于盐城市第一人民医院，由13名委员（含院外委员3人）组成，分别由从事医药、法学、社区管理以及独立于本单位之外的人员组成，另设伦理审查委员会办公室主任1名、秘书1名，委员会的人员组成和功能符合临床试验管理规范的相关要求。伦理审查委员会','10.10.10.10','10.10.10.10','1686270325','<p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">我院伦理审查委员会隶属于盐城市第一人民医院，由</span><span style=\"line-height: 120%;\">13</span><span style=\"line-height: 120%;\">名委员（含院外委员</span><span style=\"line-height: 120%;\">3</span><span style=\"line-height: 120%;\">人）组成，分别由从事医药、法学、社区管理以及独立于本单位之外的人员组成，另设伦理审查委员会办公室主任</span><span style=\"line-height: 120%;\">1</span><span style=\"line-height: 120%;\">名、秘书</span><span style=\"line-height: 120%;\">1</span><span style=\"line-height: 120%;\">名，委员会的人员组成和功能符合临床试验管理规范的相关要求。</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">伦理审查委员会设有独立的办公室，办公设施齐备，能够满足伦理审查工作的需要。伦理审查委员会制定了完善的药物临床试验审查制度、伦理审查申请指南和</span><span style=\"line-height: 120%;\">SOP</span><span style=\"line-height: 120%;\">，并建立了初始培训及继续教育培训机制，所有伦理审查委员均经过</span><span style=\"line-height: 120%;\">GCP</span><span style=\"line-height: 120%;\">及相关法规知识的培训，从而保证本伦理审查委员会的审查能力达到</span><span style=\"line-height: 120%;\">GCP</span><span style=\"line-height: 120%;\">的规范要求。</span></p><p style=\"text-indent:32px;line-height:120%\"></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"line-height: 120%;\">伦理审查委员会宗旨</span></strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">通过对涉及人的生命科学和医学研究的科学性、伦理合理性进行审查，确保研究参与者的尊严、安全和权益得到保护，促进生命科学和医学研究达到科学和伦理的高标准，增强公众对临床研究的信任和支持。</span></p><p style=\"text-indent:32px;line-height:120%\"><span style=\"line-height: 120%;\">&nbsp;</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"line-height: 120%;\">伦理审查委员会工作依据</span></strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">国家卫生健康委、科技部、教育部、中医药《涉及人的生命科学和医学研究伦理审查办法》</span><span style=\"line-height: 120%;\">2023</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">国家卫生健康委国家药监局《医疗器械临床试验质量管理规范》</span><span style=\"line-height: 120%;\">2022</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">国家卫生健康委</span> <span style=\"line-height: 120%;\">科技部</span> <span style=\"line-height: 120%;\">中医药管理局《医学科研诚信和相关行为规范》</span><span style=\"line-height: 120%;\">2021</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">国家卫生健康委《人体器官移植技术临床应用管理规范（</span><span style=\"line-height: 120%;\">2020</span><span style=\"line-height: 120%;\">年版）》</span><span style=\"line-height: 120%;\">2020</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">国家卫生健康委《涉及人的临床研究伦理审查委员会建设指南（</span><span style=\"line-height: 120%;\">2020</span><span style=\"line-height: 120%;\">版）》</span><span style=\"line-height: 120%;\">2020</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">国家药监局</span> <span style=\"line-height: 120%;\">国家卫生健康委《药物临床试验质量管理规范》</span><span style=\"line-height: 120%;\">2020</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">国务院《中华人民共和国人类遗传资源管理条例》</span><span style=\"line-height: 120%;\">2019</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">国家卫生健康委《涉及人的生物医学研究伦理审查办法》</span><span style=\"line-height: 120%;\">2016</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">国家中医药管理局《中医药临床研究伦理审查管理规范》</span><span style=\"line-height: 120%;\">2010</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">CIMOS WHO</span><span style=\"line-height: 120%;\">《涉及人的生物医学研究国际伦理准则》</span><span style=\"line-height: 120%;\">2016</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">ICH</span><span style=\"line-height: 120%;\">指导委员会《临床试验质量管理规范指导原则（</span><span style=\"line-height: 120%;\">ICH-GCP</span><span style=\"line-height: 120%;\">）》</span><span style=\"line-height: 120%;\">2016</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">世界医学会《赫尔辛基宣言》</span><span style=\"line-height: 120%;\">2013</span></p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">WHO</span><span style=\"line-height: 120%;\">《评审生物医学研究的伦理委员会工作指南》</span><span style=\"line-height: 120%;\">2000</span></p><p style=\"text-indent:32px;line-height:120%\"><span style=\"line-height: 120%;\">&nbsp;</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"line-height: 120%;\">伦理审查委员会工作职责</span></strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">负责对涉及人的生命科学和医学研究的科学性和伦理合理性进行独立、称职和及时的审查。审查范围包括药物临床试验项目、医疗器械临床试验项目、涉及人的生命科学和医学研究的科研项目、研究者发起的临床研究、新技术新项目的审查等。审查类别包括初始审查和跟踪审查。伦理审查委员会办公室负责伦理审查委员会日常行政事务的管理工作。</span></p><p style=\"text-indent:32px;line-height:120%\"><span style=\"line-height: 120%;\">&nbsp;</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"line-height: 120%;\">审查方式</span></strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">伦理审查委员会的审查方式有会议审查、紧急会议审查、简易程序审查（快速审查）。实行主审制，每个审查项目安排主审委员，填写审查工作表。会议审查是伦理审查委员会主要的审查工作方式，委员在会前预审送审项目。研究过程中出现重大或严重问题，危及研究参与者安全，召开紧急会议审查。简易程序审查是会议审查的补充形式，目的是为了提高工作效率，主要适用于临床研究方案的较小修正，不影响试验的风险受益比；尚未纳入研究参与者的研究项目的年度</span><span style=\"line-height: 120%;\">/</span><span style=\"line-height: 120%;\">定期跟踪审查；预期严重不良事件审查。</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">本伦理审查委员会将不断探索，不断提高伦理审查能力和质量，与国际接轨，通过对临床研究项目的科学性、伦理合理性进行高质量的审查，保障研究参与者的安全和权益。</span></p><p style=\"text-indent:32px;line-height:120%\"><span style=\"line-height: 120%;\">&nbsp;</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"line-height: 120%;\">联系方式</span></strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">伦理审查委员会办公室地点：江苏省盐城市人民南路</span><span style=\"line-height: 120%;\">66</span><span style=\"line-height: 120%;\">号门诊部</span><span style=\"line-height: 120%;\">4</span><span style=\"line-height: 120%;\">楼伦理审查委员会办公室</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">电话：</span><span style=\"line-height: 120%;\">0515-66696823</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">邮箱：</span><span style=\"line-height: 120%;\">ycsyylwh@163.com</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 120%;\">联系人：刘老师</span></p><p style=\"text-align: center;\"><a name=\"_GoBack\"></a></p><p></p><p style=\"text-indent:28px;line-height:150%\"><span style=\"line-height: 150%;\">&nbsp;</span></p><p><br /></p>','','','','','');
