INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3154','3979','1','','<!--#p8_attach#-->/cms/item/2023_08/19_09/bb0c11b9bfbec918.jpg.cthumb.jpg','','10.10.10.10','10.10.10.10','1692410270','<p style=\"text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_08/19_09/bb0c11b9bfbec918.jpg.cthumb.jpg\" title=\"161ebb71b12b7e04e2ce86becc90d2b.jpg\" src=\"<!--#p8_attach#-->/cms/item/2023_08/19_09/bb0c11b9bfbec918.jpg.cthumb.jpg\" alt=\"161ebb71b12b7e04e2ce86becc90d2b.jpg\" /></p><p style=\"text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_08/19_09/37cb9b576b774d77.png.cthumb.jpg\" title=\"75b20c123c203d68a29802b12653a38.png\" src=\"<!--#p8_attach#-->/cms/item/2023_08/19_09/37cb9b576b774d77.png.cthumb.jpg\" alt=\"75b20c123c203d68a29802b12653a38.png\" /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3155','3980','1','','<!--#p8_attach#-->/cms/item/2023_08/19_10/d7cf8765e72d4567.jpg.cthumb.jpg','时间飞逝，2022级学员将迎来第一次重要的考试--执业医师考试。学员们每天利用工作之余参加自习，自习室学习氛围浓烈，大家有的奋笔疾书，有的听视频讲解，有的做模拟考试，大家都按照自己的学习计划认真复习。与此同时，医院党委书记—王正书记组织考前动员大会，一遍遍','10.10.10.10','10.10.10.10','1692413776','<p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em;\"><span style=\"color: rgb(51, 51, 51);\">时间飞逝，2022级学员将迎来第一次重要的考试--执业医师考试。学员们每天利用工作之余参加自习，自习室学习氛围浓烈，大家有的奋笔疾书，有的听视频讲解，有的做模拟考试，大家都按照自己的学习计划认真复习。与此同时，医院党委书记</span><span style=\"color: rgb(51, 51, 51);\">—</span><span style=\"color: rgb(51, 51, 51);\">王正书记组织考前动员大会，一遍遍地叮嘱，给学员们解决实际困难，帮助他们无后顾之忧地学习。教育处边德志处长亲自给学员们制定复习计划，给学员们明确学习方向。教育处老师们一次次地组织模拟考试，让学员们更能牢固地掌握知识，使学员们在通往成功的路上，减少了许多阻力。在临考前夕，教育处还给学员送上了印有考试顺利、不负青春等鼓励话语的可乐，给学员们加油鼓劲。</span></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em;\"><span style=\"color: rgb(51, 51, 51); text-indent: 2em;\">一分耕耘一分收获，只有抓紧每分每秒，才能有满意的收获。相信学员们这么长时间的认真备考，定能给自己一份满意的答卷。</span></p><p>&nbsp;</p><p style=\"text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_08/19_10/d7cf8765e72d4567.jpg.cthumb.jpg\" title=\"78cecdcf6ebcb9ad7e402819fb413ff.jpg\" alt=\"78cecdcf6ebcb9ad7e402819fb413ff.jpg\" /></p><p style=\"text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_08/19_10/ede367c11da80b57.jpg.cthumb.jpg\" title=\"3d8e3b30c02b7f23d2e94b338267766.jpg\" alt=\"3d8e3b30c02b7f23d2e94b338267766.jpg\" /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3156','3981','1','','<!--#p8_attach#-->/cms/item/2023_08/23_16/692a0b955ed4875f.jpg.cthumb.jpg','为了进一步推进我院住院医师规范化培训工作，树立先进典型，提高培训质量，激发广大住院医师的责任感与荣誉感，2023年8月21日下午，我院2020级住培医师结业优秀学员表彰大会成功举办。教育处为15名优秀学员颁发了荣誉证书。相信我院的住培工作，在领导的关心下，在教育','10.10.10.10','10.10.10.10','1692780469','<p style=\"text-indent: 2em; text-align: justify; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">为了进一步推进我院住院医师规范化培训工作，树立先进典型，提高培训质量，激发广大住院医师的责任感与荣誉感，2023年8月21日下午，我院2020级住培医师结业优秀学员表彰大会成功举办。教育处为15名优秀学员颁发了荣誉证书。</p><p style=\"text-indent: 2em; text-align: justify; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">相信我院的住培工作，在领导的关心下，在教育处老师的创新下，在带教老师的探索下，我院的住培工作一定取得新的突破。</p><p style=\"text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_08/23_16/692a0b955ed4875f.jpg.cthumb.jpg\" title=\"1ec9a09c154ac8b17e1fa79e31c0cdf.jpg\" src=\"<!--#p8_attach#-->/cms/item/2023_08/23_16/692a0b955ed4875f.jpg.cthumb.jpg\" alt=\"1ec9a09c154ac8b17e1fa79e31c0cdf.jpg\" /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3157','3982','1','','','为推进我院医药领域腐败问题集中整治工作，加强医药领域违纪违法线索收集，现向社会公布盐城市第一人民医院集中整治协作机制举报方式及相关事项。一、整治时间2023年8月至2024年6月。二、举报方式电话：0515-66696055电子邮箱：ycyyjw@126.com邮寄地址：盐城市人民南路6','10.10.10.10','10.10.10.10','1693358500','<p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">为推进我院医药领域腐败问题集中整治工作，加强医药领域违纪违法线索收集，现向社会公布盐城市第一人民医院集中整治协作机制举报方式及相关事项。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>一、整治时间</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2023年8月至2024年6月。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>二、举报方式</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">电话：0515-66696055</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">电子邮箱：<a href=\"mailto:ycyyjw@126.com%EF%BC%9B\"><span style=\"color: rgb(0, 0, 0);\">ycyyjw@126.com</span></a></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">邮寄地址：盐城市人民南路66号盐城市第一人民医院监督审查室，邮政编码：224008</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（电话受理时间：工作日8:30—11:30，14:30—17:30）。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>三、相关要求</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（一）投诉举报须提供明确的时间、地点、人员、具体事项等内容，并附相关旁证材料，以便核查。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（二）鼓励实名举报，举报人对投诉举报内容的真实性负责，不得虚构和夸大事实，不得故意捏造和诬陷他人。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（三）受理部门对举报人信息、举报内容严格保密。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">感谢社会公众对盐城市第一人民医院医药领域腐败问题集中整治工作的关心支持和协助配合。</p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">盐城市第一人民医院</p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">医药领域腐败问题集中整治工作协作机制工作专班</p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">2023年8月30日</p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3158','3983','1','','<!--#p8_attach#-->/cms/item/2023_09/06_09/cb0741c1462387bf.png.cthumb.jpg','金秋九月，丹桂飘香，在医学的征程中，新的希望正在绽放。盐城市第一人民医院举行隆重的2023级住培新学员欢迎仪式，为一批医学学子打开了通向医疗梦想的大门，共同书写医院的辉煌未来。医院领导及教育处的老师们共同见证了这一激动人心的时刻。这一仪式不仅是新学员加入','10.10.10.10','10.10.10.10','1693962332','<p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">金秋九月，丹桂飘香，在医学的征程中，新的希望正在绽放。盐城市第一人民医院举行隆重的2023级住培新学员欢迎仪式，为一批医学学子打开了通向医疗梦想的大门，共同书写医院的辉煌未来。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">医院领导及教育处的老师们共同见证了这一激动人心的时刻。这一仪式不仅是新学员加入医院大家庭的标志，更是医院在人才培养方面的又一重要举措。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: center; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_09/06_09/cb0741c1462387bf.png.cthumb.jpg\" title=\"图片1.png\" src=\"<!--#p8_attach#-->/cms/item/2023_09/06_09/cb0741c1462387bf.png.cthumb.jpg\" alt=\"图片1.png\" /></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">我院作为地区医疗卫生事业的中坚力量，一直致力于培养具有优秀医德和卓越技能的医疗人才。此次迎来的2023级住培新学员，将在带教老师们的悉心指导下，接受为期三年的系统培训，努力成为胸怀梦想、具备扎实医学知识的医疗新秀。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: center; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_09/06_09/9400f95dfb79962b.png.cthumb.jpg\" title=\"图片2.png\" src=\"<!--#p8_attach#-->/cms/item/2023_09/06_09/9400f95dfb79962b.png.cthumb.jpg\" alt=\"图片2.png\" /></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">邓义军院长在仪式上致辞表示：医学道路上的每一步都需要坚定的信念和持之以恒的努力。他鼓励新学员们勇往直前，不断学习进取，将医学知识融入实际临床中，用智慧和爱心去呵护患者的健康。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: center; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_09/06_09/a1550e9d99bf8a70.png.cthumb.jpg\" title=\"图片3.png\" src=\"<!--#p8_attach#-->/cms/item/2023_09/06_09/a1550e9d99bf8a70.png.cthumb.jpg\" alt=\"图片3.png\" /></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">在随后的仪式环节中，新学员代表代表全体新学员发表了自己的誓言，表达了对医学事业的热爱和承诺。他们表示将牢记医德，不断追求卓越，用实际行动践行“以患者为中心”的医疗理念。最后院领导和学生代表合影留念。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">盐城市第一人民医院将为每位新学员提供优质的学习环境和培训资源，全力支持他们在医学领域的成长。希望通过三年的培训，新学员们能够蜕变为真正的医疗专业人才，为社会的健康事业做出积极贡献。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">在这个充满希望的时刻，让我们共同期待2023级住培新学员在医学道路上取得优异的成绩，为我院的蓬勃发展和医疗事业的繁荣做出卓越贡献。</p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3159','3984','1','','','因医院实际工作需要，于2023年9月6日进行公开招标，经评审小组评审，中标结果如下：一、项目名称：盐城市第一人民医药物临床试验一体化平台系统采购项目中标单位：江苏法迈生医学科技有限公司中标价：470000.00元项目负责人：孙阳二、项目名称：盐城市第一人民医院患者','10.10.10.10','10.10.10.10','1694069927','<p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">因医院实际工作需要，于2023年9月6日进行公开招标，经评审小组评审，中标结果如下：</p><p style=\"line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\">一、项目名称：盐城市第一人民医药物临床试验一体化平台系统采购项目</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">中标单位：江苏法迈生医学科技有限公司</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">中标价：470000.00元</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">项目负责人：孙阳</p><p style=\"line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\">二、项目名称：盐城市第一人民医院患者满意度第三方社会评价项目</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">中标单位：江苏全普市场调研咨询有限公司</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">中标价：150000.00元</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">项目负责人：史海峰</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监督审查室联系，电话0515-66696810。</p><p style=\"text-indent:37px;text-autospace:ideograph-numeric;line-height:35px;background:rgb(255,255,255)\"><span style=\"color: rgb(68, 68, 68);\">&nbsp;</span></p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">盐城市第一人民医院</p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">2023年9月7日</p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3161','3986','1','','<!--#p8_attach#-->/cms/item/2023_09/15_16/dc9275caa7398f4c.png.cthumb.jpg','各医疗器械生产企业、经营企业：为贯彻落实《江苏省消除艾滋、梅毒和乙肝母婴传播计划实施方案（2023-2025）》要求，经医院研究决定，需对检验科所需包含人免疫缺陷病毒抗体、梅毒螺旋体特异性抗体和梅毒螺旋体非特异性抗体的复合质控品进行院内遴选，欢迎符合条件的生','10.10.10.10','10.10.10.10','1694765787','<p style=\"line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">各医疗器械生产企业、经营企业：</span></p><p style=\"text-indent:32px;line-height:27px;background:rgb(255,255,255)\"><span style=\"color: rgb(68, 68, 68);\">&nbsp;</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(68, 68, 68);\">为</span><span style=\"color: rgb(68, 68, 68);\">贯彻落实《江苏省消除艾滋、梅毒和乙肝母婴传播计划实施方案（2023-2025）》要求</span><span style=\"color: rgb(68, 68, 68);\">，经医院研究决定，需对</span><span style=\"color: rgb(68, 68, 68);\">检验科所需</span>包含<strong>人免疫缺陷病毒抗体、梅毒螺旋体特异性抗体和梅毒螺旋体非特异性抗体的</strong><strong>复合质控品</strong><span style=\"color: rgb(68, 68, 68);\">进行院内遴选，</span><span style=\"color: rgb(51, 51, 51);\">欢迎符合条件的生产企业、经营企业前来参与。</span></p><p style=\"text-indent:32px;line-height:27px;background:rgb(255,255,255)\"><span style=\"color: rgb(51, 51, 51);\">&nbsp;</span></p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-weight: bold;\">1、</span><strong><span style=\"line-height: 150%;\">参数要求</span></strong></p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">&nbsp;&nbsp;&nbsp;&nbsp;包含</span><span style=\"line-height: 150%;\">人免疫缺陷病毒抗体、梅毒螺旋体特异性抗体和梅毒螺旋体非特异性抗体的</span><span style=\"line-height: 150%;\">复合质控品，3个浓度水平，每支量</span><span style=\"line-height: 150%;\">800μl</span><span style=\"line-height: 150%;\">以上</span><span style=\"line-height: 150%;\">。</span></p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"text-align: center; text-indent: 2em; font-weight: bold;\">2、</span><strong style=\"text-align: center; text-indent: 2em;\"><span style=\"line-height: 150%;\">浓度要求</span></strong></p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: center; margin-top: 15px; margin-bottom: 15px;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_09/15_16/dc9275caa7398f4c.png.cthumb.jpg\" title=\"1.png\" alt=\"1.png\" /></p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-weight: bold;\">3、</span><strong><span style=\"line-height: 150%;\">数量要求</span></strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">300套（每套包含3个水平）。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"color: rgb(51, 51, 51);\">报名须知：</span></strong></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">1.所供试剂需在江苏省药品(医用耗材)阳光采购和综合监管平台。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">2.专用设备的配置及性能。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">3.具有独立承担民事责任的能力。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">4.具有履行合同所必须具备的体外诊断试剂专业知识。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">5.具有良好的商业信誉</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">6.近三年内在经营活动中无严重不良记录</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">7</span><span style=\"color: rgb(51, 51, 51);\">.报名截止时间：2023年</span><span style=\"color: rgb(51, 51, 51);\">9</span><span style=\"color: rgb(51, 51, 51);\">&nbsp;月 </span><span style=\"color: rgb(51, 51, 51);\">21 </span><span style=\"color: rgb(51, 51, 51);\">日 24：00</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">联系人：唐老师&nbsp;&nbsp;联系方式：18921892789&nbsp; </span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">8</span><span style=\"color: rgb(51, 51, 51);\">.具体遴选时间，另行通知。</span></p><p style=\"text-indent:32px;line-height:27px;background:rgb(255,255,255)\"><span style=\"color: rgb(51, 51, 51);\">&nbsp;</span></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"color: rgb(51, 51, 51);\">报名表：</span></strong></p><p style=\"text-indent:32px;line-height:27px;background:rgb(255,255,255)\"><span style=\"color: rgb(51, 51, 51);\">&nbsp;</span></p><p style=\"text-align:center\"><span style=\"color: rgb(51, 51, 51); background: rgb(255, 255, 255);\"><img src=\"<!--#p8_attach#-->/cms/item/2023_09/15_16/af2cd03feed227ce.png.cthumb.jpg\" title=\"5d85ef4b1f53209ad4b4f2596c09bef.png\" alt=\"5d85ef4b1f53209ad4b4f2596c09bef.png\" />&nbsp;</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3162','3987','1','','','项目概况盐城市第一人民医院工会生日福利品采购项目的潜在投标人应于2023年9月25日15点00分（北京时间）前至盐城市第一人民医院南院门诊四楼会议室递交投标文件。一、项目基本情况1、项目名称：盐城市第一人民医院工会生日福利品采购2、预算金额：本项目采购预算价约为1','10.10.10.10','10.10.10.10','1694772046','<p style=\"text-indent: 2em; border-width: 1px; border-style: solid; border-color: windowtext; padding: 1px 5px; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">项目概况</p><p style=\"text-indent: 2em; border-width: 1px; border-style: solid; border-color: windowtext; padding: 1px 5px; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"text-decoration:underline;\">盐城市第一人民医院工会生日福利品采购</span><span style=\"text-decoration:underline;\">项目</span>的潜在投标人应于<strong><span style=\"text-decoration:underline;\">2023</span></strong><strong><span style=\"text-decoration:underline;\">年</span></strong><strong><span style=\"text-decoration:underline;\">9</span></strong><strong><span style=\"text-decoration:underline;\">月</span></strong><strong><span style=\"text-decoration:underline;\">25</span></strong><strong><span style=\"text-decoration:underline;\">日</span></strong><strong><span style=\"text-decoration:underline;\">15</span></strong><strong><span style=\"text-decoration:underline;\">点</span></strong><strong><span style=\"text-decoration:underline;\">00</span></strong><strong><span style=\"text-decoration:underline;\">分</span></strong><span style=\"text-decoration:underline;\">（</span>北京时间）前至<span style=\"text-decoration:underline;\">盐城市第一人民医院</span><span style=\"text-decoration:underline;\">南院门诊四楼会议室</span>递交投标文件。</p><p style=\"text-indent:0;text-autospace:ideograph-numeric;line-height:27px\">&nbsp;</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>一、项目基本情况</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1、项目名称：盐城市第一人民医院工会生日福利品采购</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2、预算金额：本项目采购预算价约为105万元，其中工会会员人均预算300元，约3410份；新入职会员人均预算150元，约180份。投标单位以生日蛋糕提货券形式发放给工会会员，工会会员的提货券实际市场售价不低于300元；新入职会员的提货券实际市场售价不低于150元。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">3、最高限价：105万元</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">4、采购需求：详见本遴选文件第三章。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">5、合同履行期限：1年。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">6、本项目不接受联合体投标。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>二、申请人的资格要求</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（一）具备《中华人民共和国政府采购法》第二十二条规定的条件：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">(1)法人或者其他组织的营业执照等证明文件。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（二）未被“信用中国”网站(www.creditchina.gov.cn)、中华人民共和国最高人民法院网站（http://www.court.gov.cn）等渠道列入信用记录失信被执行人，未被中国政府采购网(www.ccgp.gov.cn)等渠道列入政府采购严重违法失信行为记录名单等；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（三）单位负责人为同一人或者存在直接控股、管理关系的不同投标人，不得参加同一合同项下的采购活动。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（四）法定代表人为同一人的两个及两个以上的法人、母公司、全资子公司及其控股公司，都不得在同一项目中同时投标。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（五）本项目不接受联合体形式的投标及任何形式的分包或转包。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>三、</strong><strong>投标报名及</strong><strong>获取</strong><strong>遴选文件</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（1）发售（报名）时间：<strong>自</strong><strong>遴选</strong><strong>公告上网发布之时起至</strong><strong>2023</strong><strong>年</strong><strong>9</strong><strong>月</strong><strong>22</strong><strong>日</strong>（节假日除外）。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（2）工本费：本套遴选文件工本费人民币叁佰元整，售后不退。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（3）发售（报名）地点：盐城市青年西路8号8楼805。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（4）获取遴选文件时须向代理公司提供以下材料（加盖公章）：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>a、经办人的单位法人授权书</strong><strong>或单位介绍信</strong><strong>原件；b、经办人身份证复印件（如有授权）；c、报名单位营业执照复印件；d、报名单位法定代表人身份证复印件。</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">四、提交投标文件截止时间、开标时间和地点</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">投标截止时间：<strong>2023</strong><strong>年</strong><strong>9</strong><strong>月</strong><strong>25</strong><strong>日</strong><strong>15</strong><strong>时</strong><strong>00</strong><strong>分</strong>（北京时间）</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地点：盐城市第一人民医院南院门诊四楼会议室</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">五、公告期限</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">本遴选公告自2023年9月15日至2023年9月22日在<strong><span style=\"text-decoration:underline;\">“</span></strong><strong><span style=\"text-decoration:underline;\">盐城市第一人民医院</span></strong><strong><span style=\"text-decoration:underline;\">”</span></strong><strong><span style=\"text-decoration:underline;\">官</span></strong><strong><span style=\"text-decoration:underline;\">网</span></strong>发布。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">六、其他补充事宜</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>无</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">七、对本次招标提出询问，请按以下方式联系：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.招标人信息</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">名 称：盐城市第一人民医院</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地&nbsp;址：盐城市人民南路66号</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系人:张处</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系电话：0515-66696855。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2.采购代理机构信息</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">名 称：江苏仁禾中衡工程咨询房地产估价有限公司</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地&nbsp;址：盐城市青年西路8号8楼805</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系人：刘工、胡工</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系方式：13626222271、13815581495</p><p>&nbsp;</p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3163','3988','1','','','项目概况盐城市第一人民医院工会中秋节福利品采购项目的潜在投标人应于2023年9月25日15点00分（北京时间）前至盐城市第一人民医院南院门诊四楼会议室递交投标文件。一、项目基本情况1、项目名称：盐城市第一人民医院工会中秋节福利品采购项目2、预算金额：本项目采购预','10.10.10.10','10.10.10.10','1694772117','<p style=\"text-indent: 2em; border-width: 1px; border-style: solid; border-color: windowtext; padding: 1px 5px; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">项目概况</p><p style=\"text-indent: 2em; border-width: 1px; border-style: solid; border-color: windowtext; padding: 1px 5px; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"text-decoration:underline;\">盐城市第一人民医院</span><span style=\"text-decoration:underline;\">工会</span><span style=\"text-decoration:underline;\">中秋节福利品采购项目</span>的潜在投标人应于<strong><span style=\"text-decoration:underline;\">2023</span></strong><strong><span style=\"text-decoration:underline;\">年</span></strong><strong><span style=\"text-decoration:underline;\">9</span></strong><strong><span style=\"text-decoration:underline;\">月</span></strong><strong><span style=\"text-decoration:underline;\">25</span></strong><strong><span style=\"text-decoration:underline;\">日</span></strong><strong><span style=\"text-decoration:underline;\">15</span></strong><strong><span style=\"text-decoration:underline;\">点</span></strong><strong><span style=\"text-decoration:underline;\">00</span></strong><strong><span style=\"text-decoration:underline;\">分</span></strong><span style=\"text-decoration:underline;\">（</span>北京时间）前至<span style=\"text-decoration:underline;\">盐城市第一人民医院</span><span style=\"text-decoration:underline;\">南院门诊四楼会议室</span>递交投标文件。</p><p style=\"text-indent:0;text-autospace:ideograph-numeric;line-height:27px\">&nbsp;</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>一、项目基本情况</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1、项目名称：盐城市第一人民医院工会中秋节福利品采购项目</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2、预算金额：本项目采购预算价约为230.4万元，其中工会会员人均预算600元，约3750份；新入职会员人均预算300元，约180份。投标单位根据四大类: 粮油类、生鲜类（非进口产品）、食品类、乳品类，组合两个套餐。每个套餐不少于四种商品，以提货券形式发放给工会会员，工会会员的每个套餐实际市场售价不低于600元；新入职会员的每个套餐实际市场售价不低于300元。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">3、最高限价：230.4万元</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">4、采购需求：详见本遴选文件第三章。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">5、合同履行期限：1个月。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">6、本项目不接受联合体投标。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>二、申请人的资格要求</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（一）具备《中华人民共和国政府采购法》第二十二条规定的条件：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">(1)法人或者其他组织的营业执照等证明文件；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">(2)上一年度的财务报表（成立不满一年不需提供）；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">(3)依法缴纳税收和社会保障资金的相关材料；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">(4)具备履行合同所必需的设备和专业技术能力的书面声明；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">(5)参加政府采购活动前3年内在经营活动中没有重大违法记录的书面声明。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（二）未被“信用中国”网站(www.creditchina.gov.cn)、中华人民共和国最高人民法院网站（http://www.court.gov.cn）等渠道列入信用记录失信被执行人，未被中国政府采购网(www.ccgp.gov.cn)等渠道列入政府采购严重违法失信行为记录名单等；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（三）单位负责人为同一人或者存在直接控股、管理关系的不同投标人，不得参加同一合同项下的采购活动。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（四）法定代表人为同一人的两个及两个以上的法人、母公司、全资子公司及其控股公司，都不得在同一项目中同时投标。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（五）本项目不接受联合体形式的投标及任何形式的分包或转包。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>三、</strong><strong>投标报名及</strong><strong>获取</strong><strong>遴选文件</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（1）发售（报名）时间：<strong>自</strong><strong>遴选</strong><strong>公告上网发布之时起至</strong><strong>2023</strong><strong>年</strong><strong>9</strong><strong>月</strong><strong>22</strong><strong>日</strong>（节假日除外）。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（2）工本费：本套遴选文件工本费人民币叁佰元整，售后不退。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（3）发售（报名）地点：盐城市青年西路8号8楼805。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（4）获取遴选文件时须向代理公司提供以下材料（加盖公章）：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>a、经办人的单位法人授权书</strong><strong>或单位介绍信</strong><strong>原件；b、经办人身份证复印件（如有授权）；c、报名单位营业执照复印件；d、报名单位法定代表人身份证复印件。</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">四、提交投标文件截止时间、开标时间和地点</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">投标截止时间：<strong>2023</strong><strong>年</strong><strong>9</strong><strong>月</strong><strong>25</strong><strong>日</strong><strong>15</strong><strong>时</strong><strong>00</strong><strong>分</strong>（北京时间）</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地点：盐城市第一人民医院南院门诊四楼会议室</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">五、公告期限</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">本遴选公告自2023年9月15日至2023年9月22日在<strong><span style=\"text-decoration:underline;\">“</span></strong><strong><span style=\"text-decoration:underline;\">盐城市第一人民医院</span></strong><strong><span style=\"text-decoration:underline;\">”</span></strong><strong><span style=\"text-decoration:underline;\">官</span></strong><strong><span style=\"text-decoration:underline;\">网</span></strong>发布。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">六、其他补充事宜</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>无</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">七、对本次招标提出询问，请按以下方式联系：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.招标人信息</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">名 称：盐城市第一人民医院</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地址：盐城市人民南路66号</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系人:张处</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系电话：0515-66696855。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2.采购代理机构信息</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">名 称：江苏仁禾中衡工程咨询房地产估价有限公司　　　　　　　　　　　</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地　址：盐城市青年西路8号8楼805　　　　　　　　　　　　</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系人：刘工、胡工</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系方式：13626222271、13815581495</p><p>&nbsp;</p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3164','3989','1','','<!--#p8_attach#-->/cms/item/2023_09/18_15/d9cfb3ffdc67c04b.png.thumb.jpg','一、什么是数字人民币？','10.10.10.10','10.10.10.10','1695023466','<p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 1px; background: rgb(255, 255, 255);\">一、什么是数字人民币？</span></p><p style=\"text-indent: 2em; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"></p><p style=\"text-align: center;\"></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 1px; background: rgb(255, 255, 255);\">&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/d9cfb3ffdc67c04b.png.thumb.jpg\" title=\"图片1.png\" src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/d9cfb3ffdc67c04b.png.thumb.jpg\" alt=\"图片1.png\" /></span></p><p></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">数字人民币是人民币的数字化形态，是把人民币放进APP里。它和人民币完全等价，由中国人民银行发行的法定货币。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 1px; background: rgb(255, 255, 255);\">二、有什么特点？</span></p><p style=\"text-indent: 2em; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"></p><p style=\"text-align: center;\"></p><p style=\"text-align: center;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 1px; background: rgb(255, 255, 255);\">&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/33aa21fe066ab0c6.png.thumb.jpg\" title=\"图片2.png\" src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/33aa21fe066ab0c6.png.thumb.jpg\" alt=\"图片2.png\" /></span></p><p></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">数字人民币具有不可伪造、不可复制、可传递、可追踪、可加载智能合约、安全性等特点，利用NFC、蓝牙等数据传输技术可实现双方在有电力、无网络支持时的离线支付功能。持有数字人民币等同于持有现金，不会产生利息。同样，银行也不会收取兑出兑回的服务费。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 1px; background: rgb(255, 255, 255);\">三、有什么优势？</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">相比平台支付，数字人民币支付不经过第三方，顾客相当于拿现金支付，除政府部门在权限范围内调查非法交易的情况外，商户、第三方支付平台均无权获取消费者的支付数据，可控匿名，保护用户信息安全不外泄。数字人民币在提现、转账等操作环节中是免费的。在没有网络的情况下，数字人民币还支持离线支付。</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">四、数字人民币如何开通？</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">通过手机应用市场下载并安装数字人民币APP；</p><p style=\"text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"></p><p style=\"text-align: center;\"></p><p style=\"text-align: center;\">&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/ce511df7654cc413.png.thumb.jpg\" title=\"图片3.png\" src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/ce511df7654cc413.png.thumb.jpg\" alt=\"图片3.png\" /></p><p></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">输入手机号和验证码完成注册，选择一个银行或运营机构新建匿名钱包；</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"></p><p style=\"text-align: center;\"></p><p style=\"text-align: center;\"></p><p>&nbsp;</p><p></p><p style=\"text-indent: 37px; text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/945f08d50d5f9f51.png.thumb.jpg\" title=\"图片4.png\" src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/945f08d50d5f9f51.png.thumb.jpg\" alt=\"图片4.png\" />&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/a77524053663fdfd.png.thumb.jpg\" title=\"图片5.png\" src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/a77524053663fdfd.png.thumb.jpg\" alt=\"图片5.png\" /></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">目前钱包级别共有四类，刚开通的钱包默认为四类钱包，四类钱包属于匿名级别，只需一个手机号就可以使用，但如果你想将数字人民币转存进储蓄卡，可以按照以下步骤完成钱包实名认证和绑卡升级。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">点击“钱包管理”-“银行卡管理”-“升级并绑卡”</p><p style=\"text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"></p><p style=\"text-align: center;\"></p><p style=\"text-align: center;\">&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/1f688615256881b9.png.cthumb.jpg\" title=\"图片6.png\" src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/1f688615256881b9.png.cthumb.jpg\" alt=\"图片6.png\" /></p><p></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">数字人民币的使用也很便捷，<span style=\"text-decoration:underline;\">上滑</span>付款，<span style=\"text-decoration:underline;\">下滑</span>收款。^_^</p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">五、数字人民币和微信、支付宝有何不同？</p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><strong><br /></strong></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: center; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/1b23723a0f618099.png.cthumb.jpg\" title=\"165ff5e303c919b10c443555c93b829.png\" src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/1b23723a0f618099.png.cthumb.jpg\" alt=\"165ff5e303c919b10c443555c93b829.png\" /></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><br /></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">虽然数字人民币和微信、支付宝有本质区别，但在实际支付的过程中，这两种方式的体验差异并不大。</p><p style=\"text-indent:32px;line-height:40px\">&nbsp;</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"></p><p style=\"text-align: center;\"></p><p style=\"text-align: center;\"></p><p>&nbsp; &nbsp; &nbsp; 数字人民币是支付方式的重要补充，而不是某种支付方式的替代品。</p><p style=\"text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; margin-top: 15px; margin-bottom: 15px;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/fa8db26d2f9a5ea7.png.cthumb.jpg\" title=\"5b3d8e0da16242de1000beb07e226dc.png\" src=\"<!--#p8_attach#-->/cms/item/2023_09/18_15/fa8db26d2f9a5ea7.png.cthumb.jpg\" alt=\"5b3d8e0da16242de1000beb07e226dc.png\" /></p><p></p><p style=\"text-indent:32px;line-height:40px\">&nbsp;</p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3165','3990','1','','','1.要认真贯彻落实上级的决策部署，不准有令不行、有禁不止。2.要严格执行重大事项请示汇报制度，不准瞒报、迟报。3.要热情服务，坚持首问首诊负责制，不准相互推诿、态度生硬。4.要仪表端庄，着装整洁，工作时间穿工作服，不准穿短裤、拖鞋等上班。5.要严格执行请销假和','10.10.10.10','10.10.10.10','1695027136','<p style=\"margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px;\">1.要认真贯彻落实上级的决策部署，不准有令不行、有禁不止。</p><p style=\"margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px;\">2.要严格执行重大事项请示汇报制度，不准瞒报、迟报。</p><p style=\"margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px;\">3.要热情服务，坚持<span><ins datetime=\"2023-09-18T08:20\">首问</ins></span>首诊负责制，不准相互推诿、态度生硬。</p><p style=\"margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px;\">4.要仪表端庄，着装整洁，工作时间穿工作服，不准穿短裤、拖鞋等上班。</p><p style=\"margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px;\">5.要严格执行请销假和离盐报备制度，未经批假不准离岗。</p><p style=\"margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px;\">6.要保持良好的精神状态，工作、值班期间以及次日有重要任务如手术等不准饮酒，严禁酗酒。</p><p style=\"margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px;\">7.要遵守会风会纪，不准迟到早退、无故缺席。</p><p style=\"margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px;\">8.要自觉抵制不当言论，不准在互联网、微信朋友圈等媒体发布不当言论。</p><p style=\"margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px;\">9.要严格遵守廉洁从业相关规定，不准收“红包”、拿回扣、私自收费、转介患者、违规统方、骗取医保基金等。</p><p style=\"margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px;\">10.要严格遵守法律法规和纪律规定，不准从事赌博、酒驾等违纪违法活动。</p><p>&nbsp;</p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3166','3991','1','','','为进一步加强医院作风建设，提升医院管理效能和服务水平，树立医院良好形象，9月14日下午，盐城市一院召开“转作风、优服务、树形象”作风提升年活动动员部署会议。院领导班子全体成员、各党支部书记、中层干部、护士长参加会议，会议由邓义军院长主持。院党委书记王正','10.10.10.10','10.10.10.10','1695111168','<p style=\"text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">为进一步加强医院作风建设，提升医院管理效能和服务水平，树立医院良好形象，9月14日下午，盐城市一院召开“转作风、优服务、树形象”作风提升年活动动员部署会议。院领导班子全体成员、各党支部书记、中层干部、护士长参加会议，会议由邓义军院长主持。</p><p style=\"text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">院党委书记王正同志作活动动员讲话，强调开展作风建设提升行动是贯彻中央、省市委部署，践行“两个维护”的实际行动，是坚持刀刃向内、解决突出问题的迫切需要，是践行使命担当，树立医院形象的内在要求。一要统一思想，提高认识，增强作风建设的责任感与紧迫感。二要突出重点，精准发力，切实增强作风提升专项行动的针对性和实效性，煅造对党忠诚、主动作为、争先创优、清正廉洁的作风。三要加强领导，狠抓落实，坚持统筹兼顾，强化督导检查，以良好作风推动重点工作任务圆满完成。</p><p style=\"text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">此次作风提升活动将围绕责任意识不强，履职担当不够；宗旨意识淡薄，服务意识不强；纪律观念弱化，工作散漫拖沓；自我要求不严，廉洁意识不强等方面开展为期一年的作风专项整治活动，分动员部署、排查整改、总结提升三个阶段。目标是引导广大干部职工提振精神状态、转变工作作风、提高工作效率、提升服务质量，为营造风清气正的干事创业环境，加快推动区域医疗中心建设和医院高质量发展提供有力保障。</p><p style=\"text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\">会上，医疗、护理、医技、门诊、行政后勤等相关部门负责人作表态发言。</p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3167','3992','1','','','盐城市第一人民医院主院区配电房运行值守项目已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：一、项目基本情况1、项目名称：盐城市第一人民医院主院区配电房运行值守项目2、项目地','10.10.10.10','10.10.10.10','1695281114','<p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">盐城市第一人民医院主院区配电房运行值守项目已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>一、项目基本情况</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1、项目名称：盐城市第一人民医院主院区配电房运行值守项目</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2、项目地点：盐城市第一人民医院主院区（人民南路66号）</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">3、服务内容：盐城市第一人民医院主院区1#、2#变电所内所有设备及6台发电机组，院内高压等级20KV。对变配电设备巡视和倒送电操作，主要包括（但不限于）巡视设备运行状态、响声、仪表、指示灯管、保护动作情况、设备异常情况、变电所封堵、日常故障排除及倒送电操作等。具体内容详见招标人提供的项目需求。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">4、服务期限：服务期为1年</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">5、最高限价：49万元，投标人的投标报价不得高于招标人设定的最高限价，如高于最高限价的，作废标处理。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">6、本项目不接受联合体投标。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>二、申请人的资格要求：</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.满足《中华人民共和国政府采购法》第二十二条规定，并提供下列材料；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（1）法人或者其他组织的营业执照等证明文件，自然人的身份证明；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（2）上一年度的财务报表（成立不满一年不需提供）；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（3）依法缴纳税收和社会保障资金的相关材料；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（4）具备履行合同所必需的设备和专业技术能力的书面声明；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（5）参加政府采购活动前3年内在经营活动中没有重大违法记录的书面声明；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2.本项目的特定资格要求：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（1）投标人具有建设行政主管部门核发的电力工程施工总承包或输变电工程专业承包三级及以上资质的独立法人，并持有安全生产许可证和国家电监会派出机构颁发的承装（试、修）类均为四级及以上的电力设施许可证的独立法人。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（2）未被列入“信用中国”网站（www.creditchina.gov.cn）失信被执行人名单、重大税收违法案件当事人名单和中国政府采购网（www.ccgp.gov.cn）政府采购严重违法失信行为记录名单。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">三、投标报名及招标文件获取</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">请符合上述资质条件的投标人携带单位介绍信（注明联系人、邮箱、联系方式）和单位营业执照复印件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投标报名并领取招标文件，投标报名时间2023年9月21日8时30分至2023年9月28日18时。联系人：刘工，0515-69057406，13626222271。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">四、本项目采用“资格后审”，“综合评估法”。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">五、投标人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，采购人将拒绝投标报名。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">六、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话0515-66696810。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">七、对本次招标提出询问，请按以下方式联系。</p><p style=\"margin-left: 28px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">采购人：盐城市第一人民医院</p><p style=\"margin-left: 28px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"letter-spacing: 4px;\">地址</span><span style=\"letter-spacing: 0px;\">：</span>盐城市人民南路66号</p><p style=\"margin-left: 28px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系人：张女士</p><p style=\"margin-left: 28px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"letter-spacing: 4px;\">电话</span><span style=\"letter-spacing: 0px;\">：</span>0515-66696855</p><p style=\"margin-left: 28px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司</p><p style=\"margin-left: 28px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"letter-spacing: 4px;\">地址</span><span style=\"letter-spacing: 0px;\">：</span>盐城市青年西路号805室</p><p style=\"margin-left: 28px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系人：<span style=\"letter-spacing: 8px;\">刘</span><span style=\"letter-spacing: 0px;\">工</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3168','3993','1','','','盐城市第一人民医院护理人员护士鞋采购项目已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：一、盐城市招标代理有限公司受招标人委托具体负责本项目的招标事宜。二、符合本项目招标','10.10.10.10','10.10.10.10','1695281175','<p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">盐城市第一人民医院护理人员护士鞋采购项目已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">一、盐城市招标代理有限公司受招标人委托具体负责本项目的招标事宜。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">二、符合本项目招标公告资格要求的投标的申请人均可参加该项目投标。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>三、项目概况：</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1、项目名称：盐城市第一人民医院护理人员护士鞋采购项目。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2、供货地点：盐城市第一人民医院，具体供货地点根据招标人要求。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">3、招标规模：本次共采购护士鞋2010双，采购预算约30.15万元。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">4、招标范围及内容：盐城市第一人民医院护理人员护士鞋的采购、供应、运输、接卸、售后服务，具体供货内容及数量详见项目清单，招标人保留对上述招标范围进行适当调整的权利。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">5、服务期及供货时间要求：合同签订后30日历天内完成本项目的采购、供应、运输、接卸等相关伴随服务。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">6、质量要求</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">6.1 质量标准：符合招标文件中技术需求、国家现行的相关规范标准及有关部门最新颁布的相应的最新标准。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">6.2 质保期：本项目产品质保期一年。产品实行“三包”。在保修期内，一旦发生质量问题，投标人保证在接到通知24小时内响应，2天内赶到现场进行更换或退货，费用由投标人负责。如投标人在接到通知后24小时内没有答复，则视为投标人承认质量问题并承担由此发生的一切费用。更换数量及货物本身质量原因造成的直接经济损失应全部由投标人负责。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>四、投标人资</strong><strong>格要求</strong>：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1、投标人资格要求：投标人必须是在中国境内注册具有能力独立完成本次招标内容的独立法人。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2、投标人必须具有独立承担民事责任和履行合同能力。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">3、所投产品的生产厂家具有有效期内的ISO9001质量管理体系认证证书、ISO14001环境管理体系认证证书。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">4、投标人近三年内没有严重违约和重大质量问题、安全事故、投标违规等不良记录而被政府有关部门处罚，没有处于被责令停业、财产被接管、冻结、破产等状态。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">5、本次招标人一律不接受联合体投标，中标后不得以任何方式进行转包与分包。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">6、投标人应遵守国家和省、市的有关法律、法规和政策，且没有违法违规行为。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">五、招标文件获取：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">公告发布后，凡具备以上投标人资格要求，并自愿参加本次投标的投标人，须携带本单位介绍信或授权委托书以及购买人身份证原件于2023年9月21日至2023年9月28日止<strong><span style=\"text-decoration:underline;\">（上午8:30-12：00，下午14:30-18:00，节假日休息）</span></strong>到盐城市招标代理有限公司（地址：盐城市钱江财富广场A座3A楼，联系人：张工，联系电话：0515-89803566、<span style=\"color: rgb(68, 68, 68);\">13921809379</span>）行政服务部报名并购买招标文件，投标人购买招标文件后方可参加投标。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">六、投标保证金</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">本项目投标保证金为人民币5000元，具体详见招标文件。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">七、本项目资格审查办法采用“资格后审”；评标办法采用“综合评估法”，具体详见招标文件。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">八、本项目严禁挂靠，一经核实挂靠，将被取消投标、中标资格，并按相关规定进行处罚，直至建议有关部门吊销资质证书。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">九、联系方式</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">招标人：盐城市第一人民医院</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地 &nbsp;址：盐城市人民南路66号</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系人：边老师</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">电 &nbsp;话：0515-66696818</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">招标代理机构：盐城市招标代理有限公司</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地址：盐城市青年路钱江财富广场西区A座4楼</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系人：吴女士</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">电话：0515-89803566 &nbsp;15950298580</p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3169','3994','1','','','盐城市第一人民医院北区门诊楼与急诊楼消防泵控制柜及电源柜更换项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。1、工程概况1.1项目名称：盐城市第一人民医院北区门诊楼与急诊楼消防泵控制柜及电源柜更换项目1.2项目地点','10.10.10.10','10.10.10.10','1695625425','<p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">盐城市第一人民医院北区门诊楼与急诊楼消防泵控制柜及电源柜更换项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong>1、工程概况</strong></p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">1.1项目名称：盐城市第一人民医院北区门诊楼与急诊楼消防泵控制柜及电源柜更换项目</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">1.2项目地点：盐城市第一人民医院北院区（毓龙西路166号）</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">1.3招标内容：盐城市第一人民医院北区门诊楼与急诊楼消防泵控制柜及电源柜更换，消防泵专用控制柜2套，电源电缆约40米，具体详见采购清单。</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">1.4质量标准：符符合国家“合格”标准，并确保北院门诊的喷淋、消火栓系统所供水区域满足消防支队验收标准。</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">1.5工期要求：15日历天。</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">1.6最高限价：<span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">3.23</span><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">万元</span>。</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong>2 </strong><strong>投标人</strong><strong>应当具备的主要资格条件：</strong></p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">2.1投标人须具有建设行政主管部门核发的二级及以上消防设施工程专业承包资质，且取得安全生产许可证。</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">2.2项目负责人的资格要求：项目负责人须具有机电工程专业二级及以上建造师注册证书，同时应具有安全生产考核合格证（B类）。</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">2.3其他要求</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">2.3.1财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">2.3.2信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">2.4 本次招标不接受联合体投标。</p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><strong>3</strong><strong>、投标报名及招标文件的获取</strong></p><p style=\"margin: 15px 0px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"background: rgb(255, 255, 255);\">3.1</span><span style=\"background: rgb(255, 255, 255);\">投标报名时间2023年</span><span style=\"background: rgb(255, 