INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3015','3840','1','','/ewebeditor/uploadfile/image/2022/12/12/20221212100330770_s.jpg','12月7日下午，农工党盐城市第一人民医院基层委员会与中健（盐城）康复医院举行“战略合作”签约仪式。农工党市委会副主委胥江华、盐都区卫健委副主任朱海波应邀出席。农工党市一院基层委主委王军、中健（盐城）康复医院院长胡刚等参加。会上，王军主委与胡刚院长签署了','172.16.1.2','172.16.1.2','1670464886','<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">12</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">月<span>7</span>日下午，农工党盐城市第一人民医院基层委员会与中健（盐城）康复医院举行“战略合作”签约仪式。农工党市委会副主委胥江华、盐都区卫健委副主任朱海波应邀出席。农工党市一院基层委主委王军、中健（盐城）康复医院院长胡刚等参加。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">会上，王军主委与胡刚院长签署了战略合作协议。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">王军主委简要介绍了农工党市一院基层委的情况，并承诺今后将在医疗业务开展、教学人才培养、学科建设方面等给予大力支持，并通过战略合作，推进优质资源下沉，激发新思路，积累更多经验，共同为健康盐城以及大众健康发展发挥应有作用。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">胡刚院长表示，中健（盐城）康复医院将充分依托市一院的优质医疗资源，为群众提供更优质的医疗健康服务。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">大家还参观了中健（盐城）康复医院，并围绕医院管理、学科建设、医疗协作等方面进行深入交流。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\"><a href=\"/ewebeditor/uploadfile/image/2022/12/12/20221212100330770.jpg\" target=\"_blank\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<img src=\"/ewebeditor/uploadfile/image/2022/12/12/20221212100330770_s.jpg\" border=\"0\"></a><br>\r\n</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<a href=\"/ewebeditor/uploadfile/image/2022/12/12/20221212100500552.jpg\" target=\"_blank\"><img src=\"/ewebeditor/uploadfile/image/2022/12/12/20221212100500552_s.jpg\" border=\"0\"></a></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">&nbsp; </span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3016','3841','1','','','l新冠病毒感染后会相继出现哪些症状？发病第一天症状较轻。可能感到轻微咽干咽痛、身体乏力。发病第二天开始出现发热症状，部分年轻人高烧至39摄氏度左右。同时咽部不适感加重。发病第三天症状最重的一天。高热39摄氏度以上，浑身酸疼乏力，咽痛加剧。发病第四天体温的','172.16.1.2','172.16.1.2','1670897411','<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;text-indent:21.0pt;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';margin-left:21.0pt;text-align:left;\r\ntext-indent:-21.0pt;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;\r\nline-height:1.15;font-family:Wingdings\">l<span style=\"font:7.0pt \'Times New Roman\'\">&nbsp;\r\n</span></span><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:\r\n宋体\">新冠病毒感染后会相继出现哪些症状？</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">发病第一天</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">症状较轻。可能感到轻微咽干咽痛、身体乏力。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">发病第二天</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">开始出现发热症状，部分年轻人高烧至<span>39</span>摄氏度左右。同时咽部不适感加重。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">发病第三天</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">症状最重的一天。高热<span>39</span>摄氏度以上，浑身酸疼乏力，咽痛加剧。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">发病第四天</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">体温的高峰开始下降。很多人从这一天体温降至正常，不再发热，但仍然咽痛咽痒。一部分人开始流涕、咳嗽。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">发病第五天</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">体温基本降至正常。但是鼻塞、流涕、咽痛、咳嗽，身体乏力仍然存在。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">发病第六天</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">不再发热。咳嗽、流涕等症状加重。前期病毒导致的呼吸道黏膜破坏，人体通过流涕、咳嗽排除坏死的细胞。可能出现核酸转阴。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">发病第七天</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">所有的症状开始明显好转。核酸很大可能转阴。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15\"><span style=\"font-size:11.0pt;\r\nline-height:1.15;font-family:宋体\">需要注意的是，<b>普通中青年</b>的发病过程适用于以上情况，接种过新冠疫苗并完成加强免疫的人群病程更短、病情更轻。而部分老年人，尤其是<span>70</span>岁以上有基础疾病的老年人热程更为复杂。老年人感染病毒<span>5</span>天后如有明显憋气症状需用肺部<span>CT</span>影像来诊断是否有肺部感染。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15\"><span style=\"font-size:\r\n11.0pt;line-height:1.15;font-family:宋体\">&nbsp;</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;text-indent:21.0pt;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';margin-left:21.0pt;text-align:left;\r\ntext-indent:-21.0pt;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;\r\nline-height:1.15;font-family:Wingdings\">l<span style=\"font:7.0pt \'Times New Roman\'\">&nbsp;\r\n</span></span><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:\r\n宋体\">新冠感染轻症患者如出现以上症状，如何应对？</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">发热、浑身酸痛、肌肉痛等</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">明显影响休息、影响睡眠时，可服用解热镇痛的药物，<b>如对乙酰氨基酚片和布洛芬（胶囊、栓）、双氯芬酸钠（胶囊、栓）、塞来昔布胶囊、洛索洛芬钠片等</b>，儿童有<b>布洛芬混悬液、小儿布洛芬栓</b>可选用。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">需注意选择一种退烧药即可，很多感冒药为复方制剂，都含有对乙酰氨基酚，如：<b>复方氨酚烷胺胶囊、复方盐酸伪麻黄碱缓释胶囊、小儿氨酚黄那敏颗粒、复方对乙酰氨基酚片、氨麻美敏片（Ⅱ）、维<span>C</span>银翘片</b>等，使用前仔细阅读说明书，避免重复、超量用药，导致肝损伤。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">咳嗽、咳痰</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">如果出现轻度咳嗽，不必吃药</span><span style=\"font-size:11.5pt;line-height:1.15;font-family:宋体;color:#3E3E3E;\r\nletter-spacing:.4pt;background:white\">，</span><span style=\"font-size:11.0pt;\r\nline-height:1.15;font-family:宋体\">注意休息、保持良好心情；</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';margin-left:11.0pt;text-indent:-11.0pt;line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;\r\nfont-family:宋体\">对于严重咳嗽，可以适当服用止咳药进行对症处理，可选择的止咳化痰药有<b>急支糖浆、</b></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">氨溴索口服液、苏黄止咳胶囊、肺宁颗粒、橘红痰咳液、小儿消积止咳口服液、化湿败毒颗粒、肺力咳合剂、强力枇杷露<span>(</span>无糖型<span>)</span>、桂龙咳喘宁胶囊、复方甘草合剂、金振口服液（小儿）</span></b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">等。对于干咳无痰可选择<b>复方可待因口服溶液、枸橼酸喷托维林片</b>等。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';margin-left:11.05pt;text-indent:-11.05pt;line-height:1.15;background:white\"><b><span style=\"font-size:11.0pt;line-height:1.15;\r\nfont-family:宋体;color:red\">咽痛、咽干</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">多喝水有助于咽痛、咽干的缓解，<b>甘桔冰梅片、蓝芩口服液、板蓝根颗粒、蒲地蓝口服液、六神丸、六神胶囊</b>具有一定的疗效等。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">鼻塞、流涕</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">如果症状比较明显，建议使用<b>氯苯那敏、地氯雷他定（片、胶囊、糖浆）、左西替利嗪（片、口服液）</b>等抗过敏药其中一种。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">还可以选用<b>呋麻滴鼻液</b>缓解鼻塞症状。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">胃肠不适</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">注意饮食清淡，不要吃重油重盐和对胃肠道刺激性大的食物，可服用<b>藿香正气软胶囊</b>；腹泻患者应多喝水，及时服用<b>盐酸小檗碱、蒙脱石散</b>。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">基础病用药</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">新冠感染可能导致心脑血管疾病等慢性基础性疾病的加重，因此，有基础疾病者日常要规律用药，控制好病情。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">中成药</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">金花清感颗粒、连花清瘟颗粒、柴芩清宁胶囊、金银花口服液、蓝芩口服液、蒲地蓝消炎口服液、金振口服液、小儿解表口服液、荆防颗粒、小儿豉翘清热颗粒、银黄颗粒、疏风解毒胶囊、热炎宁合剂、双黄连颗粒、正柴胡饮颗粒<span>(</span>无蔗糖<span>)</span>、抗病毒口服液<span>(</span>无蔗糖<span>)</span></span></b><span style=\"font-size:11.0pt;line-height:1.15;\r\nfont-family:宋体\">等。以上药物备<span>1</span></span><span style=\"font-size:11.0pt;line-height:1.15;font-family:\'Times New Roman\',\'serif\'\">~</span><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">2</span><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">种即可，服药时选择一种，不要叠加，中成药成分较复杂，以免出现药物成分重叠导致用药过量。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">需要特别提醒的是，在轻症患者身上，以上症状可能单独、两个或者多个合并出现，但大多症状轻微，且具有一定的自限性。因此，千万不要自乱阵脚，反而应该沉着冷静应对。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">&nbsp;</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;color:red\">再次友情提醒 </span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">1.</span><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">西药退烧药选用一种即可，退烧药与中药感冒药尽量不要叠加使用，每日服用剂量不要超标，需特别注意对乙酰氨基酚每日最大剂量不超过<span>2g</span>，否则对肝脏有损伤；</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">2.</span><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">对于家里已有的感冒药物，需提前告知医师、药师，严格按说明书规定的用法用量用药，避免经验用药；</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">3.</span><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">服药期间不建议饮酒；</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">4.</span><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">抗菌药一定在医师指导下使用，不要滥用。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体;background:\r\nyellow\">&nbsp;</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;text-indent:21.0pt;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';margin-left:21.0pt;text-align:left;\r\ntext-indent:-21.0pt;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;\r\nline-height:1.15;font-family:Wingdings\">l<span style=\"font:7.0pt \'Times New Roman\'\">&nbsp;\r\n</span></span><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:\r\n黑体\">每个人均应提高自我防护意识，当好自己健康的第一责任人</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">尽快接种新冠疫苗，出门佩戴口罩，做好手卫生，家里定期开窗通风，保持良好的生活作息习惯，强身健体，增强自身抵抗力。积极调整自己的心理状态，乐观向上，避免焦躁、抑郁等负面情绪。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">&nbsp;</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';line-height:1.15;background:white\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">人生至善，就是对生活乐观，对工作愉悦，对事业兴奋，时刻保持愉悦的心情，从而达到防治疾病的目的。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><b><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">疫情终将过去，春天总会到来，祝您早日康复！</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;line-height:1.15\" align=\"left\"><span style=\"font-size:11.0pt;line-height:1.15;font-family:宋体\">&nbsp;</span></p>','','','','','');
