INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3037','3862','1','','<!--#p8_attach#-->/cms/item/2023_03/03_14/fe16d3503a7f7d43.jpg.thumb.jpg','维生素是人为维持正常的生理功能，而必须从食物中获得的一类微量有机物质。维生素在人体生长、代谢、发育过程中发挥着重要的作用。维生素有如此多种，对我们的免疫力都能起到什么作用？缺乏维生素会有哪些表现？应该怎么补充呢？脂溶性维生素不溶于水而溶于脂肪、有机溶','172.16.1.2','172.16.1.2','1677823921','<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;\r\nletter-spacing:.5pt;background:white\">维生素是人为维持正常的生理功能，</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;\r\nletter-spacing:.5pt;background:white\">而必须从食物中获得的一类微量有机物质。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;\r\nletter-spacing:.5pt;background:white\">维生素在人体生长、代谢、发育过程中发挥着重要的作用。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;\r\nletter-spacing:.5pt;background:white\">维生素有如此多种，</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;\r\nletter-spacing:.5pt;background:white\">对我们的免疫力都能起到什么作用？</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;\r\nletter-spacing:.5pt;background:white\">缺乏维生素会有哪些表现？</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;\r\nmargin-left:5.0pt;margin-bottom:.0001pt;text-align:center;line-height:18.4pt\" align=\"center\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;\r\nletter-spacing:.5pt;background:white\">应该怎么补充呢？</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';background:white\"><strong><span style=\"font-size:\r\n9.0pt;font-family:\'Microsoft YaHei UI\';color:#222222;letter-spacing:.25pt;\r\nbackground:white\">脂溶性维生素</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;line-height:18.4pt\"><span style=\"font-size:8.5pt;\r\nfont-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:\r\nwhite\">不溶于水而溶于脂肪、有机溶剂，<strong>可随脂肪被人体吸收并在体内储积</strong>。这类维生素可以在体内大量储存，<strong>其主要贮存于肝脏部位</strong>，如果<strong>摄入过量则可引发中毒。</strong></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;line-height:18.4pt\"><span style=\"font-size:8.5pt;\r\nfont-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:\r\nwhite\">包括：<strong>维生素<span>A</span>、维生素<span>D</span>、维生素<span>E</span>、维生素<span>K</span></strong>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">01</span></strong><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">维生素<span>A</span></span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:19.0pt;\r\nline-height:18.4pt\"><strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">维生素<span>A</span>能促进免疫球蛋白的合成，提高人体感染和抗肿瘤能力</span></strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';color:#595959;\r\nletter-spacing:.5pt;background:white\">；维生素<span>A</span>还能消除免疫耐受。影响免疫反应的各种细胞因子也可能在维生素<span>A</span>缺乏时发生改变，从而影响体内抗原<span>-</span>抗体反应。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:19.0pt;\r\nline-height:18.4pt\"><strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">维生素<span>A</span>多存在于动物性食物中，如动物肝脏、鱼卵、全奶、禽蛋等</span></strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';color:#595959;\r\nletter-spacing:.5pt;background:white\">；除了动物性食物，植物性食物中的类胡萝卜素可以转换为维生素<span>A</span>，其主要存在于<strong>深绿色或红橙黄色的蔬菜或水果中，如西蓝花、菠菜、空心菜、芒果、杏子、柿子等</strong>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">02</span></strong><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">维生素<span>D</span></span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:19.0pt;\r\nline-height:18.4pt\"><strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">维生素<span>D</span>是一种重要的免疫调节剂</span></strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';color:#595959;\r\nletter-spacing:.5pt;background:white\">，体内包括免疫细胞在内的大部分细胞含有维生素<span>D</span>受体。巨噬细胞中的维生素<span>D</span>可调节内源性组织蛋白酶抑制素的合成，并调节细胞因子分泌的模式；组织蛋白酶抑制素和细胞因子都能增强人体对病原体的防御能力。<strong>维生素<span>D</span>缺乏的人更容易发生呼吸道感染，其感染频率更高、患病程度也更严重。</strong></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:19.0pt;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">人体维生素<span>D</span>主要通过<strong>由皮肤接受紫外线照射而合成</strong>或从膳食中获得，<strong>食物中的维生素<span>D</span>多存在于含脂肪高的海鱼、动物肝脏、蛋黄和奶油等。</strong></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:18.4pt\"><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">&nbsp;</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:18.4pt\"><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">03</span></strong><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">维生素<span>E</span></span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:19.0pt;\r\nline-height:18.4pt\"><strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">维生素<span>E</span>对维持正常免疫功能，特别是对<span>T</span>淋巴细胞的功能很重要</span></strong><span style=\"font-size:8.5pt;\r\nfont-family:\'微软雅黑\',\'sans-serif\';color:#595959;letter-spacing:.5pt;background:\r\nwhite\">：能增强淋巴细胞对有丝分裂原的刺激反应性和抗原、抗体反应，促进吞噬；有研究表明维生素<span>E</span>可通过影响核酸、蛋白质代谢，进一步影响免疫功能。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:19.0pt;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">日常膳食中维生素<span>E</span>的<strong>最主要来源是植物油。</strong>此外，<strong>大麦、燕麦和米糠，以及坚果</strong>也是维生素<span>E</span>的优质来源。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:9.0pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">水溶性维生素</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;line-height:18.4pt\"><span style=\"font-size:8.5pt;\r\nfont-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:\r\nwhite\">可溶于水，<strong>除维生素<span>B12</span>外</strong>，其他水溶性维生素几乎不会在体内贮存，<strong>大多会随着尿液排出体外</strong>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;line-height:18.4pt\"><span style=\"font-size:8.5pt;\r\nfont-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:\r\nwhite\">包括：维生素<span>B1</span>、维生素<span>B2</span>、维生素<span>B6</span>、维生素<span>B12</span>、维生素<span>C</span>、泛酸、叶酸、烟酸和生物素。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">&nbsp;</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">01</span></strong><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">维生素<span>B6</span></span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:21.0pt;\r\nline-height:21.0pt\"><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:black\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:21.0pt;\r\nline-height:18.4pt\"><strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">维生素<span>B6</span>促进体内抗体的合成，有利于核酸和蛋白质的合成以及细胞的增殖。</span></strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';color:#595959;\r\nletter-spacing:.5pt;background:white\">缺乏维生素<span>B6</span>时抗体的合成减少，人体抵抗力下降，会损害<span>DNA</span>的合成。因此，维生素<span>B6</span>对维持适宜的免疫功能非常重要。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:21.0pt;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:black\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:21.0pt;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">维生素<span>B6</span>的食物来源广泛，<strong>动植物性食物中均含有，如鸡肉、鱼肉、动物肝脏、全谷类产品、坚果类和蛋黄等</strong>。蔬菜和水果中维生素<span>B6</span>含量也较多，如<strong>香蕉、卷心菜、菠菜</strong>等。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">02</span></strong><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">维生素<span>C</span></span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:21.0pt;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:black\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:21.0pt;\r\nline-height:18.4pt\"><strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">维生素<span>C</span>具有抗氧化作用，可以保护免疫细胞免受氧化损伤，促进抗体形成。</span></strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';color:#595959;\r\nletter-spacing:.5pt;background:white\">维生素<span>C</span>可调节吞噬细胞的功能、<span>T</span>淋巴细胞的增殖、细胞因子的产生和单核细胞黏附分子的基因表达等。此外，维生素<span>C</span>还可<strong>防止和延缓维生素<span>A</span>和维生素<span>E</span>的氧化</strong>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:21.0pt;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:black\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;text-indent:21.0pt;\r\nline-height:18.4pt\"><strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">维生素<span>C</span>主要来源于食物，新鲜蔬菜和水果中含量最多，如茼蒿、白菜、菠菜、红枣、草莓、柑橘等。</span></strong><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';color:#595959;\r\nletter-spacing:.5pt;background:white\">如能经常摄入丰富的新鲜蔬菜和水果，并合理烹调，一般能满足人体需要。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:9.0pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">缺乏维生素时会有什么表现？</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:21.0pt\"><strong><span style=\"font-size:8.5pt;font-family:PingFangSC-Regular;color:#222222;letter-spacing:\r\n.5pt;background:white\">维生素<span>A</span></span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\ntext-indent:19.0pt;line-height:18.4pt\"><strong><span style=\"font-size:8.5pt;\r\nfont-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:\r\nwhite\">缺乏维生素<span>A</span></span></strong><span style=\"font-size:\r\n8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;\r\nbackground:white\">时会出现<strong>上皮干燥、增生和角质化现象</strong>，其中以眼部、呼吸道、尿道及生殖器受影响最明显，<strong>可能导致成年人不育。</strong></span><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#00B050;\r\nletter-spacing:.5pt;background:white\">人体每日需要量<span>1.5</span>毫克。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:12.0pt;text-align:left\" align=\"left\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:18.4pt\"><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.5pt;background:white\">B</span></strong><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.5pt;background:white\">族维生素</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\ntext-indent:19.0pt;line-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\r\n\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:white\">由于国人饮食过于精细化，导致<span>B</span>族维生素严重缺乏，尤其是维生素<span>B1</span>和<span>B2</span>：</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;margin-left:5.0pt;\r\nmargin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">维生素<span>B1</span>，又称硫胺素，<strong>维生素<span>B1</span></strong>主要负责调节神经组织的各种活动，它的<strong>缺乏会引起注意力不集中、记忆力衰退及抑郁等问题，非真菌感染型的脚气病</strong>也与它有关。</span><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#00B050;\r\nletter-spacing:.5pt;background:white\">每日需<span>1-2</span>毫克。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\ntext-indent:19.0pt;line-height:18.4pt\"><strong><span style=\"font-size:8.5pt;\r\nfont-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:\r\nwhite\">维生素<span>B2</span></span></strong><span style=\"font-size:\r\n8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;\r\nbackground:white\">，又称核黄素。<strong>人体缺乏时会出现唇炎、口角炎、舌水肿、舌炎、眼角膜炎等</strong>。</span><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#00B050;\r\nletter-spacing:.5pt;background:white\">每日需<span>1.2-1.4</span>毫克。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\ntext-indent:19.0pt;line-height:18.4pt\"><strong><span style=\"font-size:8.5pt;\r\nfont-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:\r\nwhite\">维生素<span>B6</span></span></strong><span style=\"font-size:\r\n8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;\r\nbackground:white\">，又称吡哆醇，<strong>长期缺乏引起皮炎，甚至损伤中枢神经系统和造血系统</strong>。</span><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#00B050;\r\nletter-spacing:.5pt;background:white\">每日需<span>2-3</span>毫克。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:8.0pt;font-family:PingFangSC-Regular;color:white;letter-spacing:\r\n.5pt;background:white\">2&nbsp; </span></strong><strong><span style=\"font-size:\r\n8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;\r\nbackground:white\">维生素<span>B12</span></span></strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;\r\nletter-spacing:.5pt;background:white\">，<strong>缺乏时会发生巨幼红细胞性贫血</strong>。</span><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#00B050;\r\nletter-spacing:.5pt;background:white\">每日需<span>2-3</span>微克。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:12.0pt;text-align:left\" align=\"left\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\"><br>\r\n</span><strong><span style=\"font-size:8.5pt;font-family:PingFangSC-Regular;\r\ncolor:#222222;letter-spacing:.5pt;background:white\">维生素<span>C</span></span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\ntext-indent:19.0pt;line-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\r\n\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:white\">又称抗坏血酸。它是一种人体无法自身合成的水溶性维生素，需要从外界食物中获取。<strong>缺乏<span>VC</span>，创伤或溃疡不易愈合，骨骼牙齿易于脆断，同时出现坏血病，毛细血管 通透性增加，容易破裂出血，严重时肌肉、内脏出血导致死亡。</strong></span><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#00B050;\r\nletter-spacing:.5pt;background:white\">每日需<span>30-60</span>毫克。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;margin-left:5.0pt;\r\nmargin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:21.0pt\"><strong><span style=\"font-size:8.5pt;font-family:PingFangSC-Regular;color:#222222;letter-spacing:\r\n.5pt;background:white\">维生素<span>D</span></span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\ntext-indent:19.0pt;line-height:18.4pt\"><span style=\"font-size:8.5pt;\r\nfont-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:\r\nwhite\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\ntext-indent:19.0pt;line-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\r\n\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:white\">又称抗佝偻病维生素。<strong>当人体缺乏维生素<span>D</span>时会出现佝偻病、软骨病、骨质疏松症等</strong>。</span><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#00B050;\r\nletter-spacing:.5pt;background:white\">每日需<span>0.01</span>毫克。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:21.0pt\"><strong><span style=\"font-size:8.5pt;font-family:PingFangSC-Regular;color:#222222;\r\nletter-spacing:.5pt;background:white\">&nbsp;</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:21.0pt\"><strong><span style=\"font-size:8.5pt;font-family:PingFangSC-Regular;color:#222222;letter-spacing:\r\n.5pt;background:white\">维生素<span>E</span></span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">又称生育酚<strong>。维生素<span>E</span>的基本功能是保护细胞和细胞内部结构完整</strong>，防止某些酶和细胞内部成分遭到破坏。它另外一个主要生理功能就是<strong>抗不育</strong>。维生素<span>E</span>在体内一般不易缺乏，而且也是公认为不具有过剩症的脂溶性维生素，毒性非常低。</span><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#00B050;\r\nletter-spacing:.5pt;background:white\">每日需量<span>20-30</span>毫克。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:5.0pt;margin-bottom:0cm;margin-left:5.0pt;\r\nmargin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:21.0pt\"><strong><span style=\"font-size:8.5pt;font-family:PingFangSC-Regular;color:#222222;letter-spacing:\r\n.5pt;background:white\">维生素<span>K</span></span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt;background:white\"><strong><span style=\"font-size:\r\n8.5pt;font-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;\r\nbackground:white\">&nbsp;</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt;background:white\"><strong><span style=\"font-size:8.5pt;\r\nfont-family:\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:\r\nwhite\">缺乏维生素<span>K</span>时会出现皮下、骨肉及胃肠道出血及血液凝固时间延长。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt;background:white\"><span style=\"font-size:8.5pt;font-family:\r\n\'Microsoft YaHei UI\';color:#595959;letter-spacing:.5pt;background:white\">植物绿叶组织中含量多。</span><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#00B050;\r\nletter-spacing:.5pt;background:white\">每日需量<span>0.1</span>毫克。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:9.0pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">&nbsp;</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:9.0pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">关于补充维生素</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';line-height:21.0pt\"><strong><span style=\"font-size:8.5pt;font-family:\r\nPingFangSC-Regular;color:white;letter-spacing:.5pt;background:#C3D69B\">什么时候需要补充维生素？</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;\r\nfont-family:\'微软雅黑\',\'sans-serif\';color:#595959;letter-spacing:.5pt;background:\r\nwhite\">总的来说，有<span> 3 </span>种情况：<strong>消耗多、没补够、吸收差</strong>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">消耗多：长时间用眼的、爱熬夜的、女性朋友、健身人群</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">没补够：长期吃素肉没吃够的、就是不爱吃菜的、减肥的、挑食偏食的、没机会晒太阳的</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.5pt;font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:#595959;letter-spacing:.5pt;background:white\">吸收差：上了年纪的、胃肠道功能不好的、抽烟喝酒的</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:21.0pt;\r\nbackground:white\"><strong><span style=\"font-size:8.5pt;font-family:PingFangSC-Regular;\r\ncolor:white;letter-spacing:.5pt;background:#C3D69B\">维生素片能代替果蔬吗？</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt;background:white\"><span style=\"font-size:8.5pt;font-family:\r\n\'微软雅黑\',\'sans-serif\';color:#595959;letter-spacing:.5pt;background:white\">不能。这些膳食补充剂里面仅含有单个或部分维生素成分，通常仅能对其标明的成分进行补充。而水果蔬菜中除了这些必须的维生素之外，还富含大量其他的必需营养物质，如膳食纤维、蛋白质、糖类、脂类、矿物质等。<strong>《中国居民膳食指南（<span>2022</span>）》中推荐每天摄入不少于<span>300g</span>的新鲜蔬菜和<span>200-350g</span>的新鲜水果。</strong></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt;background:white\"><span style=\"font-size:8.5pt;font-family:\r\n\'微软雅黑\',\'sans-serif\';color:#595959;letter-spacing:.5pt;background:white\">补充维生素最理想的方式是从食物中摄取，果蔬比单纯的维生素补充剂营养更均衡，不可因为吃了维生素片，就放弃食用果蔬。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:21.0pt;\r\nbackground:white\"><strong><span style=\"font-size:8.5pt;font-family:PingFangSC-Regular;\r\ncolor:white;letter-spacing:.5pt;background:#C3D69B\">维生素摄入是多多益善吗？</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt;background:white\"><span style=\"font-size:8.5pt;font-family:\r\n\'微软雅黑\',\'sans-serif\';color:#595959;letter-spacing:.5pt;background:white\">其实当饮食种类丰富时，不必额外补充维生素，过量摄入反而可能诱发不良症状。<strong>对于身体状况正常的人群来说，日常饮食就能满足许多微量元素的需求</strong>，如果没吃够，<strong>优先考虑食补。</strong></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-right:5.0pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt;background:white\"><span style=\"font-size:8.5pt;font-family:\r\n\'微软雅黑\',\'sans-serif\';color:#595959;letter-spacing:.5pt;background:white\">饮食改善实在有难度，<strong>再考虑营养素补充剂</strong>。如果同时吃几种膳食补充剂，记得看一下几个产品中是否有相同的元素，<strong>每天的服用剂量相加不应该超过这个元素摄入量的上限。</strong></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:21.0pt;\r\nbackground:white\"><span style=\"font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:black;letter-spacing:.75pt\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:center;line-height:21.0pt\" align=\"center\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:21.0pt;\r\nbackground:white\"><span style=\"font-family:\'微软雅黑\',\'sans-serif\';\r\ncolor:black;letter-spacing:.75pt\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:center;line-height:18.4pt\" align=\"center\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:18.4pt\"><span style=\"font-size:7.0pt;font-family:\'Microsoft YaHei UI\';color:#D8D8D8;\r\nletter-spacing:.25pt;background:white\">来源：中国疾控动态、丁香医生、广州健康通</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph;line-height:18.4pt\"><span style=\"font-size:7.0pt;font-family:\'Microsoft YaHei UI\';color:#D8D8D8;\r\nletter-spacing:.25pt;background:white\">版权归作者所有，如有侵权请及时联系，我们将立即删除，谢谢</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3038','3863','1','','<!--#p8_attach#-->/cms/item/2023_03/03_14/2c84a2fafb93be8a.jpg.thumb.jpg','鸡蛋，低调的外表下，藏着一颗营养丰富的心：富含蛋白质及17种氨基酸、维生素A、钙、铁等成分，还含有现代人易缺乏的磷脂、胆碱……因此也被称为“全能营养食品”。再加上蒸、煮、煎、炒多样化的做法，俘获了无数人的胃，成为了餐桌常客。但是鸡蛋的胆固醇含量较高，也','172.16.1.2','172.16.1.2','1676873870','<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">鸡蛋，低调的外表下，藏着一颗营养丰富的心：富含蛋白质及<span>17</span>种氨基酸、维生素<span>A</span>、钙、铁等成分，还含有现代人易缺乏的磷脂、胆碱……因此也被称为“全能营养食品”。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">再加上蒸、煮、煎、炒多样化的做法，俘获了无数人的胃，成为了餐桌常客。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">但是鸡蛋的胆固醇含量较高，也因此备受争议，有人把它当物美价廉的“营养佳品”，天天吃；有人却担心它成为“血管杀手”，甚至网上传言，每天增加<span>50g</span>鸡蛋，心血管疾病的死亡率<span>9%</span>……</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">那么问题来了，鸡蛋究竟是有害还是有益呢？今天可可就带大家一起来研究研究！看完后就能解除困惑了<span>~</span></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';background:white\"><strong><span style=\"font-size:11.0pt;font-family:\'Microsoft YaHei UI\';color:white;\r\nletter-spacing:.25pt;background:#3158BA\">1</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-left:1.0pt;text-align:justify;text-justify:inter-ideograph;\r\nbackground:#3158BA;vertical-align:top\"><span style=\"color:white\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:center;line-height:18.4pt\" align=\"center\"><strong><span style=\"font-size:10.0pt;font-family:宋体;color:#3158BA;letter-spacing:.25pt;\r\nbackground:white\">鸡蛋胆固醇高，到底“是敌是友”？</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:center;line-height:18.4pt\" align=\"center\"><strong><span style=\"font-size:10.0pt;font-family:宋体;color:#3158BA;letter-spacing:.25pt;\r\nbackground:white\">国内外的研究告诉你答案！</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">每个鸡蛋中约有<strong><span>141</span>–<span>234mg</span>的胆固醇</strong>，但并不代表，吃鸡蛋会诱发心血管事件。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><strong><span style=\"font-size:8.0pt;font-family:宋体;\r\ncolor:#3158BA;letter-spacing:.25pt;background:white\">①</span></strong><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;\r\nbackground:white\">我国一项研究对<span>2.8</span>万名没有心血管疾病人群进行了近<span>10</span>年的随访发现，每天一个鸡蛋不会显著增加心血管病风险，反而<strong>有利于降低中风的风险</strong>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><strong><span style=\"font-size:8.0pt;font-family:宋体;\r\ncolor:#3158BA;letter-spacing:.25pt;background:white\">②</span></strong><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;\r\nbackground:white\">北京大学公共卫生学院纳入了<span>416213</span>人，探索鸡蛋摄入频率与心血管疾病、缺血性心脏病、主要冠脉事件、缺血性卒中以及出血性卒中风险之间的关联。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">结果发现，比起基本不吃鸡蛋的人，<strong>每天吃鸡蛋的人心血管事件发生风险</strong></span><strong><span style=\"font-size:8.0pt;font-family:宋体;color:#C00000;letter-spacing:.25pt;\r\nbackground:white\">降低了<span>11%</span></span></strong><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;\r\nbackground:white\">，<strong>死亡风险</strong></span><strong><span style=\"font-size:\r\n8.0pt;font-family:宋体;color:#C00000;letter-spacing:.25pt;background:white\">降低<span>18%</span></span></strong><span style=\"font-size:8.0pt;font-family:\r\n宋体;color:#222222;letter-spacing:.25pt;background:white\">。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">其实鸡蛋中除了胆固醇，还有很多</span><span style=\"font-size:8.0pt;font-family:宋体;color:#C00000;letter-spacing:.25pt;\r\nbackground:white\">利于预防心血管病的成分</span><span style=\"font-size:8.0pt;font-family:\r\n宋体;color:#222222;letter-spacing:.25pt;background:white\">，比如<strong>多种<span>B</span>族维生素、甜菜碱、叶黄素、玉米黄素和少量<span>Omega-3</span>脂肪酸</strong>，所以适量摄入的话并不会威胁健康，还会带来各种好处：</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">1</span></strong><strong><span style=\"font-size:9.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;\r\nbackground:white\">改善大脑功能</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">鸡蛋中含<strong>大量蛋白质、维生素以及丰富的抗氧化剂</strong>，可<strong>改善大脑前额叶功能及执行功能</strong>。东芬兰大学的一项研究也证实了吃鸡蛋对大脑的好处。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">该研究招募了近<span>2500</span>名<span>42</span>～<span>60</span>岁的受试者，一组每天早上吃一个鸡蛋，另一组正常饮食。在<span>22</span>年的随访后，测试了参试者的记忆、认知以及心理情况。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">结果发现，吃鸡蛋的参试者测试成绩普遍较好，<strong>患老年痴呆的比例远低于未吃鸡蛋的一组。</strong></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">2</span><strong><span style=\"font-size:\r\n9.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">改善糖代谢能力</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">鸡蛋中的<strong>蛋白质、氨基酸、钾、镁等多种营养物质有助于恢复胰岛功能</strong>，改善人体代谢糖的能力，还有助于<strong>抑制体内炎症</strong>，进而降低糖尿病等多种慢病风险。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;\r\ncolor:#222222;letter-spacing:.25pt;background:white\">2015</span><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;\r\nbackground:white\">年，《美国临床营养学杂志》刊登的一项东芬兰大学的研究发现，<strong>每周吃<span>4</span>个鸡蛋，患<span>2</span>型糖尿病的风险</strong></span><strong><span style=\"font-size:8.0pt;\r\nfont-family:宋体;color:#C00000;letter-spacing:.25pt;background:white\">降低<span>38%</span></span></strong><strong><span style=\"font-size:8.0pt;\r\nfont-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">3</span><strong><span style=\"font-size:\r\n9.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">帮助降压</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">爱尔兰皇家外科医学院在《科学报告》上发表的研究显示，吃天然富含<strong><span>Omega-3</span>多不饱和脂肪酸</strong>的鸡肉和鸡蛋<strong>可帮助降低血压</strong>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">4</span><strong><span style=\"font-size:\r\n9.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">保护肝脏</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">鸡蛋中的<strong>蛋白质对肝脏组织损伤有修复作用</strong>；蛋黄中的<strong>卵磷脂可促进肝细胞的再生</strong>；还可提高人体血浆蛋白量，<strong>增强机体的代谢功能和免疫功能。</strong></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">因此对于肝损伤的人群，每天早上吃个蛋很有好处。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:11.0pt;font-family:\'Microsoft YaHei UI\';color:white;\r\nletter-spacing:.25pt;background:#3158BA\">2</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-left:1.0pt;text-align:justify;text-justify:inter-ideograph;\r\nbackground:#3158BA;vertical-align:top\"><span style=\"color:white\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:center;line-height:18.4pt\" align=\"center\"><strong><span style=\"font-size:10.0pt;font-family:宋体;color:#3158BA;letter-spacing:.25pt;\r\nbackground:white\">国人每天吃几个鸡蛋最合适？</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:center;line-height:18.4pt\" align=\"center\"><strong><span style=\"font-size:10.0pt;font-family:宋体;color:#3158BA;letter-spacing:.25pt;\r\nbackground:white\">这个“上限”要心中有数</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">1</span><strong><span style=\"font-size:\r\n9.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">每周<span>3~6</span>个鸡蛋，可改善血脂</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">中国医学科学院阜外医院发表的研究指出，对中国成年人来说，每周吃<strong><span>3~6</span>个鸡蛋</strong>，可<strong>明显改善血脂代谢</strong>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">这项研究纳入了<span>60952</span>名成年人，随访一年后发现，与每周吃鸡蛋<span>&lt;3</span>个的人相比，每周吃鸡蛋<span>3~6</span>个的人血脂指标更好，具体表现为：</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">总胆固醇、甘油三酯、低密度脂蛋白胆固醇、非高密度脂蛋白胆固醇水平分别降低了<span>0.606</span>、<span>1.465</span>、<span>0.848</span>、<span>1.071mg/dl</span>，而高密度脂蛋白胆固醇水平升高了<span>0.461mg/dl</span>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">2</span><strong><span style=\"font-size:\r\n9.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">每周超过<span>6</span>个，死亡风险高</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">中国农业大学研究团队在《欧洲公共卫生杂志》上发表研究显示，吃太多鸡蛋不利于健康，<strong>每周摄入鸡蛋不应超过<span>6</span>个</strong>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">研究人员招募了<span>9444</span>名年龄在<span>45-64</span>岁的受试者，历经<span>14</span>年发现：鸡蛋的食用量与死亡风险密切相关——每周食用鸡蛋<span>3</span>个以下和<span>3~6</span>个的受试者，死亡风险没有发生明显的变化，而<strong>每周食用鸡蛋<span>6</span>个以上</strong>的受试者，<strong>死亡风险升高了</strong></span><strong><span style=\"font-size:8.0pt;font-family:宋体;color:#C00000;letter-spacing:\r\n.25pt;background:white\">35%</span></strong><span style=\"font-size:8.0pt;\r\nfont-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;\r\ncolor:#222222;letter-spacing:.25pt;background:white\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">总的来说，普通人群每周可以吃</span><strong><span style=\"font-size:8.0pt;font-family:宋体;color:#C00000;letter-spacing:\r\n.25pt;background:white\">3~6</span></strong><strong><span style=\"font-size:8.0pt;\r\nfont-family:宋体;color:#C00000;letter-spacing:.25pt;background:white\">个</span></strong><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;\r\nbackground:white\">鸡蛋，而</span><strong><span style=\"font-size:8.0pt;font-family:\r\n宋体;color:#C00000;letter-spacing:.25pt;background:white\">高血脂、胆囊炎患者则要适当“减量”。</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><strong><span style=\"font-size:11.0pt;font-family:\'Microsoft YaHei UI\';color:white;\r\nletter-spacing:.25pt;background:#3158BA\">3</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-left:1.0pt;text-align:justify;text-justify:inter-ideograph;\r\nbackground:#3158BA;vertical-align:top\"><span style=\"color:white\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:center;line-height:18.4pt\" align=\"center\"><strong><span style=\"font-size:10.0pt;font-family:宋体;color:#3158BA;letter-spacing:.25pt;\r\nbackground:white\">鸡蛋有个最佳吃法，</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:center;line-height:18.4pt\" align=\"center\"><strong><span style=\"font-size:10.0pt;font-family:宋体;color:#3158BA;letter-spacing:.25pt;\r\nbackground:white\">但这<span>4</span>种没营养还危险</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><strong><span style=\"font-size:8.0pt;font-family:宋体;\r\ncolor:#3158BA;letter-spacing:.25pt;background:white\">蒸煮蛋</span></strong><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;\r\nbackground:white\">是最佳的鸡蛋吃法，吸收率和营养素保存最高，但时间别太久，在<strong>煮沸状态下保持<span>3~5</span>分钟</strong>，蛋清凝固，蛋黄刚刚凝固、质地软嫩、颜色金黄，这时候营养物质的<strong>氧化程度相对较低</strong>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">不过，很多人嫌水煮蛋没味道，于是出现了各种各样的吃法，但要注意的是，以下四种吃法不但会损失营养，还可能带来伤害。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">1</span><strong><span style=\"font-size:\r\n9.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">生鸡蛋、溏心蛋</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">鸡蛋容易沾染多种致病菌，比如常见的<strong>沙门氏菌和大肠杆菌</strong>。这些细菌能通过鸡蛋壳上的微孔侵入鸡蛋内部，可能引发食物中毒。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">我国约有<span>10%</span>的鸡蛋表面被沙门氏菌污染，<span>64%</span>的蛋壳大肠杆菌严重超标，所以更建议</span><strong><span style=\"font-size:\r\n8.0pt;font-family:宋体;color:#3158BA;letter-spacing:.25pt;background:white\">煮熟吃</span></strong><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;\r\nbackground:white\">。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">2</span><strong><span style=\"font-size:\r\n9.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">煎蛋、炸蛋</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">不论是煎蛋还是炸蛋，都要用较多的食用油，不仅热量较高，还容易导致蛋白变性，甚至变焦，产生有害物质，影响身体健康。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span>&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">3</span><strong><span style=\"font-size:\r\n9.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">茶叶蛋、卤蛋</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">制作茶叶蛋和卤蛋需要使用大量茶叶、香料、酱油、盐等，容易过多摄入<strong>钠元素</strong>。而且为了入味，煮的时间往往很长，易造成鸡蛋的营养流失，<strong>蛋白质过分凝固，胆固醇、脂肪酸氧化，会进一步加重肠胃负担。</strong></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">&nbsp;</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left\" align=\"left\"><span style=\"font-size:8.5pt;font-family:\'Microsoft YaHei UI\';color:#222222;\r\nletter-spacing:.25pt;background:white\">4</span><strong><span style=\"font-size:\r\n9.0pt;font-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">毛鸡蛋</span></strong></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">毛鸡蛋又叫死胎蛋，是一种没有成功完成孵化的鸡蛋。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;font-family:宋体;color:#222222;\r\nletter-spacing:.25pt;background:white\">毛鸡蛋很<strong>容易被沙门氏菌等污染</strong>，如果储存、加工不规范，在适宜的温度下非常利于细菌繁殖，食用后对健康有危害。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-bottom:2.5pt;text-align:justify;text-justify:inter-ideograph;\r\nline-height:18.4pt\"><span style=\"font-size:8.0pt;\r\nfont-family:宋体;color:#222222;letter-spacing:.25pt;background:white\">总而言之，吃鸡蛋对身体有好处，关键是不要过量，同时注意吃的方式，才能让鸡蛋成为真正的“营养佳品”。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span style=\"font-size:7.0pt;font-family:\'Microsoft YaHei UI\';color:#D8D8D8;\r\nletter-spacing:.25pt;background:white\">来源：我是大医生</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';text-align:justify;text-justify:inter-ideograph\"><span style=\"font-size:7.0pt;font-family:\'Microsoft YaHei UI\';color:#D8D8D8;\r\nletter-spacing:.25pt;background:white\">版权归原作者所有，如有侵权请及时联系，我们将立即删除谢谢</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\'\"><span>&nbsp;</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3039','3864','1','','/ewebeditor/uploadfile/image/2023/03/06/20230306093510889_s.jpg','3月2日，正值学雷锋纪念日即将到来之际，农工党盐城市一院基层委员会走进东进社区开展“健康服务行暨学雷锋”宣教义诊活动。活动现场，专家党员发挥专业特长，积极与前来就诊的患者沟通，详细了解他们的健康情况，认真解答患者问题，提出合理的治疗方案及建议，并耐心细','172.16.1.2','172.16.1.2','1678066512','<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">3</span><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">月<span>2</span>日，正值学雷锋纪念日即将到来之际，农工党盐城市一院基层委员会走进东进社区开展“健康服务行暨学雷锋”宣教义诊活动。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">活动现场，专家党员发挥专业特长，积极与前来就诊的患者沟通，详细了解他们的健康情况，认真解答患者问题，提出合理的治疗方案及建议，并耐心细致地讲解疾病防控要点。期间，专家党员还结合实例及课件，就“外伤应急处理”“<span>CPR</span>”等主题为项目安全管理人员及现场人员作了实操讲座。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">市一院基层委员会主委王军表示，此次义诊宣教活动是为弘扬雷锋精神，为广大患者提供的一次便民服务，今后基层委将持续发扬精神，进一步加强义诊、宣教活动力度，努力为提升基层医疗水平添砖加瓦，做良好社会风尚的创造者和传播者。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-align:left;text-indent:28.0pt\" align=\"left\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">&nbsp;</span></p>\r\n&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<a href=\"/ewebeditor/uploadfile/image/2023/03/06/20230306093510889.jpg\" target=\"_blank\"><img src=\"/ewebeditor/uploadfile/image/2023/03/06/20230306093510889_s.jpg\" border=\"0\"></a>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3040','3865','1','','/ewebeditor/uploadfile/image/2023/03/06/20230306100843970_s.jpg','3月2日，徐州医科大学党委书记夏有兵一行莅临我院进行2022-2023学年中期实习检查。盐城市卫生健康委员会副主任邓义军，市一院党委书记查文章，党委副书记、院长王正及院领导班子其他成员，教育处、医务处、护理部负责人，内、外、妇、儿、麻醉科主任、教师代表参加会议','172.16.1.2','172.16.1.2','1678068529','<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-indent:28.0pt\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">3</span><span style=\"font-size:14.0pt;\r\nfont-family:仿宋,仿宋_GB2312\">月<span>2</span>日，徐州医科大学党委书记夏有兵一行莅临我院进行<span>2022-2023</span>学年中期实习检查。盐城市卫生健康委员会副主任邓义军，市一院党委书记查文章，党委副书记、院长王正及院领导班子其他成员，教育处、医务处、护理部负责人，内、外、妇、儿、麻醉科主任、教师代表参加会议。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-indent:28.0pt\"><span style=\"font-size:14.0pt;\r\nfont-family:仿宋,仿宋_GB2312\">本次检查既是对我院实习教学工作的一次集中检阅，更是对我院实习教学工作的一次“健康体检”，充分体现了徐医大对我院实习教学工作的关心关怀和高度重视。通过检查，进一步规范了我院的教学管理工作，加强和巩固了院校合作关系，为今后提升教学质量、培养更多优秀人才起到了积极的推动作用。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-indent:28.0pt\"><span style=\"font-size:14.0pt;\r\nfont-family:仿宋,仿宋_GB2312\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<a href=\"/ewebeditor/uploadfile/image/2023/03/06/20230306100843970.jpg\" target=\"_blank\"><img src=\"/ewebeditor/uploadfile/image/2023/03/06/20230306100843970_s.jpg\" border=\"0\"></a><br>\r\n</span></p>','','','','','');
