INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3069','3894','1','','','盐城市第一人民医院主院区冷却塔维修保养项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。1、项目概况与招标内容1.1项目名称：盐城市第一人民医院主院区冷却塔维修保养项目。1.2项目地点：盐城市第一人民医院主院区（人','172.16.1.2','172.16.1.2','1683275583','<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:28.0pt\"><span style=\"color:black\">盐城市第一人民医院主院区冷却塔维修保养项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:28.0pt\"><span style=\"color:black\">1</span><span style=\"color:black\">、项目概况与招标内容</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:28.0pt\"><span style=\"color:black\">1.1</span><span style=\"color:black\">项目名称：盐城市第一人民医院主院区冷却塔维修保养项目。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:28.0pt\"><span style=\"color:black\">1.2</span><span style=\"color:black\">项目地点：盐城市第一人民医院主院区（人民南路<span>66</span>号）</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:28.0pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">1.3</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">招标内容：盐城市第一人民医院主院区冷却塔维修保养项目，</span><span style=\"font-size:12.0pt;font-family:宋体\">具体内容详见招标人提供的工程量清单。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:28.0pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">1.4</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">工期：<span>15</span>日历天。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:28.0pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">1.5</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">最高限价：<span>6</span>万元</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:28.0pt\"><span style=\"color:black\">2</span><span style=\"color:black\">、投标人资格要求</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:28.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">2.1</span><span style=\"font-size:12.0pt;font-family:宋体\">投标人须具有建设行政主管部门核发的建筑机电安装工程专业承包三级及以上施工资质的独立法人，并取得有效的安全生产许可证。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:28.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">2.2</span><span style=\"font-size:12.0pt;font-family:宋体\">项目负责人的资格要求：项目负责人须具有机电工程专业二级及以上建造师注册证书；同时具有《建筑施工企业项目负责人安全生产考核合格证》（<span>B</span>类）。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:28.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">2.3</span><span style=\"font-size:12.0pt;font-family:宋体\">其他要求</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:28.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">2.3.1</span><span style=\"font-size:12.0pt;font-family:宋体\">财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。<span>&nbsp;</span></span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:28.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">2.3.2</span><span style=\"font-size:12.0pt;font-family:宋体\">信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:28.0pt\"><span style=\"font-size:12.0pt;font-family:宋体\">2.4</span><span style=\"font-size:12.0pt;font-family:宋体\">本次招标不接受联合体投标。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:28.0pt;\r\nbackground:white\"><span style=\"color:black\">3</span><span style=\"color:black\">、投标报名及招标文件的获取</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:28.0pt\"><span style=\"color:black;background:white\">请符合上述资质条件的投标人携带单位介绍信到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路<span>8</span>号<span>8</span>楼<span>805</span>室）投标报名并领取招标文件，投标报名时间<span>2023</span>年<span>5</span>月<span>6</span>日<span>8</span>时<span>30</span>分至<span>2023</span>年<span>5</span>月<span>11</span>日<span>18</span>时。联系人：刘工，<span>0515-69057406</span>；手机：<span>13626222271</span>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:28.0pt\"><span>4</span>、<b>评标办法：采用“合理低价法”。</b></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:28.0pt\"><span style=\"color:black\">5</span><span style=\"color:black\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin:0cm;margin-bottom:.0001pt;text-indent:24.0pt;line-height:28.0pt\"><span style=\"color:black\">6</span><span style=\"color:black\">、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话<span>0515-66696810</span>。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin-top:0cm;margin-right:-6.0pt;margin-bottom:0cm;margin-left:0pt;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;\r\ntext-indent:24.0pt;line-height:28.0pt\">招标人：盐城市第一人民医院</p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin-top:0cm;margin-right:-6.0pt;margin-bottom:0cm;margin-left:0pt;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;\r\ntext-indent:24.0pt;line-height:28.0pt\">地址：盐城市人民南路<span>66</span>号</p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:宋体;margin-top:0cm;margin-right:-6.0pt;margin-bottom:0cm;margin-left:0pt;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;\r\ntext-indent:24.0pt;line-height:28.0pt\">联系人：张女士</p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0