INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3080','3905','1','','','今年5月28日，是《中华人民共和国民法典》颁布三周年纪念日。日前，中央宣传部、司法部、全国普法办联合印发《2023年“美好生活·民法典相伴”主题宣传方案》，以“美好生活·民法典相伴”为主题，在全国组织开展第三个“民法典宣传月”活动。民法典三问：1.民法典是什','172.16.1.2','172.16.1.2','1683685397','<p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">今年<span>5</span>月<span>28</span>日，是《中华人民共和国民法典》颁布三周年纪念日。日前，中央宣传部、司法部、全国普法办联合印发《<span>2023</span>年“美好生活·民法典相伴”主题宣传方案》，以“美好生活·民法典相伴”为主题，在全国组织开展第三个“民法典宣传月”活动。</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">民法典三问：</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">1.民法典是什么？</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">《中华人民共和国民法典》被称为“社会生活的百科全书”，是新中国第一部以法典命名的法律，在法律体系中居于基础性地位，也是市场经济的基本法。</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">2.民法典有那些内容？</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">《中华人民共和国民法典》共<span>7</span>编、<span>1260</span>条，各编依次为总则、物权、合同、人格权、婚姻家庭、继承、侵权责任，以及附则。</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">3.民法典的施行日期？</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">《中华人民共和国民法典》于<span>2021</span>年<span>1</span>月<span>1</span>日起施行。</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">一、总则编</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">“总则”规定了民事活动必须遵循的基本原则和一般性规则，统领民法典各分编。总则编将“弘扬社会主义核心价值观”作为一项重要的立法目的，体现坚持依法治国与以德治国相结合的鲜明中国特色。同时，规定了民事权利及其他合法权益受法律保护，确立了平等、自愿、公平、诚信、守法和公序良俗、绿色原则等民法基本原则。</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">二、物权编</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">物权是民事主体依法享有的重要财产权，物权法律制度调整因物的归属和利用而产生的民事关系，是最重要的民事基本制度之一。</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">三、合同编</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">合同是民事主体之间设立、变更、终止民事法律关系的协议，合同法律制度的调整范围是因合同产生的民事关系。</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">四、人格权编</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">民法典人格权独立成编是民法典编纂的亮点和创新之一。人格权是社会和个人生存发展的基础之一，是人民美好生活的需要。</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">五、婚姻编</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">婚姻家庭制度是调整因婚姻家庭产生的民事关系，是规范夫妻关系和家庭关系的基本准则。婚姻编规定，家庭应当树立优良家风，弘扬家庭美德，重视家庭文明建设。</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">六、继承编</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">继承制度调整因继承产生的民事关系，是关于自然人死亡后财富传承的基本制度。继承编规定，继承权男女平等，在没有遗嘱的情况下，遗产按照法定顺序继承。</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">七、侵权责任编</p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\">侵权责任制度调整因侵害民事权益产生的民事关系。侵权责任编规定，行为人因过错侵害他人民事权益造成损害的，应当承担侵权责任。</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px;\"><span style=\"color: rgb(136, 136, 136); letter-spacing: 2px; background: white;\">来源<span> |&nbsp;</span>中国普法</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3082','3907','1','','','为进一步加强卫生健康系统人才队伍建设，更好地选拔优秀适岗人才，优化人员结构，盐城市卫生健康委部分直属事业单位面向社会公开招聘专业技术人员92名。根据《江苏省事业单位公开招聘人员办法》（苏办发〔2020〕9号）等规定，现将有关事项公告如下：一、招聘岗位本次公','172.16.1.2','172.16.1.2','1684889093','<p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">为进一步加强卫生健康系统人才队伍建设，更好地选拔优秀适岗人才，优化人员结构，盐城市卫生健康委部分直属事业单位面向社会公开招聘专业技术人员</span><span style=\"color: rgb(51, 51, 51);\">92</span><span style=\"color: rgb(51, 51, 51);\">名。根据《江苏省事业单位公开招聘人员办法》（苏办发〔</span><span style=\"color: rgb(51, 51, 51);\">2020</span><span style=\"color: rgb(51, 51, 51);\">〕</span><span style=\"color: rgb(51, 51, 51);\">9</span><span style=\"color: rgb(51, 51, 51);\">号）等规定，现将有关事项公告如下：</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">一、招聘岗位</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">本次公开招聘相关信息在盐城市人力资源和社会保障局网（</span><span style=\"color: rgb(51, 51, 51);\">https://jsychrss.yancheng.gov.cn/</span><span style=\"color: rgb(51, 51, 51);\">）、盐城市卫生健康委员会网（</span><span style=\"color: rgb(51, 51, 51);\">https://wsj.yancheng.gov.cn/</span><span style=\"color: rgb(51, 51, 51);\">）及各招聘单位网站上发布，具体的招聘单位、招聘岗位、数量、资格条件等详见《盐城市卫生健康委部分直属事业单位</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年公开招聘专业技术人员岗位表》（附件</span><span style=\"color: rgb(51, 51, 51);\">1</span><span style=\"color: rgb(51, 51, 51);\">，以下简称《招聘岗位表》）。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">二、报考条件</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">1</span><span style=\"color: rgb(51, 51, 51);\">．具有中华人民共和国国籍；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">2</span><span style=\"color: rgb(51, 51, 51);\">．遵守中华人民共和国宪法和法律，拥护中国共产党领导和社会主义制度；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">3</span><span style=\"color: rgb(51, 51, 51);\">．具有良好的品行；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">4</span><span style=\"color: rgb(51, 51, 51);\">．身心健康，能适应岗位要求；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">5</span><span style=\"color: rgb(51, 51, 51);\">．年龄在</span><span style=\"color: rgb(51, 51, 51);\">35</span><span style=\"color: rgb(51, 51, 51);\">周岁及以下（</span><span style=\"color: rgb(51, 51, 51);\">1987</span><span style=\"color: rgb(51, 51, 51);\">年</span><span style=\"color: rgb(51, 51, 51);\">5</span><span style=\"color: rgb(51, 51, 51);\">月</span><span style=\"color: rgb(51, 51, 51);\">25</span><span style=\"color: rgb(51, 51, 51);\">日及以后出生）。对年龄有具体要求的，以《招聘岗位表》中写明的为准，年龄计算方法不变；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">6</span><span style=\"color: rgb(51, 51, 51);\">．具备《招聘岗位表》中岗位要求的资格条件：</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑴资格条件中的“</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年毕业生”，指在</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年毕业并已取得学历（学位）证书，且仍无工作单位的人员。其中，能够提供《毕业生就业推荐表》（原件）的普通高校毕业生，取得学历（学位）证书的日期可放宽至</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年</span><span style=\"color: rgb(51, 51, 51);\">8</span><span style=\"color: rgb(51, 51, 51);\">月</span><span style=\"color: rgb(51, 51, 51);\">31</span><span style=\"color: rgb(51, 51, 51);\">日；国（境）外同期毕业人员，取得学历（学位）证书的日期可适当放宽，但须在</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年</span><span style=\"color: rgb(51, 51, 51);\">12</span><span style=\"color: rgb(51, 51, 51);\">月</span><span style=\"color: rgb(51, 51, 51);\">31</span><span style=\"color: rgb(51, 51, 51);\">日前完成教育部留学服务中心学历认证。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">2021</span><span style=\"color: rgb(51, 51, 51);\">年和</span><span style=\"color: rgb(51, 51, 51);\">2022</span><span style=\"color: rgb(51, 51, 51);\">年普通高校毕业生，若仍未落实工作单位，其档案关系仍保留在原毕业学校，或保留在各级毕业生就业主管部门（毕业生就业指导服务中心）、人才交流服务机构和公共就业服务机构的，以及国（境）外同期毕业且已完成学历认证但仍未落实工作单位的人员，可应聘面向</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年毕业生岗位。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">参加基层服务项目的人员，如参加服务项目前无工作经历，服务期满且考核合格后</span><span style=\"color: rgb(51, 51, 51);\">2</span><span style=\"color: rgb(51, 51, 51);\">年内的，可应聘面向</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年毕业生岗位。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">以普通高校应届毕业生应征入伍服义务兵的人员，退役后</span><span style=\"color: rgb(51, 51, 51);\">1</span><span style=\"color: rgb(51, 51, 51);\">年内的，可应聘面向</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年毕业生岗位。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑵《招聘岗位表》中有工作经历要求的，其计算的截止时间为</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年</span><span style=\"color: rgb(51, 51, 51);\">6</span><span style=\"color: rgb(51, 51, 51);\">月</span><span style=\"color: rgb(51, 51, 51);\">30</span><span style=\"color: rgb(51, 51, 51);\">日；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">7</span><span style=\"color: rgb(51, 51, 51);\">．资格条件中“专业”的设置目录参照《江苏省</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年度考试录用公务员专业参考目录》。部分未列入专业参考目录的专业以高等学校实际开设的为准；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">8</span><span style=\"color: rgb(51, 51, 51);\">．取得祖国大陆普通高校学历的台湾学生和取得祖国大陆承认学历的其他台湾居民应聘时按国家和江苏省的有关规定执行；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">9</span><span style=\"color: rgb(51, 51, 51);\">．有下列情形之一的，不得报考：</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑴现役军人和普通高校在读非</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年毕业生；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑵尚未解除纪律处分或者正在接受纪律审查的人员、刑事处罚期限未满或者涉嫌违法犯罪正在接受调查的人员；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑶应聘人员与事业单位工作人员有夫妻关系、直系血亲关系、三代以内旁系血亲关系、近姻亲及其他须回避的亲属关系的，不得应聘具有直接上下级领导关系的管理岗位，以及《事业单位人事管理回避规定》要求回避的岗位；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑷依据《江苏省事业单位公开招聘人员办法》（苏办发〔</span><span style=\"color: rgb(51, 51, 51);\">2020</span><span style=\"color: rgb(51, 51, 51);\">〕</span><span style=\"color: rgb(51, 51, 51);\">9</span><span style=\"color: rgb(51, 51, 51);\">号）被聘用到江苏省地方各类事业单位服务期未满的人员；</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑸国家和省另有规定不得应聘的人员。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">三、报名</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">1</span><span style=\"color: rgb(51, 51, 51);\">．报名时间、方式</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">报名时间：</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年</span><span style=\"color: rgb(51, 51, 51);\">5</span><span style=\"color: rgb(51, 51, 51);\">月</span><span style=\"color: rgb(51, 51, 51);\">25</span><span style=\"color: rgb(51, 51, 51);\">日</span><span style=\"color: rgb(51, 51, 51);\">-2023</span><span style=\"color: rgb(51, 51, 51);\">年</span><span style=\"color: rgb(51, 51, 51);\">6</span><span style=\"color: rgb(51, 51, 51);\">月</span><span style=\"color: rgb(51, 51, 51);\">2</span><span style=\"color: rgb(51, 51, 51);\">日。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">报名方式：采取网络报名（电子邮件）的方式，各招聘单位报名邮箱详见附件</span><span style=\"color: rgb(51, 51, 51);\">1</span><span style=\"color: rgb(51, 51, 51);\">。符合相应报名条件的应聘人员，下载并填写《盐城市卫生健康委部分直属事业单位</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年公开招聘专业技术人员报名表》（附件</span><span style=\"color: rgb(51, 51, 51);\">2</span><span style=\"color: rgb(51, 51, 51);\">）。报名人员通过电子邮件形式发送至相关单位报名邮箱，邮件以“应聘岗位</span><span style=\"color: rgb(51, 51, 51);\">+</span><span style=\"color: rgb(51, 51, 51);\">应聘人姓名”命名，按照报名所需资料要求将原件正面扫描制定为</span><span style=\"color: rgb(51, 51, 51);\">PDF</span><span style=\"color: rgb(51, 51, 51);\">文件以附件形式发送。应聘人员须在规定时间按要求进行电子邮件报名，逾期不再接受报名，所需报名材料请勿以复印件扫描，否则视为无效材料。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">2</span><span style=\"color: rgb(51, 51, 51);\">．报名注意事项及资格审查</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑴每位报考者只能选择一个岗位进行报名。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑵报考者报名时所需材料：①《盐城市卫生健康委部分直属事业单位</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年公开招聘专业技术人员报名表》（附件</span><span style=\"color: rgb(51, 51, 51);\">2</span><span style=\"color: rgb(51, 51, 51);\">）；②本人近期免冠正面二寸（</span><span style=\"color: rgb(51, 51, 51);\">35</span><span style=\"color: rgb(51, 51, 51);\">×</span><span style=\"color: rgb(51, 51, 51);\">45</span><span style=\"color: rgb(51, 51, 51);\">毫米）证件照，</span><span style=\"color: rgb(51, 51, 51);\">jpg</span><span style=\"color: rgb(51, 51, 51);\">格式，大小为</span><span style=\"color: rgb(51, 51, 51);\">20kb</span><span style=\"color: rgb(51, 51, 51);\">以下；③身份证扫描件；④学历、学位证书、专业方向证明等扫描件，其中未取得毕业证书的</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年毕业生须提供所在院校出具的省教育厅统一印制的《毕业生双向选择就业推荐表》；</span><span style=\"color: rgb(51, 51, 51);\">2021</span><span style=\"color: rgb(51, 51, 51);\">年、</span><span style=\"color: rgb(51, 51, 51);\">2022</span><span style=\"color: rgb(51, 51, 51);\">年毕业生应聘面向</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年毕业生岗位的还需提供原始的《毕业生就业协议书》、《“</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年毕业生”身份承诺书》（附件</span><span style=\"color: rgb(51, 51, 51);\">3</span><span style=\"color: rgb(51, 51, 51);\">）；⑤其它材料：岗位报名需要的执业资格证书、专业技术资格证书、工作经历证明等扫描件。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑶资格初审。应聘人员网上提交报名信息后，招聘单位根据报名信息进行审核并及时反馈。应聘人员在报名结束前没有收到审核结果的，应联系招聘单位了解审核情况。应聘人员如对初审结果有异议，可从收到审核结果之日起</span><span style=\"color: rgb(51, 51, 51);\">2</span><span style=\"color: rgb(51, 51, 51);\">个工作日内向招聘单位陈述申辩，逾期视为没有异议。应聘人员因个人信息填写不完整、不准确导致资格初审未通过等导致报名不成功的，均视为报名无效，由应聘人员本人承担责任。应聘人员须使用在有效期内的第二代居民身份证进行报名，报名与考试使用的身份证必须一致。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑷招聘岗位数与通过资格审核人数达不到开考比例的，相应核减岗位招聘计划数，直至取消该岗位招聘计划。对符合《</span><span style=\"color: rgb(51, 51, 51);\">2023</span><span style=\"color: rgb(51, 51, 51);\">年度盐城市直卫生健康单位紧缺专业人才需求目录》中公布的紧缺专业岗位，经核准，可以适当降低开考比例。报考被取消岗位的人员，可在规定时间内改报其他符合条件的岗位。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑸应聘人员对所填写的报名信息和提交报名材料的真实性、准确性、完整性、有效性负责。除本公告另有说明，或者国家、省另有规定外，信息真实性以报名时的实际情况为准。资格审查工作贯穿于招聘工作全过程。凡弄虚作假的，一经查实，即取消应聘资格。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">四、考试方式、时间和实施办法</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">考生应携带准考证和身份证等相关材料，在规定的时间到考点参加考试。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">1.&nbsp;&nbsp;</span><span style=\"color: rgb(51, 51, 51);\">硕士研究生学历岗位</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">考试采取专业化面试方式，主要考核招聘岗位应具备的专业知识。面试总分为</span><span style=\"color: rgb(51, 51, 51);\">100</span><span style=\"color: rgb(51, 51, 51);\">分，设</span><span style=\"color: rgb(51, 51, 51);\">60</span><span style=\"color: rgb(51, 51, 51);\">分合格线，低于合格线者不予录用。面试成绩当场公布。面试前，招聘单位对进入面试人选进行资格复审（复审时提供所有报名材料原件，在职人员还必须提供现工作单位同意报考证明）。没有通过资格复审的，将被取消面试资格。面试具体时间、地点另行通知。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">2.&nbsp;</span><span style=\"color: rgb(51, 51, 51);\">其他岗位</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">考试采取笔试和面试相结合的办法进行。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑴笔试：应聘人员参加专业理论考试，笔试总分</span><span style=\"color: rgb(51, 51, 51);\">100</span><span style=\"color: rgb(51, 51, 51);\">分，</span><span style=\"color: rgb(51, 51, 51);\">60</span><span style=\"color: rgb(51, 51, 51);\">分为笔试合格分数线。笔试具体时间、地点另行通知。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑵确定面试人选：在笔试成绩合格人员中，按照招聘岗位</span><span style=\"color: rgb(51, 51, 51);\">1</span><span style=\"color: rgb(51, 51, 51);\">：</span><span style=\"color: rgb(51, 51, 51);\">3</span><span style=\"color: rgb(51, 51, 51);\">的比例从高分到低分确定参加面试的人员（含末位同分者），不足</span><span style=\"color: rgb(51, 51, 51);\">1</span><span style=\"color: rgb(51, 51, 51);\">：</span><span style=\"color: rgb(51, 51, 51);\">3</span><span style=\"color: rgb(51, 51, 51);\">的按照实际人数确定面试人选。在上述范围内的应聘人员，以及其他排名较前的人员，请在成绩公布后保持联系方式畅通，以便招聘单位通知资格复审或递补，联系不到者视为自动放弃。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑶资格复审：面试前，招聘单位对进入面试人选进行资格复审（复审时提供所有报名材料原件，在职人员还必须提供现工作单位同意报考证明），如有复审不通过出现缺额的，在应聘同岗位的笔试合格人员中从高分到低分依次递补资格复审人员。没有通过资格复审的，将被取消面试资格。资格复审的具体时间、地点另行通知。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">⑷面试：面试采取结构化面试的形式进行，面试总分</span><span style=\"color: rgb(51, 51, 51);\">100</span><span style=\"color: rgb(51, 51, 51);\">分，设</span><span style=\"color: rgb(51, 51, 51);\">60</span><span style=\"color: rgb(51, 51, 51);\">分合格线，低于合格线者不予录用。面试成绩当场公布。面试具体时间、地点另行通知。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">3</span><span style=\"color: rgb(51, 51, 51);\">．总成绩计算方式</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">考试结束后，无笔试岗位面试成绩即为总成绩；其他岗位的笔试成绩、面试成绩均按</span><span style=\"color: rgb(51, 51, 51);\">50%</span><span style=\"color: rgb(51, 51, 51);\">计算。采用百分制计算应聘人员总成绩。成绩均保留两位小数，第三位小数按“四舍五入”办法处理。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">五、聘用</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">考试结束后，根据考试总成绩，按招聘岗位拟招聘人数</span><span style=\"color: rgb(51, 51, 51);\">1</span><span style=\"color: rgb(51, 51, 51);\">：</span><span style=\"color: rgb(51, 51, 51);\">1</span><span style=\"color: rgb(51, 51, 51);\">的比例从高分到低分确定参加体检人选。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">若总成绩相同，无笔试的岗位对成绩相同的人员组织加试；有笔试的岗位面试成绩高者优先，如面试成绩仍相同，对成绩相同的人员组织加试（加试时间、方式、地点另行通知）。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">体检标准参照最新的《公务员录用体检通用标准（试行）》执行。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">体检结束后，招聘单位对体检合格人员组织考察（制定考察方案，明确考察的内容、方法等，考察标准参照盐城市公务员招录考察相关要求进行），并根据考察结果，研究确定拟聘用人员名单。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">因个人放弃、体检、考察不合格等原因出现招聘岗位空缺时，在该岗位的考试合格人员中，按总成绩从高分到低分依次递补（成绩相同取舍办法同前）。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">拟聘用人员名单将在市人力资源和社会保障局、市卫生健康委网站上公示</span><span style=\"color: rgb(51, 51, 51);\">7</span><span style=\"color: rgb(51, 51, 51);\">个工作日。公示内容包括招聘单位、岗位名称、拟聘用人员姓名、学历、专业、毕业院校、现工作单位、招聘考试的各项成绩、总成绩、排名等。拟聘用人员名单公示后，应聘人员如无正当理由放弃聘用资格的，招聘单位可以在名单公示结束后的</span><span style=\"color: rgb(51, 51, 51);\">1</span><span style=\"color: rgb(51, 51, 51);\">年内取消其再次应聘本单位的资格。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">对公示无异议人员，招聘单位按规定为其办理有关聘用手续，与拟聘用人员签订聘用合同，约定试用期。试用期满考核合格，予以定岗定级；考核不合格者，取消聘用资格，解除聘用合同。拟聘用人员与招聘单位订立</span><span style=\"color: rgb(51, 51, 51);\">3</span><span style=\"color: rgb(51, 51, 51);\">年以上（含试用期）聘用合同的，除依法依规解除聘用合同外，应当在招聘单位最低服务</span><span style=\"color: rgb(51, 51, 51);\">3</span><span style=\"color: rgb(51, 51, 51);\">年（含试用期）。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">六、招聘纪律</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">应聘人员在报名、考试、体检过程中或聘用后查实存在违纪违规行为的，按照《事业单位公开招聘违纪违规行为处理规定》（中华人民共和国人力资源和社会保障部第</span><span style=\"color: rgb(51, 51, 51);\">35</span><span style=\"color: rgb(51, 51, 51);\">号令），视具体情形和情节轻重，分别给予取消报考资格、取消考试成绩、不予聘用、记入诚信档案库等处理，构成犯罪的依法追究刑事责任。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">招聘单位和招聘工作人员有违纪违规行为的，按照《事业单位公开招聘违纪违规行为处理规定》（中华人民共和国人力资源和社会保障部第</span><span style=\"color: rgb(51, 51, 51);\">35</span><span style=\"color: rgb(51, 51, 51);\">号令）进行处理。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">七、咨询、监督</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">招聘工作坚持公开、公平、公正的原则，全过程接受纪检监察部门的监督。</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">咨询电话：</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">0515-66696806</span><span style=\"color: rgb(51, 51, 51);\">（盐城市第一人民医院人事处）</span></p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51); background: white;\">0515-88245600</span><span style=\"color: rgb(51, 51, 51); background: white;\">（盐城市疾病预防控制中心组织人事科）</span></p><p style=\"text-align: left; text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51); background: white;\">0515-88166308</span><span style=\"color: rgb(51, 51, 51); background: white;\">（盐城市中医院组织人事科）</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">0515-88569209</span><span style=\"color: rgb(51, 51, 51);\">（南京鼓楼医院盐城分院</span><span style=\"color: rgb(51, 51, 51);\">&lt;</span><span style=\"color: rgb(51, 51, 51);\">盐城市第二人民医院</span><span style=\"color: rgb(51, 51, 51);\">&gt;</span><span style=\"color: rgb(51, 51, 51);\">组织人事科）</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">0515-88282075</span><span style=\"color: rgb(51, 51, 51);\">（盐城市妇幼保健院组织人事科）</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">0515-68668911</span><span style=\"color: rgb(51, 51, 51);\">（盐城市第四人民医院组织人事科）</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">0515-88305176</span><span style=\"color: rgb(51, 51, 51);\">（盐城市口腔医院人事科）</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">0515-88582123</span><span style=\"color: rgb(51, 51, 51);\">（盐城市急救医疗中心办公室）</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">0515-88581911</span><span style=\"color: rgb(51, 51, 51);\">（盐城市中心血站办公室）</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">0515-88226330</span><span style=\"color: rgb(51, 51, 51);\">（盐城市卫生健康委组织人事处）</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">监督电话：</span></p><p style=\"text-indent: 2em; line-height: 2em; background: white; font-size: 16px; font-family: 微软雅黑, Microsoft YaHei; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(51, 51, 51);\">0515-80501632</span><span style=\"color: rgb(51, 51, 51);\">（盐城市纪委监委第十五派驻纪检监察组）</span></p><p style=\"text-indent:36px;line-height:37px;background:white\"><span style=\"color: rgb(51, 51, 51);\"></span></p><p style=\"line-height: 16px;\"><img style=\"vertical-align: middle; margin-right: 2px;\" src=\"/ueditor/dialogs<!--#p8_attach#-->/fileTypeImages/icon_xls.gif\" src=\"/ueditor/dialogs<!--#p8_attach#-->/fileTypeImages/icon_xls.gif\" /><a style=\"font-size:1