INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3229','4055','1','','<!--#p8_attach#-->/cms/item/2023_12/18_09/38868715dafc0b26.jpg.cthumb.jpg','12月7日江苏省医师协会核医学医师分会举办了第四届“青年教师之星”核医学专业住院医师规范化培训带教老师授课比赛，我院核医学科青年骨干带教老师薛秀青同志参加了比赛，并取得了三等奖。本次比赛选手是来自全省各核医学专业基地的优秀带教老师，选手们通过课件展示了','10.10.10.10','10.10.10.10','1702688651','<p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">12月7日</span><span style=\"line-height: 150%;\">江苏省医师协会核医学医师分会举办</span><span style=\"line-height: 150%;\">了</span><span style=\"line-height: 150%;\">第四届“青年教师之星”核医学专业住院医师规范化培训带教老师授课比赛</span><span style=\"line-height: 150%;\">，</span><span style=\"line-height: 150%;\">我院核医学科青年骨干带教老师薛秀青同志参加了比赛，并取得了三等奖。</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">本次比赛选手是来自全省各核医学专业基地的优秀带教老师，选手们通过课件展示了小讲课、病例分析、教学查房、科普知识等与核医学相关的专业知识，体现了带教老师的临床思维和处置能力。此次比赛对</span><span style=\"line-height: 150%;\">提高我省核医学专业住院医师规范化培训带教师资的教学能力，进一步推动我省核医学快速发展</span><span style=\"line-height: 150%;\">起到了积极的推动作用。 &nbsp;</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">我院</span><span style=\"line-height: 150%; color: rgb(64, 64, 64); letter-spacing: 0px;\">作为</span><span style=\"line-height: 150%; color: rgb(64, 64, 64); letter-spacing: 0px;\">全国首批住院医师规范化培训基地</span><span style=\"line-height: 150%; color: rgb(64, 64, 64); letter-spacing: 0px;\">，</span><span style=\"line-height: 150%; color: rgb(64, 64, 64); letter-spacing: 0px;\">全院</span><span style=\"line-height: 150%; color: rgb(64, 64, 64); letter-spacing: 0px;\">高度重视</span><span style=\"line-height: 150%; color: rgb(64, 64, 64); letter-spacing: 0px;\">住培</span><span style=\"line-height: 150%; color: rgb(64, 64, 64); letter-spacing: 0px;\">教学工作</span><span style=\"line-height: 150%; color: rgb(64, 64, 64); letter-spacing: 0px;\">。</span><span style=\"line-height: 150%; color: rgb(64, 64, 64); letter-spacing: 0px;\">我院一直</span><span style=\"line-height: 150%;\">以“提高教学活动质量”为抓手，以院级教学督导组为依托，对全院科室的住培教学查房、病例讨论、入科教育、小讲座、出科考核进行抽查督导，切实改善提高住培师资教学水平，有效提高住培学员的专业能力、临床诊治能力和临床思维能力。</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">本次大赛成绩的取得，既是师资教学风采的展示，也是教学水平在</span><span style=\"line-height: 150%; color: rgb(64, 64, 64); letter-spacing: 0px;\">全省的横向坐</span><span style=\"line-height: 150%;\">标。我院将继续审视差距，不断进步，把已获得的荣誉作为新起点，为我省住院医师规范化培训工作增光添彩！</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\"><br /></span></p><p style=\"text-align: center;\">&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2023_12/18_09/38868715dafc0b26.jpg.cthumb.jpg\" title=\"45304de4fdd958fa61339d69179d385.jpg\" src=\"<!--#p8_attach#-->/cms/item/2023_12/18_09/38868715dafc0b26.jpg.cthumb.jpg\" alt=\"45304de4fdd958fa61339d69179d385.jpg\" /></p><p style=\"text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_12/18_09/0417b1e91501f2fb.jpg.cthumb.jpg\" title=\"e935670def94c6e9002023308615898.jpg\" src=\"<!--#p8_attach#-->/cms/item/2023_12/18_09/0417b1e91501f2fb.jpg.cthumb.jpg\" alt=\"e935670def94c6e9002023308615898.jpg\" /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3230','4056','1','','','一、项目基本情况1.项目名称：盐城市第一人民医院患者陪护服务采购项目2.采购方式：公开招标3.报价要求：供应商提供一定数量的护理员为院方做增值服务，薪资由供应商负责。4.采购需求：本项目为盐城市第一人民医院患者陪护服务采购项目，医院现有床位3748张，本项目共分','10.10.10.10','10.10.10.10','1703228318','<p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>一、项目基本情况</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.项目名称：盐城市第一人民医院患者<span style=\"letter-spacing: 0px;\">陪护服务采购项目</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"letter-spacing: 0px;\">2.采购方式：公开招标</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"letter-spacing: 0px;\">3</span><span style=\"letter-spacing: 0px;\">.</span><span style=\"letter-spacing: 0px;\">报价要求：供应商提供一定数量的护理员为院方做增值服务，薪资由供应商负责。</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"letter-spacing: 0px;\">4.采购需求：本项目为盐城市第一人</span><span style=\"letter-spacing: 0px;\">民医院患者</span>陪护服务采购项目，医院现有床位3748张，本项目共分两个标段投标。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">标段一：北院区（1950张）、儿童医院院区（162张）；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">标段二：主院区（1495张）、五官科院区（141张）；<span style=\"letter-spacing: 0px;\">具体详见招标文件。</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">供应商可以同时兼投两个标段，但不允许兼中（有放弃者或其他情形除外）。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">5.项目履行期限：服务期限三年，一年一签。经院方考核考评达90分及以上且无重大投诉事件，可续签下一年合同，续签不超过两次（具体考核内容由院方根据实际情况而定）。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">6.本项目不接受联合体投标。</p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>二、申请人的资格要求：</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.在中华人民共和国境内注册；具有独立承担民事责任的能力；<strong>营业执照经营范围须</strong><strong>含有与护理服务、患者陪护、养老服务、</strong><strong>健康管理</strong><strong>等相关项目</strong>。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2.具有良好的商业信誉和健全的财务会计制度。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">3.具有履行合同所必需的设备和专业技术能力。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">4.有依法缴纳税收和社会保障资金的良好记录。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">5.参加招标活动前三年内，在经营活动中没有重大违法记录。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">6.法律、行政法规规定的其他条件。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">7.单位负责人为同一人或者存在直接控股、管理关系的不同供应商，不得参加同一合同项下的招标活动。</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">8.未被“信用中国”网站（www.creditchina.gov.cn）、列入失信被执行人、重大税收违法案件当事人名单、严重失信行为记录名单。</p><p style=\"line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>三、获取招标文件</strong></p><p style=\"text-indent: 2em; vertical-align: baseline; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.投标报名时间2023年12月22日8时30分至2023年12月29日18时。联系人：刘工，0515-69057406，13626222271。</p><p style=\"text-indent: 2em; vertical-align: baseline; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2.现场报名方式：请携带单位营业执照复印件、介绍信或授权委托书（注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号805室）。</p><p style=\"text-indent: 2em; vertical-align: baseline; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">3.网上报名方式：<a href=\"mailto:%E5%B0%86%E5%8D%95%E4%BD%8D%E8%90%A5%E4%B8%9A%E6%89%A7%E7%85%A7%E5%A4%8D%E5%8D%B0%E4%BB%B6%E3%80%81%E4%BB%8B%E7%BB%8D%E4%BF%A1%E6%88%96%E6%8E%88%E6%9D%83%E5%A7%94%E6%89%98%E4%B9%A6%EF%BC%88%E6%B3%A8%E6%98%8E%E8%81%94%E7%B3%BB%E4%BA%BA%E7%94%B5%E8%AF%9D%E5%8F%8A%E9%82%AE%E7%AE%B1%EF%BC%89%E6%89%AB%E6%8F%8F%E4%BB%B6%E5%8F%91%E9%80%81%E8%87%B3%E9%82%AE%E7%AE%B11501068472@qq.com%E5%B9%B6%E7%94%B5%E8%AF%9D%E7%A1%AE%E8%AE%A4%E6%8A%A5%E5%90%8D%E3%80%82\">将单位营业执照复印件、介绍信或授权委托书（注明联系人电话及邮箱）扫描件发送至邮箱1501068472@qq.com并电话确认报名。</a></p><p style=\"vertical-align: baseline; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>四、本项目采用“资格后审”，“综合评估”法。</strong></p><p style=\"vertical-align: baseline; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>五</strong><strong>、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话0515-66696810。</strong></p><p style=\"vertical-align: baseline; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>六</strong><strong>、对本次招标提出询问，请按以下方式联系。</strong></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>1.采购人信息</strong></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">名 &nbsp;&nbsp;&nbsp;称：盐城市第一人民医院</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地 &nbsp;&nbsp;&nbsp;址：盐城市人民南路66号</p><p style=\"margin-bottom: 15px; text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px;\">联 系 人：张处</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系方式：0515-66696805</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>2.采购代理机构信息</strong></p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">名称：江苏仁禾中衡工程咨询房地产估价有限公司</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地址：盐城市青年西路8号</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系人：胡工</p><p style=\"text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系方式：0515-69057406</p><p style=\"text-indent:32px;line-height:150%\"><span style=\"line-height: 150%;\">&nbsp;</span></p><p><br /></p>','','','','','');
