INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3233','4059','1','','<!--#p8_attach#-->/cms/item/2023_12/25_10/c43b5ab9cca5b648.jpg.cthumb.jpg','2023年12月19日，盐城市第一人民医院组织召开2024年住培学员结业考核动员大会，旨在指导和帮助住培学员备战结业考核，督促参加2024年结业考核的学员能利用有限的时间抓紧学习，提升理论知识水平、技能操作能力，争取全员一次性通过结业考核。市一院党委委员、教育处处长','10.10.10.10','10.10.10.10','1703472845','<p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">2023年12月19日，盐城市第一人民医院组织召开2024年住培学员结业考核动员大会，旨在指导和帮助住培学员备战结业考核，督促参加2024年结业考核的学员能利用有限的时间抓紧学习，提升理论知识水平、技能操作能力，争取全员一次性通过结业考核。</span></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">市一院党委委员、教育处处长边德志，规培办公室老师及143名拟参加2024年结业考核的住培学员参加了会议。</span></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">市一院党委委员、教育处处长边德志在讲话中提出：要高度重视住培结业考核工作。结业考核既是对基地培训质量的评估，更是对个人学习情况和临床履职能力的综合考评。培训基地、专业基地、学员三级层面要高度重视此项工作，做好充分准备，积极迎考。住培办要为结业考核提供强有力的保障，为住培学员理论和技能培训提供硬件及软件支持，开通模拟考试题库，保证实训中心全天开放，供各专业基地、各位住培学员随时能够培训、练习。各专业基地要强化通关模拟考试考核，制定培训计划，落实培训任务，保证培训效果；熟悉每个学员的实际能力水平，针对每个学员的特点有的放矢的开展相关培训工作。最重要的是学员本人要重视结业考核工作，发挥自身内源驱动力，鼓足干劲做好冲刺。各位学员要合理利用剩余时间，做好理论和技能时间训练分配，明确学习目标，改进学习方法，充分利用线上线下学习资源，争取顺利通过结业考核。</span></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">本次会议为202</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">4</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">年度住院医师规范化培训结业考核的复习备战吹响了号角，旨在指导和帮助住培学员备战结业考核，督促参加202</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">4</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">年结业考核的学员能利用有限的时间抓紧学习，提升理论知识水平、技能操作能力，提高结业考核通过率</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">以及临床实践能力</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">。</span></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: center; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\"><img src=\"<!--#p8_attach#-->/cms/item/2023_12/25_10/c43b5ab9cca5b648.jpg.cthumb.jpg\" title=\"1.jpg\" src=\"<!--#p8_attach#-->/cms/item/2023_12/25_10/c43b5ab9cca5b648.jpg.cthumb.jpg\" alt=\"1.jpg\" /></span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3234','4060','1','','<!--#p8_attach#-->/cms/item/2023_12/25_10/4dbd18774a0a8d6a.jpg.cthumb.jpg','2022年度住院医师规范化培训优秀住培学员表彰大会于2023年12月19日在我院举行。院长邓义军，党委委员、教育处处长边德志出席。住培办对2021级和2022级的249名住培学员进行了系统考核。考核内容涵盖医德医风、日常考勤、参加教学学习情况、出科考核、年度考核等。按考核','10.10.10.10','10.10.10.10','1703472921','<p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">202</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">2年度</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">住院医师规范化培训优秀住培学员表彰大会</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">于2023年12月19日在我院</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">举行。院长</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">邓义军</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">，</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">党委委员</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">、</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">教育处处长边德志</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">出席。</span></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">住培办</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">对20</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">21</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">级</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">和2022级</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">的</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">249</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">名住培学员进行了系统考核。考核内容涵盖医德医风、日常考勤、参加</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">教学</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">学习情况、出科考核、</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">年度</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">考核等。按考核结果，对表现优异的</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">35</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">人进行了表彰并予以奖励。</span></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">市一院院长邓义军</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">希望各位学员要向</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">这些</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">优秀学员学习，在今后的学习中要严谨勤奋，珍惜</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">学习</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">机会，尊师好学，在临床实践工作中，努力提高专业知识水平，不断强化理论和技能培训，将自己锻炼成一名优秀的临床医师，为今后的工作奠定良好的基础。</span></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">市一院党委委员、教育处处长边德志</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">表示，规培学员在学习、工作、生活中无论遇到的任何困难，医院都会为大家提供最大的帮助。他希望学员们在平时的工作中要充分发挥主观能动性，克服困难，合理安排时间，学会应对压力，抓住机会多学多问，力争在培训期间打下扎实的临床基础，争取成为一名合格优秀的医师。</span></p><p style=\"text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">学员们纷纷表示，</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">会以优秀学员为榜样，</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">通过</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">系统的</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">理论学习及实践知识培训</span><span style=\"color: rgb(34, 34, 34); letter-spacing: 0px; background: rgb(255, 255, 255);\">，努力提升自身能力，为医院发展贡献自己的力量。</span></p><p style=\"text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2023_12/25_10/4dbd18774a0a8d6a.jpg.cthumb.jpg\" title=\"4d4af1dc4a5de73233d717715874991.jpg\" src=\"<!--#p8_attach#-->/cms/item/2023_12/25_10/4dbd18774a0a8d6a.jpg.cthumb.jpg\" alt=\"4d4af1dc4a5de73233d717715874991.jpg\" /></p><p><br /></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3235','4061','1','','','盐城市第一人民医院污染物自行监测、净化区域空气检测项目已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：一、招标人名称：盐城市第一人民医院二、招标代理机构：江苏双清工程造价','10.10.10.10','10.10.10.10','1703557678','<p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">盐城市第一人民医院污染物自行监测、净化区域空气检测项目已具备招标条件，项目所需资金来源为自筹资金并已落实。现通过公开招标方式邀请潜在投标人参加本项目的投标报名。现就有关事项公告如下：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">一、招标人名称：盐城市第一人民医院</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">二、招标代理机构：江苏双清工程造价咨询有限公司</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>三、项目概况：</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1、项目名称：盐城市第一人民医院污染物自行监测、净化区域空气检测项目。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2、项目地点：盐城市第一人民医院。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">3、本项目最高限价：22万元。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">4、招标范围及内容：盐城市第一人民医院污染物自行监测、净化区域空气检测项目，包括但不限于：</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（1）污染物排放监测服务；</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（2）依据《医院洁净手术部建筑技术规范》（GB50333-2013）；《洁净室施工及验收规范》（GB50591-2010）等规范要求；检测项目：高效过滤器气密性、洁净度、平均风速与换气次数、静压差、温湿度、噪声、照度等；招标人保留对上述暂定招标内容适当调整的权利。具体招标内容详见项目需求。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">5、服务期限：<span style=\"text-decoration:underline;\">1</span>年。服务期满，经招标人考核合格、服务符合要求后，可续签下一年合同，续签不超过两次。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">6、服务质量：符合国家规范及行业标准，满足质量检测标准及招标人要求。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>四、投标人资格要求：</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1、投标人是在中华人民共和国境内登记注册的具有独立法人资格的单位，能独立承担民事责任，且具有CMA计量认证证书及附件，证书资质范围须满足本项目包括的全部检验检测项目。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2、项目负责人资格类别和等级：项目经理须是投标人本单位在职职工且非法定代表人。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">3、本次招标不接受联合体投标。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">4、财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。&nbsp;</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">5、信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">6、投标申请人须保证委托代理人、项目负责人均为本单位的正式职工，并确保从投标截止之日当月向前连续6个月均已在本单位缴纳养老保险（例：如开标日期为2020年7月的某一天，则投标单位需保证以上人员2020年1-6月养老保险均在本单位缴纳）。投标时无须提供证明材料，如有质疑或招标人要求，再按要求提供相关证明材料。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>五、招标文件获取：</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">请符合要求的潜在投标人于<span style=\"color: rgb(0, 0, 255);\">2023年</span><span style=\"color: rgb(0, 0, 255);\">12</span><span style=\"color: rgb(0, 0, 255);\">月</span><span style=\"color: rgb(0, 0, 255);\">26</span><span style=\"color: rgb(0, 0, 255);\">日至202</span><span style=\"color: rgb(0, 0, 255);\">4</span><span style=\"color: rgb(0, 0, 255);\">年</span><span style=\"color: rgb(0, 0, 255);\">1</span><span style=\"color: rgb(0, 0, 255);\">月</span><span style=\"color: rgb(0, 0, 255);\">3</span><span style=\"color: rgb(0, 0, 255);\">日</span>（每日上午8：30时至12：00时，下午14：30时至18：00时）携带单位介绍信原件（或授权委托书原件，注明联系人电话及邮箱）至江苏双清工程造价咨询有限公司（盐城市紫薇广场C区7楼）报名并领取招标文件（联系人：周会计，联系电话：0515-88200706，18705110288），逾期不再受理。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>六、投标保证金</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">本项目投标保证金为人民币<span style=\"text-decoration:underline;\">4</span><span style=\"text-decoration:underline;\">000元</span>，具体详见招标文件。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">七、本项目资格审查办法采用<strong>资格后审</strong>；评标办法采用<strong><span style=\"text-decoration:underline;\">综合评估法</span></strong>，具体详见招标文件。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">八、本项目严禁挂靠，一经核实挂靠，将被取消投标、中标资格，并按相关规定进行处罚，直至建议有关部门吊销资质证书。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>九、联系方式</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">招标人：盐城市第一人民医院</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地址：人民南路66号</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系人：张女士</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">电话：18961972922</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">招标代理机构：江苏双清工程造价咨询有限公司</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">地址：盐城市紫薇广场C区7楼</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">联系人：张先生</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">电话：0515-88200316、13515130964</p><p style=\"margin-left:0;text-indent:0;line-height:150%\">&nbsp;</p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3236','4062','1','','','盐城市第一人民医院主院区精准医学中心电源电缆敷设项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。1、工程概况1.1项目名称：盐城市第一人民医院主院区精准医学中心电源电缆敷设项目1.2项目地点：盐城市第一人民医院主院','10.10.10.10','10.10.10.10','1703745146','<p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">盐城市第一人民医院主院区精准医学中心电源电缆敷设项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>1、工程概况</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.1项目名称：盐城市第一人民医院主院区精准医学中心电源电缆敷设项目</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.2项目地点：盐城市第一人民医院主院区（人民南路66号）</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.3招标内容：盐城市第一人民医院主院区精准医学中心电源电缆敷设项目，具体以招标人提供的工程量清单为准。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.4质量标准：符合国家“合格”标准。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.5工期要求：20日历天，具体开工日期以招标人的通知。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">1.6最高限价：<span style=\"text-decoration:underline;\">33.5</span>万元。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>2 </strong><strong>投标人</strong><strong>应当具备的主要资格条件：</strong></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2.1投标人具有建设行政主管部门核发的电力工程施工总承包三级及以上或输变电工程专业承包三级及以上施工资质，同时具有国家能源局派出机构颁发《承装（修、试）电力设施许可证》，承装、承修、承试五级及以上电力设施许可证的独立法人，持有有效的安全生产许可证。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2.2拟选派项目负责人资质等级：项目负责人具有机电工程专业二级及以上建造师资格，同时应具有安全生产考核合格证（B类）。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2.3其他要求</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2.3.1财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2.3.2信誉要求：投标人没有被国家、江苏省省级有关部门及盐城市级有关部门暂停招投标或市场准入资格且在公示处罚期内。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">2.4本次招标不接受联合体投标。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><strong>3</strong><strong>、投标报名及招标文件的获取</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">请3.1投标报名时间2023年12月28日8时30分至2024年1月5日18时。联系人：刘工，0515-69057406，13626222271。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">3.2现场报名方式：请携带单位营业执照复印件、介绍信或授权委托书（注明联系人电话及邮箱）至江苏仁禾中衡工程咨询房地产估价有限公司（盐城市青年西路8号805室）。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">3.3网上报名方式：<a href=\"mailto:%E5%B0%86%E5%8D%95%E4%BD%8D%E8%90%A5%E4%B8%9A%E6%89%A7%E7%85%A7%E5%A4%8D%E5%8D%B0%E4%BB%B6%E3%80%81%E4%BB%8B%E7%BB%8D%E4%BF%A1%E6%88%96%E6%8E%88%E6%9D%83%E5%A7%94%E6%89%98%E4%B9%A6%EF%BC%88%E6%B3%A8%E6%98%8E%E8%81%94%E7%B3%BB%E4%BA%BA%E7%94%B5%E8%AF%9D%E5%8F%8A%E9%82%AE%E7%AE%B1%EF%BC%89%E6%89%AB%E6%8F%8F%E4%BB%B6%E5%8F%91%E9%80%81%E8%87%B3%E9%82%AE%E7%AE%B11501068472@qq.com%E5%B9%B6%E7%94%B5%E8%AF%9D%E7%A1%AE%E8%AE%A4%E6%8A%A5%E5%90%8D%E3%80%82\">将单位营业执照复印件、介绍信或授权委托书（注明联系人电话及邮箱）扫描件发送至邮箱1501068472@qq.com并电话确认报名。</a></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">4、<strong>评标办法</strong><strong>：</strong><strong>采用“综合评估法”。</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">5、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\">6、招投标监督管理机构及投诉电话：盐城市第一人民医院监督审查室，联系电话0515-66696810。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">招标人：盐城市第一人民医院</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">地址：盐城市人民南路66号</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">联系人：张女士</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">联系电话：0515-66696855</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">招标代理机构：江苏仁禾中衡工程咨询房地产估价有限公司</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">地址：盐城市青年西路8号805室</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">联系人：刘工</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">电话：0515-69057406</p><p>&nbsp;</p><p><br /></p>','','','','','');
