INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3307','4133','1','','','为进一步加强卫生人才队伍建设，更好地选拔优秀适岗人才，优化人员结构，盐城市卫生健康委部分直属事业单位面向社会公开招聘专业技术人员84名。根据《江苏省事业单位公开招聘人员办法》（苏办发﹝2020﹞9号）等规定，现将有关事项公告如下：一、招聘岗位本次招聘相关信','172.16.70.1','172.16.70.1','1712193850','<div style=\"box-sizing: border-box; margin: 15px 0px; padding: 0px; color: rgb(51, 51, 51); text-wrap: wrap; background-color: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em;\"><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">为进一步加强卫生人才队伍建设，更好地选拔优秀适岗人才，优化人员结构，盐城市卫生健康委部分直属事业单位面向社会公开招聘专业技术人员</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">84</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">名。根据《江苏省事业单位公开招聘人员办法》（苏办发﹝</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2020</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">﹞</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">9</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">号）等规定，</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">现将有关事项公告如下：</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">一、招聘岗位</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">本次招聘相关信息在盐城市人力资源和社会保障局网（</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">http://jsychrss.yancheng.gov.cn</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">）、盐城市卫生健康委员会网（</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">http://wsj.yancheng.gov.cn</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">）及各招聘单位网站上发布，具体的招聘单位、招聘岗位、数量、资格条件等详见《盐城市卫生健康委部分直属事业单位面向社会公开招聘卫生类专业技术人员岗位表》（附件</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">1</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">，以下简称《招聘岗位表》）。</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">二、招聘条件</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">（一）具有中华人民共和国国籍，拥护中国共产党的领导和社会主义制度，遵守中华人民共和国宪法和法律；</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">（二）遵纪守法，品行端正，团结同志，廉洁奉公，具有良好的职业道德；</span></p></div><div style=\"box-sizing: border-box; margin: 15px 0px; padding: 0px; color: rgb(51, 51, 51); text-wrap: wrap; background-color: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em;\"><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">（三）身心健康，能适应岗位要求；</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">（四）年龄在</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">35</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">周岁及以下（</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">1988</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">4</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">月</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">5</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">日及以后出生）。对年龄有具体要求的，以《招聘岗位表》中写明的为准，年龄计算方法不变；</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">（五）具备《招聘岗位表》中岗位要求的资格条件：</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">1</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">．</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">资格条件中的“</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2024</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年毕业生”，指在</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2024</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年毕业并已取得学历（学位）证书，且现无工作单位的人员。其中，能够提供《毕业生就业推荐表》（原件）的普通高校毕业生，取得学历（学位）证书的日期可放宽至</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2024</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">12</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">月</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">31</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">日；国（境）外同期毕业人员，取得学历（学位）证书的日期可适当放宽，但须在</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2024</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">12</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">月</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">31</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">日前完成教育部留学服务中心学历认证。</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2022</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年和</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2023</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年普通高校毕业生，若仍未落实工作单位，其档案关系仍保留在原毕业学校，或保留在各级毕业生就业主管部门（毕业生就业指导服务中心）、人才交流服务机构和公共就业服务机构的，以及国（境）外同期毕业且已完成学历认证但仍未落实工作单位的人员，可应聘面向</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2024</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年毕业生岗位。</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">参加基层服务项目的人员，如参加服务项目前无工作经历，服务期满且考核合格后</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年内的，可应聘面向</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2024</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年毕业生岗位。</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">以普通高校应届毕业生应征入伍服义务兵的人员，退役后</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">1</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年内，可应聘面向</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2024</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年毕业生岗位。</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">．《招聘岗位表》中有工作经历要求的，其计算的截止时间为</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2024</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">4</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">月</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">30</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">日；</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">（六）</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">具备报考岗位要求的资格条件（详见附件），其中“专业”条件按《江苏省</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2024</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">年考试录用公务员专业参考目录》设置，专业条件中还包括部分未列入专业参考目录但确有高等学校开设且有事业单位需要的专业。大学本科及以上学历应聘人员须同时具有相应学历和学位证书，且学历学位证书上毕业专业必须与应聘专业相一致。非</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">2024</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">届毕业生报考时应具有学历、学位证书及符合岗位要求的其他条件。</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">专业名称已经调整的专业，如调整前或调整后的专业符合招聘岗位专业要求，由毕业院校（科研机构）出具调整前后的专业为同一专业或专业课程基本一致的书面证明，视为符合招聘岗位的专业要求。</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">其中留学人员的毕业专业，由招聘单位根据招聘公告中明确的专业条件、专业参考目录，及其所学课程、研究方向、论文等材料认定是否符合招聘岗位的专业要求。</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">（七）面向社会招收的普通高校应届毕业生培训对象，住院医师规范化培训合格当年在医疗卫生机构就业的，在招聘中按当年应届毕业生同等对待。对经住培合格的本科学历临床医师，在人员招聘中与临床医学、口腔医学、中医专业学位硕士研究生同等对待（其中住培合格证书中的培训专业原则上应当与招聘岗位的专业或类别要求相一致）。（以下简称“两个同等对待对象”）</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-align: left; text-indent: 2em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">（八）</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">取得祖国大陆普通高校学历的台湾学生和取得祖国大陆承认学历的其他台湾居民应聘时按国家和江苏省的有关规定执行；</span></p><p style=\"box-sizing: border-box; margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; text-ali