INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3315','4141','1','','','为推进我院“红包”专项整治工作，加强线索收集，现向社会公布“红包”专项整治工作举报方式及相关事项。一、举报方式专用电话：19952885069邮箱:ycyyjw@126.com微信号：ycsyyjw（电话受理时间：工作日8:30-11:30,14:30-17:30）二、相关要求（一）投诉举报须提供明确的','172.16.70.1','172.16.70.1','1712650720','<p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">为推进我院“红包”专项整治工作，加强线索收集，现向社会公布“红包”专项整治工作举报方式及相关事项。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">一、举报方式</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">专用电话：19952885069</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">邮箱:ycyyjw@126.com</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">微信号：ycsyyjw</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（电话受理时间：工作日8:30-11:30,14:30-17:30）</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">二、相关要求</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（一）投诉举报须提供明确的时间、地点、人员、具体事项等内容，并附相关旁证材料，以便核查。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（二）鼓励实名举报，举报人对投诉举报内容的真实性负责，不得虚构和夸大事实，不得故意捏造和诬陷他人。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">（三）受理部门对举报人信息、举报内容严格保密。</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\">感谢社会公众对盐城市第一人民医院“红包”专项整治工作的关心支持和协助配合。</p><p style=\"text-indent: 43px;line-height: 37px\">&nbsp;</p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: right; margin-top: 15px; margin-bottom: 15px;\">盐城市第一人民医院</p><p style=\"text-indent: 363px; line-height: 37px; text-align: right;\">&nbsp;</p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3316','4142','1','','<!--#p8_attach#-->/cms/item/2024_04/11_10/fb915b75a941d6cc.jpg.cthumb.jpg','4月15日将迎来第九个全民国家安全教育日今年的主题是“总体国家安全观 创新引领10周年”树立国家安全意识自觉关心、维护国家安全是每个公民的基本义务','172.16.70.1','172.16.70.1','1712801469','<p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: center; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\">4月15日将迎来</p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: center; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"text-indent: 2em;\">第九个全民国家安全教育日</span></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: center; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"text-indent: 2em;\">今年的主题是“总体国家安全观 创新引领10周年”</span></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: center; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"text-indent: 2em;\">树立国家安全意识自觉关心、维护国家安全是每个公民的基本义务</span></p><p style=\"text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2024_04/11_10/fb915b75a941d6cc.jpg.cthumb.jpg\" title=\"58e5711d506ce72f0e4b31577f1eb99.jpg\" src=\"<!--#p8_attach#-->/cms/item/2024_04/11_10/fb915b75a941d6cc.jpg.cthumb.jpg\" alt=\"58e5711d506ce72f0e4b31577f1eb99.jpg\" /></p><p style=\"text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2024_04/11_10/be6f3d9a4946c8dc.jpg.cthumb.jpg\" title=\"94eed4b7ad9b9da77b049597c26e7f6.jpg\" src=\"<!--#p8_attach#-->/cms/item/2024_04/11_10/be6f3d9a4946c8dc.jpg.cthumb.jpg\" alt=\"94eed4b7ad9b9da77b049597c26e7f6.jpg\" /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3317','4143','1','','','一、招标公告第2.1条：“投标人必须是在中国境内注册，有独立完成本项目所有工作的能力的独立法人”，更正为：投标人必须是法人或者其他组织（投标时须提供营业执照）；二、招标公告第2.2条：“项目负责人为本单位的正式职工，并确保从投标截止之日当月向前连续6个月均','172.16.70.1','172.16.70.1','1712884352','<p style=\"text-indent: 2em; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">一、招标公告第2.1条：“投标人必须是在中国境内注册，有独立完成本项目所有工作的能力的独立法人”，更正为：投标人必须是法人或者其他组织（投标时须提供营业执照）；</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">二、招标公告第2.2条：“项目负责人为本单位的正式职工，并确保从投标截止之日当月向前连续6个月均已在本单位缴纳养老保险（例：如开标日期为2023年2月的某一天，则投标人需保证以上人员2022年7月-2023年1月养老保险均在本单位缴纳）（投标时提供社保证明材料）”，更正为：</span><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">如投标人是独立法人的，</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">项目负责人和授权委托人（可为同一人）为本单位的正式职工，并确保从投标截止之日当月向前连续6个月均已在本单位缴纳养老保险（例：如开标日期为2023年2月的某一天，则投标人需保证以上人员2022年7月-2023年1月养老保险均在本单位缴纳）（投标时提供社保证明材料）；</span><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">如投标人是其他组织的，</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">项目负责人须为本组织的法定代表人或经营者（投标时提供项目负责人身份证复印件）。</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">三、招标公告第3.1条：投标报名时间2024年4月7日8时30分至2024年4月12日18时，更正为：投标报名时间延长至2024年4月18日18时。</span></p><p style=\"text-indent: 40px;line-height: 35px\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">&nbsp;</span></p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">盐城市第一人民医院</span></p><p style=\"text-indent: 2em; text-align: right; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; margin-top: 15px; margin-bottom: 15px;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">2024年4月12日</span></p><p><br /></p>','','','','','');
