INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3342','4168','1','','<!--#p8_attach#-->/cms/item/2024_05/28_09/9ab3fbe1cd858356.jpg.thumb.jpg','如今超市的奶制品柜台五花八门，纯牛奶、水牛奶、羊奶、驼奶、酸奶、豆奶……让人眼花缭乱。关于这些奶制品的说法也层出不穷骆驼奶能延长寿命、降血糖？羊奶喝了不过敏？水牛奶更有营养价值？真的是这样吗？奶制品那么多，不同奶制品的营养价值有何不同？不同人群又该如','172.16.70.1','172.16.70.1','1716167965','<p style=\"margin: 15px 0px; padding: 0px; text-align: center; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">如今超市的奶制品柜台五花八门，</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">纯牛奶、水牛奶、羊奶、驼奶、酸奶、豆奶……</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">让人眼花缭乱。</span></p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">关于这些奶制品的说法</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">也层出不穷</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">骆驼奶能延长寿命、降血糖？</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">羊奶喝了不过敏？</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">水牛奶更有营养价值？</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(192, 0, 0); letter-spacing: 0px; background: rgb(255, 255, 255);\">真的是这样吗？</span></strong></p><p style=\"margin-top: 15px; margin-right: 0px; margin-bottom: 15px; text-indent: 2em; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">奶制品那么多，不同奶制品的营养价值有何不同？不同人群又该如何选择呢？今天我们就来一起了解这些奶制品。</span></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph\">&nbsp;</p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">要评估哪种奶更好</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(41, 108, 212); letter-spacing: 0px; background: rgb(255, 255, 255);\">还要看不同奶类营养成分</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"></p><p style=\"text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2024_05/28_09/d070fff1aa1e1fd4.png.cthumb.jpg\" src=\"<!--#p8_attach#-->/cms/item/2024_05/28_09/d070fff1aa1e1fd4.png.cthumb.jpg\" /></p><p>&nbsp;</p><p></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph\">&nbsp;</p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">结合上表中的营养成分</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(41, 108, 212); letter-spacing: 0px; background: rgb(255, 255, 255);\">我们可以将市面上各式各样的奶</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(41, 108, 212); letter-spacing: 0px; background: rgb(255, 255, 255);\">跟纯牛奶做个对比</span></strong></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;background:rgb(255,255,255)\"><span style=\"letter-spacing: 0px;\">&nbsp;</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(41, 108, 212); letter-spacing: 0px; background: rgb(255, 255, 255);\">（1）水牛奶&nbsp;VS&nbsp;纯牛奶</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">优点：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">水牛奶营养密度高，含钙量高。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">缺点：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">饱和脂肪酸和胆固醇含量高，市面上销售的水牛奶多为脱脂水牛奶，脱脂工艺后的水牛奶营养价值不如纯牛奶。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(41, 108, 212); letter-spacing: 0px; background: rgb(255, 255, 255);\">（2）羊奶&nbsp;VS&nbsp;纯牛奶</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">优点：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">羊奶的脂肪球小，易消化，过敏性稍低（主要是由于羊奶中αS1-酪蛋白和β－乳球蛋白含量低于牛奶）。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">缺点：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">一些包装牛奶的脂肪球也经过处理变小，因此，市面上的包装牛奶与羊奶营养成分差距不大，但羊奶的价格较高。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(41, 108, 212); letter-spacing: 0px; background: rgb(255, 255, 255);\">（3）驼奶&nbsp;VS&nbsp;纯牛奶</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">优点：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">驼奶中的不饱和脂肪酸含量高，乳糖含量较低，更适合乳糖不耐受人群。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">缺点</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">：驼奶价格较高，同时，驼奶有一定的病毒、细菌感染风险。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(192, 0, 0); letter-spacing: 0px; background: rgb(255, 255, 255);\">注意：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">驼奶不是“灵丹妙药”，不能治病。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(41, 108, 212); letter-spacing: 0px; background: rgb(255, 255, 255);\">（4）酸奶VS&nbsp;纯牛奶</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">优点：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">酸奶不含乳糖，富含益生菌，可调节肠道菌群。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">缺点：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">市面上的大多酸奶含糖，热量较高。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(41, 108, 212); letter-spacing: 0px; background: rgb(255, 255, 255);\">（5）豆奶&nbsp;VS&nbsp;纯牛奶</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">优点：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">豆奶不含乳糖，植物性蛋白质含量高，含少量大豆异黄酮。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">缺点：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">钙含量低且吸收率低</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">注意：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">事实上，豆奶、燕麦奶、杏仁奶、椰奶等植物奶都为</span><strong><span style=\"color: rgb(192, 0, 0); letter-spacing: 0px; background: rgb(255, 255, 255);\">“植物蛋白饮料”</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">，并不是真正的奶，喝豆奶不如喝豆浆！</span></p><p>&nbsp;</p><p style=\"margin: 15px 0px; text-indent: 2em; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong><span style=\"color: rgb(41, 108, 212); letter-spacing: 0px; background: rgb(255, 255, 255);\">不同人群分别适合喝的奶类</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: center; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(41, 108, 212); letter-spacing: 0px; background: rgb(255, 255, 255);\">也有所不同</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">1. 儿童，健康成年人：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">纯牛奶</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">2. 乳糖不耐受人群：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">舒化奶、酸奶、豆奶</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">3. 血糖高的人群：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">纯牛奶、无糖酸奶</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">4. 血脂高、肥胖人群：</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">低脂牛奶、脱脂牛奶</span></p><p style=\"margin-top: 15px; margin-right: 0px; margin-bottom: 15px; text-indent: 2em; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">此外，有些人认为刚挤出来的奶原汁原味，没有任何添加剂，营养价值最高。但值得注意的是，尽管刚挤出的奶营养物质丰富，但挤奶操作、运输保存不当等过程都容易让细菌迅速繁殖。</span><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">喝了刚挤出来，但没有经过杀菌处理的奶很可能让人感染一些细菌与病毒，比如感染</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">布鲁氏菌病</span><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">等人畜共患病等。</span></strong></p><p style=\"margin-top: 15px; margin-right: 0px; margin-bottom: 15px; text-indent: 2em; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; background: rgb(255, 255, 255);\">让我们一起学习营养知识，合理搭配日常膳食，养成良好饮食习惯，为健康加把力！</span></p><p style=\"margin-top: 15px; margin-right: 0px; margin-bottom: 15px; text-indent: 2em; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; background: rgb(255, 255, 255);\"></span></p><p style=\"padding: 0px; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(136, 136, 136); letter-spacing: 0px; background: rgb(255, 255, 255);\">来源：</span><span style=\"color: rgb(136, 136, 136); letter-spacing: 0px; background: rgb(255, 255, 255);\">公共卫生与预防医学公众号、</span><span style=\"color: rgb(136, 136, 136); letter-spacing: 0px; background: rgb(255, 255, 255);\">广东疾控</span><span style=\"color: rgb(136, 136, 136); letter-spacing: 0px; background: rgb(255, 255, 255);\">、</span><span style=\"color: rgb(136, 136, 136); letter-spacing: 0px; background: rgb(255, 255, 255);\">广州卫健委</span></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(136, 136, 136); letter-spacing: 1px; background: rgb(255, 255, 255);\">版权归作者所有，如有侵权请及时联系，我们将立即删除，谢谢！</span></p><p>&nbsp;</p><p style=\"margin-top: 15px; margin-right: 0px; margin-bottom: 15px; text-indent: 2em; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 0px; background: rgb(255, 255, 255);\"><br /></span><br /></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3343','4169','1','','<!--#p8_attach#-->/cms/item/2024_05/31_09/14492c338fc32141.png.cthumb.jpg','为了进一步提升全科医生的临床能力，增进临床基地与基层实践基地交流协作，推动全科医学教育的发展，盐城市第一人民医院全科基地主任边德志带领教学团队, 于2024年5月16日，同五星街道社区卫生服务中心及大洋街道社区卫生服务中心两家全科基层实践基地召开联合教学会议','172.16.70.1','172.16.70.1','1717119005','<p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">为了进一步提升全科医生的临床能力，增进临床基地与基层实践基地交流协作，推动全科医学教育的发展，盐城市第一人民医院全科基地主任边德志带领教学团队, 于</span><span style=\"line-height: 150%;\">2024年5</span><span style=\"line-height: 150%;\">月16</span><span style=\"line-height: 150%;\">日，</span><span style=\"line-height: 150%;\">同五星街道</span><span style=\"line-height: 150%;\">社区卫生服务中心及</span><span style=\"line-height: 150%;\">大洋街道社区卫生服务中心</span><span style=\"line-height: 150%;\">两家全科基层实践基地</span><span style=\"line-height: 150%;\">召开联合教学会议</span><span style=\"line-height: 150%;\">。</span></p><p style=\"line-height: 150%; text-align: center;\"><span style=\"line-height: 150%;\">&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2024_05/31_09/14492c338fc32141.png.cthumb.jpg\" title=\"图片1.png\" src=\"<!--#p8_attach#-->/cms/item/2024_05/31_09/14492c338fc32141.png.cthumb.jpg\" alt=\"图片1.png\" /></span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">会议期间，盐城市第一人民医院边主任结合最新</span><span style=\"line-height: 150%;\">2023年全科基地评估指标</span><span style=\"line-height: 150%;\">，在</span><span style=\"line-height: 150%;\">建设基层实践基地及优化基层实践基地教学等方面</span><span style=\"line-height: 150%;\">与各基地老师</span><span style=\"line-height: 150%;\">进行了探讨</span><span style=\"line-height: 150%;\">。指出目前基层基地需对标相关指标，重新梳理全科住院医师基层实践基地内容，由主基地带动基层，将基层实践基地建设</span><span style=\"line-height: 150%;\">地</span><span style=\"line-height: 150%;\">更加完善。严格师资遴选，采取竞争上岗模式，并加强师资培训，进一步提升师资水平。</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">盐城市第一人民医院全科教学主任温芬对标国家相关文件及最新基地评估标准详细解读相关要求，</span><span style=\"line-height: 150%;\">就如何系统梳理基地建设及提升带教内涵与五星街道社区卫生服务中心、大洋街道社区卫生服务中心进行交流探讨</span><span style=\"line-height: 150%;\">。</span><span style=\"line-height: 150%;\">带教老师张佩、应天青、张敏、杨建华分别分享了自身带教过程中的体会与思考</span><span style=\"line-height: 150%;\">。</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: center;\"><span style=\"line-height: 150%;\">&nbsp;<img src=\"<!--#p8_attach#-->/cms/item/2024_05/31_09/3c51b0af9da027d3.png.cthumb.jpg\" title=\"图片2.png\" src=\"<!--#p8_attach#-->/cms/item/2024_05/31_09/3c51b0af9da027d3.png.cthumb.jpg\" alt=\"图片2.png\" /></span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">在联合教学活动环节中，盐城市第一人民医院赵雪主任以“高脂血症”为例为学员指导门诊接诊</span><span style=\"line-height: 150%;\">，结束后基层老师进行点评及学习</span><span style=\"line-height: 150%;\">。</span><span style=\"line-height: 150%;\">本次联合教学活动</span><span style=\"line-height: 150%;\">展示了全科接诊的重要性、全面性、人文性，</span><span style=\"line-height: 150%;\">培养了学员的全科临床思维及医患沟通能力。</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">在基层师资培训活动环节中，王超楠秘书从组织安排、准备、实施及总结四个方面汇报病例讨论规范。随后温芬主任以“横纹肌溶解综合征”为例激发学员探索诊疗思路，循循善诱，加深了学员对疾病的认识。教学相长，给在座师生展示了一堂高质量病例讨论课程。</span></p><p style=\"text-indent: 2em; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"line-height: 150%;\">此次联合教学会议的召开，拓宽了盐城市第一人民医院全科实践基地与基层实践基地的交流协作之路，激发了全科医学教育的发展活力。“千里之行，始于足下”，未来，盐城市第一人民医院全科实践基地将一如既往地致力于全科医学教育的发展，以更高的标准、更严的要求、更优的教学，培养更多优秀全科医生。</span></p><p><br /></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` (`id`,`iid`,`page`,`addon_title`,`addon_frame`,`addon_summary`,`ip`,`last_update_ip`,`timestamp`,`content`,`custom_f`,`custom_g`,`custom_h`,`custom_i`,`custom_j`) VALUES ('3344','4170','1','','<!--#p8_attach#-->/cms/item/2024_06/01_17/fac68d7512a64f4f.png.cthumb.jpg','','172.16.70.1','172.16.70.1','1717234390','<p style=\"text-align: center;\"><img src=\"<!--#p8_attach#-->/cms/item/2024_06/01_17/fac68d7512a64f4f.png.cthumb.jpg\" title=\"4.png\" src=\"<!--#p8_attach#-->/cms/item/2024_06/01_17/fac68d7512a64f4f.png.cthumb.jpg\" alt=\"4.png\" /></p>','','','','','');
