INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('9965','9','cms','item','add','0','856','','宣传处','添加内容 | 这样喝水将亲手“毁掉”肾！这三大饮水习惯，看看你中招了没？','/admin.php/cms/item-add','172.16.70.1','array (\n  \'__referer\' => \'\',\n  \'ajax_check_contnet\' => \'1\',\n  \'cid\' => \'856\',\n  \'title\' => \'这样喝水将亲手“毁掉”肾！这三大饮水习惯，看看你中招了没？\',\n  \'timestamp\' => \'\',\n  \'list_order\' => \'\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'author_x\' => \'\',\n  \'author_y\' => \'\',\n  \'author_z\' => \'\',\n  \'editer\' => \'\',\n  \'source\' => \'公共卫生处\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'url\' => \'\',\n  \'views\' => \'\',\n  \'crawByUrl\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'#field_starttime_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'starttime\' => \'\',\n    \'endtime\' => \'\',\n    \'content\' => \'<p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">“毁肾”的三大饮水习惯</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">01 口渴再喝水</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">水喝很少不一定会口渴，但感到口渴时</span><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">身体细胞已经脱水，是中枢神经发出的求救信号。长期如此，肾脏排毒能力会大大降低，导致肾病发生。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">02 饮料当水喝</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">常年把饮料当水喝，饮料中的糖、磷酸盐会促进人体钙流失，尿液中钙含量随之增多，继而形成肾结石。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">03 长期喝浓茶</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">过浓的茶对身体不好，不仅伤胃，也不利于肾脏健康。</span></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">关于喝水的5大常识</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">01每天应该喝1500~1700ml水</span></strong></p><p style=\"margin-top: 15px; margin-right: 0px; margin-bottom: 15px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">根据《中国居民膳食指南（2022）》建议，</span><span style=\"color: rgb(25, 27, 31); letter-spacing: 1px; background: rgb(255, 255, 255);\">在温和气候条件下，</span><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">成年女性</span><span style=\"color: rgb(25, 27, 31); letter-spacing: 1px; background: rgb(255, 255, 255);\">轻体力活动水平每天需要喝</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">1500ml</span></strong><span style=\"color: rgb(25, 27, 31); letter-spacing: 1px; background: rgb(255, 255, 255);\">，</span><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">成年男性</span><span style=\"color: rgb(25, 27, 31); letter-spacing: 1px; background: rgb(255, 255, 255);\">每天需要喝</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">1700ml</span></strong><span style=\"color: rgb(43, 43, 43); letter-spacing: 1px; background: rgb(255, 255, 255);\">，</span><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">建议喝水一次喝</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">200~300ml</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin-top: 15px; margin-right: 0px; margin-bottom: 15px; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left;\"><span style=\"letter-spacing: 0px;\">但是，</span><span style=\"letter-spacing: 1px;\">水也不是喝的越多越好，过多的摄入容易造成肾脏的额外压力或发生水中毒。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">02喝水的最佳水温在10~40℃</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">《中国居民膳食指南（2022）》建议饮水的适宜温度在</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">10~40℃，接近于凉白开</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">刚出锅的汤、刚泡好的咖啡/茶饮，温度为80~95℃，</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">不宜直接饮用哦</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。世界卫生组织、国际癌症机构都曾经提示，饮用65℃以上热饮，会烫伤食管黏膜、增加患食管癌的风险。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">而饮用温度过低的水会造成肠胃血管突然收缩，使供给肠胃的血液突然减少，</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">并可能会导致</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">胃痉挛</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">、腹痛、腹泻等情况</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">03含糖饮料不可以代替饮用水</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(33, 33, 34); letter-spacing: 1px; background: rgb(255, 255, 255);\">过多摄入含糖饮料</span></strong><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">可增加龋齿、超重肥胖、2型糖尿病、血脂异常的发病风险。</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">因此，建议用白水或淡茶水替代含糖饮料。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">有些人</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">（尤其是儿童）</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">不喜欢喝没有味道的水，可以在水中加入1~2片新鲜柠檬片、3~4片薄荷叶等增加水的色彩和味道。也可以自制一些传统饮品，如绿豆汤、酸梅汤等，</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">注意不要添加糖</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">04不建议长期只喝纯净水</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 1px; background: rgb(255, 255, 255);\">纯净水指的是不含有杂质或细菌的水，是以符合生活饮用水卫生标准的水为原水。虽然</span><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">去除了水中有害物质，但同时，</span><strong><span style=\"color: rgb(33, 33, 34); letter-spacing: 1px; background: rgb(255, 255, 255);\">也去除了对人体有益的矿物质和微量元素。</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">不论饮食结构如何丰富，</span><strong><span style=\"color: rgb(33, 33, 34); letter-spacing: 1px; background: rgb(255, 255, 255);\">人体都需要从饮用水中摄取一部分矿物元素</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。如果只喝纯净水，尤其对于膳食中缺乏矿物质的人，会有各种潜在的健康风险。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">有研究显示，纯净水中钙、镁含量较低，长期饮用可能会</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">增加心血管疾病风险，亦可能对骨质健康有不利影响</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">05桶（瓶）装水可能不卫生</span></strong></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:25px\"><span style=\"letter-spacing: 0px;\">&nbsp;</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">与自来水相比，桶（瓶）装水被认为干净、安全。然而，我国某地质量监督部门抽检204批次桶（瓶）装饮用水产品，</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">不合格率为14.22%</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">因此，在选购和使用桶（瓶）装饮用水时应注意以下几点：</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(33, 33, 34); letter-spacing: 1px; background: rgb(255, 255, 255);\">①</span></strong><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">千万不要购买无证产品</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">②</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">应尽量在最短时间内喝完，开封后放置时间太长易滋生细菌；人数少的家庭可选用容量较小的桶（瓶）装水，</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">缩短饮用时间</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">，以保证安全卫生。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">③</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">桶装水如配合饮水机使用时，要注意</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">仔细清洗饮水机内胆</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">正确喝水的方式</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">01 先含一口，浸润口腔后再喝下去。</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">这样可利用生物回馈机制传达口渴中枢，让身体细胞知道有水分要进入身体，以便充分吸收。同时，还能</span><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">避免吞入过多的空气而导致胀气，也不会因为过冷或过热而刺激肠道</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">02 小口慢饮，从容不迫。</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">体内就像火炉，所以需要慢慢地喝，让身体能</span><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">有充分时间进行新陈代谢。</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">03 喝温水，更止渴。</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">35℃-40℃的温水是最佳的选择</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">。入口的水温与人的体温接近，身体不需额外反应就能完成较佳的生物回馈反应给口渴中枢，所以比较有助于身体吸收利用，从而更解渴。</span></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">正确喝水的时间表</span></strong></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph\">&nbsp;</p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">6:30</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">经过一夜的睡眠，身体会开始缺水，起床后可以先喝一小杯水，促进排便。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">8:30</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">早上的时间一般比较紧凑，情绪比较紧张，可以喝杯水缓解一下，防止出现脱水现象。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">11:00</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">经过一上午的工作，可以在这个时候起身活动下，顺便喝杯水，补充流失的水分。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">12:50</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">吃完午饭的半小时后，可以喝一杯水，加强身体的消化功能。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">15:00</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">经过一天的学习工作，下午3点属于困乏期，可以喝一杯水提神醒脑，恢复清醒，继续学习。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">17:30</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">下班前可以喝一杯水，补充水分，同时可以缓解饥饿感，等待迎接晚餐的到来。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">睡前半小时</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">睡前1至半小时可以喝点水但不宜过多，以免晚上频繁起夜影响睡眠质量。</span></p><p style=\"padding: 0px; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(136, 136, 136); letter-spacing: 0px; background: rgb(255, 255, 255);\">来源：</span><span style=\"color: rgb(136, 136, 136); letter-spacing: 0px; background: rgb(255, 255, 255);\">公共卫生与预防医学公众号、</span><span style=\"color: rgb(136, 136, 136); letter-spacing: 0px; background: rgb(255, 255, 255);\">广东疾控、有来医生</span></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(136, 136, 136); letter-spacing: 1px; background: rgb(255, 255, 255);\">版权归作者所有，如有侵权请及时联系，我们将立即删除，谢谢！</span></p><p>&nbsp;</p><p><br/></p>\',\n  ),\n  \'#field_endtime_posted\' => \'\',\n  \'addon_title\' => \'\',\n  \'verify\' => 1,\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'custom_a\' => \'\',\n  \'level_time\' => \'权重有效期\',\n  \'level\' => \'\',\n  \'template\' => \'\',\n  \'attachment_pdf\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'title_color\' => \'\',\n  \'keywords\' => \'\',\n  \'drafts_release\' => \'0\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'10d114adfa22e029\',\n  \'html\' => \'1\',\n  \'verifier\' => \'宣传处\',\n)','1724466734');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('9966','9','cms','item','add','0','856','','宣传处','添加内容 | 这样喝水将亲手“毁掉”肾！这三大饮水习惯，看看你中招了没？','/admin.php/cms/item-add','172.16.70.1','array (\n  \'__referer\' => \'\',\n  \'cid\' => \'856\',\n  \'title\' => \'这样喝水将亲手“毁掉”肾！这三大饮水习惯，看看你中招了没？\',\n  \'timestamp\' => \'\',\n  \'list_order\' => \'\',\n  \'sub_title\' => \'\',\n  \'author\' => \'\',\n  \'author_x\' => \'\',\n  \'author_y\' => \'\',\n  \'author_z\' => \'\',\n  \'editer\' => \'\',\n  \'source\' => \'公共卫生处\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'url\' => \'\',\n  \'views\' => \'\',\n  \'crawByUrl\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'#field_starttime_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'starttime\' => \'\',\n    \'endtime\' => \'\',\n    \'content\' => \'<p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">“毁肾”的三大饮水习惯</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">01 口渴再喝水</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">水喝很少不一定会口渴，但感到口渴时</span><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">身体细胞已经脱水，是中枢神经发出的求救信号。长期如此，肾脏排毒能力会大大降低，导致肾病发生。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">02 饮料当水喝</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">常年把饮料当水喝，饮料中的糖、磷酸盐会促进人体钙流失，尿液中钙含量随之增多，继而形成肾结石。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">03 长期喝浓茶</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">过浓的茶对身体不好，不仅伤胃，也不利于肾脏健康。</span></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">关于喝水的5大常识</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">01每天应该喝1500~1700ml水</span></strong></p><p style=\"margin-top: 15px; margin-right: 0px; margin-bottom: 15px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">根据《中国居民膳食指南（2022）》建议，</span><span style=\"color: rgb(25, 27, 31); letter-spacing: 1px; background: rgb(255, 255, 255);\">在温和气候条件下，</span><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">成年女性</span><span style=\"color: rgb(25, 27, 31); letter-spacing: 1px; background: rgb(255, 255, 255);\">轻体力活动水平每天需要喝</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">1500ml</span></strong><span style=\"color: rgb(25, 27, 31); letter-spacing: 1px; background: rgb(255, 255, 255);\">，</span><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">成年男性</span><span style=\"color: rgb(25, 27, 31); letter-spacing: 1px; background: rgb(255, 255, 255);\">每天需要喝</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">1700ml</span></strong><span style=\"color: rgb(43, 43, 43); letter-spacing: 1px; background: rgb(255, 255, 255);\">，</span><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">建议喝水一次喝</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">200~300ml</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin-top: 15px; margin-right: 0px; margin-bottom: 15px; padding: 0px; line-height: 2em; background: rgb(255, 255, 255); font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left;\"><span style=\"letter-spacing: 0px;\">但是，</span><span style=\"letter-spacing: 1px;\">水也不是喝的越多越好，过多的摄入容易造成肾脏的额外压力或发生水中毒。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">02喝水的最佳水温在10~40℃</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">《中国居民膳食指南（2022）》建议饮水的适宜温度在</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">10~40℃，接近于凉白开</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">刚出锅的汤、刚泡好的咖啡/茶饮，温度为80~95℃，</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">不宜直接饮用哦</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。世界卫生组织、国际癌症机构都曾经提示，饮用65℃以上热饮，会烫伤食管黏膜、增加患食管癌的风险。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">而饮用温度过低的水会造成肠胃血管突然收缩，使供给肠胃的血液突然减少，</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">并可能会导致</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">胃痉挛</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">、腹痛、腹泻等情况</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">03含糖饮料不可以代替饮用水</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(33, 33, 34); letter-spacing: 1px; background: rgb(255, 255, 255);\">过多摄入含糖饮料</span></strong><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">可增加龋齿、超重肥胖、2型糖尿病、血脂异常的发病风险。</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">因此，建议用白水或淡茶水替代含糖饮料。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">有些人</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">（尤其是儿童）</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">不喜欢喝没有味道的水，可以在水中加入1~2片新鲜柠檬片、3~4片薄荷叶等增加水的色彩和味道。也可以自制一些传统饮品，如绿豆汤、酸梅汤等，</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">注意不要添加糖</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">04不建议长期只喝纯净水</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"color: rgb(51, 51, 51); letter-spacing: 1px; background: rgb(255, 255, 255);\">纯净水指的是不含有杂质或细菌的水，是以符合生活饮用水卫生标准的水为原水。虽然</span><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">去除了水中有害物质，但同时，</span><strong><span style=\"color: rgb(33, 33, 34); letter-spacing: 1px; background: rgb(255, 255, 255);\">也去除了对人体有益的矿物质和微量元素。</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">不论饮食结构如何丰富，</span><strong><span style=\"color: rgb(33, 33, 34); letter-spacing: 1px; background: rgb(255, 255, 255);\">人体都需要从饮用水中摄取一部分矿物元素</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。如果只喝纯净水，尤其对于膳食中缺乏矿物质的人，会有各种潜在的健康风险。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">有研究显示，纯净水中钙、镁含量较低，长期饮用可能会</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">增加心血管疾病风险，亦可能对骨质健康有不利影响</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">05桶（瓶）装水可能不卫生</span></strong></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph;line-height:25px\"><span style=\"letter-spacing: 0px;\">&nbsp;</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">与自来水相比，桶（瓶）装水被认为干净、安全。然而，我国某地质量监督部门抽检204批次桶（瓶）装饮用水产品，</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">不合格率为14.22%</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">因此，在选购和使用桶（瓶）装饮用水时应注意以下几点：</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"color: rgb(33, 33, 34); letter-spacing: 1px; background: rgb(255, 255, 255);\">①</span></strong><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">千万不要购买无证产品</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">②</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">应尽量在最短时间内喝完，开封后放置时间太长易滋生细菌；人数少的家庭可选用容量较小的桶（瓶）装水，</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">缩短饮用时间</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">，以保证安全卫生。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\"><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">③</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">桶装水如配合饮水机使用时，要注意</span><strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">仔细清洗饮水机内胆</span></strong><span style=\"letter-spacing: 1px; background: rgb(255, 255, 255);\">。</span></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">正确喝水的方式</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">01 先含一口，浸润口腔后再喝下去。</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">这样可利用生物回馈机制传达口渴中枢，让身体细胞知道有水分要进入身体，以便充分吸收。同时，还能</span><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">避免吞入过多的空气而导致胀气，也不会因为过冷或过热而刺激肠道</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">02 小口慢饮，从容不迫。</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">体内就像火炉，所以需要慢慢地喝，让身体能</span><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">有充分时间进行新陈代谢。</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">03 喝温水，更止渴。</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">35℃-40℃的温水是最佳的选择</span></strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">。入口的水温与人的体温接近，身体不需额外反应就能完成较佳的生物回馈反应给口渴中枢，所以比较有助于身体吸收利用，从而更解渴。</span></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><strong><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">正确喝水的时间表</span></strong></p><p style=\"margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;padding:0 0 0 0 ;text-align:justify;text-justify:inter-ideograph\">&nbsp;</p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">6:30</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">经过一夜的睡眠，身体会开始缺水，起床后可以先喝一小杯水，促进排便。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">8:30</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">早上的时间一般比较紧凑，情绪比较紧张，可以喝杯水缓解一下，防止出现脱水现象。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">11:00</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">经过一上午的工作，可以在这个时候起身活动下，顺便喝杯水，补充流失的水分。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">12:50</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">吃完午饭的半小时后，可以喝一杯水，加强身体的消化功能。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">15:00</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">经过一天的学习工作，下午3点属于困乏期，可以喝一杯水提神醒脑，恢复清醒，继续学习。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">17:30</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">下班前可以喝一杯水，补充水分，同时可以缓解饥饿感，等待迎接晚餐的到来。</span></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><strong><span style=\"color: rgb(249, 110, 87); letter-spacing: 0px; background: rgb(255, 255, 255);\">睡前半小时</span></strong></p><p style=\"margin: 15px 0px; padding: 0px; text-align: left; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; line-height: 2em;\"><span style=\"letter-spacing: 0px; background: rgb(255, 255, 255);\">睡前1至半小时可以喝点水但不宜过多，以免晚上频繁起夜影响睡眠质量。</span></p><p style=\"padding: 0px; line-height: 2em; font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(136, 136, 136); letter-spacing: 0px; background: rgb(255, 255, 255);\">来源：</span><span style=\"color: rgb(136, 136, 136); letter-spacing: 0px; background: rgb(255, 255, 255);\">公共卫生与预防医学公众号、</span><span style=\"color: rgb(136, 136, 136); letter-spacing: 0px; background: rgb(255, 255, 255);\">广东疾控、有来医生</span></p><p style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em; text-align: left; line-height: 2em; margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(136, 136, 136); letter-spacing: 1px; background: rgb(255, 255, 255);\">版权归作者所有，如有侵权请及时联系，我们将立即删除，谢谢！</span></p><p>&nbsp;</p><p><br/></p>\',\n  ),\n  \'#field_endtime_posted\' => \'\',\n  \'addon_title\' => \'\',\n  \'verify\' => 1,\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'custom_a\' => \'\',\n  \'level_time\' => \'权重有效期\',\n  \'level\' => \'\',\n  \'template\' => \'\',\n  \'attachment_pdf\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'area_ip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'title_color\' => \'\',\n  \'keywords\' => \'\',\n  \'drafts_release\' => \'0\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\'