INSERT INTO `p8_cms_item_article_` (`id`,`model`,`cid`,`uid`,`username`,`title`,`title_color`,`title_bold`,`sub_title`,`frame`,`verify_frame`,`attachment_pdf`,`url`,`attributes`,`summary`,`source`,`author`,`author_x`,`author_y`,`author_z`,`authority`,`editer`,`keywords`,`verified`,`verifier`,`verify_time`,`timestamp`,`create_time`,`list_order`,`update_time`,`pages`,`html_view_url_rule`,`template`,`views`,`score`,`level`,`level_time`,`comments`,`seo_keywords`,`seo_description`,`label_postfix`,`config`,`custom_a`,`custom_b`,`custom_c`,`custom_d`,`custom_e`,`starttime`,`endtime`) VALUES ('2011','article','939','3','admin789',' 许丹','','0','主任医师','<!--#p8_attach#-->/cms/item/2021_07/13_17/52cd314583f21078.jpg','','','','6','女，产科副主任，主任医师，博士研究生。擅长羊水栓塞、产后出血、产褥感染、子痫、前置胎盘、宫内缺氧、胎盘早剥、早产等产科疑难危重疾病的诊治及手术技术，熟练进行各种复杂剖宫产，产钳助产，经阴道宫颈环扎手术，能够采取子宫动脉结扎，宫颈内口提拉缝合，子宫捆绑缝合等技术保留产后大出血的子宫手术，在产科危重症、产科合并症及并发症、胎儿宫内发育、遗传咨询，孕期营养和体重管理等方面均有深入的临床及基础研究。','','','','','','','','','1','admin789','0','1626167971','1626167971','1626167971','1713429174','1','','','4','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:4:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"area_ip\\\";a:0:{}s:9:\\\"ruleoutip\\\";a:0:{}}}','羊水栓塞、产后出血、产褥感染、子痫、前置胎盘、宫内缺氧、胎盘早剥、早产等产科疑难危重疾病的诊治及手术技术，熟练进行各种复杂剖宫产，产钳助产，经阴道宫颈环扎手术，能够采取子宫动脉结扎，宫颈内口提拉缝合，子宫捆绑缝合等技术保留产后大出血的子宫手术，在产科危重症、产科合并症及并发症、胎儿宫内发育、遗传咨询，孕期营养和体重管理等方面均有深入的临床及基础研究。','','','','','','');
